INSERT INTO texts VALUES(22638495,'Dinoster Power, the Mighty Dracoslayer',replace(replace('1 "Dracoslayer" Pendulum Monster + 1 Pendulum Monster\r\nMust first be Special Summoned (from your Extra Deck) by Tributing the above cards. (You do not use "Polymerization".) Pendulum Monster Cards in your Monster Zones and Pendulum Zones cannot be destroyed by battle or your opponent''s card effects. Once per turn: You can Special Summon 1 "Dracoslayer" Pendulum Monster from your hand or Graveyard, but it cannot be used as a Fusion Material.','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
