INSERT INTO texts VALUES(35514096,'Lord British Space Fighter',replace('If this card destroys an opponent''s monster by battle, select and activate 1 of these effects:\n● You can attack once again in a row. \n● Select and destroy 1 Set card on the field. \n● Special Summon 1 "Multi Token" (Machine-Type/LIGHT/Level 4/ATK 1200/DEF 1200).','\n',char(10)),'Select effect','Attack again','Select and destroy 1 Set card on the field. ','Special Summon 1 "Multi Token" (Machine-Type/LIGHT/Level 4/ATK 1200/DEF 1200).','','','','','','','','','','','','');
