INSERT INTO texts VALUES(27551,'限制苏生','从自己墓地选择1只攻击力1000以下的怪兽，攻击表示特殊召唤。那只怪兽变成守备表示时，那只怪兽和这张卡破坏。这张卡从场上离开时，那只怪兽破坏。那只怪兽破坏时这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(32864,'第13人的埋葬者','原本没有任何人的一零三号墓地突然出现的丧尸。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35699,'秘旋谍-龙卷风',replace(replace('这张卡不能通常召唤。把自己墓地3张「秘旋谍」卡除外的场合才能特殊召唤。\r\n①：1回合1次，以自己场上1张「秘旋谍」卡和对方场上最多2张卡为对象才能发动。那些卡破坏。这个效果在对方回合也能发动。\r\n②：场上的这张卡被战斗·效果破坏送去墓地的场合发动。自己场上的卡全部破坏，从自己的手卡·卡组·墓地选1只「秘旋谍-花公子」特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41546,'DD 魔导贤者 托马斯',replace(replace('←6 【灵摆】 6→\r\n「DD 魔导贤者 托马斯」的灵摆效果1回合只能使用1次。\r\n①：自己主要阶段才能发动。从自己的额外卡组把1只表侧表示的「DD」灵摆怪兽加入手卡。\r\n【怪兽效果】\r\n「DD 魔导贤者 托马斯」的怪兽效果1回合只能使用1次。\r\n①：以自己的灵摆区域1张「DD」卡为对象才能发动。那张卡破坏，从卡组把1只8星的「DDD」怪兽守备表示特殊召唤。这个回合，这个效果特殊召唤的怪兽的效果无效化，对方受到的战斗伤害变成一半。','\r',char(13)),'\n',char(10)),'加入手卡','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(41777,'宝石优化','把自己场上存在的1只名字带有「宝石骑士」的怪兽解放，选择自己墓地存在的1只名字带有「宝石骑士」的怪兽发动。选择的怪兽从墓地特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(50755,'魔术师的法阵','①：自己或者对方的魔法师族怪兽的攻击宣言时才能发动。双方玩家各自从自己卡组把1只攻击力2000以下的魔法师族怪兽攻击表示特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(56889,'竞斗-交叉次元',replace(replace('①：以自己场上1只「古代的机械」怪兽为对象才能发动。那只怪兽除外。这个效果除外的怪兽在下次的准备阶段回到场上，那个攻击力直到那个回合的结束时变成原本攻击力的2倍。\r\n②：自己场上的「古代的机械巨人」或者「古代的机械巨人-究极重击」被战斗·效果破坏的场合，可以作为代替把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(62121,'暗晦之城','反转：场上表侧表示的全部不死族的怪兽攻击力·守备力加200。只要这张卡在场上表侧表示存在，自己的每个准备阶段不死族的攻守力都会加200。这个效果持续到自己的第4个准备阶段。','反转','攻击上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(96540,'下拍',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：把自己场上1只表侧表示怪兽解放才能发动。和解放的怪兽是原本的种族·属性相同而原本等级低1星的1只怪兽从卡组特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(98905,'光波分光','①：自己场上的持有超量素材的「光波」超量怪兽被战斗或者对方的效果破坏送去自己墓地的场合，以那1只怪兽为对象才能发动。那只怪兽从墓地特殊召唤，把1只和那只怪兽同名的超量怪兽从额外卡组特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(102380,'溶岩魔神',replace(replace('这张卡不能通常召唤。把对方场上2只怪兽解放的场合可以在对方场上特殊召唤。把这张卡特殊召唤的回合，自己不能通常召唤。\r\n①：自己准备阶段发动。自己受到1000伤害。','\r',char(13)),'\n',char(10)),'特殊召唤','1000伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(111280,'黑魔导强化',replace(replace('①：双方的场上·墓地的「黑魔术师」「黑魔术少女」数量的以下效果适用。\r\n●1只以上：选场上1只魔法师族·暗属性怪兽，那个攻击力直到回合结束时上升1000。\r\n●2只以上：这个回合，对方不能对应自己的魔法·陷阱卡的效果的发动把魔法·陷阱·怪兽的效果发动，自己场上的魔法·陷阱卡不会被对方的效果破坏。\r\n●3只以上：自己场上的魔法师族·暗属性怪兽直到回合结束时不受对方的效果影响。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(114932,'板块轰击者','把自己场上存在的1张表侧表示的永续魔法或者永续陷阱卡送去墓地。给与对方基本分500分伤害。这个效果1回合可以使用最多2次。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(122520,'娱乐伙伴 天空徒弟',replace(replace('「娱乐伙伴 天空徒弟」的①的效果1回合只能使用1次。\r\n①：让自己场上1只5星以上的「娱乐伙伴」怪兽回到持有者手卡才能发动。这张卡从手卡特殊召唤。这个效果在对方回合也能发动。\r\n②：这张卡和对方怪兽进行战斗的场合，直到伤害步骤结束时那只怪兽的效果无效化。\r\n③：自己场上有其他的「娱乐伙伴」怪兽存在的场合，这张卡向对方怪兽攻击的伤害计算前才能发动。那只对方怪兽破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(123709,'熔岩枪杖手','这张卡可以不用解放作召唤。这个方法召唤的这张卡在结束阶段时送去墓地。场上存在的这张卡被破坏送去墓地时，可以选择从游戏中除外的1只自己的炎属性怪兽加入手卡。','不用解放召唤','这张卡送去墓地','加入手卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(126218,'恶魔的骰子','①：掷1次骰子。对方场上的怪兽的攻击力·守备力直到回合结束时下降出现的数目×100。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(128454,'娱乐伙伴 弹簧鹅',replace(replace('「娱乐伙伴 弹簧鹅」的效果1回合只能使用1次。\r\n①：自己主要阶段把墓地的这张卡除外，从自己的灵摆区域的「魔术师」卡、「娱乐伙伴」卡以及自己场上的灵摆怪兽之中以2张为对象才能发动。那2张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'回到手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(131182,'奇迹反转士','「奇迹反转士」在自己场上存在的场合，这张卡不能召唤·反转召唤·特殊召唤。只要这张卡在场上表侧表示存在，对方不能选择其他的表侧表示的怪兽作为攻击对象。这张卡被战斗破坏的场合，这张卡在对方场上特殊召唤。这张卡被魔法·陷阱的效果破坏的场合，把对方场上1只怪兽破坏。','特殊召唤','对方场上1只怪兽破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(135598,'钥匙鼠','这张卡被战斗破坏送去墓地时，可以从自己卡组把1只3星以下的兽族怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(164710,'小人的恶作剧',replace(replace('①：这个回合，双方手卡的怪兽的等级下降1星。\r\n②：把墓地的这张卡除外才能发动。这个回合，双方手卡的怪兽的等级下降1星。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(168917,'哈普托大日','1回合1次，可以选择当作装备卡使用在自己场上存在的1张名字带有「大日」的怪兽卡表侧守备表示特殊召唤。这个效果特殊召唤的怪兽从场上离开的场合从游戏中除外。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(176392,'核成试验台','场上表侧表示存在的名字带有「核成」的怪兽在结束阶段时被破坏的场合，可以作为代替把这张卡破坏。此外，场上表侧表示存在的名字带有「核成」的怪兽在结束阶段时被破坏时，可以在自己场上把1只「核成衍生物」（岩石族·地·4星·攻/守1800）特殊召唤。','是否要使用「核成试验台」的效果代替破坏？','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(176393,'核成衍生物','由「核成试验台」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(191749,'英雄一闪！！','把自己墓地的「H-火热之心」「E-紧急呼唤」「R-正义审判」「O-超越灵魂」从游戏中除外发动。从自己卡组特殊召唤1只名字带有「元素英雄」的通常怪兽。这个回合自己场上的名字带有「元素英雄」的通常怪兽可以直接攻击对方玩家。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(209710,'滑动触控王冠人',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：自己场上有这张卡以外的电子界族怪兽2只以上存在，自己手卡是0张的场合，支付1000基本分才能发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(213326,'E-紧急呼唤','①：从卡组把1只「元素英雄」怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(218704,'芬里尔','这张卡不能进行通常召唤。从自己墓地里除外2只水属性怪兽进行特殊召唤。这张卡战斗破坏对方怪兽时，略过对方的下一个抽卡阶段。','跳过抽卡阶段','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(242146,'破坏城壁的大枪','装备怪兽攻击里侧守备怪兽的场合，装备怪兽的攻击力上升1500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(255998,'疾速战士','主要阶段1才能发动。这个回合这张卡可以直接攻击对方玩家。这个效果发动的回合，这张卡以外的怪兽不能攻击。','直接攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(259314,'甲虫装机的手甲','发动后这张卡变成守备力上升1000的装备卡，给自己场上1只名字带有「甲虫装机」的怪兽装备。装备怪兽不会被对方的卡的效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(269012,'缚神冢',replace(replace('①：场上的10星以上的怪兽不会成为效果的对象，不会被效果破坏。\r\n②：场上的10星以上的怪兽战斗破坏怪兽送去墓地的场合发动。破坏的怪兽的控制者受到1000伤害。\r\n③：场上的这张卡被效果破坏送去墓地时才能发动。从卡组把1只神属性怪兽加入手卡。','\r',char(13)),'\n',char(10)),'1000伤害','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(269510,'天火的牢狱',replace(replace('①：场上的龙族怪兽的守备力上升300。\r\n②：双方不能把连接标记数量比场上的连接怪兽少的连接怪兽连接召唤，连接怪兽以外的怪兽不能攻击。\r\n③：场上有电子界族连接怪兽2只以上存在的场合，以下效果适用。\r\n●电子界族怪兽发动的效果无效化。\r\n●场上的电子界族怪兽不能攻击，不会成为攻击对象，也不会成为效果的对象。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(276357,'灵魂的圆环',replace(replace('「灵魂的圆环」的①②的效果1回合各能使用1次。\r\n①：这张卡在魔法与陷阱区域存在，自己场上的表侧表示的灵魂怪兽回到自己手卡的场合，以对方场上1张卡为对象才能发动。那张卡破坏。\r\n②：对方怪兽的攻击宣言时把自己墓地1只灵魂怪兽除外才能发动。那次攻击无效，那之后战斗阶段结束。','\r',char(13)),'\n',char(10)),'卡片破坏','攻击无效','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(282886,'水精鳞-深渊象鼻鱼兵','这张卡战斗破坏对方怪兽送去墓地时，把手卡1只水属性怪兽丢弃去墓地才能发动。从卡组把1只名字带有「水精鳞」的怪兽表侧守备表示特殊召唤。「水精鳞-深渊象鼻鱼兵」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(286392,'喷气战士',replace(replace('「喷气同调士」＋调整以外的怪兽1只以上\r\n「喷气战士」的①②的效果1回合各能使用1次。\r\n①：这张卡同调召唤成功的场合，以对方场上1张卡为对象才能发动。那张卡回到持有者手卡。\r\n②：这张卡在墓地存在的场合，把自己场上1只2星以下的怪兽解放才能发动。这张卡从墓地守备表示特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'弹回手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(293542,'科技属 狼人','4星以下的怪兽特殊召唤成功时，这张卡可以从手卡特殊召唤。场上存在的这张卡被破坏送去墓地的回合的结束阶段时，可以从自己卡组把「科技属 狼人」以外的1只名字带有「科技属」的怪兽加入手卡。','特殊召唤','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(295517,'传说之都 亚特兰蒂斯',replace(replace('这个卡名在规则上当作「海」使用。\r\n①：场上的水属性怪兽的攻击力·守备力上升200。\r\n②：只要这张卡在场地区域存在，双方的手卡·场上的水属性怪兽的等级下降1星。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(296499,'一族之规','发动时宣言1个种族。那个种族的怪兽不能攻击宣言。每次自己的准备阶段若不把1只怪兽作为祭品这张卡破坏。','是否要解放一只怪兽维持「一族之规」？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(303660,'电脑增幅器','「人造人-念力震慑者」才能装备。只要装备着这张卡，装备怪兽的控制者不会因装备怪兽的效果使陷阱卡的效果无效化。这张卡从场上离开时，装备怪兽破坏。这张卡的发动与效果不能被无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(313513,'魔法齿车','①：把自己场上3张表侧表示的「古代的机械」卡送去墓地才能发动。从手卡以及卡组各把最多1只「古代的机械巨人」无视召唤条件特殊召唤。那之后，自己场上有「古代的机械巨人」以外的怪兽存在的场合，那些怪兽全部破坏。这个效果的发动后，用自己回合计算的2回合内，自己不能通常召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(336369,'薰风松鼠','这张卡被卡的效果破坏送去墓地时，可以从自己卡组把1只5星以上的名字带有「薰风」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(340002,'娱乐伙伴 槌子巨象',replace(replace('①：自己场上有「娱乐伙伴」卡2张以上存在的场合，这张卡可以不用解放作召唤。\r\n②：自己场上没有其他的「娱乐伙伴」卡存在的场合，这张卡不能攻击。\r\n③：这张卡的攻击宣言时才能发动。对方场上的魔法·陷阱卡全部回到持有者手卡。','\r',char(13)),'\n',char(10)),'不用解放召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(359563,'入魔梦魇骑士',replace(replace('暗属性4星怪兽×2\r\n对方对怪兽的特殊召唤成功时，可以把这张卡1个超量素材取除，那些特殊召唤的怪兽变成里侧守备表示。','\r',char(13)),'\n',char(10)),'变成里侧守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(368382,'雾动机龙·腕龙',replace(replace('←6 【灵摆】 6→\r\n①：只在这张卡在灵摆区域存在才有1次，可以把以这张卡以外的自己场上的「雾动机龙」卡为对象发动的效果无效。那之后，这张卡破坏。\r\n【怪兽效果】\r\n①：自己的怪兽区域没有「雾动机龙·腕龙」存在，场上的攻击力最高的怪兽在对方场上存在的场合，这张卡可以从手卡特殊召唤。','\r',char(13)),'\n',char(10)),'是否使用「雾动机龙·腕龙」的效果？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(387282,'我我我妹妹','这张卡召唤成功时，可以从卡组把1张名字带有「我我我」的魔法·陷阱卡加入手卡。此外，选择这张卡以外的自己场上1只名字带有「我我我」的怪兽才能发动。选择的怪兽和这张卡直到结束阶段时变成各自等级合计的等级。「我我我妹妹」的这个效果1回合只能使用1次。','检索','等级变化','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(402568,'电气长颈鹿','这张卡可以直接攻击对方玩家。这张卡直接攻击给与对方基本分战斗伤害时，对方直到这个回合的结束阶段时不能把魔法·陷阱·效果怪兽的效果发动。','对方发动限制','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(403847,'联合军','每1只自己的场上表侧表示存在的战士族，魔法师族的怪兽，自己场上的全部战士族怪兽攻击力上升200。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(423585,'召唤僧',replace(replace('①：这张卡召唤·反转召唤成功的场合发动。这张卡变成守备表示。\r\n②：只要这张卡在怪兽区域存在，这张卡不能解放。\r\n③：1回合1次，从手卡丢弃1张魔法卡才能发动。从卡组把1只4星怪兽特殊召唤。这个效果特殊召唤的怪兽在这个回合不能攻击。','\r',char(13)),'\n',char(10)),'变成守备表示','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(423705,'铁骑士 基亚·弗里德','①：这张卡有装备卡被装备的场合发动。那些装备卡破坏。','装备卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(425934,'强欲哥布林','只要这张卡在自己场上以表侧表示存在，双方都不能发动「通过丢弃手卡来发动」的效果。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(440556,'龙神鲨',replace(replace('水属性4星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除才能发动。把1只水属性·3阶以下的超量怪兽从额外卡组特殊召唤。这个回合这张卡不能攻击。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(458748,'法之圣典',replace(replace('「法之圣典」在1回合只能发动1张。\r\n①：把自己场上1只「召唤兽」怪兽解放才能发动。原本属性和解放的怪兽不同的1只「召唤兽」怪兽当作融合召唤从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(464362,'魔玩具·剪刀虎',replace(replace('「锋利小鬼·剪刀」＋「毛绒动物」怪兽1只以上\r\n①：「魔玩具·剪刀虎」在自己场上只能有1只表侧表示存在。\r\n②：这张卡融合召唤成功时，以最多有作为这张卡的融合素材的怪兽数量的场上的卡为对象才能发动。那些卡破坏。\r\n③：只要这张卡在怪兽区域存在，自己场上的「魔玩具」怪兽的攻击力上升自己场上的「毛绒动物」怪兽以及「魔玩具」怪兽数量×300。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(473469,'猛吹雪','自己的陷阱卡被对方控制的卡的效果破坏，从场地送去墓地时才能发动。场上的1张魔法·陷阱卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(487395,'冰水精灵','据说是在古代南极大陆的永久冻土拥有了生命的冰水精灵。能够渗透进各种物质里。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(494922,'超重荒神 须佐之男-O',replace(replace('机械族调整＋调整以外的「超重武者」怪兽1只以上\r\n这张卡在规则上也当作「超重武者」卡使用。\r\n①：这张卡可以用表侧守备表示的状态作出攻击。那个场合，这张卡用守备力当作攻击力使用进行伤害计算。\r\n②：自己墓地没有魔法·陷阱卡存在的场合，1回合1次，以对方墓地1张魔法·陷阱卡为对象才能发动。那张卡在自己场上盖放。这个效果盖放的卡从场上离开的场合除外。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(525110,'迷你番茄小子','这张卡被战斗破坏送去墓地时，可以从自己卡组把最多2只名字带有「番茄小子」的怪兽特殊召唤。这个效果特殊召唤的怪兽这个回合不能作为同调素材。','特殊召唤','是否还要特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(549481,'硬毛鼠','毛像硬皮一样聚集在一起，守备力非常的高。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(564541,'民艺龙','龙族怪兽上级召唤的场合，这张卡可以作为2只的数量解放。自己的准备阶段时这张卡在墓地存在，自己场上没有怪兽存在的场合，可以把这张卡表侧攻击表示特殊召唤。这个效果在自己墓地有龙族以外的怪兽存在的场合不能发动。这个效果特殊召唤的这张卡从场上离开的场合从游戏中除外。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(575512,'PSY骨架回路',replace(replace('①：自己场上有「PSY骨架」怪兽特殊召唤的场合才能发动。只用自己场上的「PSY骨架」怪兽为同调素材作同调召唤。\r\n②：自己的「PSY骨架」怪兽和对方怪兽进行战斗的伤害步骤开始时，把手卡1只「PSY骨架」怪兽丢弃才能发动。那只进行战斗的自己怪兽的攻击力直到回合结束时上升因为这个效果发动而丢弃的怪兽的攻击力数值。','\r',char(13)),'\n',char(10)),'用自己场上的「PSY骨架」怪兽为同调素材作同调召唤','攻击上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(581014,'大薰风骑士 翠玉',replace(replace('4星怪兽×2\r\n①：1回合1次，可以把这张卡1个超量素材取除，从以下效果选择1个发动。\r\n●以自己墓地3只怪兽为对象才能发动。那3只怪兽加入卡组洗切。那之后，自己从卡组抽1张。\r\n●以效果怪兽以外的自己墓地1只怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'选择一个效果发动','选择自己墓地存在的3只怪兽，加入卡组洗切。那之后，从自己卡组抽1张卡。','选择自己墓地存在的1只效果怪兽以外的怪兽特殊召唤。','','','','','','','','','','','','','');
INSERT INTO texts VALUES(596051,'毒蛇之牙','选择场上表侧表示存在的1只怪兽发动。选择的怪兽的守备力直到结束阶段时下降500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(598988,'罪 虹龙','这张卡不能通常召唤。从自己的手卡·卡组把1只「究极宝玉神 虹龙」从游戏中除外的场合才能特殊召唤。名字带有「罪」的怪兽在场上只能有1只表侧表示存在。只要这张卡表侧表示存在，自己的其他怪兽不能攻击宣言。没有场地魔法卡表侧表示存在的场合这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(601193,'彼岸的诗人 维吉尔',replace(replace('调整＋调整以外的怪兽1只以上\r\n「彼岸的诗人 维吉尔」的③的效果1回合只能使用1次。\r\n①：「彼岸的诗人 维吉尔」在自己场上只能有1只表侧表示存在。\r\n②：1回合1次，从手卡丢弃1张「彼岸」卡，以对方的场上·墓地1张卡为对象才能发动。那张卡回到持有者卡组。\r\n③：场上的这张卡被战斗·效果破坏送去墓地的场合才能发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'弹回卡组','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(612115,'剑斗兽 网斗','这张卡用名字带有「剑斗兽」的怪兽的效果特殊召唤成功时，可以选择对方墓地1张卡除外。这张卡进行战斗的战斗阶段结束时可以通过让这张卡回到卡组，从卡组把「剑斗兽 网斗」以外的1只名字带有「剑斗兽」的怪兽特殊召唤。','除外','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(645087,'电子界工具',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：这张卡召唤成功时，以自己墓地1只2星以下的怪兽为对象才能发动。那只怪兽守备表示特殊召唤。这个效果特殊召唤的怪兽的效果无效化。\r\n②：这张卡从场上送去墓地的场合才能发动。在自己场上把1只「工具衍生物」（电子界族·光·2星·攻/守0）特殊召唤。','\r',char(13)),'\n',char(10)),'墓地怪兽特殊召唤','衍生物特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(645088,'工具衍生物','由「电子界工具」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(645794,'威风妖怪·蛤蟆',replace(replace('←5 【灵摆】 5→\r\n【怪兽效果】\r\n「威风妖怪·蛤蟆」的①的怪兽效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功时才能发动。从卡组选1张「威风妖怪」魔法·陷阱卡在自己场上盖放。这个效果盖放的卡在这个回合不能发动。\r\n②：这张卡只要在怪兽区域存在，不会成为对方的效果的对象，不会被对方的效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(652362,'外星菊石','这张卡召唤成功时，可以把自己墓地存在的1只4星以下的名字带有「外星」的怪兽特殊召唤。这个效果特殊召唤的怪兽在结束阶段时破坏。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(674561,'暗黑爆发','①：以自己墓地1只攻击力1500以下的暗属性怪兽为对象才能发动。那只暗属性怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(675319,'十二阵·签订',replace(replace('①：自己场上的「十二兽」怪兽的攻击力·守备力上升300。\r\n②：只要这张卡在场地区域存在，对方不能选择除自己场上的攻击力最高的兽战士族怪兽以外的自己的兽战士族怪兽作为攻击对象。\r\n③：1回合1次，自己场上的「十二兽」怪兽被效果破坏的场合，可以作为那1只「十二兽」怪兽的代替而把自己的手卡·场上1只怪兽破坏。','\r',char(13)),'\n',char(10)),'是否使用「十二阵·签订」的效果把其他怪兽作为代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(691925,'太阳交换','①：从手卡丢弃1只「光道」怪兽才能发动。自己从卡组抽2张。那之后，从自己卡组上面把2张卡送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(698785,'雷霆终结龙',replace(replace('8星通常怪兽×2\r\n1回合1次，把这张卡1个超量素材取除才能发动。这张卡以外的场上存在的怪兽全部破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(706925,'海皇的狙击兵','这张卡给与对方基本分战斗伤害时，可以从卡组把「海皇的狙击兵」以外的1只4星以下的名字带有「海皇」的海龙族怪兽特殊召唤。此外，这张卡为让水属性怪兽的效果发动而被送去墓地时，选择对方场上盖放的1张卡破坏。','特殊召唤','盖卡破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(712559,'亚马逊之里',replace(replace('①：场上的「亚马逊」怪兽的攻击力上升200。\r\n②：1回合1次，「亚马逊」怪兽被战斗·效果破坏送去墓地时才能发动。自己把持有那「亚马逊」怪兽的原本等级以下的等级的1只「亚马逊」怪兽从卡组特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(732302,'骷髅寺院','全部由骷髅和骨头建立而成的寺院。会吸收接近的人的灵魂。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(734741,'彼岸的恶鬼 鲁比坎泰',replace(replace('「彼岸的恶鬼 鲁比坎泰」的①的效果1回合只能使用1次。把这张卡作为同调素材的场合，不是「彼岸」怪兽的同调召唤不能使用。\r\n①：自己场上没有魔法·陷阱卡存在的场合才能发动。这张卡从手卡特殊召唤。\r\n②：自己场上有「彼岸」怪兽以外的怪兽存在的场合这张卡破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(739444,'弹鼓龙',replace(replace('龙族·暗属性怪兽3只\r\n这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡特殊召唤成功的场合才能发动。自己从卡组抽1张。\r\n②：这张卡的①的效果适用的回合只要这张卡在怪兽区域存在，作为这张卡所连接区的没有使用的怪兽区域不能使用。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(756652,'多隆','分身夹击的怪兽，要小心！','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(759393,'火灵使 希塔','反转：只要这张卡在场上表侧表示存在，获得对方场上1只炎属性怪兽的控制权。','获得对方场上1只炎属性怪兽的控制权','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(770365,'魔导皇圣 特莉丝',replace(replace('魔法师族5星怪兽×2\r\n这张卡的攻击力上升自己场上的超量素材数量×300的数值。此外，1回合1次，把这张卡1个超量素材取除才能发动。把自己卡组洗切。那之后，从卡组上面把5张卡翻开，选最多有那之中的名字带有「魔导书」的卡数量的场上的怪兽破坏。那之后，翻开的卡用喜欢的顺序回到卡组上面。','\r',char(13)),'\n',char(10)),'确认卡组','是否要选择场上的怪兽破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(799183,'超战士之盾',replace(replace('①：自己场上有「混沌战士」怪兽存在，场上的怪兽为对象的怪兽的效果·魔法·陷阱卡发动时才能发动。那个发动无效并破坏。\r\n②：这张卡在墓地存在的场合，把自己场上1个魔力指示物取除才能发动。墓地的这张卡在自己场上盖放。这个效果盖放的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(811734,'DDD的契约变更',replace(replace('①：对方怪兽的攻击宣言时可以从以下效果选择1个发动。\r\n●选自己墓地1只「DDD」怪兽除外。攻击怪兽的攻击力下降这个效果除外的怪兽的攻击力数值。\r\n●从卡组把1只4星以下的「DD」灵摆怪兽加入手卡。','\r',char(13)),'\n',char(10)),'怪兽除外','卡组检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(847915,'№系之壁','自己场上有名字带有「No.」的怪兽存在的场合才能发动。只要这张卡在场上存在，双方场上的名字带有「No.」的怪兽不会被卡的效果破坏，不会被和名字带有「No.」的怪兽以外的战斗破坏。自己场上的名字带有「No.」的怪兽被破坏时，这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(876330,'龙骑兵团武器-银槲剑','这张卡可以把自己场上表侧表示存在的1只名字带有「龙骑兵团」的怪兽送去墓地，从手卡特殊召唤。这张卡从手卡召唤·特殊召唤成功时，可以选择自己墓地存在的1只名字带有「龙骑兵团」的龙族怪兽当作装备卡使用给这张卡装备。','装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(879958,'并口护甲',replace(replace('①：以自己场上1只连接怪兽为对象才能把这张卡发动。这张卡当作装备卡使用给那只怪兽装备。装备怪兽不会成为对方的效果的对象，不会被战斗破坏。\r\n②：从自己墓地把这张卡和2只连接怪兽除外，以自己场上1只连接怪兽为对象才能发动。这个回合，那只怪兽在同1次的战斗阶段中可以作2次攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(900787,'龙骑兵团骑士-魔枪龙骑士',replace(replace('龙族调整＋调整以外的鸟兽族怪兽1只以上\r\n①：这张卡进行战斗的从伤害步骤开始时到伤害计算前1次，把自己墓地1只鸟兽族怪兽除外才能发动。这张卡的攻击力直到回合结束时上升除外的那只怪兽的原本攻击力数值。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(904185,'垂直着陆','把衍生物以外的自己场上的风属性怪兽任意数量解放才能发动。解放的怪兽数量的「幻兽机衍生物」（机械族·风·3星·攻/守0）在自己场上特殊召唤。「垂直着陆」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(904186,'幻兽机衍生物','由「垂直着陆」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(911883,'粗人预料','①：自己场上没有怪兽存在的场合才能发动。从卡组把1只4星以下的通常怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(917796,'我我我组队','自己场上的全部名字带有「我我我」的怪兽的攻击力直到下次的自己的准备阶段时上升自己场上的名字带有「我我我」的怪兽数量×500的数值。「我我我组队」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(923596,'齿轮齿轮铠甲人','这张卡1回合只有1次可以变成里侧守备表示。这张卡反转时，可以从卡组把「齿轮齿轮铠甲人」以外的1只名字带有「齿轮齿轮」的怪兽加入手卡。','变成里侧守备','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(967928,'惩罚游戏！',replace(replace('对方的手卡4张的时候，选择下面1个效果发动。\r\n●对方下个抽卡阶段不能抽卡。\r\n●这个回合对方不能发动魔法·陷阱卡。','\r',char(13)),'\n',char(10)),'对方下个抽卡阶段不能抽卡','这个回合对方不能发动魔法·陷阱卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(975299,'巨大要塞 泽洛斯',replace(replace('①：作为这张卡的发动时的效果处理，可以从卡组把1张「头目连战」加入手卡。\r\n②：自己场上的「巨大战舰」怪兽的攻击力·守备力上升500，不会成为对方的效果的对象，不会被对方的效果破坏。\r\n③：1回合1次，自己主要阶段才能发动。从手卡把1只「巨大战舰」怪兽特殊召唤。\r\n④：自己场上有「巨大战舰」怪兽召唤·特殊召唤的场合发动。给那些怪兽放置1个自身的效果使用的指示物。','\r',char(13)),'\n',char(10)),'是否把「头目连战」加入手卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(980973,'武装龙 LV3','①：自己准备阶段把场上的这张卡送去墓地才能发动。从手卡·卡组把1只「武装龙 LV5」特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(981540,'半伤不破','选择场上1只怪兽才能发动。这个回合，选择的怪兽不会被战斗破坏，那只怪兽的战斗发生的对自己的战斗伤害变成一半。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(983995,'篮板球','要让场上的卡回到手卡的效果由对方发动时才能发动。那个效果无效，从对方的手卡·场上选1张卡送去墓地。此外，盖放的这张卡被对方破坏送去墓地时，从卡组抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(984114,'特快机人','①：这张卡召唤·反转召唤·特殊召唤成功时，以「特快机人」以外的自己墓地2只「机人」怪兽为对象才能发动。那些怪兽加入手卡。','加入手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(987311,'DDD 疾风王 亚历山大',replace(replace('「DD」调整＋调整以外的怪兽1只以上\r\n「DDD 疾风王 亚历山大」的效果1回合只能使用1次。\r\n①：这张卡在怪兽区域存在，自己场上有这张卡以外的「DD」怪兽召唤·特殊召唤的场合，以自己墓地1只4星以下的「DD」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1003028,'老虎狗潜水员','对方场上有怪兽存在，自己场上表侧表示存在的怪兽只有4星怪兽的场合，这张卡可以从手卡特殊召唤。这个方法的「老虎狗潜水员」的特殊召唤1回合只能有1次。把这张卡作为超量召唤的素材的场合，不是战士族怪兽的超量召唤不能使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1003840,'星光立交桥',replace(replace('「星光立交桥」的①②的效果1回合各能使用1次。\r\n①：把自己场上1只调整解放才能把这个效果发动。和解放的怪兽等级不同的1只「同调士」怪兽从卡组特殊召唤。\r\n②：对方回合从自己的额外卡组把同调怪兽特殊召唤的场合，以场上1张卡为对象发动。那张卡回到持有者卡组。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1005587,'炼狱的落穴','对方把攻击力2000以上的怪兽特殊召唤时才能发动。那1只攻击力2000以上的怪兽的效果无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1006081,'废品变更者',replace(replace('「废品变更者」的效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合，可以以场上1只「废品」怪兽为对象从以下效果选择1个发动。\r\n●作为对象的怪兽的等级上升1星。\r\n●作为对象的怪兽的等级下降1星。','\r',char(13)),'\n',char(10)),'等级变更','等级上升1星','等级下降1星','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1033312,'苦涩的决断',replace(replace('「苦涩的决断」在1回合只能发动1张。\r\n①：从卡组把1只4星以下的通常怪兽送去墓地，那1张同名卡从卡组加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1036974,'不幸','选择对方场上表侧表示存在的1只怪兽发动。给与对方基本分选择的怪兽的原本攻击力一半的伤害。使用这个效果的回合，自己的怪兽不能攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1045143,'齿轮齿轮人 Mk-2','这张卡召唤·反转召唤成功时，可以从自己的手卡·墓地选1只名字带有「齿轮齿轮」的怪兽表侧守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1050186,'星因士 天市右垣七',replace(replace('「星因士 天市右垣七」的效果1回合只能使用1次。\r\n①：这张卡召唤·反转召唤·特殊召唤成功的场合才能发动。从卡组把「星因士 天市右垣七」以外的1张「星守」卡送去墓地。','\r',char(13)),'\n',char(10)),'送去墓地','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1050684,'废铁区','从自己卡组把1只名字带有「废铁」的调整加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1061200,'方程式运动员街道大奖赛','场上的「方程式运动员」怪兽的等级在主要阶段内和战斗阶段内上升2星。自己场上的「方程式运动员」怪兽不会成为对方的卡的效果的对象。场上的表侧表示的这张卡被卡的效果破坏的场合：可以从卡组把「方程式运动员街道大奖赛」以外的1张「方程式运动员」卡加入手卡。「方程式运动员街道大奖赛」的这个效果1回合只能使用1次。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1073952,'魔法花盆','①：把自己场上1张表侧表示的永续陷阱卡送去墓地才能发动。自己从卡组抽2张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1082946,'命运之火钟','1张卡片的倒数回合前进1回合。','请选择要让回合计数前进的卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1102515,'暗黑之宝箱怪 LV3','这张卡因为战斗送去墓地的场合，这张卡的控制者从卡组抽1张卡。这张卡因为「暗黑之宝箱怪 LV1」的效果特殊召唤的场合改成抽2张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1118137,'守护者之力',replace(replace('①：装备怪兽进行战斗的攻击宣言时发动。给这张卡放置1个魔力指示物。\r\n②：装备怪兽的攻击力·守备力上升这张卡的魔力指示物数量×500。\r\n③：装备怪兽被战斗·效果破坏的场合，可以作为代替把自己场上1个魔力指示物取除。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1127737,'异次元的古战场-死域海','每次超量召唤成功，那个玩家受到500分伤害。此外，控制超量怪兽的玩家各自在每次自己的结束阶段受到500分伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1149109,'卡组封锁','只要这张卡在场上存在，双方不能用抽卡以外的方法从卡组把卡加入手卡，也不能作从卡组的特殊召唤。发动后第2次的自己的准备阶段时这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1151281,'地中族邪界兽·爱奥荧冰精',replace(replace('这个卡名的③的效果1回合只能使用1次。\r\n①：自己场上的表侧表示怪兽变成里侧表示时，自己场上没有表侧表示怪兽存在的场合才能发动。这张卡从手卡守备表示特殊召唤。\r\n②：自己主要阶段才能发动。这张卡变成里侧守备表示（1回合只有1次）。\r\n③：这张卡反转的场合才能发动。从卡组选1张卡送去墓地。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1154611,'伯吉斯异兽·林乔利虫',replace(replace('①：以除外的1张自己或者对方的卡为对象才能发动。那张卡回到墓地。\r\n②：陷阱卡发动时，连锁那个发动才能把这个效果在墓地发动。这张卡变成通常怪兽（水族·水·2星·攻1200/守0）在怪兽区域特殊召唤（不当作陷阱卡使用）。这个效果特殊召唤的这张卡不受怪兽的效果影响，从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1184620,'魔物狩人','猎杀人类的猎手，具有连岩石都能击碎的力量。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1197847,'幻煌龙的螺旋波',replace(replace('通常怪兽才能装备。「幻煌龙的螺旋波」的②的效果1回合只能使用1次。\r\n①：装备怪兽1回合只有1次不会被战斗破坏。\r\n②：装备怪兽进行战斗的战斗阶段结束时才能发动。从自己的手卡·卡组·墓地选1只「幻煌龙 螺旋」特殊召唤，这张卡给那只怪兽装备。那之后，有对方手卡的场合，对方选1张手卡丢弃。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1200843,'八汰镜','灵魂怪兽才能装备。装备怪兽在结束阶段时回到手卡效果可以不发动。装备怪兽被战斗破坏的场合，作为代替把这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1224927,'邪神之大灾害','①：对方怪兽的攻击宣言时才能发动。场上的魔法·陷阱卡全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1248895,'连锁破坏','①：攻击力2000以下的怪兽召唤·反转召唤·特殊召唤成功时，以那1只表侧表示怪兽为对象才能发动。从那只表侧表示怪兽的控制者的手卡·卡组把作为对象的怪兽的同名卡全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1249315,'圣光之宣告者',replace(replace('2星怪兽×2\r\n把这张卡1个超量素材取除，选择自己墓地1只怪兽才能发动。选择的怪兽加入手卡，那之后，1张手卡回到持有者卡组。「圣光之宣告者」的效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1264319,'宝石骑士融合',replace(replace('①：从自己的手卡·场上把「宝石骑士」融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。\r\n②：这张卡在墓地存在的场合，把自己墓地1只「宝石骑士」怪兽除外才能发动。墓地的这张卡加入手卡。','\r',char(13)),'\n',char(10)),'融合召唤','加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1274455,'星际仙踪-飞猴队',replace(replace('「星际仙踪-飞猴队」的①的效果1回合只能使用1次。\r\n①：把场上的这张卡除外才能发动。从手卡把1只4星以上的「星际仙踪」怪兽特殊召唤。这个效果在对方回合也能发动。\r\n②：1回合1次，支付1000基本分，以自己墓地1只念动力族「星际仙踪」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'从手卡把「星际仙踪」怪兽特殊召唤','从墓地把「星际仙踪」怪兽特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1278431,'古代的机械素体',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：丢弃1张手卡才能发动。把1只「古代的机械巨人」或者1张有「古代的机械巨人」的卡名记述的魔法·陷阱卡从卡组加入手卡。\r\n②：这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。\r\n③：表侧表示的这张卡因对方的效果从场上离开的场合才能发动。从手卡把「古代的机械巨人」「古代的机械巨人-究极重击」合计最多3只无视召唤条件特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1281505,'大日四面体','这张卡从怪兽卡区域上送去墓地的场合，可以支付500基本分，把这张卡当作装备卡使用给自己场上表侧表示存在的1只怪兽装备。这张卡的装备怪兽被破坏的场合，可以作为代替把这张卡破坏。','当作装备卡装备','是否使用「大日四面体」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1287123,'物质猎鹰','持有「把场上的魔法·陷阱卡破坏的效果」的效果怪兽的效果发动时，可以把1张手卡送去墓地让那个发动无效并破坏。','发动无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1315120,'科技属 突击兵','对方场上有怪兽存在，自己场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。场上存在的这张卡被破坏送去墓地的回合的结束阶段时，可以从自己卡组把「科技属 突击兵」以外的1只名字带有「科技属」的怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1322368,'秘旋谍-双螺旋特工',replace(replace('「秘旋谍」怪兽2只\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：这张卡的卡名只要在场上·墓地存在当作「秘旋谍-花公子」使用。\r\n②：宣言卡的种类（怪兽·魔法·陷阱）才能发动。对方卡组最上面的卡给双方确认，宣言的种类的卡的场合，从自己的卡组·墓地选1只「秘旋谍」怪兽加入手卡或在作为这张卡所连接区的自己场上特殊召唤。','\r',char(13)),'\n',char(10)),'宣言卡的种类','是否特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1344018,'时空之灵摆读阵',replace(replace('「时空之灵摆读阵」的②的效果1回合只能使用1次。\r\n①：只要这张卡在魔法与陷阱区域存在，对方不能把自己场上的魔法师族怪兽作为陷阱卡的效果的对象。\r\n②：以自己的怪兽区域·灵摆区域1张「魔术师」灵摆怪兽卡和对方场上1张卡为对象才能发动。那些卡破坏。没能因这个效果把2张卡破坏的场合，可以选场上1张卡送去墓地。','\r',char(13)),'\n',char(10)),'卡片破坏','是否选场上1张卡送去墓地？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1347977,'神圣守卫者','反转：选择场上的1只表侧表示的怪兽回到卡组的最上面。这个时候如果自己的场上有表侧表示的战士族怪兽存在的场合，可以再选择场上的1只表侧表示的怪兽回到持有者的手卡。','返回卡组','是否要再选择场上的1只表侧表示的怪兽回到手卡？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1353770,'神之居城-瓦尔哈拉','①：1回合1次，自己主要阶段才能把这个效果发动。从手卡把1只天使族怪兽特殊召唤。这个效果在自己场上没有怪兽存在的场合才能发动和处理。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1357146,'粹蛙','这张卡的卡名只要在场上表侧表示存在当作「死亡青蛙」使用。此外，可以把自己墓地存在的1只名字带有「青蛙」的怪兽从游戏中除外，墓地存在的这张卡在自己场上特殊召唤。这张卡不能作为同调素材。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1362589,'光子粉碎者','这张卡攻击的场合，伤害步骤结束时变成守备表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1371589,'吸血树熊','这张卡和怪兽的战斗给与对方基本分战斗伤害时，自己基本分回复给与的战斗伤害的数值。','回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1372887,'合乘','这张卡发动的回合，每次对方用抽卡以外的方法从卡组·墓地把卡加入手卡，自己从卡组抽1张卡。「合乘」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1409474,'阳炎兽 斯芬克司','只要这张卡在场上表侧表示存在，对方不能把这张卡作为卡的效果的对象。此外，1回合1次，自己的主要阶段时，宣言卡的种类（怪兽·魔法·陷阱）才能发动。自己卡组最上面的卡送去墓地，宣言的种类的卡的场合，可以再从自己的手卡·墓地选1只炎属性怪兽特殊召唤。「阳炎兽 斯芬克司」的这个效果1回合只能使用1次。','宣言种类','是否要从自己的手卡·墓地选1只炎属性怪兽特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1412158,'稀有金属骑士',replace(replace('「稀有金属女郎」＋「稀有金属战士」\r\n这张卡在对怪兽的战斗伤害计算时，攻击力上升1000，在场上的这张卡可以和融合区的「稀有金属女武神」互换（这张卡特殊召唤的回合不可以互换。）','\r',char(13)),'\n',char(10)),'攻击上升','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1426714,'No.48 暗影巫妖',replace(replace('3星怪兽×2\r\n对方回合1次，把这张卡1个超量素材取除才能发动。在自己场上把1只「幻影衍生物」（恶魔族·暗·1星·攻/守500）特殊召唤。此外，只要自己场上有「幻影衍生物」存在，对方不能把这张卡作为攻击对象。这张卡的攻击力上升自己场上的「幻影衍生物」数量×500的数值。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
