INSERT INTO texts VALUES(1474910,'小蜜蜂','把这张卡和自己场上表侧表示存在的「小蜜蜂」以外的1只昆虫族怪兽解放发动。从自己卡组抽2张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1475311,'暗之诱惑','①：自己从卡组抽2张，那之后把手卡1只暗属性怪兽除外。手卡没有暗属性怪兽的场合，手卡全部送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1482001,'副语术士克拉拉与洛希卡',replace(replace('通常召唤的怪兽1只\r\n这张卡的连接召唤不在主要阶段2不能进行。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1498130,'六武众的影武者','自己场上表侧表示存在的名字带有「六武众」的怪兽1只成为魔法·陷阱·效果怪兽的效果的对象时，可以把那个效果的对象转换为场上表侧表示存在的这张卡。','对象转移','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1508649,'幻变骚灵·十六巫赫斯提',replace(replace('「幻变骚灵」怪兽2只\r\n这个卡名的③的效果1回合只能使用1次。\r\n①：这张卡的攻击力上升这张卡所连接区的「幻变骚灵」怪兽的原本攻击力数值。\r\n②：魔法·陷阱卡的效果发动时，把这张卡所连接区1只「幻变骚灵」怪兽解放才能发动。那个发动无效并破坏。\r\n③：这张卡从场上送去墓地的场合才能发动。从卡组把1张「幻变骚灵」卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1516510,'秘术眼灵摆龙',replace(replace('「异色眼灵摆龙」＋魔法师族怪兽\r\n①：这张卡得到「异色眼灵摆龙」以外的作为融合素材的怪兽的原本等级的以下效果。\r\n●4星以下：这张卡在同1次的战斗阶段中最多2次可以向怪兽攻击。\r\n●5星以上：这张卡在同1次的战斗阶段中最多3次可以向怪兽攻击。\r\n②：场上的灵摆召唤的怪兽作为素材让这张卡融合召唤成功的回合，这张卡不受对方的效果影响。','\r',char(13)),'\n',char(10)),'最多2次可以向怪兽攻击','最多3次可以向怪兽攻击','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1525329,'有7种武器的猎人','这张卡片召唤上场时，宣言1个种族发动，这张卡与宣言的种族的怪兽战斗时，这张卡攻击力只在伤害计算时上升1000。','宣言种族','攻击上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1527418,'空牙团的睿智 薇兹',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡特殊召唤成功的场合才能发动。自己回复「空牙团的睿智 薇兹」以外的自己场上的「空牙团」怪兽种类×500基本分。\r\n②：对方把魔法·陷阱卡的效果发动时，从手卡丢弃1张「空牙团」卡才能发动。那个发动无效。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1533292,'真次世代锅炉人','这张卡召唤成功时，可以从自己卡组把1只2星的名字带有「真次世代」的怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
