INSERT INTO texts VALUES(1561110,'ABC-神龙歼灭者',replace(replace('「A-突击核」＋「B-破坏龙兽」＋「C-粉碎翼龙」\r\n把自己的场上·墓地的上记卡除外的场合才能从额外卡组特殊召唤（不需要「融合」）。\r\n①：1回合1次，丢弃1张手卡，以场上1张卡为对象才能发动。那张卡除外。这个效果在对方回合也能发动。\r\n②：对方回合把这张卡解放，以除外的自己的机械族·光属性的同盟怪兽3种类为对象才能发动。那些怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'丢弃1张手卡，把场上1张卡除外','把这张卡解放，把除外的3种类同盟怪兽特殊召唤','','','','','','','','','','','','','','');
