INSERT INTO texts VALUES(1644289,'大日断片','名字带有「大日」的怪兽才能装备。装备怪兽不会成为对方的陷阱·效果怪兽的效果的对象。场上表侧表示存在的这张卡被送去墓地的场合，可以从自己卡组把1张名字带有「大日」的魔法卡加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1662004,'炎星师-张天','把这张卡作为同调素材的场合，不是兽战士族怪兽的同调召唤不能使用。这张卡召唤成功时，可以从自己墓地选择1只守备力200以下的炎属性·3星怪兽表侧守备表示特殊召唤。这个效果特殊召唤过的场合，这个回合自己不能用兽战士族以外的怪兽攻击。「炎星师-张天」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1669772,'魔力净化','丢弃1张手卡。场上表侧表示存在的永续魔法全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1686814,'奥特玛雅·卓尔金',replace(replace('规则上，这张卡的等级当作12星使用。这张卡不能同调召唤，把自己场上的表侧表示的5星以上而相同等级的调整和调整以外的怪兽各1只送去墓地的场合才能特殊召唤。\r\n①：1回合1次，自己场上有魔法·陷阱卡被盖放时才能发动。把1只「动力工具」同调怪兽或者7·8星的龙族同调怪兽从额外卡组特殊召唤。\r\n②：场上的这张卡只要其他的自己的同调怪兽存在，不会成为攻击对象以及效果的对象。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1689516,'百兽大行进','自己场上表侧表示的兽族怪兽的攻击力，在结束阶段前提升自己场上兽族怪兽数目×200的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1697104,'PSY骨架装备·ε',replace(replace('这张卡不能通常召唤，用卡的效果才能特殊召唤。\r\n①：自己场上没有怪兽存在，对方的陷阱卡发动时才能发动。选手卡的这张卡和自己的手卡·卡组·墓地1只「PSY骨架驱动者」特殊召唤，那个发动无效并破坏。这个效果特殊召唤的怪兽全部在结束阶段除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1710476,'罪 电子终结龙','这张卡不能通常召唤。从自己的额外卡组把1只「电子终结龙」从游戏中除外的场合才能特殊召唤。名字带有「罪」的怪兽在场上只能有1只表侧表示存在。只要这张卡表侧表示存在，自己的其他怪兽不能攻击宣言。没有场地魔法卡表侧表示存在的场合这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1735088,'发条修补 发条微机',replace(replace('「发条」怪兽2只\r\n这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡连接召唤成功的场合才能发动。从卡组把1张「发条」卡加入手卡。\r\n②：把自己场上1只表侧表示的「发条」怪兽里侧表示除外才能发动。把1只和那只是同名的怪兽从卡组特殊召唤。\r\n③：这张卡被破坏送去墓地的场合，以自己场上1只「发条」超量怪兽为对象才能发动。把这张卡在那只怪兽下面重叠作为超量素材。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1761063,'猫妖精','猫精灵。与可爱的身姿相反、敏捷的攻击对方。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1764972,'死亡帝王龙/爆裂体','这张卡不能通常召唤。「爆裂模式」的效果才能特殊召唤。这张卡特殊召唤成功时，把自己或者对方墓地存在的不死族怪兽任意数量在自己场上特殊召唤。这个效果特殊召唤的效果怪兽的效果无效化，这个回合的结束阶段时破坏。此外，场上存在的这张卡被破坏时，可以把自己墓地存在的1只「死亡帝王龙」特殊召唤。','特殊召唤','特殊召唤「死亡帝王龙」','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1781310,'风林火山',replace(replace('①：风·水·炎·地属性怪兽在场上全部存在的场合才能发动。从以下效果选1个适用。\r\n●对方场上的怪兽全部破坏。\r\n●对方场上的魔法·陷阱卡全部破坏。\r\n●对方手卡随机选2张丢弃。\r\n●自己从卡组抽2张。','\r',char(13)),'\n',char(10)),'对方场上怪兽全部破坏','对方场上魔法·陷阱卡全部破坏','对方随机丢弃2张手卡','抽2张卡','','','','','','','','','','','','');
INSERT INTO texts VALUES(1784619,'荒野盗龙','擅长奔跑的恐龙，利用尖锐的爪子进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1784686,'蒂迈欧之眼',replace(replace('这个卡名在规则上也当作「传说之龙 蒂迈欧」使用。这个卡名的卡在1回合只能发动1张。\r\n①：以自己场上1只「黑魔术」怪兽为对象才能发动。那只怪兽作为融合素材送去墓地，把有那个卡名作为融合素材记述的1只融合怪兽从额外卡组融合召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1801154,'离心分离力场','融合怪兽因为卡的效果破坏送去墓地时，从自己墓地选择那只融合怪兽记述的1只融合素材，特殊召唤到自己场上。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1802450,'木遁封印式','1回合1次，可以把自己场上表侧表示存在的1只地属性怪兽解放，选择对方墓地存在的最多2张卡从游戏中除外。','是否现在使用「木遁封印式」的效果？','除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1804528,'诅咒之棺',replace(replace('里侧盖伏的这张卡被破坏送去墓地时，对方选择下面的1个效果：\r\n●对方随机选择他自己的1张手卡丢弃。\r\n●对方选择他自己场上的1只怪兽破坏。','\r',char(13)),'\n',char(10)),'选择一个效果','随机选择自己的1张手卡丢弃','选择自己场上的1只怪兽破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1826676,'玩具骑士','这张卡不能从卡组特殊召唤。对方场上的怪兽数量比自己场上的怪兽数量多的场合，这张卡可以从手卡特殊召唤。这张卡召唤·特殊召唤成功时，可以从手卡把1只「玩具骑士」特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1828513,'六武众之影-紫炎',replace(replace('4星「六武众」怪兽×2\r\n①：1回合1次，把这张卡1个超量素材取除，以自己场上1只攻击力未满2000的「六武众」怪兽为对象才能发动。那只怪兽的原本攻击力直到回合结束时变成2000。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'攻击变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1833916,'英豪挑战者 千刀兵',replace(replace('「英豪挑战者 千刀兵」的②的效果1回合只能使用1次。\r\n①：1回合1次，从手卡丢弃1张「英豪」卡才能发动。从卡组把1只「英豪」怪兽特殊召唤，这张卡变成守备表示。这个效果的发动后，直到回合结束时自己不是「英豪」怪兽不能特殊召唤。\r\n②：这张卡在墓地存在，战斗·效果让自己受到伤害时才能发动。这张卡从墓地攻击表示特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1834107,'电气治疗','自己场上存在的雷族怪兽给与对方基本分战斗伤害时，自己基本分回复给与的战斗伤害的数值。','回复LP','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1834753,'最大瞬移者','这张卡不能特殊召唤。可以支付2000基本分，从自己卡组把2只3星的念动力族怪兽在自己场上特殊召唤。这个效果只在这张卡在场上表侧表示存在能使用1次。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1845204,'简易融合',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：支付1000基本分才能发动。把1只5星以下的融合怪兽当作融合召唤从额外卡组特殊召唤。这个效果特殊召唤的怪兽不能攻击，结束阶段破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1855932,'武神帝-迦具土',replace(replace('兽战士族4星怪兽×2\r\n这张卡超量召唤成功时，从自己卡组上面把5张卡送去墓地。这张卡的攻击力上升这个效果送去墓地的名字带有「武神」的卡数量×100的数值。此外，自己场上的名字带有「武神」的兽战士族怪兽被战斗或者卡的效果破坏的场合，可以作为那1只破坏的怪兽的代替而把这张卡1个超量素材取除。「武神帝-迦具土」在自己场上只能有1只表侧表示存在。','\r',char(13)),'\n',char(10)),'攻击上升','是否使用「武神帝-迦具土」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1861629,'解码语者',replace(replace('效果怪兽2只以上\r\n①：这张卡的攻击力上升这张卡所连接区的怪兽数量×500。\r\n②：自己场上的卡为对象的对方的魔法·陷阱·怪兽的效果发动时，把这张卡所连接区1只自己怪兽解放才能发动。那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'发动无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1896112,'次元诱爆','把自己场上表侧表示存在的1只融合怪兽回到融合卡组才能发动。双方选择从游戏中除外的怪兽最多2只，在各自场上特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1918087,'哥布林小役人','对方的基本分3000以下的场合才可以发动。每次对方的准备阶段对方基本分受到500分的伤害。','给与对方500伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1929294,'心钥妖精','非常细小的天使。无论是谁都被那可爱魅力折服而敞开心扉。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1945387,'元素英雄 新星主',replace(replace('「元素英雄」怪兽＋炎属性怪兽\r\n这张卡不用融合召唤不能特殊召唤。\r\n①：这张卡战斗破坏对方怪兽的场合发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1948619,'特异英雄 神杖先驱',replace(replace('「英雄」怪兽2只\r\n这个卡名的①的效果1回合只能使用1次。\r\n①：作为这张卡所连接区的自己场上有「英雄」怪兽召唤·特殊召唤的场合，以自己墓地的「融合」魔法卡、「变化」速攻魔法卡之内任意1张为对象发动。那张卡在自己场上盖放。\r\n②：这张卡被战斗或者对方的效果破坏送去墓地的场合才能发动。从手卡把1只「英雄」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1953925,'古代的机械工兵',replace(replace('①：只要这张卡在怪兽区域存在，这张卡为对象的陷阱卡的效果无效化并破坏。\r\n②：这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。\r\n③：这张卡攻击的伤害步骤结束时，以对方场上1张魔法·陷阱卡为对象发动。那张对方的魔法·陷阱卡破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1965724,'愤怒的悠悠','「悠悠」在自己场上表侧表示存在时，自己场上天使族怪兽被破坏的场合，这个回合的结束阶段前自己场上「悠悠」的攻击力变为3000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1969506,'傀儡仪式','自己基本分比对方少2000以上的场合才能发动。从自己墓地选择2只名字带有「机关傀儡」的8星怪兽特殊召唤。「傀儡仪式」在1回合只能发动1张，这张卡发动的回合，自己不能进行战斗阶段。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1992816,'No.9 天盖星 戴森球',replace(replace('9星怪兽×2\r\n持有超量素材的这张卡被攻击的战斗步骤时才能发动1次。那次攻击无效。这张卡没有超量素材的状态被选择作为攻击对象时，可以选择自己墓地2只怪兽在这张卡下面重叠作为超量素材。此外，持有比这张卡的攻击力高的攻击力的怪兽在对方场上存在的场合，自己的主要阶段1可以通过把这张卡1个超量素材取除，这个回合这张卡可以直接攻击对方玩家。','\r',char(13)),'\n',char(10)),'攻击无效','增加素材','直接攻击','','','','','','','','','','','','','');
INSERT INTO texts VALUES(1995985,'沉默剑士 LV3',replace(replace('①：只要这张卡在怪兽区域存在，这张卡为对象的对方的魔法卡的效果无效化。\r\n②：自己准备阶段把场上的这张卡送去墓地才能发动。从手卡·卡组把1只「沉默剑士 LV5」特殊召唤。这个效果在这张卡召唤·特殊召唤·反转的回合不能发动。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2009101,'黑羽-疾风之盖尔',replace(replace('①：自己场上有「黑羽-疾风之盖尔」以外的「黑羽」怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：1回合1次，以对方场上1只表侧表示怪兽为对象才能发动。那只对方怪兽的攻击力·守备力变成一半。','\r',char(13)),'\n',char(10)),'攻防减半','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2047519,'伏兵','对方的召唤·反转召唤怪兽时才能发动。从手卡选1只4星以下的暗属性怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2055403,'时空的落穴','①：对方从手卡·额外卡组把怪兽特殊召唤时才能发动。从手卡·额外卡组特殊召唤的那些怪兽回到持有者卡组。那之后，自己失去回去的怪兽数量×1000基本分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2061963,'No.104 假面魔蹈士 闪光',replace(replace('4星怪兽×3\r\n战斗阶段中对方的效果怪兽的效果发动时，把这张卡1个超量素材取除才能发动。那个发动无效，给与对方基本分800分伤害。此外，1回合1次，自己的主要阶段时才能发动。对方卡组最上面的卡送去墓地。','\r',char(13)),'\n',char(10)),'效果无效','卡组破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2067935,'剑斗兽 教斗','这张卡用名字带有「剑斗兽」的怪兽的效果特殊召唤成功时，选择自己墓地存在的1只名字带有「剑斗兽」的怪兽才能发动。选择的怪兽从游戏中除外，直到结束阶段时当作和那只怪兽同名卡使用。这张卡进行战斗的战斗阶段结束时可以让这张卡回到卡组，从卡组把「剑斗兽 教斗」以外的1只名字带有「剑斗兽」的怪兽在自己场上特殊召唤。','除外','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2084239,'湿地草原','全部的水族·水属性·2星以下怪兽的攻击力上升1200。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2088870,'维多利异虫','反转：名字带有「异虫」的爬虫族怪兽以外的场上表侧表示存在的怪兽全部破坏。这张卡的攻击力上升自己墓地存在的名字带有「异虫」的爬虫族怪兽数量×500的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2091298,'星圣·鬼星团',replace(replace('4星「星圣」怪兽×2\r\n①：1回合1次，自己的「星圣」怪兽进行战斗的从伤害步骤开始时到伤害计算前，把这张卡1个超量素材取除才能发动。那只怪兽的攻击力直到回合结束时上升1000。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2095764,'水龙星-赑屃',replace(replace('「水龙星-赑屃」的①的效果1回合只能使用1次。\r\n①：自己场上的这张卡被战斗·效果破坏送去墓地时才能发动。从卡组把「水龙星-赑屃」以外的1只「龙星」怪兽攻击表示特殊召唤。\r\n②：1回合1次，对方的主要阶段以及战斗阶段才能发动。只用自己场上的「龙星」怪兽为同调素材作同调召唤。\r\n③：这张卡为同调素材的同调怪兽不受陷阱卡的效果影响。','\r',char(13)),'\n',char(10)),'特殊召唤','「水龙星-赑屃」效果适用中','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2099841,'笑容世界','①：场上的表侧表示怪兽的攻击力直到回合结束时上升场上的怪兽数量×100。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2111707,'XY-神龙炮',replace(replace('「X-首领加农」＋「Y-龙头」\r\n把自己场上的上记卡除外的场合才能从额外卡组特殊召唤（不需要「融合」）。这张卡不能作从墓地的特殊召唤。\r\n①：丢弃1张手卡，以对方场上1张表侧表示的魔法·陷阱卡为对象才能发动。那张对方的表侧表示的魔法·陷阱卡破坏。','\r',char(13)),'\n',char(10)),'魔陷破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2118022,'兵主部','河童的首领，攻击力意外的高，守备力偏低。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2122975,'陷阱捕获','自己把陷阱卡发动时，丢弃1张手卡连锁发动。连锁发动的陷阱卡被送去墓地时，那张卡回到手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2129638,'青眼双爆裂龙',replace(replace('「青眼白龙」＋「青眼白龙」\r\n这张卡用融合召唤以及以下方法才能特殊召唤。\r\n●把自己的怪兽区域的上记卡送去墓地的场合可以从额外卡组特殊召唤（不需要「融合」）。\r\n①：这张卡不会被战斗破坏。\r\n②：这张卡在同1次的战斗阶段中最多2次可以向怪兽攻击。\r\n③：没被这张卡的攻击破坏对方怪兽的伤害步骤结束时才能发动。那只对方怪兽除外。','\r',char(13)),'\n',char(10)),'对方怪兽除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2130625,'白衣天使','自己因战斗或者卡的效果受到伤害时才能发动。自己回复1000基本分。自己墓地有「白衣天使」存在的场合，再回复那个数量的500基本分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2134346,'阿修罗','这张卡不能特殊召唤。召唤·反转回合的结束阶段时回到主人的手卡。可以对对方场上的全部怪兽攻击1次。','回到手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2137678,'机皇兵 神陆一型','这张卡的攻击力上升这张卡以外的场上表侧表示存在的名字带有「机皇」的怪兽数量×100的数值。这张卡召唤成功时，可以选择对方场上表侧表示存在的1只怪兽，那只怪兽的攻击力直到结束阶段时变成一半。','攻击变成一半','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2144946,'方程式运动员越野大奖赛',replace(replace('场上的「方程式运动员」怪兽的等级只在主要阶段内上升2星。「方程式运动员越野大奖赛」的以下每个效果1回合各能使用1次。\r\n●自己场上的「方程式运动员」怪兽被战斗破坏时：可以从对方手卡随机把1张卡丢弃。\r\n●场上的表侧表示的这张卡被卡的效果破坏的场合：可以从卡组把「方程式运动员越野大奖赛」以外的1张「方程式运动员」卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2148918,'机甲忍法 锈蚀水雾','自己场上有名字带有「忍者」的怪兽存在，对方场上有怪兽特殊召唤时，那些特殊召唤的怪兽的攻击力变成一半。','攻击变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2158562,'电子化女主角','①：这张卡上级召唤成功的场合发动。场上的表侧表示的魔法卡全部破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2191144,'夜蝶刺客',replace(replace('4星怪兽×3\r\n1回合1次，把这张卡1个超量素材取除才能发动。这张卡的攻击力上升场上的超量素材数量×300的数值。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2196767,'传说的赌博师',replace(replace('进行3次投掷硬币。\r\n●3次都是表的场合，对方场上怪兽全部破坏。\r\n●2次表的场合，对方手卡随机丢弃1张。\r\n●1次表的场合，自己场上存在的1张卡破坏。\r\n●3次都是里的场合，自己手卡全部丢弃。\r\n这个效果1回合只有1次在自己的主要阶段才能使用。','\r',char(13)),'\n',char(10)),'投掷硬币','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2203790,'XX-剑士 迅雷',replace(replace('调整＋调整以外的名字带有「X-剑士」的怪兽1只以上\r\n这张卡同调召唤成功时，可以选择场上存在的最多3张魔法·陷阱卡破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2204140,'生者之书-禁断的咒术-','自己墓地存在的1只不死族怪兽特殊召唤，对方墓地存在的1只怪兽从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2220237,'安全守卫者',replace(replace('电子界族连接怪兽1只\r\n这张卡不能作为连接素材。\r\n①：「安全守卫者」在自己场上只能有1只表侧表示存在。\r\n②：这张卡特殊召唤成功的回合，自己受到的效果伤害变成0。\r\n③：自己因战斗·效果受到伤害的场合，1回合只有1次让那次伤害变成0。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2250266,'变形斗士·订书机',replace(replace('这张卡得到这张卡的表示形式的以下效果。\r\n●攻击表示：只要这张卡在场上表侧表示存在，对方不能选择其他怪兽作为攻击对象。这张卡被战斗破坏的场合，把让这张卡破坏的怪兽的攻击力下降300。\r\n●守备表示：这张卡不会被战斗破坏。这张卡被攻击的场合，那次伤害计算后选择对方场上表侧攻击表示存在的1只怪兽变成守备表示，这张卡的表示形式变成攻击表示。','\r',char(13)),'\n',char(10)),'攻击下降','变成攻击表示','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2266498,'复仇死者的还魂结合','把手卡1只「复仇死者」仪式怪兽给对方观看，等级合计直到和给人观看的仪式怪兽的等级相同为止，把除外的自己的卡名不同的「复仇死者」怪兽任意数量里侧守备表示特殊召唤，把那些里侧守备表示怪兽全部解放，从手卡把那只仪式怪兽仪式召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2273734,'星因士 河鼓二',replace(replace('「星因士 河鼓二」的效果1回合只能使用1次。\r\n①：这张卡召唤·反转召唤·特殊召唤成功的场合，以「星因士 河鼓二」以外的自己墓地1只「星守」怪兽为对象才能发动。那只怪兽守备表示特殊召唤。这个效果的发动后，直到回合结束时「星守」怪兽以外的自己场上的怪兽不能攻击。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2287848,'复仇死者的还魂进化','丢弃1张手卡，之后选择自己墓地1只「复仇死者」怪兽和1张仪式魔法卡；那只怪兽守备表示特殊召唤，那张仪式魔法卡加入手卡。可以把墓地的这张卡除外，之后选择除外的5只自己的不死族怪兽；那些怪兽加入卡组洗切，之后从卡组抽1张卡。「复仇死者的还魂进化」的每个效果1回合各能使用1次。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2295440,'一对一','①：从手卡把1只怪兽送去墓地才能发动。从手卡·卡组把1只1星怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2295831,'拼图之圈','①：对方场上有怪兽3只以上存在，自己场上没有卡存在的场合，自己抽卡阶段通过把通常抽卡的这张卡持续公开，那个回合的主要阶段1才能发动。自己从卡组选1张卡，给双方确认加入手卡。','是否要持续公开「拼图之圈」？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2311603,'机关炮装甲车','装备着重型机枪的装甲车，再恶劣的地形都能逾越。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2314238,'黑·魔·导','①：自己场上有「黑魔术师」存在的场合才能发动。对方场上的魔法·陷阱卡全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2316186,'魔法之国的王女-库兰','这张卡不能通常召唤。这张卡只能通过「王女的试炼」的效果才能特殊召唤。自己准备阶段时，给与对方基本分对方场上存在的怪兽数量×600分数值的伤害。','LP伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2322421,'王道战士',replace(replace('「王道同调士」＋调整以外的怪兽2只以上\r\n①：1回合1次，自己主要阶段才能发动。从卡组把1只2星以下的战士族·机械族怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2326738,'死亡骆驼',replace(replace('①：自己主要阶段才能发动。这张卡变成里侧守备表示（1回合只有1次）。\r\n②：这张卡反转召唤成功的场合发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'变成里侧守备表示','抽一张卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2333365,'极星将 提尔','场上没有这张卡以外的名字带有「极星」的怪兽表侧表示存在的场合，这张卡破坏。只要这张卡在场上表侧表示存在，对方不能选择「极星将 提尔」以外的名字带有「极星」的怪兽作为攻击对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2356994,'伟大天狗','这张卡不能特殊召唤。召唤·反转回合的结束阶段时回到主人的手卡。这张卡给与对方玩家战斗伤害的场合，跳过下次的对方回合的战斗阶段。','返回手牌','跳过战斗阶段','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2359348,'灵摆区','①：自己场上的怪兽只有灵摆怪兽的场合，以自己的灵摆区域2张卡为对象才能发动。那2张卡破坏，这个回合双方不能作灵摆召唤以外的特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2362787,'救援光','支付800基本分。把从游戏中除外的1只自己的光属性怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2370081,'钢甲壳','水属性的怪兽才能装备。装备的怪兽攻击力上升400，守备力下降200。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2371506,'超量反射','场上的超量怪兽为对象的效果怪兽的效果·魔法·陷阱卡的发动无效并破坏。那之后，给与对方基本分800分伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2376209,'伯吉斯异兽·伊尔东钵',replace(replace('①：以场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力·守备力直到回合结束时上升500。\r\n②：陷阱卡发动时，连锁那个发动才能把这个效果在墓地发动。这张卡变成通常怪兽（水族·水·2星·攻1200/守0）在怪兽区域特殊召唤（不当作陷阱卡使用）。这个效果特殊召唤的这张卡不受怪兽的效果影响，从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2377034,'新炎狱刺猬','这张卡被战斗破坏的场合，选择对方墓地存在的1张卡从游戏中除外。场上存在的这张卡被卡的效果破坏的场合，选择自己墓地存在的「新炎狱刺猬」以外的1只守备力200以下的炎属性怪兽加入手卡。','除外','加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2390019,'扰乱魔改造',replace(replace('①：把额外卡组1只机械族·光属性的融合怪兽给对方观看，把自己的手卡·场上·墓地的「扰乱」怪兽任意数量除外才能发动。从自己的手卡·卡组·墓地选除外的怪兽数量的在给人观看的怪兽有卡名记述的融合素材怪兽特殊召唤（同名卡最多1张）。\r\n②：把墓地的这张卡除外，以除外的3只自己的「扰乱」怪兽为对象才能发动。那些怪兽加入卡组洗切。那之后，自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'是否继续选择怪兽除外？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2396042,'迪隆的钢铁骑兵',replace(replace('←5 【灵摆】 5→\r\n【怪兽效果】\r\n①：这张卡和灵摆怪兽进行战斗的伤害步骤开始时发动。这张卡的攻击力·守备力直到伤害步骤结束时变成一半。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2403771,'动力工具龙',replace(replace('调整＋调整以外的怪兽1只以上\r\n1回合1次，自己的主要阶段时才能发动。从自己卡组选择3张装备魔法卡，对方从那之中随机选择1张。对方选择的1张卡加入自己手卡，剩下的卡回到卡组洗切。此外，有装备魔法卡装备的这张卡被破坏的场合，可以作为代替把这张卡装备的1张装备魔法卡送去墓地。','\r',char(13)),'\n',char(10)),'选择装备卡加入手卡','是否使用「动力工具龙」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2407147,'熔岩炎火山的侍女','自己墓地有「熔岩炎火山的侍女」以外的名字带有「熔岩」的怪兽存在的场合这张卡被送去墓地时，可以从自己卡组把1只名字带有「熔岩」的怪兽送去墓地。','送去墓地','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2407234,'No.69 纹章神 盾徽',replace(replace('4星怪兽×3\r\n这张卡特殊召唤成功时，这张卡以外的场上的全部超量怪兽的效果无效。此外，自己的主要阶段时，选择这张卡以外的1只超量怪兽才能发动。直到结束阶段时，这张卡当作和选择的怪兽同名卡使用，得到相同效果。「No.69 纹章神 盾徽」的这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'效果无效','获得效果','复制效果结束','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2414168,'中断反抗军',replace(replace('①：自己受到战斗伤害时才能发动。这张卡从手卡特殊召唤。这个效果特殊召唤的这张卡的攻击力上升受到的伤害的数值。\r\n②：1回合1次，这张卡以外的自己的守备表示怪兽被对方怪兽攻击的伤害计算时才能发动。那只自己怪兽只在那次伤害计算时变成和这张卡相同守备力，不会被那次战斗破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2420921,'光道精灵 夏尔','墓地中每有1种名字带有「光道」的怪兽卡，这张卡的攻击力就上升300。每次自己的结束阶段时，将自己卡组最上方的2张卡送去墓地。','从卡组送2张卡去墓地','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2460565,'冲锋陷阵的队长',replace(replace('①：这张卡召唤成功时才能发动。从手卡把1只4星以下的怪兽特殊召唤。\r\n②：只要这张卡在怪兽区域存在，对方不能选择其他的战士族怪兽作为攻击对象。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2461031,'甲虫装机 瓢虫','1回合1次，可以从自己的手卡·墓地把1只名字带有「甲虫装机」的怪兽当作装备卡使用给这张卡装备。这张卡当作装备卡使用而装备中的场合，装备怪兽的等级上升2星，攻击力·守备力上升这张卡的各自数值。此外，可以把当作装备卡使用而装备中的这张卡送去墓地，选择自己场上1只怪兽，等级上升最多2星。','装备','等级上升','等级上升１星','等级上升２星','','','','','','','','','','','','');
INSERT INTO texts VALUES(2468169,'封印师 明晴','少数会使用封印符咒的人物。其来历至今还都是个谜团。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2483611,'水之舞女','从瓶中不断流淌的水，变成龙的形状发动攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2504891,'骷髅祭司','「恶魔的智慧」＋「魔天老」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2511717,'真六武众-阴鬼','这张卡召唤成功时，可以从手卡把1只4星以下的名字带有「六武众」的怪兽特殊召唤。只要自己场上有「真六武众-阴鬼」以外的名字带有「六武众」的怪兽表侧表示存在，这张卡的攻击力上升1500。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2519690,'天翔之龙骑士 盖亚',replace(replace('「暗黑骑士 盖亚」怪兽＋龙族怪兽\r\n①：这张卡只要在怪兽区域存在，卡名当作「龙骑士 盖亚」使用。\r\n②：这张卡特殊召唤成功的场合才能发动。从自己的卡组·墓地选1张「螺旋枪杀」加入手卡。\r\n③：这张卡向对方怪兽攻击宣言时才能发动。那只对方怪兽的表示形式变更。','\r',char(13)),'\n',char(10)),'选1张「螺旋枪杀」加入手卡','对方怪兽的表示形式变更','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2521011,'威炎星-飞燕灼','这张卡可以把自己场上表侧表示存在的3张名字带有「炎舞」的魔法·陷阱卡送去墓地，从手卡特殊召唤。这张卡召唤·特殊召唤成功时，可以从卡组选1张名字带有「炎舞」的陷阱卡在自己场上盖放。「威炎星-飞燕灼」的这个效果1回合只能使用1次。此外，只要这张卡在场上表侧表示存在，自己场上的兽战士族怪兽不会成为对方的卡的效果的对象。','盖放','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2525268,'魔导骑士 防御者','这张卡召唤成功时，给这张卡放置1个魔力指示物（最多1个）。场上表侧表示存在的魔法师族怪兽被破坏的场合，可以作为代替每有1只破坏的魔法师族怪兽把自己场上存在的1个魔力指示物取除。这个效果1回合只能使用1次。','放置魔力指示物','是否使用「魔导骑士 防御者」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2530830,'银河眼光波刃龙',replace(replace('9星怪兽×3\r\n这张卡也能在自己场上的8阶「银河眼」超量怪兽上面重叠来超量召唤。这张卡不能作为超量召唤的素材。\r\n①：1回合1次，把这张卡1个超量素材取除，以场上1张卡为对象才能发动。那张卡破坏。\r\n②：超量召唤的这张卡被对方怪兽的攻击或者对方的效果破坏送去墓地的场合，以自己墓地1只「银河眼光波龙」为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'是否在「银河眼」超量怪兽上面重叠来超量召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2542230,'真红莲之茧',replace(replace('①：以自己场上1只龙族同调怪兽为对象才能把这张卡发动。这张卡当作装备卡使用给那只怪兽装备。\r\n②：用这张卡的效果把这张卡装备的怪兽和对方怪兽进行战斗的场合，直到那次伤害步骤结束时对方场上的全部表侧表示怪兽的效果无效化。\r\n③：这张卡被送去墓地的回合的结束阶段，以自己墓地1只「红莲魔龙」为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2561846,'侵食细胞「A」','对方场上表侧表示存在的1只怪兽放置1个A指示物。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2572890,'威风妖怪大暴风',replace(replace('①：可以把自己场上1只魔法师族·风属性怪兽解放把以下效果发动。\r\n●怪兽的效果发动时才能发动。那个发动无效并破坏。\r\n●自己或者对方把怪兽特殊召唤之际才能发动。那次特殊召唤无效，那些怪兽破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2584136,'章鲨','这张卡被和对方怪兽的战斗破坏送去墓地时，可以把这张卡当作装备卡使用给那只对方怪兽装备。用这个效果把这张卡装备的怪兽攻击力变成0，不能把表示形式变更。','装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2602411,'破坏剑-魔法破坏之剑',replace(replace('①：自己主要阶段以自己场上1只「巴斯达·布雷达」为对象才能发动。从自己的手卡·场上把这只怪兽当作装备卡使用给那只自己怪兽装备。\r\n②：这张卡装备中的场合，对方不能把墓地的怪兽的效果发动。\r\n③：把装备的这张卡送去墓地，以「破坏剑-魔法破坏之剑」以外的自己墓地1只「破坏剑」怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2618045,'光子充能人','1回合1次，自己的主要阶段时才能发动。这张卡的攻击力直到下次的自己的准备阶段时变成原本攻击力的2倍。这个效果发动的回合，这张卡不能攻击。','攻击变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2619149,'剑斗兽 盾斗','这张卡用名字带有「剑斗兽」的怪兽的效果特殊召唤成功的场合，这张卡战斗破坏对方怪兽送去墓地时，可以从自己卡组把1张名字带有「剑斗兽」的卡加入手卡。这张卡进行战斗的战斗阶段结束时可以让这张卡回到卡组，从卡组把「剑斗兽 盾斗」以外的1只名字带有「剑斗兽」的怪兽在自己场上特殊召唤。','检索卡组','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2648201,'异热同心武器-极星神马圣铠','自己的主要阶段时，手卡或者自己场上的这只怪兽可以当作攻击力上升1000的装备卡使用给自己场上的名字带有「希望皇 霍普」的怪兽装备。装备怪兽被对方破坏让这张卡被送去墓地时，可以选择自己墓地1只名字带有「希望皇 霍普」的怪兽特殊召唤。「异热同心武器-极星神马圣铠」在自己场上只能有1张表侧表示存在。','装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2671330,'超级槌首龙','与这张卡进行战斗且未被破坏的对方怪兽，在伤害步骤终了时弹回其持有者手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2694423,'美杜莎异虫','这张卡1个回合1次可以变成里侧守备表示。这张卡反转召唤成功时，对方场上1只怪兽破坏。','变成里侧守备','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2729285,'毛绒动物·猫',replace(replace('「毛绒动物·猫」的效果1回合只能使用1次。\r\n①：这张卡成为融合召唤的素材送去墓地的场合，以自己墓地1张「融合」为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2732323,'诱饵龙','①：这张卡被选择作为攻击对象的场合，以自己墓地1只7星以上的龙族怪兽为对象发动。那只怪兽特殊召唤，攻击对象转移为那只怪兽进行伤害计算。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2743001,'水晶机巧-轴子凤凰',replace(replace('同调怪兽调整＋调整以外的同调怪兽1只以上\r\n①：这张卡同调召唤成功的场合才能发动。对方的场上·墓地的魔法·陷阱卡全部除外。\r\n②：同调召唤的这张卡被战斗·效果破坏的场合，以这张卡以外的自己墓地1只怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2759860,'灰篮撞击',replace(replace('「灰篮撞击」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：以这张卡以外的自己场上1张「灰篮」卡和对方场上1张卡为对象才能把这个效果发动。那些卡破坏。\r\n②：自己结束阶段才能把这个效果发动。从卡组把1张「灰篮」卡加入手卡。','\r',char(13)),'\n',char(10)),'卡片破坏','从卡组把1张「灰篮」卡加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2766877,'大薰风凤凰',replace(replace('2星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除，选择自己场上表侧表示存在的1只风属性怪兽才能发动。这个回合，选择的怪兽在同1次的战斗阶段中可以作2次攻击。','\r',char(13)),'\n',char(10)),'两次攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2772236,'电气奇美拉',replace(replace('名字带有「电气」的调整＋调整以外的雷族怪兽1只以上\r\n这张卡可以直接攻击对方玩家。这张卡直接攻击给与对方基本分战斗伤害时，对方手卡随机1张到卡组最上面放置。','\r',char(13)),'\n',char(10)),'返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2779999,'棘之壁','自己场上表侧表示存在的植物族怪兽被选择作为攻击对象时才能发动。对方场上存在的攻击表示怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2783661,'青色眼睛的威光',replace(replace('「青色眼睛的威光」在1回合只能发动1张。\r\n①：从手卡·卡组把1只「青眼」怪兽送去墓地，以场上1只表侧表示怪兽为对象才能发动。那只怪兽只要在场上表侧表示存在不能攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2792265,'异形的从者','这张卡可以直接攻击对方玩家。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2810642,'PSY骨架装备·β',replace(replace('这张卡不能通常召唤，用卡的效果才能特殊召唤。\r\n①：自己场上没有怪兽存在，对方怪兽的攻击宣言时才能发动。选手卡的这张卡和自己的手卡·卡组·墓地1只「PSY骨架驱动者」特殊召唤，那只攻击怪兽破坏。那之后，战斗阶段结束。这个效果特殊召唤的怪兽全部在结束阶段除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2819435,'幻煌之都 帕西菲斯',replace(replace('这张卡的卡名在规则上当作「海」使用。这张卡的效果发动的回合，自己不能把效果怪兽召唤·特殊召唤。\r\n①：1回合1次，自己对通常怪兽1只的召唤·特殊召唤成功的场合发动。从卡组把1张「幻煌龙」卡加入手卡。\r\n②：自己场上没有衍生物存在，对方把魔法·陷阱·怪兽的效果发动的场合才能发动。在自己场上把1只「幻煌龙衍生物」（幻龙族·水·8星·攻/守2000）特殊召唤。','\r',char(13)),'\n',char(10)),'卡组检索','衍生物','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2819436,'幻煌龙衍生物','由「幻煌之都 帕西菲斯」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2830619,'火焰毒蛇','敏捷的行动、从口中吐出火焰的蛇。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2830693,'彩虹栗子球',replace(replace('「彩虹栗子球」的①②的效果1回合各能使用1次。\r\n①：对方怪兽的攻击宣言时，以那1只攻击怪兽为对象才能发动。这张卡从手卡当作装备卡使用给那只怪兽装备。装备怪兽不能攻击。\r\n②：这张卡在墓地存在的场合，对方怪兽的直接攻击宣言时才能发动。这张卡从墓地特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2833249,'龙之血族','自己场上所有怪兽，直到结束阶段时为止全部变成龙族。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2843014,'迅捷翻车鱼','这张卡被战斗破坏送去墓地时，从自己卡组把1只鱼族怪兽送去墓地。那之后，可以从自己卡组把1只「迅捷翻车鱼」特殊召唤。','送墓','是否要特殊召唤「迅捷翻车鱼」？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2851070,'魔镜导士 限界反射者','攻击表示的这张卡被对方怪兽攻击的场合，对方受到数值与攻击怪兽攻击力相同的伤害。战斗伤害计算后，这张卡被破坏。','伤害','自坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2857636,'梦幻崩影·凤凰',replace(replace('卡名不同的怪兽2只\r\n这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡连接召唤成功的场合，丢弃1张手卡，以对方场上1张魔法·陷阱卡为对象才能发动。那张卡破坏。这个效果的发动时这张卡是互相连接状态的场合，再让自己可以从卡组抽1张。\r\n②：只要这张卡在怪兽区域存在，自己场上的互相连接状态的怪兽不会被战斗破坏。','\r',char(13)),'\n',char(10)),'','是否抽卡？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2863439,'幻象鸟','能够从手里的镜子中召唤出同伴的鸟怪。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2903036,'牲祭人偶','祭掉自己场上1只怪兽发动。从手卡特殊召唤1只可以通常召唤的7星怪兽。特殊召唤出的怪兽本回合不能攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2906250,'捕猎蛇','奸诈狡猾的蛇，注意其用粗长的身体束缚攻击！','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2924048,'借机巧整备藏','自己场上有名字带有「机巧」的怪兽表侧守备表示存在的场合才能发动。对方发动的魔法·陷阱卡的发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2926176,'王家的诅咒','①：只以场上的魔法·陷阱卡1张为对象并要让那张卡破坏的魔法·陷阱卡发动时才能发动。那个发动无效并破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2930675,'通往星遗物的钥匙',replace(replace('①：这张卡的发动时，可以从除外的自己的卡之中以1只「机界骑士」怪兽或者1张「星遗物」卡为对象。那个场合，那张卡加入手卡。\r\n②：只要自己场上有「机界骑士」怪兽存在，和那怪兽相同纵列发动的对方的陷阱卡的效果无效化。','\r',char(13)),'\n',char(10)),'是否把除外的卡加入手卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2948263,'隆隆隆石人-黄金形态','这张卡不能通常召唤。把自己场上1只名字带有「隆隆隆」的怪兽解放的场合才能特殊召唤。这张卡的攻击力变成解放的那只怪兽的原本攻击力2倍的数值。这张卡的战斗发生的对对方的战斗伤害变成一半。此外，1回合1次，对方场上有效果怪兽的效果发动时发动。这张卡的攻击力下降1500，那个效果无效。','效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2956282,'自然木鳞龙',replace(replace('地属性调整＋调整以外的地属性怪兽1只以上\r\n①：陷阱卡发动时，把自己墓地2张卡除外才能发动。这张卡在场上表侧表示存在的场合，那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'陷阱卡的发动无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2957055,'魔头邪龙','长有两个头的龙。用两张嘴咬碎敌人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2964201,'蛋龙','被蛋壳包围的龙。如果认为只是个小鬼的话会吃苦头的哦。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2971090,'战神 奥利安','被誉为战神的天使。不过谁也没见过那场战事。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2978414,'No.46 神影龙 胶子龙',replace(replace('龙族8星怪兽×2\r\n自己场上没有这张卡以外的怪兽存在的场合，1回合1次，可以把这张卡1个超量素材取除，从以下效果选择1个发动。\r\n●从手卡把1只龙族怪兽特殊召唤。\r\n●选择对方场上1只龙族怪兽得到控制权。\r\n●直到对方回合结束时，对方场上的龙族怪兽不能把效果发动。','\r',char(13)),'\n',char(10)),'从手卡把1只龙族怪兽特殊召唤','选择对方场上1只龙族怪兽得到控制权','对方场上的龙族怪兽不能把效果发动','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2980764,'神圣之灯','这张卡不会被和暗属性怪兽的战斗破坏，那次战斗发生的对自己的战斗伤害变成0。只要这张卡在场上表侧表示存在，暗属性怪兽不能攻击宣言，双方不能把暗属性怪兽召唤·特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(2986553,'夜蔷薇之骑士','只要这张卡在场上表侧表示存在，对方不能选择植物族怪兽作为攻击对象。这张卡召唤成功时，可以从手卡把1只4星以下的植物族怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3019642,'电子暗黑龙骨',replace(replace('①：这张卡召唤成功的场合，以自己墓地1只3星以下的龙族怪兽为对象发动。那只龙族怪兽当作装备卡使用给这张卡装备。\r\n②：这张卡的攻击力上升这张卡的效果装备的怪兽的原本攻击力数值。\r\n③：这张卡战斗破坏对方怪兽的场合发动。给与对方300伤害。\r\n④：这张卡被战斗破坏的场合，作为代替把这张卡的效果装备的怪兽破坏。','\r',char(13)),'\n',char(10)),'装备','伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3027001,'伪陷阱','把自己场上的陷阱卡破坏的魔法·陷阱·效果怪兽的效果对方发动时才能发动，把这张卡作为代替破坏，其他的自己的陷阱卡不破坏。盖放的卡破坏的场合，那些卡全部翻开确认。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3030892,'奴隶猿','这张卡被战斗破坏送去墓地时，可以从卡组把1只名字带有「剑斗兽」的4星以下怪兽在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3055837,'黑板擦的陷阱','给与战斗伤害以外伤害的效果发动时才能发动。使自己所受的效果伤害无效，对方从手卡中选择1张丢弃。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3056267,'王座侵略者','反转：选择对方场上存在的1只怪兽，那只怪兽和这张卡的控制权交换。这个效果在战斗阶段不能发动。','交换控制权','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3064425,'超重武者装留 爆炎',replace(replace('①：自己主要阶段以自己场上1只「超重武者」怪兽为对象才能发动。自己的手卡·场上的这只怪兽当作守备力上升1000的装备卡使用给那只怪兽装备。\r\n②：自己场上有守备表示的「超重武者」怪兽存在，对方在战斗阶段把魔法·陷阱·怪兽的效果发动时，把墓地的这张卡除外才能发动。那个发动无效并破坏。那之后，场上的怪兽全部破坏，双方玩家受到1000伤害。','\r',char(13)),'\n',char(10)),'给「超重武者」怪兽装备','无效并破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3070049,'雪龙','这张卡被战斗或者卡的效果破坏送去墓地时，给场上表侧表示存在的全部怪兽放置1个冰指示物。','放置指示物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3072077,'回归僵尸','自己的准备阶段时，这张卡在墓地存在并且自己手卡是0张的场合，可以支付500基本分把这张卡加入手卡。','加入手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3072808,'黑羽-天狗风之飞廉','这张卡在墓地存在，对方怪兽的直接攻击让自己受到2000以上的战斗伤害时，选择自己墓地存在的1只名字带有「黑羽」的3星以下的怪兽发动。选择的怪兽和这张卡从墓地特殊召唤。这个效果特殊召唤的怪兽的效果无效化。「黑羽-天狗风之飞廉」的效果在决斗中只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3078576,'八汰乌','这张卡不能特殊召唤。召唤·反转的回合的结束阶段时回到主人的手卡。这张卡给与对方玩家战斗伤害的场合，跳过下次的对方的回合的抽卡阶段。','回到手牌','跳过下次抽卡阶段','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3105404,'明亮火花',replace(replace('「明亮火花」在1回合只能发动1张。\r\n①：自己场上的「宝石骑士」怪兽被对方怪兽的攻击或者对方的效果破坏的场合，以破坏的那1只怪兽为对象才能发动。给与对方那只怪兽的原本攻击力数值的伤害。\r\n②：这张卡在墓地存在的场合，把手卡1张「宝石骑士」卡送去墓地才能发动。这张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3113667,'钢铁袭击者',replace(replace('①：只要这张卡在场地区域存在，自己的机械族·暗属性怪兽在1回合各有1次不会被战斗破坏，那次战斗让自己受到战斗伤害的场合，攻击力上升那个数值。\r\n②：1回合1次，自己场上的原本的种族·属性是机械族·暗属性的怪兽用战斗或者自身的效果破坏场上的卡的场合才能发动。从手卡把1只机械族·暗属性怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3113836,'宝石骑士·斜绿',replace(replace('「宝石骑士」怪兽＋光属性怪兽\r\n这张卡用以上记卡为融合素材的融合召唤才能从额外卡组特殊召唤。\r\n①：只要这张卡在怪兽区域存在，自己在通常召唤外加上只有1次，自己主要阶段可以把1只怪兽通常召唤。','\r',char(13)),'\n',char(10)),'「宝石骑士·斜绿」的效果召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3117804,'超重武者 大弁庆-K',replace(replace('①：这张卡召唤·特殊召唤成功时才能发动。这张卡的表示形式变更。\r\n②：只要这张卡在怪兽区域存在，自己场上的「超重武者」怪兽可以用表侧守备表示的状态作出攻击。那个场合，那怪兽用守备力当作攻击力使用进行伤害计算。','\r',char(13)),'\n',char(10)),'改变表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3129635,'诅咒之像','这张卡发动后变成怪兽卡（岩石族·暗·4星·攻1800/守1000）在自己的怪兽卡区域特殊召唤。这张卡在场上当作怪兽使用而存在，这张卡以外的当作怪兽使用的陷阱卡和对方怪兽进行战斗的场合，那只对方怪兽在伤害计算后破坏。这张卡也当作陷阱卡使用。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3134241,'飞螳螂','拥有飞行能力的螳螂，非常喜欢吃昆虫。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3136426,'等级限制B地区','场上以表侧表示存在的所有4星以上的怪兽全部变成守备表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3146695,'念力反应器','自己场上有念动力族怪兽表侧表示存在的场合才能发动。自己场上表侧表示存在的念动力族怪兽在这个回合和对方怪兽进行过战斗时，把那只念动力族怪兽和对方怪兽从游戏中除外。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3149764,'图坦假面','场上表侧表示存在的1只不死族怪兽为对象的魔法·陷阱卡的发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3160805,'明与宵的逆转',replace(replace('可以从以下效果选择1个发动。「明与宵的逆转」的效果1回合只能使用1次。\r\n●从手卡把1只战士族·光属性怪兽送去墓地。那之后，和那只怪兽相同等级的1只战士族·暗属性怪兽从卡组加入手卡。\r\n●从手卡把1只战士族·暗属性怪兽送去墓地。那之后，和那只怪兽相同等级的1只战士族·光属性怪兽从卡组加入手卡。','\r',char(13)),'\n',char(10)),'发动效果','是否现在使用「明与宵的逆转」的效果？','从手卡把1只战士族·光属性怪兽送去墓地。那之后，和那只怪兽相同等级的1只战士族·暗属性怪兽从卡组加入手卡','从手卡把1只战士族·暗属性怪兽送去墓地。那之后，和那只怪兽相同等级的1只战士族·光属性怪兽从卡组加入手卡','','','','','','','','','','','','');
INSERT INTO texts VALUES(3167573,'人偶国王',replace(replace('①：对方用抽卡以外的方法从卡组把怪兽加入手卡时才能发动。这张卡从手卡特殊召唤。\r\n②：这张卡用这张卡的①的效果特殊召唤成功的场合，下次的自己回合的结束阶段发动。这张卡破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3170832,'橐蜚','这鸟出现的时候，就是什么不幸之事发生前的征兆。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3171055,'连锁解咒','①：魔法·陷阱卡发动时才能发动。从发动的那张魔法·陷阱卡的控制者卡组把同名卡全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3204467,'索利德异虫','这张卡的守备力上升自己墓地存在的名字带有「异虫」的爬虫族怪兽数量×100的数值。这张卡被攻击，对方玩家受到战斗伤害的场合，那个伤害步骤结束时把对方场上存在的1张魔法或者陷阱卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3211439,'禁断的异本','①：宣言1个怪兽卡的种类（融合·同调·超量）才能发动。宣言的种类的怪兽在场上有2只以上表侧表示存在的场合，双方玩家必须把场上的那个种类的怪兽全部送去墓地。','融合','同调','超量','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3244563,'盾矛','选择场上表侧表示存在的1只怪兽发动。选择的怪兽的攻击力·守备力直到结束阶段时上升400。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3280747,'第六感','自己在1至6的范围里宣言2个数字。对方丢出1个「骰子」，丢出的数目是宣言的数字的其中1个，自己就抽相等数目的卡。猜不中的场合，自己卡组从上面丢弃「骰子」丢出数目的卡去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3283679,'进化虫·兔鳄','这张卡召唤成功时，可以从卡组把1只名字带有「进化龙」的怪兽送去墓地。此外，这张卡反转时，可以从卡组把1只名字带有「进化虫」的怪兽特殊召唤。','送墓','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3298689,'升阶魔法-幻影骑士团的出击',replace(replace('①：自己·对方的主要阶段，以自己场上1只没有超量素材的暗属性超量怪兽为对象才能发动。比那只自己怪兽阶级高1阶的1只暗属性超量怪兽在作为对象的怪兽上面重叠当作超量召唤从额外卡组特殊召唤，把这张卡在下面重叠作为超量素材。\r\n②：自己主要阶段把墓地的这张卡除外，以自己场上1只暗属性超量怪兽为对象才能发动。把手卡1只「幻影骑士团」怪兽在那只怪兽下面重叠作为超量素材。','\r',char(13)),'\n',char(10)),'特殊召唤','超量素材','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3300267,'圣刻龙-舒龙','这张卡可以把自己场上1只名字带有「圣刻」的怪兽解放从手卡特殊召唤。1回合1次，可以把这张卡以外的自己的手卡·场上1只名字带有「圣刻」的怪兽解放，选择对方场上1张魔法·陷阱卡破坏。这张卡被解放时，从自己的手卡·卡组·墓地选1只龙族的通常怪兽，攻击力·守备力变成0特殊召唤。','破坏','特殊召唤','出场方式为特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3366982,'乘龙的翼龙战士',replace(replace('「宝贝龙」＋「翼龙战士」\r\n①：对方场上的表侧表示怪兽只有地·水·炎属性怪兽的场合，这张卡可以直接攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3370104,'电子凤凰','只要这张卡在自己场上表侧攻击表示存在，以自己场上存在的1只机械族怪兽为对象的魔法·陷阱卡的效果无效。场上表侧表示存在的这张卡被战斗破坏送去墓地时，可以从自己的卡组抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3381441,'守墓的巫女','只要这张卡在场上表侧表示存在，场地变成「王家长眠之谷」。场地魔法卡表侧表示存在的场合，这个效果不适用。此外，只要这张卡在场上表侧表示存在，场上的名字带有「守墓」的怪兽的攻击力·守备力上升200。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3395226,'幻奏的音姬 天才之莫扎特',replace(replace('这张卡的效果发动的回合，自己不能把光属性以外的怪兽特殊召唤。\r\n①：1回合1次，自己主要阶段才能发动。从手卡把1只天使族·光属性怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3405259,'C-粉碎翼龙',replace(replace('①：1回合1次，可以从以下效果选择1个发动。\r\n●以自己场上1只机械族·光属性怪兽为对象，把这张卡当作装备卡使用给那只怪兽装备。装备怪兽被战斗·效果破坏的场合，作为代替把这张卡破坏。\r\n●装备的这张卡特殊召唤。\r\n②：装备怪兽不受其他的对方的陷阱卡的效果影响。\r\n③：这张卡从场上送去墓地的场合才能发动。从手卡把1只同盟怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3422200,'水晶机巧-柠晶龙',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡在手卡·墓地存在的场合，从手卡把「水晶机巧-柠晶龙」以外的1张「水晶机巧」卡丢弃才能发动。这张卡守备表示特殊召唤。那之后，选自己场上1张卡破坏。\r\n②：场上的这张卡被战斗·效果破坏的场合才能发动。从卡组把1只「水晶机巧」怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3428069,'使破坏剑的高手-巴斯达·布雷达',replace(replace('①：这张卡的卡名只要在场上·墓地存在当作「巴斯达·布雷达」使用。\r\n②：对方场上的怪兽被战斗·效果破坏送去墓地的场合，以破坏的那1只怪兽为对象才能发动。那只怪兽当作装备卡使用给这张卡装备。\r\n③：1回合1次，把这张卡装备的1张怪兽卡送去墓地才能发动。和送去墓地的那张怪兽卡相同种族的对方场上的怪兽全部破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3429238,'钻头战士',replace(replace('「钻头同调士」＋调整以外的怪兽1只以上\r\n1回合1次，自己的主要阶段时把这张卡的攻击力变成一半数值，这个回合这张卡可以直接攻击对方玩家。此外，自己的主要阶段时只有1次，可以丢弃1张手卡把这张卡从游戏中除外。下次的自己的准备阶段时，这张卡的效果除外的这张卡在自己场上特殊召唤。那之后，把自己墓地存在的1只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'攻击力变成一半数值，这个回合这张卡可以直接攻击','把这张卡从游戏中除外','特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3431737,'爆裂兽','把这张卡从手卡丢弃去墓地发动。从自己卡组把1张「爆裂模式」加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3492538,'附火器的机甲铠',replace(replace('昆虫族怪兽才能装备。\r\n①：装备怪兽的攻击力上升700。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3493058,'骰子旋风',replace(replace('①：掷1次骰子，出现的数目的效果适用。\r\n●2·3·4：选场上1张魔法·陷阱卡破坏。\r\n●5：选场上2张魔法·陷阱卡破坏。\r\n●1·6：自己受到1000伤害。','\r',char(13)),'\n',char(10)),'投掷骰子','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3493978,'首领龟','这张卡召唤·反转召唤成功时，可以从自己的手卡特殊召唤任意数量的「首领龟」上场。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3510565,'隐形鸟','1回合1次，可以变成里侧守备表示。这张卡反转召唤成功时，给与对方基本分1000分的伤害。','变成里侧守备表示','给予对方1000的伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3534077,'热血兽士 狼吠',replace(replace('「热血兽士 狼吠」的效果1回合只能使用1次。\r\n①：以自己墓地1只兽战士族·炎属性·4星怪兽为对象才能发动。那只怪兽守备表示特殊召唤。这个效果特殊召唤的怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3536537,'入魔妖龙 扎哈克','①：场上的表侧表示的这张卡被对方破坏送去墓地的场合，以场上1只特殊召唤的5星以上的怪兽为对象发动。那只5星以上的怪兽破坏。','怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3549275,'骰子壶','反转：双方各自投掷1个骰子。投掷出来的数目比另一方低的玩家，受到（另一方投掷出的数目×500）的基本分的伤害。如果输给投掷出数目6的场合，输的玩家受到的是6000分的伤害。平局的场合再掷1次。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(3557275,'白尘妖','小恶魔尘妖一族的白色家伙。对身上连自己也惊讶的洁白好像灰常渍豪。','','','','','','','','','','','','','','','','');
