INSERT INTO texts VALUES(3814632,'超巨大空中宫殿 钟声协和号',replace(replace('10星怪兽×2\r\n这个卡名的效果1回合只能使用1次，这个效果发动的回合，这张卡不能攻击。\r\n①：把这张卡1个超量素材取除，以对方场上1张卡为对象才能发动。那张对方的卡破坏，给与对方1000伤害。','\r',char(13)),'\n',char(10)),'破坏并伤害','','','','','','','','','','','','','','','');
