INSERT INTO texts VALUES(4857085,'Ω眼镜','这张卡只有自己场上的怪兽才能装备。1回合1次，对方手卡随机选择1张确认。这个效果发动的回合，装备怪兽不能攻击。','确认手牌','','','','','','','','','','','','','','','');
