INSERT INTO texts VALUES(4929256,'冰帝 美比乌斯','①：这张卡上级召唤成功时，以场上最多2张魔法·陷阱卡为对象才能发动。那些卡破坏。','场上的最多2张魔法·陷阱卡破坏','','','','','','','','','','','','','','','');
