INSERT INTO texts VALUES(4939890,'影依刺猬',replace(replace('「影依刺猬」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：这张卡反转的场合才能发动。从卡组把1张「影依」魔法·陷阱卡加入手卡。\r\n②：这张卡被效果送去墓地的场合才能发动。从卡组把「影依刺猬」以外的1只「影依」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'检索魔陷','检索怪兽','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(4941482,'密探蜘蛛','场上存在的这张卡被卡的效果送去墓地时，得到对方场上守备表示存在的1只怪兽的控制权。','获得控制权','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(4998619,'方界超兽 破坏之乾闼尔',replace(replace('这张卡不能通常召唤。把自己场上3只「方界」怪兽送去墓地的场合才能特殊召唤。\r\n①：这个方法特殊召唤的这张卡的攻击力上升3000。\r\n②：这张卡在同1次的战斗阶段中可以作3次攻击。\r\n③：这张卡被对方送去墓地的场合，以自己墓地最多3只「方界」怪兽为对象才能发动。那些怪兽特殊召唤。那之后，可以从自己的卡组·墓地选1张「方界」卡加入手卡。','\r',char(13)),'\n',char(10)),'是否把1张「方界」卡加入手卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5010422,'占术姬 通灵板摩莉甘','①：这张卡反转的场合发动。那个回合的结束阶段把对方场上的守备表示怪兽全部破坏，给与对方破坏的怪兽数量×500伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5014629,'潜航母舰 航空鲨',replace(replace('3星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除才能发动。给与对方基本分从游戏中除外的自己怪兽数量×100的数值的伤害。','\r',char(13)),'\n',char(10)),'伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5026221,'神星波动','①：1回合1次，自己主要阶段以及对方战斗阶段才能把这个效果发动。从手卡把1只「星守」怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5037726,'光灵术-「圣」','把自己场上1只光属性怪兽解放，选择从游戏中除外的1只怪兽才能发动。对方可以从手卡把1张陷阱卡给人观看让这张卡的效果无效。没给观看的场合，选择的怪兽在自己场上特殊召唤。','是否把1张陷阱卡给对方观看？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5043010,'防火龙',replace(replace('怪兽2只以上\r\n①：只在这张卡在场上表侧表示存在才有1次，以最多有和这张卡互相连接的怪兽数量的自己或者对方的场上·墓地的怪兽为对象才能发动。那些怪兽回到持有者手卡。这个效果在对方回合也能发动。\r\n②：这张卡所连接区的怪兽被战斗破坏的场合或者被送去墓地的场合才能发动。从手卡把1只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'回到持有者手卡','特殊召唤','已发动过效果','','','','','','','','','','','','','');
