INSERT INTO texts VALUES(5043012,'防火龙',replace(replace('怪兽2只以上\r\n①：只在这张卡在场上表侧表示存在才有1次，以最多有和这张卡互相连接的怪兽数量的自己或者对方的场上·墓地的怪兽为对象才能发动。那些怪兽回到持有者手卡。这个效果在对方回合也能发动。\r\n②：这张卡所连接区的怪兽被战斗破坏的场合或者被送去墓地的场合才能发动。从手卡把1只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'回到持有者手卡','特殊召唤','已发动过效果','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5050644,'芳香花园',replace(replace('①：1回合1次，自己场上有「芳香」怪兽存在的场合才能发动。自己回复500基本分。这个效果的发动后，直到下次的对方回合结束时自己场上的怪兽的攻击力·守备力上升500。\r\n②：自己场上的「芳香」怪兽被战斗·效果破坏送去墓地的场合发动。自己回复1000基本分。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
