INSERT INTO texts VALUES(5265750,'海贼船 血骸骨号','船头仿照红色骸骨的海盗船。在所有海域神出鬼没地出现，袭击客船和货船。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5284653,'入魔鬼火','和这张卡进行战斗的效果怪兽的效果在伤害计算后无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5285665,'元素英雄 新水泡侠','这张卡不能通常召唤。把自己场上存在的「元素英雄 水泡侠」和手卡的「突然变异」送去墓地的场合才能特殊召唤。只要这张卡在场上表侧表示存在，卡名当作「元素英雄 水泡侠」使用。和这张卡进行战斗的怪兽在伤害步骤结束时破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5288597,'升级转变','把自己场上表侧表示存在的1只怪兽送去墓地才能发动。和送去墓地的怪兽种族·属性相同而等级高1星的1只怪兽从卡组特殊召唤。「升级转变」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5291803,'先史遗产 图拉守护者','场地魔法卡表侧表示存在的场合，这张卡可以从手卡特殊召唤。「先史遗产 图拉守护者」在自己场上只能有1只表侧表示存在。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5309481,'苏生的魔王 哈·迪斯',replace(replace('「僵尸带菌者」＋调整以外的不死族怪兽1只以上\r\n只要这张卡在场上表侧表示存在，自己场上存在的不死族怪兽战斗破坏的效果怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5318639,'旋风','①：以场上1张魔法·陷阱卡为对象才能发动。那张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5325424,'牙龙转生','选择从游戏中除外的1只自己的龙族怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5338223,'强制进化','把自己场上1只名字带有「进化虫」的怪兽解放发动。从卡组把1只名字带有「进化龙」的怪兽特殊召唤。这个效果特殊召唤的怪兽变成当作用名字带有「进化虫」的怪兽的效果特殊召唤使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5361647,'燃烧拳击手 不堪一击拳手','这张卡被选择作为攻击对象时，这张卡破坏。这张卡被卡的效果送去墓地时，可以从自己墓地选择「燃烧拳击手 不堪一击拳手」以外的1只名字带有「燃烧拳击手」的怪兽加入手卡。','破坏','加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5368615,'蒸汽旋翼机人','「旋翼机人」＋「蒸汽机人」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5371656,'食魂的魔刀','这张卡只能装备在自己场上存在的3星以下的通常怪兽身上。这张卡发动时，祭掉自己场上除装备这张卡的怪兽以外的所有通常怪兽（衍生物除外）。每祭掉1只通常怪兽，装备这张卡的怪兽攻击力上升1000点。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5373478,'电子龙二型','这张卡向对方怪兽攻击的场合，伤害步骤内攻击力上升300。1回合1次，可以把手卡1张魔法卡给对方观看，这张卡的卡名直到结束阶段时当作「电子龙」使用。此外，这张卡在墓地存在的场合，这张卡的卡名当作「电子龙」使用。','卡名当成「电子龙」','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5388481,'燃烧士兵','热烈燃烧的特种部队工作者，火药专家。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5399521,'音响战士 麦克风',replace(replace('←1 【灵摆】 1→\r\n①：另一边的自己的灵摆区域没有「音响战士」卡存在的场合，这张卡的灵摆刻度变成4。\r\n②：自己结束阶段，以除外的1只自己的「音响战士」怪兽为对象才能发动。那只怪兽加入手卡。\r\n【怪兽效果】\r\n①：这张卡可以把自己场上3个音响指示物取除，从手卡特殊召唤。\r\n②：这张卡召唤·特殊召唤成功的回合，自己在通常召唤外加上只有1次，自己主要阶段可以把1只怪兽召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5405694,'混沌战士','「混沌的仪式」降临。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5434080,'无头骑士','被判决为反叛者遭处刑的骑士的亡灵，为寻找失去的东西，无论遇到谁都会受到他的攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5438492,'荒野的女战士','①：这张卡被战斗破坏送去墓地时才能发动。从卡组把1只攻击力1500以下的战士族·地属性怪兽攻击表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5464695,'火炎木人18','全身被灼热火焰吞没的巨木化身。火焰攻击虽然强力，但由于自身着火所以时日不多了。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5479217,'朱罗纪冲击','自己场上有攻击力2500以上的恐龙族怪兽存在的场合才能发动。场上的卡全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5489987,'花札卫-樱上幕帘-',replace(replace('这张卡不能通常召唤。这张卡的①的效果可以特殊召唤。\r\n①：把手卡的这张卡给对方观看才能发动。自己从卡组抽1张，给双方确认。那是「花札卫」怪兽的场合，这张卡特殊召唤。不是的场合，那张卡和这张卡送去墓地。\r\n②：自己的「花札卫」怪兽和对方怪兽进行战斗的从伤害步骤开始时到伤害计算前，把这张卡从手卡丢弃才能发动。那只进行战斗的自己怪兽的攻击力直到回合结束时上升1000。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5494820,'旋风镭射','只有「超时空战斗机 V形蛇」可以装备。攻击力上升300。装备这张卡的怪兽攻击守备表示的怪兽时，超过被攻击的守备力的那个数值，对方的基本分受到这个差值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5498296,'暗黑界的斥侯 斯卡','这张卡被战斗破坏送去墓地时，从卡组选择1张名字中带有「暗黑界」的4星以下怪兽卡加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5506791,'威风妖怪·猫',replace(replace('←2 【灵摆】 2→\r\n【怪兽效果】\r\n「威风妖怪·猫」的①的怪兽效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功时才能发动。这个回合的结束阶段，从卡组把1张「威风妖怪」卡加入手卡。\r\n②：这张卡只要在怪兽区域存在，不会成为对方的效果的对象，不会被对方的效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5519829,'心理咨询师 莉莉','这张卡被同调怪兽的同调召唤使用送去墓地的场合，可以支付500基本分让这张卡为同调素材的同调怪兽的攻击力直到这个回合的结束阶段时上升1000。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5554990,'电气金眼鲷','这张卡直接攻击给与对方基本分战斗伤害时，对方手卡随机丢弃1张。','丢弃手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5556499,'机甲要塞','这张卡可以把手卡的机械族怪兽等级合计直到8以上丢弃，从手卡或者墓地特殊召唤。这张卡被战斗破坏送去墓地时，选择对方场上存在的1张卡破坏。此外，自己场上表侧表示存在的这张卡成为对方的效果怪兽的效果的对象时，把对方手卡确认并丢弃1张。','对方场上一张卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5556668,'手札对换','互相公开手卡，互相选择对方1张手卡。选择的卡加入到自己的手卡，在这次决斗中可以使用。（送去墓地的场合回到自己的主人的墓地）','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5560911,'亡龙之战栗-死欲龙',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：这张卡在手卡·墓地存在的场合，把基本分支付一半，以自己场上1只6星以下的怪兽为对象才能发动。这张卡特殊召唤。这个效果特殊召唤的这张卡等级下降作为对象的怪兽的等级数值，从场上离开的场合回到持有者卡组最下面。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5562461,'暗之幻影','场上表侧表示存在的暗属性怪兽为对象的效果怪兽的效果·魔法·陷阱卡的发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5577649,'废铁崩溃','自己场上存在的名字带有「废铁」的怪兽被破坏送去墓地时才能发动。场上表侧表示存在的魔法·陷阱卡全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5592689,'献祭之莲','自己的结束阶段时这张卡在墓地存在，自己场上没有魔法·陷阱卡存在的场合，可以在自己场上表侧攻击表示特殊召唤。这张卡在场上表侧表示存在的场合，每次自己的准备阶段控制者受到1000分伤害。','特殊召唤','伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5600127,'人形鳞虫','「鳞虫」＋「人形史莱姆」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5606466,'异次元的落穴','对方把1只怪兽守备表示盖伏时才能发动。盖伏的那1只怪兽和自己场上的1只怪兽破坏并从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5609226,'调整防护罩','选择自己场上表侧表示存在的1只调整发动。直到下个回合的结束阶段时，选择的1只调整不会被战斗或者卡的效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5614808,'白斗气白鲸',replace(replace('水属性调整＋调整以外的水属性怪兽1只以上\r\n①：这张卡同调召唤成功时才能发动。对方场上的攻击表示怪兽全部破坏。\r\n②：这张卡在同1次的战斗阶段中最多2次可以向怪兽攻击。\r\n③：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。\r\n④：这张卡被对方破坏送去墓地的场合，把这张卡以外的自己墓地1只水属性怪兽除外才能发动。这张卡当作调整使用特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5616412,'反击拳','被攻击的怪兽的守备力比对方攻击怪兽攻击力高的场合，伤害步骤结束后那个攻击的怪兽破坏。（伤害计算适用）','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5628232,'飞企鹅','用看似耳朵一般的头上羽翼在空中飞，很珍奇的企鹅。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5640330,'汉尼拔死灵师',replace(replace('①：这张卡召唤成功的场合发动。给这张卡放置1个魔力指示物（最多1个）。\r\n②：把这张卡1个魔力指示物取除，以场上1张表侧表示的陷阱卡为对象才能发动。那张表侧表示的陷阱卡破坏。','\r',char(13)),'\n',char(10)),'放置指示物','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5645210,'华丽金星','①：只要这张卡在怪兽区域存在，天使族以外的场上的怪兽的攻击力·守备力下降500，自己场上的魔法·陷阱卡的效果的发动以及那些发动的效果不会被无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5650082,'神风防护罩 -大气之力-','①：对方怪兽的攻击宣言时才能发动。对方场上的攻击表示怪兽全部回到持有者手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5672432,'娱乐伙伴复活','①：自己场上的怪兽被战斗·效果破坏的场合才能发动。从自己的手卡·墓地选1只「娱乐伙伴」怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5697558,'地中界 香巴拉',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：作为这张卡的发动时的效果处理，可以从卡组把1只「地中族」怪兽加入手卡。\r\n②：1回合1次，自己主要阶段才能发动。选自己1只里侧守备表示的「地中族」怪兽变成表侧攻击表示或者表侧守备表示。\r\n③：1回合1次，对方怪兽的攻击宣言时才能发动。选自己1只里侧守备表示的「地中族」怪兽变成表侧攻击表示或者表侧守备表示。那之后，可以把那次攻击无效。','\r',char(13)),'\n',char(10)),'是否把「地中族」怪兽加入手卡？','改变表示形式','是否把攻击无效？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5703682,'千之能量','自己场上所有以表侧表示存在的2星通常怪兽（衍生物除外）的原始攻击力·守备力上升1000点。结束阶段时，自己场上存在的2星通常怪兽全部被破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5728014,'对手登场！','选择对方场上表侧表示存在的1只怪兽。从手卡特殊召唤1只和选择怪兽等级一样的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5758500,'魂之解放','从双方的墓地选择最多5张卡，那些卡从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5763020,'泣神的石像','把自己墓地存在的1只调整从游戏中除外发动。这张卡从手卡特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5780210,'创造共鸣者','自己场上有8星以上的同调怪兽表侧表示存在的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5795882,'吸血鬼的领域',replace(replace('①：1回合1次，支付500基本分才能发动。这个回合自己在通常召唤外加上只有1次，自己主要阶段可以把1只「吸血鬼」怪兽召唤。\r\n②：只要这张卡在魔法与陷阱区域存在，自己的「吸血鬼」怪兽给与对方战斗伤害的场合发动。自己基本分回复那个数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5795980,'帝王的轰毅',replace(replace('①：把自己场上1只5星以上的通常召唤的表侧表示怪兽解放，以场上1张表侧表示的卡为对象才能发动。那张卡的效果直到回合结束时无效。那之后，自己从卡组抽1张。\r\n②：自己主要阶段把墓地的这张卡除外，宣言1个属性才能发动。场上的全部表侧表示怪兽直到回合结束时变成宣言的属性。','\r',char(13)),'\n',char(10)),'效果无效','属性改变','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5817857,'核成食尸奴','这张卡的控制者在每次自己的结束阶段从手卡把1张「核成兽的钢核」送去墓地或把手卡1只不死族怪兽给对方观看。或者都不进行让这张卡破坏。自己场上存在的名字带有「核成」的怪兽1只被战斗或者卡的效果破坏的场合，可以作为代替把自己墓地存在的1只名字带有「核成」的怪兽从游戏中除外。','选择一张「核成兽的钢核」送去墓地','选择一张不死族怪兽给对方观看','破坏「核成食尸奴」','是否要使用「核成食尸奴」的效果代替破坏？','','','','','','','','','','','','');
INSERT INTO texts VALUES(5818294,'武神器-边津','自己场上的名字带有「武神」的怪兽成为卡的效果的对象时，把墓地的这张卡从游戏中除外才能发动。那个效果无效。这个效果在对方回合也能发动。','效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5818798,'幻兽王 加泽尔','奔跑起来的速度太快，身姿看似幻影的野兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5821478,'拓扑逻辑轰炸龙',replace(replace('效果怪兽2只以上\r\n①：这张卡在怪兽区域存在，场上的连接怪兽所连接区有这张卡以外的怪兽特殊召唤的场合发动。双方的主要怪兽区域的怪兽全部破坏。这个回合，这张卡以外的自己怪兽不能攻击。\r\n②：这张卡向对方怪兽攻击的伤害计算后发动。给与对方那只对方怪兽的原本攻击力数值的伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5832914,'卡通手提箱','①：自己场上有卡通怪兽存在，对方把怪兽召唤·反转召唤·特殊召唤时才能发动。那些怪兽回到持有者卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5851097,'虚无空间',replace(replace('①：只要这张卡在魔法与陷阱区域存在，双方不能把怪兽特殊召唤。\r\n②：从卡组或者场上有卡被送去自己墓地的场合发动。这张卡破坏。','\r',char(13)),'\n',char(10)),'这张卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5861892,'秘仪之力EX-光之支配者',replace(replace('这张卡不能通常召唤。把自己场上存在的3只怪兽送去墓地的场合才能特殊召唤。这张卡特殊召唤成功时，进行1次投掷硬币得到以下效果。\r\n●表：战斗破坏对方怪兽送去墓地时，可以从自己墓地把1张卡加入手卡。\r\n●里：这张卡为对象的效果怪兽的效果·魔法·陷阱卡的发动无效并破坏。每次这个效果把卡的发动无效，这张卡的攻击力下降1000。','\r',char(13)),'\n',char(10)),'投掷硬币','回收','无效并破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5901497,'女王的影武者','这张卡可以直接攻击对方玩家。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5908650,'幻奏的音姬 殊荣之弗朗索瓦',replace(replace('这张卡的效果发动的回合，自己不能把光属性以外的怪兽的效果发动。\r\n①：1回合1次，以自己墓地1只天使族·光属性怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5914184,'倍返','对方的卡的效果让自己受到1000以上的伤害时才能发动。那个时候受到的伤害每有1000，给这张卡放置1个倍倍指示物。下次的对方回合的结束阶段时，这张卡破坏并给与对方基本分这张卡放置的倍倍指示物数量×2000的数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5915629,'强欲的赠物','对方从卡组抽2张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5929801,'急袭猛禽-模糊伯劳',replace(replace('「急袭猛禽-模糊伯劳」的①②的效果1回合各能使用1次，这张卡的效果发动的回合，自己不是「急袭猛禽」怪兽不能特殊召唤。\r\n①：自己场上有「急袭猛禽-模糊伯劳」以外的「急袭猛禽」怪兽存在的场合才能发动。这张卡从手卡特殊召唤。\r\n②：这张卡被送去墓地的场合才能发动。从卡组把1只「急袭猛禽-模糊伯劳」加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5972394,'模仿黏土',replace(replace('「模仿黏土」在1回合只能发动1张。\r\n①：对方场上有怪兽存在的场合，以自己场上1只2星以下的表侧守备表示怪兽为对象才能发动。那1只同名怪兽从卡组守备表示特殊召唤。这个效果特殊召唤的怪兽从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5973663,'世界树',replace(replace('每次场上存在的植物族怪兽被破坏，给这张卡放置1个花指示物。可以把这张卡放置的花指示物任意数量取除，以下效果适用。\r\n●1个：直到这个回合的结束阶段时，场上表侧表示存在的1只植物族怪兽的攻击力·守备力上升400。\r\n●2个：把场上1张卡破坏。\r\n●3个：选择自己墓地存在的1只植物族怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'场上表侧表示存在的1只植物族怪兽的攻击力·守备力上升400','把场上1张卡破坏','选择自己墓地存在的1只植物族怪兽特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5975022,'剑斗兽 鱼斗','这张卡用名字带有「剑斗兽」的怪兽的效果特殊召唤成功时，把场上1只表侧表示怪兽破坏。这张卡进行战斗的战斗阶段结束时可以让这张卡回到卡组，从卡组把「剑斗兽 鱼斗」以外的1只名字带有「剑斗兽」的怪兽在自己场上特殊召唤。','破坏','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5990062,'大逆转谜题','将自己手卡和场上的卡全部送去墓地。猜自己卡组最上面1张卡的种类（魔法/陷阱/怪兽），若猜对，则将自己与对方的基本分值互换。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(5998840,'XX-剑士 波加特骑士',replace(replace('把这张卡作为同调素材的场合，不是「X-剑士」怪兽的同调召唤不能使用。\r\n①：这张卡召唤成功时才能发动。从手卡把1只4星以下的「X-剑士」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6007213,'神炎皇 乌利亚',replace(replace('这张卡不能通常召唤。把自己场上3张表侧表示的陷阱卡送去墓地的场合才能特殊召唤。\r\n①：这张卡的攻击力上升自己墓地的永续陷阱卡数量×1000。\r\n②：1回合1次，以对方场上盖放的1张魔法·陷阱卡为对象才能发动。盖放的那张卡破坏。不能对应这个效果的发动让魔法·陷阱卡发动。','\r',char(13)),'\n',char(10)),'魔陷破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6021033,'死亡帝王龙',replace(replace('「僵尸带菌者」＋调整以外的不死族怪兽1只以上\r\n①：这张卡特殊召唤成功时，以对方墓地1只不死族怪兽为对象才能发动。那只不死族怪兽在自己场上攻击表示特殊召唤。这张卡从场上离开时那只怪兽破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6022371,'水龙-团簇',replace(replace('这张卡不能通常召唤。「化学结合」魔法·陷阱卡的效果才能特殊召唤。\r\n①：这张卡特殊召唤成功的场合才能发动。对方场上的效果怪兽直到回合结束时攻击力变成0，不能把效果发动。\r\n②：把这张卡解放才能发动。从手卡·卡组把2只「水龙」无视召唤条件守备表示特殊召唤。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6039967,'吸血鬼千金',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：怪兽的攻击宣言时才能发动。这张卡从手卡守备表示特殊召唤。\r\n②：自己的不死族怪兽和对方怪兽进行战斗的伤害计算时1次，支付100的倍数的基本分才能发动（最多3000）。那只自己怪兽的攻击力·守备力只在那次伤害计算时上升支付的数值。\r\n③：这张卡战斗破坏怪兽的战斗阶段结束时才能发动。那些怪兽从墓地尽可能往自己场上特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6061630,'魔力引导者','每次自己或者对方把魔法卡发动，给这张卡放置2个魔力指示物。可以把这张卡放置的魔力指示物任意数量取除，和取除数量相同等级的1只魔法师族怪兽从手卡或者自己墓地特殊召唤。这个效果1回合只能使用1次。','特殊召唤','请选择要取除的魔力指示物数量','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6075801,'巨龙之圣骑士',replace(replace('①：这张卡召唤·特殊召唤成功的场合才能发动。从自己的手卡·卡组把1只7·8星的龙族怪兽当作装备卡使用给这张卡装备。\r\n②：有装备卡装备的这张卡不受其他怪兽的效果影响。\r\n③：把自己场上1只怪兽和这张卡解放，以自己墓地1只7·8星的龙族怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6077601,'魔玩具融合',replace(replace('「魔玩具融合」在1回合只能发动1张。\r\n①：从自己的场上·墓地把「魔玩具」融合怪兽卡决定的融合素材怪兽除外，把那1只融合怪兽从额外卡组融合召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6103114,'鸟嘴蛇','用长长的身体把对方缠起来，用大嘴进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6103294,'绿洲的使者','当自己场上存在表侧表示的3星以下的通常怪兽时，对方不能选择这张卡作为攻击对象。当这张卡在自己场上以表侧表示存在时，这张卡的控制者所受到的，由3星以下的通常怪兽进行战斗所造成的战斗伤害为零。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6104968,'死亡仓鼠','①：这张卡反转的场合才能发动。从卡组把1只「死亡仓鼠」里侧守备表示特殊召唤。','特殊召唤','是否要把一张「死亡仓鼠」里侧守备表示特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6112401,'身剑一体','自己场上存在的怪兽只有表侧表示的名字带有「X-剑士」的怪兽1只的场合才能发动。发动后这张卡变成攻击力上升800的装备卡，给那1只怪兽装备。装备怪兽战斗破坏对方怪兽的场合，从自己卡组抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6133894,'大魔王 魔杰拉','这张卡不能进行通常召唤。这张卡仅当场上存在「万魔殿-恶魔的巢窟-」时，祭掉自己场上1只以表侧表示存在的「杰拉的战士」才能特殊召唤。这张卡特殊召唤成功时，对方随机丢弃3张手卡。此效果仅当自己场上存在「万魔殿 -恶魔的巢窟-」时才适用。','手牌丢弃','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6137095,'误作动','支付500基本分。陷阱卡的发动无效，那张卡按原本的形式放回。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6142213,'一击喷射士','这张卡攻击的场合，这张卡不会被战斗破坏。那次伤害计算后，给与对方基本分攻击对象怪兽的攻击力一半数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6142488,'毛绒动物·鼠',replace(replace('这张卡的效果发动的回合，自己不是「魔玩具」怪兽不能从额外卡组特殊召唤。\r\n①：只在这张卡在场上表侧表示存在才有1次在自己主要阶段才能发动。从卡组把最多2只「毛绒动物·鼠」特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6148016,'齿轮齿轮齿轮','从卡组把2只名字带有「齿轮齿轮人」的怪兽特殊召唤。这个效果特殊召唤的怪兽的等级上升1星。「齿轮齿轮齿轮」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6150044,'天位骑士',replace(replace('「王后骑士」＋「卫兵骑士」＋「国王骑士」\r\n这张卡的融合召唤不用上记卡不能进行。\r\n①：1回合1次，场上的这张卡为对象的怪兽的效果·魔法·陷阱卡发动时，把和那张卡相同种类（怪兽·魔法·陷阱）的1张手卡丢弃才能发动。那个效果无效。','\r',char(13)),'\n',char(10)),'效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6150045,'天位骑士',replace(replace('「王后骑士」＋「卫兵骑士」＋「国王骑士」\r\n这张卡的融合召唤不用上记卡不能进行。\r\n①：1回合1次，场上的这张卡为对象的怪兽的效果·魔法·陷阱卡发动时，把和那张卡相同种类（怪兽·魔法·陷阱）的1张手卡丢弃才能发动。那个效果无效。','\r',char(13)),'\n',char(10)),'效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6165656,'混沌No.88 机关傀儡-灾厄狮子',replace(replace('9星怪兽×4\r\n这张卡用以「No.88 机关傀儡-命运狮子」为对象发动的名字带有「升阶魔法」的魔法卡的效果才能特殊召唤。场上的这张卡不会成为卡的效果的对象。1回合1次，把这张卡1个超量素材取除才能发动。给与对方基本分1000分伤害。此外，自己的结束阶段时对方基本分是2000以下而这张卡没有超量素材的场合，这张卡的控制者决斗胜利。','\r',char(13)),'\n',char(10)),'伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6172122,'真红眼融合',replace(replace('这个卡名的卡在1回合只能发动1张，这张卡发动的回合，自己不能用这张卡的效果以外把怪兽召唤·特殊召唤。\r\n①：从自己的手卡·卡组·场上把融合怪兽卡决定的融合素材怪兽送去墓地，把以「真红眼」怪兽为融合素材的那1只融合怪兽从额外卡组融合召唤。这个效果特殊召唤的怪兽的卡名当作「真红眼黑龙」使用。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6178850,'斗魂','装备怪兽的攻击力对方场上存在的怪兽每有1只上升300。装备怪兽被战斗破坏的场合，可以作为代替把这张卡破坏。','是否使用「斗魂」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6205579,'寄生融合虫',replace(replace('这张卡在这张卡的①的效果适用的场合才能作为融合素材。\r\n①：这张卡可以作为融合怪兽卡有卡名记述的1只融合素材怪兽的代替。那个时候，其他的融合素材怪兽必须是正规品。\r\n②：这张卡特殊召唤成功的场合才能发动。融合怪兽卡决定的包含这张卡的融合素材怪兽从自己场上送去墓地，把那1只融合怪兽从额外卡组融合召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6214884,'暗黑界的狂王 布隆','这张卡给与对方基本分战斗伤害时，可以从自己的手卡选择1张卡丢弃去墓地。','选择1张手卡丢弃去墓地','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6256844,'真次世代涡轮人','这张卡召唤成功时，可以从自己卡组把1只1星的名字带有「次世代」的怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6260554,'弹幕回避','把自己场上的「幻兽机衍生物」全部解放才能发动。效果怪兽的效果·魔法·陷阱卡的发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6276588,'机巧忍者 九一九','这张卡可以攻击的场合必须作出攻击。场上表侧表示存在的这张卡被选择作为攻击对象时，这张卡的表示形式变更。这张卡战斗破坏对方怪兽送去墓地时，选择自己墓地存在的1只4星以下的名字带有「机巧」的怪兽表侧守备表示特殊召唤。','表示形式变更','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6283472,'无形噬体·傲慢',replace(replace('←3 【灵摆】 3→\r\n这张卡的控制者在每次自己准备阶段把自己场上1只怪兽解放。或者不解放让这张卡破坏。\r\n①：只要自己场上有「无形噬体」怪兽存在，双方受到的效果伤害变成0。\r\n【怪兽效果】\r\n①：只要灵摆召唤·反转过的这张卡在怪兽区域存在，双方不是「无形噬体」怪兽不能从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'是否解放自己场上1只怪兽？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6285791,'邪恶蠕虫兽','自己的回合的结束阶段时，场上表侧表示存在的这张卡回到持有者的手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6297941,'强盗鼠','奸诈狡猾的老鼠。使用左手的大钩爪进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6320631,'核成岩人','这张卡被战斗破坏送去墓地时，可以从自己卡组把1张「核成兽的钢核」或者1只4星以下的名字带有「核成」的怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6330307,'暗黑异热同心武器-魔装鵺妖衣','自己的主要阶段时，手卡或者自己场上的这只怪兽可以当作装备卡使用给自己场上的名字带有「混沌No.39」的怪兽装备。此外，这张卡当作装备卡使用而装备中的场合，装备怪兽不会被战斗破坏。装备怪兽的攻击没让对方怪兽被破坏的伤害步骤结束时，可以把那只对方怪兽的攻击力变成0并只再1次可以向同只怪兽继续攻击。','装备','多次攻击','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6337436,'连弹之魔术师','只要这张卡在场上表侧表示存在，每次自己发动通常魔法卡，给与对方基本分400分的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6343408,'奇迹之发掘','①：除外的自己怪兽5只以上存在的场合，以那之内3只为对象才能发动。那些怪兽回到墓地。','请选择要回到墓地的卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6351548,'曙光之骑士','场上的这张卡被送去墓地的场合，可以从卡组把1只光属性怪兽送去墓地。此外，这张卡从卡组送去墓地的场合，选择自己墓地1只光属性怪兽在卡组最上面放置。「曙光之骑士」的效果1回合只能使用1次。','送墓','返回卡组','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6353603,'暗炎星-熊志','1回合1次，这张卡给与对方基本分战斗伤害时，可以从卡组选1张名字带有「炎舞」的魔法卡在自己场上盖放。此外，1回合1次，把自己场上表侧表示存在的1张名字带有「炎舞」的魔法·陷阱卡送去墓地才能发动。选择场上1只怪兽破坏。','盖放','怪兽破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6357341,'忍之六武','①：自己场上有「六武众」怪兽6只存在，那些属性全部不同的场合才能发动。下次的对方回合跳过。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6367785,'艾尔丁','用手中的杖，使出各种魔法攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6368038,'暗黑骑士 盖亚','乘坐跑得比风更快之马的骑士。要注意突进攻击。','','','','','','','','','','','','','','','','');
