INSERT INTO texts VALUES(6387204,'混沌No.6 先史遗产 混沌大西洲巨人',replace(replace('7星怪兽×3\r\n这张卡的效果发动过的回合，对方受到的全部伤害变成0。1回合1次，可以选择对方场上1只怪兽当作攻击力上升1000的装备卡使用给这张卡装备。此外，这张卡有「No.6 先史遗产 大西洲巨人」在作为超量素材的场合，得到以下效果。\r\n●把这张卡3个超量素材取除，把这张卡的效果装备的名字带有「No.」的怪兽全部送去墓地才能发动。对方基本分变成100。','\r',char(13)),'\n',char(10)),'装备','基本分变成100','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6390406,'龙破坏之证','①：从自己的卡组·墓地选1只「巴斯达·布雷达」加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6400512,'突击兵','装备火箭发射器与反坦克火箭炮的实战部队。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6417578,'与神之假身的接触',replace(replace('「与神之假身的接触」在1回合只能发动1张。\r\n①：从自己的手卡·场上把「影依」融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6430659,'病毒邮件','1回合1次，选择自己场上表侧表示存在的1只4星以下的怪兽才能发动。这个回合，选择的怪兽可以直接攻击对方玩家。那只怪兽在战斗阶段结束时送去墓地。','直接攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6442944,'帝王海蛇',replace(replace('「帝王海蛇」的①的方法的特殊召唤1回合只能有1次。\r\n①：对方场上有怪兽存在，自己场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：这张卡用这张卡的①的方法特殊召唤成功时才能发动。从自己的手卡·墓地选1只海龙族·8星怪兽特殊召唤。这个效果特殊召唤的怪兽的攻击力·守备力变成0。\r\n③：特殊召唤的这张卡的等级变成4星，原本攻击力变成0。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6459419,'亚马逊转生术','自己场上表侧表示存在的名字带有「亚马逊」的怪兽全部破坏。那之后，可以把最多和破坏数量相同数量的自己墓地存在的4星以下的名字带有「亚马逊」的怪兽表侧守备表示特殊召唤。','是否要特殊召唤？','是否要特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6480253,'支援犬','这张卡被战斗破坏送去墓地时，从自己墓地选择1张名字带有「元素英雄」的卡以及1张「融合」魔法卡加入手卡。','加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6483224,'半月龙 半径龙','对方场上有超量怪兽存在的场合，这张卡可以从手卡特殊召唤。这个方法特殊召唤的这张卡的等级变成8星。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6511113,'芙莉西亚之虫惑魔',replace(replace('4星怪兽×2\r\n①：持有超量素材的这张卡不受陷阱卡的效果影响。\r\n②：只要这张卡在怪兽区域存在，「芙莉西亚之虫惑魔」以外的自己场上的「虫惑魔」怪兽不会被战斗·效果破坏，不会成为对方的效果的对象。\r\n③：1回合1次，把这张卡1个超量素材取除，把满足发动条件的1张「洞」通常陷阱卡或者「落穴」通常陷阱卡从卡组送去墓地才能发动。这个效果变成和那张陷阱卡发动时的效果相同。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6540606,'水灵术-「葵」','自己场上存在的1只水属性怪兽做祭品。确认对方手卡，选择对方1张手卡送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6544078,'伊奘凪','这张卡可以把手卡1只灵魂怪兽从游戏中除外从手卡特殊召唤。只要这张卡在自己场上表侧表示存在，自己场上存在的灵魂怪兽在结束阶段时回到手卡效果可以不发动。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6552938,'急袭猛禽-方向舵林鸮',replace(replace('①：这张卡召唤成功的场合或者用「急袭猛禽」卡的效果从手卡特殊召唤成功的场合才能发动。给与对方600伤害。\r\n②：这张卡为攻击对象的对方怪兽的攻击宣言时才能发动。从手卡把最多2只「急袭猛禽」怪兽特殊召唤。这个回合，对方不能选择这个效果特殊召唤的怪兽作为攻击对象。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6556178,'枪管上膛',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：以自己墓地1只「弹丸」怪兽为对象才能把这张卡发动。那只怪兽特殊召唤，把这张卡装备。这张卡从场上离开时那只怪兽除外。\r\n②：装备怪兽被破坏让这张卡被送去墓地的场合才能发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6579928,'影六武众-木猿',replace(replace('①：这张卡特殊召唤成功时才能发动。自己场上存在的属性以外的1只「六武众」怪兽从卡组加入手卡。\r\n②：只让自己场上的「六武众」怪兽1只被效果破坏的场合，可以作为代替把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6588580,'次世代热能人',replace(replace('「次世代控制员」＋调整以外的炎属性怪兽1只以上\r\n这张卡的攻击力上升自己墓地存在的炎属性怪兽数量×200的数值。这张卡战斗破坏对方怪兽的场合，给与对方基本分自己墓地存在的名字带有「次世代」的怪兽数量×200的数值的伤害。','\r',char(13)),'\n',char(10)),'伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6595475,'拟声连携',replace(replace('把1张手卡送去墓地才能发动。从卡组把以下怪兽之内各1只合计最多2只加入手卡。「拟声连携」在1回合只能发动1张。\r\n●名字带有「刷拉拉」的怪兽\r\n●名字带有「我我我」的怪兽\r\n●名字带有「隆隆隆」的怪兽\r\n●名字带有「怒怒怒」的怪兽','\r',char(13)),'\n',char(10)),'是否要继续选择怪兽加入手卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6602300,'重爆击禽 炸弹不死魔鸟',replace(replace('机械族怪兽＋炎族怪兽\r\n自己的主要阶段时，可以给与对方基本分场上存在的卡每1张300分伤害。这个效果发动的回合这张卡不能攻击。这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6614221,'雾之王','这张卡召唤的场合，可以用1只祭品或者不用祭品作召唤。这张卡的攻击力变成祭品召唤时作为祭品的怪兽的原本攻击力合计数值。只要这张卡在场上表侧表示存在，任何形式的祭品都不能进行。','用1只祭品召唤','不用祭品作召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6622715,'编码语者',replace(replace('电子界族怪兽2只以上\r\n①：1回合1次，这张卡所连接区的自己怪兽和比那只怪兽攻击力高的对方怪兽进行战斗的伤害计算前才能发动。那只自己怪兽不会被那次战斗破坏，那次战斗发生的对自己的战斗伤害变成0。那次伤害计算后，选这张卡或者这张卡所连接区1只自己怪兽，那个攻击力直到回合结束时上升那只进行战斗的对方怪兽的攻击力数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6625096,'鰤子姬','这张卡召唤·特殊召唤成功时，把这张卡从游戏中除外才能发动。从卡组把「鰤子姬」以外的1只4星以下的鱼族怪兽特殊召唤。「鰤子姬」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6628343,'开辟之骑士',replace(replace('「开辟之骑士」的①②的效果1回合各能使用1次。\r\n①：使用这张卡仪式召唤的「混沌战士」怪兽得到以下效果。\r\n●1回合1次，以对方场上1只怪兽为对象才能发动。那只怪兽除外。\r\n●这张卡战斗破坏对方怪兽送去墓地时才能发动。这张卡只再1次可以继续攻击。\r\n②：墓地的这张卡被除外的场合才能发动。从卡组把1张仪式魔法卡加入手卡。','\r',char(13)),'\n',char(10)),'对方场上1只怪兽除外（开辟之骑士）','再1次可以继续攻击（开辟之骑士）','「开辟之骑士」效果适用中','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6631034,'冰霜腕龙','托迟钝的神经和感性的福，虽然被冰雪覆盖却拥有冰河期也能熬过的威胁生命力，耐寒能力异常强大。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6691855,'附锁链的尖盾','发动后这张卡变成攻击力上升500的装备卡，给场上1只怪兽装备。装备怪兽以守备表示进行战斗的场合，装备怪兽的守备力只在伤害计算时上升那个攻击力数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6713443,'极炎舞-「星斗」','把自己墓地7张名字带有「炎舞」的魔法·陷阱卡从游戏中除外才能发动。从自己墓地把名字带有「炎星」的怪兽尽可能特殊召唤。那之后，可以从卡组选最多有这个效果特殊召唤的怪兽数量的「极炎舞-「星斗」」以外的名字带有「炎舞」的魔法·陷阱卡在自己场上盖放。','是否要选择名字带有「炎舞」的魔法·陷阱卡在自己场上盖放？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6733059,'死灵之巢','除外自己的墓地存在的任意数目的怪兽，破坏1只场上的和这个数目相同等级的表侧表示的怪兽。','是否现在使用「死灵之巢」的效果？','破坏','请选择要除外的怪兽的数量','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6740720,'圣夜龙','用圣火焰烧尽邪恶，拥有神圣之力的龙。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6766208,'DDD 疾风大王 决策亚历山大',replace(replace('调整＋调整以外的怪兽1只以上\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：场上有「DDD」怪兽3只以上存在的场合，这张卡的攻击力上升3000。\r\n②：这张卡在怪兽区域存在，自己场上有这张卡以外的「DD」怪兽召唤·特殊召唤的场合，以自己墓地1只「DD」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6783559,'自爆蚁',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡反转的场合发动。双方玩家受到1000伤害。\r\n②：这张卡被战斗·效果破坏送去墓地的场合发动。给与对方1000伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6795211,'物体显形','①：1回合1次，只有对方场上才有怪兽存在的场合，支付800基本分才能发动。从手卡把1只念动力族怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6799227,'半反击','自己场上存在的怪兽被攻击的场合，那次伤害计算时才能发动。成为攻击对象的1只自己怪兽的攻击力直到结束阶段时上升攻击怪兽的原本攻击力一半的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6830480,'黄昏之忍者-下弦',replace(replace('←10 【灵摆】 10→\r\n①：自己不是「忍者」怪兽不能灵摆召唤。这个效果不会被无效化。\r\n②：1回合1次，自己的「忍者」怪兽的攻击宣言时才能发动。那只怪兽的攻击力直到伤害步骤结束时上升1000。\r\n【怪兽效果】\r\n①：把这张卡解放，以自己场上1只「忍者」怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升800。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6836211,'大皇帝企鹅','把这张卡解放发动。从自己卡组把「大皇帝企鹅」以外的最多2只名字带有「企鹅」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
