INSERT INTO texts VALUES(6849042,'超古代恐兽',replace(replace('这张卡可以把1只恐龙族怪兽解放表侧攻击表示上级召唤。\r\n①：这张卡在怪兽区域存在，从自己墓地有恐龙族怪兽特殊召唤时才能发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'把1只恐龙族怪兽解放上级召唤','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6850209,'与恶魔的暗中交易','这张卡只能在有8星以上的怪兽从自己场上被送去墓地的回合发动。从自己的手卡或卡组特殊召唤「狂暴死龙」上场。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6853254,'复活之福音',replace(replace('①：以自己墓地1只7·8星的龙族怪兽为对象才能发动。那只怪兽特殊召唤。\r\n②：自己场上的龙族怪兽被战斗·效果破坏的场合，可以作为代替把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'是否使用「复活之福音」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6859683,'成功确率0%','从对方额外卡组随机选择2张融合怪兽送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6890729,'化学结合-DHO',replace(replace('①：从自己的手卡·墓地让「氘素龙」「氢素龙」「氧素龙」各1只回到卡组才能发动。从自己的手卡·墓地选1只「水龙-团簇」特殊召唤。\r\n②：把墓地的这张卡除外才能发动。从自己的卡组·墓地选1只「水龙」或者「水龙-团簇」加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6901008,'幻影王 幽骑','自己场上表侧表示存在的这张卡在同调召唤使用的场合，这张卡可以当作调整以外的怪兽使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6903857,'强力破坏者','这张卡被对方怪兽的攻击或者对方的效果破坏送去墓地时，选择对方场上表侧表示存在的1张魔法·陷阱卡破坏。这张卡攻击的场合，伤害步骤结束时变成守备表示。','破坏','变成守备表示','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6917479,'吸血鬼·红男爵',replace(replace('①：1回合1次，支付1000基本分，以对方场上1只怪兽和这张卡以外的自己场上1只「吸血鬼」怪兽为对象才能发动。那2只怪兽的控制权交换。\r\n②：这张卡战斗破坏怪兽的战斗阶段结束时才能发动。那些怪兽从墓地尽可能往自己场上特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6924874,'鹰身女妖的宠物仔龙',replace(replace('这张卡追加自己场上存在的除「鹰身女妖的宠物仔龙」以外的名字带有「鹰身」的怪兽的数量的效果。\r\n●1只：只要这张卡在场上表侧表示存在，对方不能选择自己场上存在的除「鹰身女妖的宠物仔龙」以外的名字带有「鹰身」的怪兽作为攻击对象。\r\n●2只：这张卡的原本攻击力·守备力变成2倍。\r\n●3只：1回合1次，可以把对方场上的1张卡破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6930746,'限界龙 施瓦西龙','对方场上有攻击力2000以上的怪兽存在的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6958551,'影子卡通',replace(replace('「影子卡通」在1回合只能发动1张。\r\n①：自己场上有「卡通世界」存在的场合，以对方场上1只表侧表示怪兽为对象才能发动。给与对方那只表侧表示怪兽的攻击力数值的伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6967870,'黑蝎-拆除陷阱的克里夫',replace(replace('这张卡对对方造成战斗伤害时，可以从下列效果中选择1项发动：\r\n●破坏场上1张魔法或陷阱卡。\r\n●将对方卡组最上面2张卡送去墓地。','\r',char(13)),'\n',char(10)),'选择一个效果发动','破坏场上1张魔法或陷阱卡。','将对方牌组最上面2张卡送去墓地。','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6979239,'树叶妖精','装备在这张卡上的1张装备卡送去墓地。对方玩家受到500分的伤害。','LP伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(6983839,'龙卷龙',replace(replace('4星怪兽×2\r\n①：1回合1次，把这张卡1个超量素材取除，以场上1张魔法·陷阱卡为对象才能发动。那张卡破坏。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7019529,'空中的昆虫兵','攻击风属性的怪兽时，伤害阶段时攻击力上升1000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7025445,'强力巨人','这张卡可以把手卡1只4星以下的怪兽送去墓地，从手卡特殊召唤。这个方法特殊召唤的场合，这张卡的等级下降从手卡送去墓地的怪兽的等级数值。此外，这张卡进行战斗的场合，直到那次伤害步骤结束时自己受到的效果伤害变成0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7030340,'百机夜工','自己墓地存在的名字带有「变形斗士」的怪兽全部从游戏中除外。自己场上表侧表示存在的1只机械族怪兽的攻击力直到这个回合的结束阶段时这个效果每除外1只怪兽上升200。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7044562,'魂之卡',replace(replace('「魂之卡」在1回合只能发动1张。\r\n①：把自己卡组确认。可以从那之中把1只攻击力·守备力的合计是和自己基本分相同数值的怪兽加入手卡。','\r',char(13)),'\n',char(10)),'是否选择怪兽加入手卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7076131,'消失的后者','选择自己场上表侧表示存在的1只怪兽。从自己卡组选1张和选择的卡同名的卡送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7080743,'豪腕特急 矿车火车头',replace(replace('①：场上的这张卡为素材作超量召唤的怪兽得到以下效果。\r\n●这次超量召唤成功的场合发动。这张卡的攻击力上升800。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7084129,'魔术师之杖',replace(replace('「魔术师之杖」的①②的效果1回合各能使用1次。\r\n①：这张卡召唤成功时才能发动。把有「黑魔术师」的卡名记述的1张魔法·陷阱卡从卡组加入手卡。\r\n②：这张卡在墓地存在的状态，自己在对方回合把魔法·陷阱卡的效果发动的场合，把自己场上1只魔法师族怪兽解放才能发动。墓地的这张卡加入手卡。','\r',char(13)),'\n',char(10)),'加入手卡','墓地的这张卡加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7089711,'哈尼哈尼','反转：选择场上1只怪兽回到原持有人手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7092142,'激流苏生','自己场上的水属性怪兽被战斗或者卡的效果破坏送去墓地时才能发动。那个时候被破坏从场上送去自己墓地的怪兽全部特殊召唤，给与对方基本分特殊召唤的怪兽数量×500的数值的伤害。「激流苏生」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7093411,'宝玉兽 青玉飞马',replace(replace('①：这张卡召唤·反转召唤·特殊召唤成功时才能发动。从自己的手卡·卡组·墓地选1只「宝玉兽」怪兽当作永续魔法卡使用在自己的魔法与陷阱区域表侧表示放置。\r\n②：表侧表示的这张卡在怪兽区域被破坏的场合，可以不送去墓地当作永续魔法卡使用在自己的魔法与陷阱区域表侧表示放置。','\r',char(13)),'\n',char(10)),'破坏时放置魔法陷阱区','放置魔法陷阱区','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7127502,'龙魔王 霸道矢·灵摆',replace(replace('←5 【灵摆】 5→\r\n①：只要这张卡在灵摆区域存在，对方场上的表侧表示的灵摆怪兽的效果无效化。\r\n【怪兽效果】\r\n①：这张卡和灵摆怪兽进行战斗的伤害步骤开始时发动。那只怪兽和这张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7152333,'阴影食尸鬼','1回合1次，自己的主要阶段时才能发动。这张卡的攻击力变成0，从手卡把1只攻击力0的名字带有「阴影」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7153114,'场地防护罩','场地魔法卡不能破坏。此外，场地魔法卡不能发动。「场地防护罩」在自己场上只能有1张表侧表示存在。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7161742,'捕食植物 冬虫夏草','①：自己准备阶段把墓地的这张卡除外，以自己墓地2只4星以下的「捕食植物」怪兽为对象才能发动。那些怪兽特殊召唤。这个效果的发动后，直到回合结束时自己不能通常召唤，不是融合怪兽不能特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7165085,'诱饵人偶','强制发动1张盖放表示的陷阱卡。是发动时机不正确的陷阱卡的场合，那张卡的效果无效并破坏。发动后这张卡不送去墓地，加入卡组洗切。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7171149,'卡通古代的机械巨人',replace(replace('①：这张卡在召唤·反转召唤·特殊召唤的回合不能攻击。\r\n②：自己场上有「卡通世界」存在，对方场上没有卡通怪兽存在的场合，这张卡可以直接攻击。\r\n③：这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。\r\n④：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7180418,'魔导铠甲 执行者','这张卡召唤·反转召唤·特殊召唤的回合不能进行攻击。在自己与对方的每1个准备阶段，从自己场上除去1个魔力指示物，如果不除去魔力指示物，则这张卡被破坏。','是否要除去一个魔力指示物？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7183277,'光道德鲁伊 奥库斯','只要这张卡在场上表侧表示存在，双方玩家不能把名字带有「光道」的怪兽作为魔法·陷阱·效果怪兽的效果的对象。此外，每次自己的结束阶段发动。从自己卡组上面把2张卡送去墓地。','从卡组送2张卡去墓地','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7194917,'No.52 钻蟹王',replace(replace('4星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除才能发动。直到回合结束时，这张卡的守备力变成0，攻击力变成3000。这张卡攻击的场合，战斗阶段结束时变成守备表示。此外，没有超量素材的这张卡被攻击的场合伤害步骤结束时变成攻击表示。「No.52 钻蟹王」在自己场上只能有1只表侧表示存在。','\r',char(13)),'\n',char(10)),'攻守变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7198399,'巧克力魔术少女',replace(replace('①：1回合1次，从手卡丢弃1只魔法师族怪兽才能发动。自己从卡组抽1张。\r\n②：1回合1次，这张卡被选择作为攻击对象的场合，以「巧克力魔术少女」以外的自己墓地1只魔法师族怪兽为对象才能发动。那只怪兽特殊召唤。那之后，攻击对象转移为那只怪兽，攻击怪兽的攻击力变成一半。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7200041,'金属射手','这张卡召唤成功时，给这张卡放置2个指示物。这个效果每放置有1个指示物，这张卡的攻击力上升800。这张卡被其他卡的效果破坏的场合，把这张卡的1个指示物取除作代替。','放置指示物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7225792,'蒙·拉瓦斯','进化过的野兽。力量有相当的提高。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7241272,'狱炎诅咒之龙',replace(replace('①：这张卡召唤·特殊召唤成功的场合，以场上1张场地魔法卡为对象才能发动。那张卡破坏。\r\n②：1回合1次，自己主要阶段才能发动。融合怪兽卡决定的包含这张卡的融合素材怪兽从自己场上送去墓地，把那1只融合怪兽从额外卡组融合召唤。','\r',char(13)),'\n',char(10)),'场地魔法卡破坏','融合召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7243511,'树熊海獭树熊',replace(replace('「树熊海獭」＋「海獭树熊」\r\n从手卡把1只兽族怪兽送去墓地，选择对方场上存在的1只怪兽发动。选择的怪兽破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7264861,'永火兽','自己手卡是0张的场合，得到以下效果。这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7268133,'娱乐伙伴 生命剑士',replace(replace('①：对方基本分回复时才能发动。这张卡从手卡特殊召唤。这个效果特殊召唤的这张卡的攻击力变成和这个时候对方回复的数值相同。\r\n②：把这张卡解放，以自己场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升1000。','\r',char(13)),'\n',char(10)),'特殊召唤','攻击上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7279373,'魔界剧团-闪烁小明星',replace(replace('←9 【灵摆】 9→\r\n①：自己不是「魔界剧团」怪兽不能灵摆召唤。这个效果不会被无效化。\r\n②：1回合1次，以自己场上1只「魔界剧团」怪兽为对象才能发动。这个回合，那只怪兽在同1次的战斗阶段中最多3次可以向怪兽攻击，作为对象的怪兽以外的自己怪兽不能攻击。\r\n【怪兽效果】\r\n①：这张卡在自己回合不会被战斗破坏，同1次的战斗阶段中最多3次可以向怪兽攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7291576,'影六武众-玄蕃',replace(replace('①：这张卡召唤成功时，以除外的1只自己的「六武众」怪兽为对象才能发动。那只怪兽加入手卡。\r\n②：只让自己场上的「六武众」怪兽1只被效果破坏的场合，可以作为代替把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7304544,'自然菠萝','在自己场上表侧表示存在的怪兽作为植物族。自己的准备阶段这张卡在墓地存在，自己场上不存在魔法·陷阱卡的场合，这张卡可以在自己场上特殊召唤。这个效果在自己场上不存在表侧表示的「自然菠萝」，自己墓地只存在植物族·兽族怪兽的场合才能发动。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7305060,'无形噬体·强欲',replace(replace('←3 【灵摆】 3→\r\n这张卡的控制者在每次自己准备阶段把自己场上1只怪兽解放。或者不解放让这张卡破坏。\r\n①：只要自己场上有「无形噬体」怪兽存在，双方不能把「无形噬体」卡以外的陷阱卡的效果发动。\r\n【怪兽效果】\r\n①：只要灵摆召唤·反转过的这张卡在怪兽区域存在，双方不是「无形噬体」怪兽不能从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'是否解放自己场上1只怪兽？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7359741,'机械猎手','听从机械王的命令，在抓住目标前都会紧追不舍的猎捕。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7369217,'金属化寄生生物-月之衣','1回合1次，在自己的准备阶段，这张卡可以视为1张装备卡装备在自己场上的表侧表示怪兽上，或者解除装备状态表侧攻击表示特殊召唤。装备怪兽不受对方所控制的魔法卡效果的影响。（1只怪兽至多只能装备1张同盟。当装备了同盟的怪兽被战斗破坏时，同盟会代替怪兽被破坏。）','装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7391448,'御用守护者',replace(replace('地属性调整＋调整以外的怪兽1只以上\r\n①：这张卡战斗破坏对方怪兽送去墓地时才能发动。那只怪兽在自己场上守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7392745,'鼠骨','①：这张卡反转的场合发动。在对方场上把3只「小鼠骨衍生物」（不死族·地·1星·攻100/守300）守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7392746,'小鼠骨衍生物','由「鼠骨」的效果特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7394770,'明亮融合',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：这张卡的发动时，从自己卡组把「宝石骑士」融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽攻击力·守备力变成0从额外卡组融合召唤。这张卡从场上离开时那只怪兽破坏。\r\n②：1回合1次，把手卡1张魔法卡丢弃才能发动。这张卡的效果特殊召唤的怪兽的攻击力·守备力直到对方回合结束时上升原本数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7405310,'额外之门','宣言从1到12的任意等级发动。对方把额外卡组存在的1只持有宣言的等级的怪兽从游戏中除外。持有宣言的等级的怪兽不在对方的额外卡组的场合，选择自己1张手卡丢弃。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7409792,'骏足之河马 马力巨犀',replace(replace('这个卡名的①的方法的特殊召唤1回合只能有1次。\r\n①：这张卡可以从手卡特殊召唤。\r\n②：这张卡的①的方法特殊召唤成功的场合发动。对方可以从自己或者对方的墓地选1只4星怪兽在自身场上特殊召唤。','\r',char(13)),'\n',char(10)),'对方特殊召唤','是否特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7445307,'双汇编亚龙',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡在手卡·墓地存在的场合，从手卡以及自己场上的表侧表示怪兽之中把2只电子界族怪兽除外才能发动。这张卡特殊召唤。这个效果特殊召唤的这张卡的攻击力变成一半。\r\n②：1回合1次，把1张手卡除外才能发动。选持有这张卡的攻击力以下的攻击力的场上1只怪兽除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7452945,'天命之圣剑','战士族怪兽才能装备。装备怪兽1回合只有1次不会被战斗以及卡的效果破坏。场上表侧表示存在的这张卡被破坏送去墓地的场合，可以选择自己场上1只名字带有「圣骑士」的战士族怪兽把这张卡装备。「天命之圣剑」的这个效果1回合只能使用1次。此外，「天命之圣剑」在自己场上只能有1张表侧表示存在。','装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7459013,'暗黑界的骑士 祖尔','暗黑界里其名无人不知的高傲骑士。绝对不会对弱者出手。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7478431,'自然向日葵','把自己场上存在的这张卡和1只名字带有「自然」的怪兽解放发动。对方的效果怪兽的效果的发动无效并破坏。','效果怪兽的效果发动无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7489323,'金毛犼','只要这张卡在场上表侧表示存在，全部地属性的怪兽攻击力上升500。风属性的怪兽攻击力下降400。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7500772,'鹰鲨','对方场上的怪兽是2只以上的场合，这张卡可以不用解放作召唤。此外，自己场上有「豹鲨」存在的场合，这张卡可以从手卡特殊召唤。「鹰鲨」在自己场上只能有1只表侧表示存在。','不解放进行召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7512044,'精神统一','从卡组中选择1张「精神统一」加入手卡。这张卡1回合只能使用1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7526150,'钢铁魔人','从与异次元相通的洞里出来的钢铁大魔人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7541475,'歌舞伎龙',replace(replace('①：1回合1次，自己怪兽向对方怪兽攻击的伤害计算前才能发动。那只对方怪兽的表示形式变更。\r\n②：1回合1次，对方怪兽向自己怪兽攻击的伤害计算前才能发动。那只自己怪兽的表示形式变更。','\r',char(13)),'\n',char(10)),'表示形式变更','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7562372,'超音速之眼','来自宇宙尽头的杀人机器。用不明金属制作。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7563579,'娱乐法师 火布偶',replace(replace('←5 【灵摆】 5→\r\n「娱乐法师 火布偶」的灵摆效果1回合只能使用1次。\r\n①：自己场上的表侧表示的「娱乐法师」怪兽被战斗·效果破坏的场合才能发动。灵摆区域的这张卡特殊召唤。那之后，自己受到500伤害。\r\n【怪兽效果】\r\n①：场上的这张卡被战斗·效果破坏的场合才能发动。从手卡·卡组把「娱乐法师 火布偶」以外的1只「娱乐法师」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7565547,'力之集约','选择场上1只表侧表示的怪兽。场上的存在的全部的装备卡装备到选择对象的身上。对象不正确的场合，那些装备卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7572887,'异次元的女战士','①：这张卡和对方怪兽进行战斗的伤害计算后才能发动。那些进行战斗的各自怪兽除外。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7573135,'剑斗兽 奥古斯都',replace(replace('①：这张卡用「剑斗兽」怪兽的效果特殊召唤成功时才能发动。从手卡把1只「剑斗兽」怪兽守备表示特殊召唤。这个效果特殊召唤的怪兽在结束阶段回到持有者卡组。\r\n②：这张卡进行战斗的战斗阶段结束时让这张卡回到持有者卡组才能发动。从卡组把「剑斗兽 奥古斯都」以外的1只「剑斗兽」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7576264,'幻兽 野角驼鹿','这张卡攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7582066,'念力恶魔',replace(replace('调整＋调整以外的念动力族怪兽1只以上\r\n①：1回合1次，自己主要阶段才能发动。对方手卡随机选1张，对那张卡的种类（怪兽·魔法·陷阱）作猜测。猜中的场合，这张卡的攻击力直到下次的对方回合的结束时上升1000。','\r',char(13)),'\n',char(10)),'猜手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7602840,'飞碟机人','这张卡被战斗破坏送去墓地时，可以从卡组选择1只攻击力1500以下的机械族怪兽表侧攻击表示特殊召唤上场，之后卡组洗切。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7617062,'鬼计博物馆','只要这张卡在场上存在，名字带有「鬼计」的怪兽以外的自己场上的怪兽不能攻击，双方场上的怪兽不能向里侧守备表示怪兽攻击，可以在对方场上的怪兽只有里侧守备表示怪兽的场合直接攻击对方玩家。此外，给与玩家战斗伤害的怪兽在伤害步骤结束时变成里侧守备表示。','变成里侧守备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7617253,'虹之行方','对方怪兽的攻击宣言时，选择自己的魔法与陷阱卡区域存在的1张名字带有「宝玉兽」的卡送去墓地发动。可以把1只对方怪兽的攻击无效，从自己卡组选择1张名字带有「究极宝玉神」的卡加入手卡。','是否要把一张「究极宝玉神」的卡加入手牌？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7623640,'暗黑界的导师 塞鲁利','这张卡被卡的效果从手卡丢弃去墓地的场合，这张卡在对方场上表侧守备表示特殊召唤。这张卡用名字带有「暗黒界」的卡的效果特殊召唤成功时，对方选择1张手卡丢弃。','特殊召唤','对方选择1张手卡丢弃','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7625614,'贵金铠甲','只要控制装备这张卡的怪兽，对方不能攻击装备怪兽以外的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7634581,'山铜合神偶',replace(replace('这张卡不能通常召唤。这张卡的①的效果可以特殊召唤。\r\n①：自己的通常怪兽被战斗破坏的场合，伤害步骤结束时才能发动。这张卡从手卡特殊召唤。\r\n②：这张卡的攻击力上升对方场上的怪兽数量×1000。\r\n③：只要这张卡在怪兽区域存在，场上的4星通常怪兽不会被效果破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7653207,'喷水引擎','选择自己场上表侧表示存在的1只鱼族·海龙族·水族怪兽发动。选择的怪兽的攻击力上升1000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7670542,'生化植物','地下研究所的试验大失败所产生的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7672244,'紫炎之间者','选择自己场上1只表侧表示存在的怪兽发动。直到这个回合的结束阶段前，选择的卡的控制权转移给对方。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7700132,'异虫贵族','反转：被对方怪兽的攻击反转的场合，给与对方基本分向这张卡攻击的对方怪兽的攻击力一半数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7714344,'娱乐伙伴 小独',replace(replace('①：这张卡召唤·特殊召唤成功的回合的自己主要阶段才能发动1次。从手卡把1只3星以下的「娱乐伙伴」怪兽攻击表示特殊召唤。\r\n②：对方回合，从自己墓地把这张卡和1只「娱乐伙伴 小独」以外的「娱乐伙伴」怪兽除外才能发动。这个回合自己受到的战斗伤害只有1次变成0。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7736719,'空气循环机',replace(replace('①：这张卡召唤成功时才能发动。让2张手卡回到卡组洗切。那之后，自己从卡组抽2张。\r\n②：这张卡被破坏的场合发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'滤抽','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7778726,'极超流星',replace(replace('光属性怪兽2只\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：场上的光属性怪兽的攻击力·守备力上升500，暗属性怪兽的攻击力·守备力下降400。\r\n②：这张卡被战斗·效果破坏的场合，以自己墓地1只光属性怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7802006,'魔草 曼德拉草','反转：给场上表侧表示存在的可以放置魔力指示物的卡全部放置1个魔力指示物。','放置指示物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7805147,'兵队龙','①：1回合1次，对方把魔法·陷阱·怪兽的效果发动时才能发动。从卡组把1只2星以下的龙族怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7805359,'雏鸡','把敌人整个吞下，并消化作为能量来补充自己。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7811875,'重力崩坏','把自己场上表侧表示存在的1只同调怪兽送去墓地发动。对方怪兽的召唤·反转召唤·特殊召唤无效并破坏。直到这个回合的结束阶段时对方不能把怪兽召唤·反转召唤·特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7817703,'变形斗士·动力装置','名字带有「变形斗士」的3星的怪兽才能装备。装备怪兽的原本攻击力变成2倍。发动后第2次的自己的准备阶段时这张卡破坏，自己受到装备怪兽的原本攻击力数值的伤害。','这张卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7841112,'救世星龙',replace(replace('「救世龙」＋「星尘龙」＋调整以外的怪兽1只\r\n对方把魔法·陷阱·效果怪兽的效果发动时，可以把这张卡解放让那个发动无效，对方场上的卡全部破坏。1回合1次，可以直到结束阶段时让对方1只表侧表示怪兽的效果无效化。此外，可以把无效化怪兽记述的效果作为这张卡的效果只有1次发动。结束阶段时这张卡回到额外卡组，把自己墓地存在的1只「星尘龙」特殊召唤。','\r',char(13)),'\n',char(10)),'发动无效，对方场上的卡全部破坏','效果无效化','返回额外卡组','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7841921,'疾走的暗黑骑士 盖亚',replace(replace('「疾走的暗黑骑士 盖亚」的③的效果1回合只能使用1次。\r\n①：这张卡可以不用解放作召唤。\r\n②：不用解放作召唤的这张卡的原本攻击力变成1900。\r\n③：这张卡被解放的场合才能发动。从卡组把1只「混沌战士」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'不用解放作召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7845138,'茫漠的死者','自己基本分是2000以下的场合，这张卡可以从手卡特殊召唤。这张卡召唤·特殊召唤成功时，这张卡的攻击力变成对方基本分一半的数值。','攻击变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7852509,'破坏循环','这张卡的卡名只要在场上存在当作「破坏轮」使用。1回合1次，场上的怪兽被卡的效果破坏的场合（伤害步骤以外）：可以选择场上1只怪兽；破坏那只怪兽，双方玩家受到500伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7852878,'三千根针','守备表示的这张卡受到攻击的场合，若这张卡的守备力超过对方攻击怪兽的攻击力，伤害步骤结束时那只攻击怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7864030,'超重武者 松明-2',replace(replace('①：对方怪兽的攻击宣言时自己墓地没有魔法·陷阱卡存在的场合，把这张卡从手卡送去墓地，以自己场上1只「超重武者」怪兽为对象才能发动。攻击对象转移为那只怪兽进行伤害计算。\r\n②：1回合1次，对方怪兽的攻击宣言时才能发动。攻击对象转移为这张卡进行伤害计算。\r\n③：这张卡不会被战斗破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7868571,'炼装勇士·银鸟',replace(replace('←1 【灵摆】 1→\r\n①：1回合1次，以这张卡以外的自己场上1张表侧表示的卡为对象才能发动。那张卡破坏，从卡组选1张「炼装」魔法·陷阱卡在自己场上盖放。\r\n【怪兽描述】\r\n操纵着白银亚光速喷气机的美丽狙击手。想捕捉到用超常识速度飞驰的她是几乎不可能的事，没有办法能从快如光速时施展的一击中逃脱出去。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7892180,'念力河童','能使用多种超能力避开攻击的河童。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7902349,'被封印者的左腕','被封印的左腕。假若解开封印，能得无限之力。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7914843,'电磁蓑衣虫',replace(replace('①：这张卡反转的场合，以对方场上1只机械族怪兽为对象发动。直到下次的自己结束阶段，得到那只机械族怪兽的控制权。\r\n②：这张卡被和怪兽的战斗破坏的场合发动。那只怪兽的攻击力·守备力下降500。','\r',char(13)),'\n',char(10)),'获得控制权','攻守下降','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7922915,'魔术师的导门阵',replace(replace('①：从手卡把1只「黑魔术师」特殊召唤。那之后，从卡组把1只7星以下的魔法师族·暗属性怪兽特殊召唤。\r\n②：自己场上有「黑魔术师」存在的场合，把墓地的这张卡除外，以对方场上1张表侧表示的魔法·陷阱卡为对象才能发动。那张卡的效果直到回合结束时无效。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7935043,'海龙神的加护','直到这个回合的结束阶段时，自己场上表侧表示存在的3星以下的水属性怪兽不会被战斗以及卡的效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7953868,'苍血鬼','这张卡召唤·反转召唤成功时，变成守备表示。1回合1次，可以把自己场上存在的1个超量素材取除，选择自己墓地存在的1只4星的不死族怪兽特殊召唤。','变成守备表示','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(7969770,'移魂使魔','①：1回合1次，以自己场上1只怪兽为对象才能发动。那只自己怪兽的位置向其他的自己的主要怪兽区域移动。','','请选择要移动到的位置','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8034697,'魔法操纵人偶','自己与对方每发动1次魔法，就在这张卡上放置1个魔力指示物。这张卡上每存在1个魔力指示物，这张卡的攻击力就上升200。从这张卡上每除去2个魔力指示物，破坏场上1只怪兽。','放置魔力指示物','破坏一只怪兽','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8038143,'时空转生','自己场上有名字带有「银河眼」的怪兽存在的场合才能发动。这张卡的发动时积累的连锁上的全部对方的效果怪兽的效果·魔法·陷阱卡的发动无效，用这个效果把发动无效的场上的卡全部回到持有者卡组。自己场上有名字带有「银河眼时空龙」的怪兽存在的场合，这张卡可以从手卡发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8041569,'熔岩炎湖畔的淑女','自己墓地有名字带有「熔岩」的怪兽3种类以上存在的场合，把自己墓地存在的这张卡和1只名字带有「熔岩」的怪兽从游戏中除外才能发动。选择对方场上盖放的1张卡破坏。','对方场上盖放的1张卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8057630,'核心再炼成','选择自己墓地存在的1只名字带有「核成」的怪兽，攻击表示特殊召唤。自己的结束阶段时那只怪兽被破坏时，这张卡的控制者受到那只怪兽的攻击力数值的伤害。这张卡不在场上存在时，那只怪兽破坏。那只怪兽破坏时这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8058240,'封印之锁','咯吱咯吱的勒住对方、拥有封印的力量。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8062132,'毒蛇神 维诺米纳迦','这张卡不能通常召唤。这张卡用「蛇神降临」的效果以及这张卡的效果才能特殊召唤。这张卡的攻击力自己墓地每有1只爬虫族怪兽上升500。只要这张卡在场上表侧表示存在，不能成为这张卡以外的怪兽·魔法·陷阱的效果的对象并且不受效果影响。这张卡被战斗破坏送去墓地时，可以把自己墓地这张卡以外的1只爬虫族怪兽从游戏中除外把这张卡特殊召唤。每次这张卡给与对方玩家战斗伤害，给这张卡放置1个超毒指示物。这张卡放置有3个超毒指示物时，这张卡的控制者决斗胜利。','特殊召唤','放置指示物','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8078366,'海皇的突击兵','自己场上有这张卡以外的鱼族·海龙族·水族怪兽存在的场合，这张卡的攻击力上升800。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8091563,'超级防卫机器人·狮斗士','这张卡召唤成功时，可以从手卡把1只名字带有「超级防卫机器人」的怪兽或者「轨道 7」特殊召唤。此外，1回合1次，从自己墓地有名字带有「超级防卫机器人」的怪兽或者「轨道 7」1只加入自己手卡时，可以把那只怪兽从手卡特殊召唤。','特殊召唤','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8102334,'门之防壁','①：只要这张卡在怪兽区域存在，对方不能把其他的自己场上的怪兽作为效果的对象，不能给场上的卡放置指示物。此外，对方的场地魔法卡的效果无效。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8124921,'被封印者的右足','被封印的右足。假若解开封印，能得无限之力。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8131171,'杀人蛇',replace(replace('「杀人蛇」的效果1回合只能使用1次。\r\n①：这张卡在墓地存在的场合，自己准备阶段才能发动。这张卡回到手卡。下次的对方结束阶段选自己墓地1只「杀人蛇」除外。','\r',char(13)),'\n',char(10)),'返回手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8175346,'袖珍龙','这张卡召唤成功时，可以从自己卡组把1只「袖珍龙」加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8192327,'光灵神 福斯奥拉热',replace(replace('这张卡不能通常召唤。自己墓地的光属性怪兽是5只的场合才能特殊召唤。这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡特殊召唤成功时才能发动。对方场上的怪兽全部破坏。\r\n②：表侧表示的这张卡从场上离开的场合，下次的自己回合的战斗阶段跳过。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8198620,'冥界龙 龙亡',replace(replace('不死族怪兽×2\r\n这张卡用融合召唤才能从额外卡组特殊召唤。和这张卡进行战斗的怪兽不会被那次战斗破坏。此外，这张卡和怪兽进行战斗的伤害步骤结束时，那只怪兽的攻击力变成0，把持有那只怪兽的原本的等级·攻击力的1只「暗魂体衍生物」（不死族·暗·?星·攻?/守0）在自己场上特殊召唤。「冥界龙 龙亡」在自己场上只能有1只表侧表示存在。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8198621,'暗魂体衍生物','由「冥界龙 龙亡」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8198712,'世界末日','「破灭之女神 露茵」「终焉之王 迪米斯」降临时才能使用。必须用场上·手卡合计和仪式召唤的怪兽相同等级数量的祭品。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8201910,'海星小子','只要这张卡在场上表侧表示存在，全部水属性的怪兽攻击力上升500。炎属性的怪兽攻击力下降400。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8226374,'机关傀儡-小矮蛋','这张卡召唤·特殊召唤成功时，可以从手卡把1只名字带有「机关傀儡」的怪兽特殊召唤。「机关傀儡-小矮蛋」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8233522,'正义盟军 周期解读虫','把这张卡从手卡丢弃去墓地发动。选择对方墓地存在的最多2只光属性怪兽从游戏中除外。这个效果在对方回合也能发动。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8240199,'青色眼睛的贤士',replace(replace('「青色眼睛的贤士」的②的效果1回合只能使用1次。\r\n①：这张卡召唤成功时才能发动。从卡组把「青色眼睛的贤士」以外的1只光属性·1星调整加入手卡。\r\n②：把这张卡从手卡丢弃，以自己场上1只效果怪兽为对象才能发动。那只怪兽送去墓地，从卡组把1只「青眼」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8251996,'扰乱三角飓风！！','当「扰乱·绿」「扰乱·黄」「扰乱·黑」在自己场上以表侧表示存在时这张卡才能发动。破坏对方场上存在的所有卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8267140,'宇宙旋风','①：支付1000基本分，以场上1张魔法·陷阱卡为对象才能发动。那张卡除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8275702,'宝玉的契约','选择自己的魔法与陷阱卡区域存在的1张名字带有「宝玉兽」的卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8279188,'深渊护符','对方怪兽的攻击宣言时才能发动。把1张手卡丢弃去墓地，让1只对方怪兽的攻击无效。这张卡在发动后第3次的对方的结束阶段时破坏。','是否现在使用「深渊护符」的效果？','攻击无效','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8284390,'疾行机人 56飞机',replace(replace('「疾行机人 56飞机」的①的效果1回合只能使用1次。\r\n①：自己场上有怪兽特殊召唤的场合才能发动。这张卡从手卡特殊召唤。这个效果的发动后，直到回合结束时自己不是风属性怪兽不能特殊召唤。\r\n②：这张卡召唤·特殊召唤成功的场合，以场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时下降600。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8310162,'罪 矛盾龙',replace(replace('「罪 平行齿轮」＋调整以外的名字带有「罪」的怪兽1只\r\n这张卡同调召唤成功时，可以选择自己或者对方墓地存在的1只同调怪兽特殊召唤。「罪 矛盾龙」在场上只能有1只表侧表示存在。场上没有表侧表示「罪世界」存在的场合，这张卡破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8316565,'猛力反击','自己场上有名字带有「燃烧拳击手」的怪兽存在的场合才能发动。战斗阶段中发动的效果怪兽的效果·魔法·陷阱卡的发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8316661,'魔轰神 米兹托西','把这张卡从手卡送去墓地，选择自己场上表侧表示存在的1只名字带有「魔轰神」的怪兽发动。选择怪兽只要在场上表侧表示存在当作调整使用。','选择一只怪兽当调整使用','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8321183,'灵兽的誓还','①：从手卡把1只「灵兽」怪兽除外，从自己墓地的怪兽或者除外的自己怪兽之中以1只「灵兽」怪兽为对象才能发动。那只怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8323633,'夜雾的狙击手','宣言1个怪兽卡名。对方把宣言怪兽召唤·特殊召唤·反转的场合，宣言怪兽和这张卡从游戏中除外。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8327462,'死亡鸟','「橐蜚」＋「骷髅寺院」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8339504,'超量调换','把自己场上1只超量怪兽解放才能发动。和解放的怪兽相同种族·属性·阶级而卡名不同的1只怪兽从额外卡组特殊召唤，把这张卡在下面重叠作为超量素材。这个效果特殊召唤的怪兽在结束阶段时送去墓地。「超量调换」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8353769,'空气吸收者','吸收周围的空气，使对方窒息的妖怪。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8384771,'娱乐伙伴 橡胶绵羊',replace(replace('←1 【灵摆】 1→\r\n①：1回合1次，自己怪兽和对方怪兽进行战斗的攻击宣言时才能发动。那只自己怪兽不会被那次战斗破坏。\r\n【怪兽效果】\r\n①：1回合1次，自己怪兽和对方怪兽进行战斗的攻击宣言时才能发动。那只自己怪兽不会被那次战斗破坏。','\r',char(13)),'\n',char(10)),'不会被那次战斗破坏','不会被那次战斗破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8396952,'秘仪之力1-魔术师',replace(replace('这张卡召唤·反转召唤·特殊召唤成功时，进行1次投掷硬币得到以下效果。\r\n●表：魔法卡发动时，直到那个回合的结束阶段时这张卡的原本攻击力变成2倍。\r\n●里：每次魔法卡发动对方回复500基本分。','\r',char(13)),'\n',char(10)),'投掷硬币','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8400623,'邪龙 阿难陀','这张卡不能通常召唤。把自己的场上以及墓地存在的爬虫族怪兽全部从游戏中除外才能特殊召唤。这张卡的攻击力·守备力变成特殊召唤时从游戏中除外的爬虫族怪兽数量×600的数值。只要这张卡在自己场上存在，自己回合的结束阶段时把场上1张卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8414337,'薰风的风尘','这张卡发动的回合，自己场上存在的名字带有「薰风」的怪兽的攻击宣言时，对方不能把魔法·陷阱·效果怪兽的效果发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8437145,'炼狱的死徒',replace(replace('①：以自己场上1只「狱火机」怪兽为对象才能发动。这个回合，那只自己怪兽不受对方的效果影响。\r\n②：自己场上的「狱火机」怪兽被效果破坏的场合，可以作为那1只破坏的怪兽的代替而把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'作为代替把墓地中的「炼狱的死徒」除外？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8463720,'DDD 克龙王 贝奥武夫',replace(replace('「DDD」怪兽＋「DD」怪兽\r\n①：只要这张卡在怪兽区域存在，自己的「DD」怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。\r\n②：自己准备阶段才能发动。双方的魔法与陷阱区域的卡全部破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8471389,'高科技狼','全身由钢铁制成的狼，用锋利的牙齿撕咬敌人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8483333,'嵌合蝎尾狮','①：这张卡上级召唤成功的场合，下次的自己回合的准备阶段发动。为这张卡的上级召唤而解放的怪兽从墓地尽可能往自己场上特殊召唤。这个效果特殊召唤的怪兽不能攻击宣言，效果无效化。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8487449,'糖果小丑',replace(replace('①：「糖果小丑」在自己场上只能有1只表侧表示存在。\r\n②：这张卡可以从手卡攻击表示特殊召唤。\r\n③：这张卡的②的方法特殊召唤的场合，下次的对方结束阶段以对方场上1只表侧表示怪兽为对象发动。那只对方的表侧表示怪兽和表侧表示的这张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8491308,'闪刀姬-飒天',replace(replace('风属性以外的「闪刀姬」怪兽1只\r\n自己对「闪刀姬-飒天」1回合只能有1次特殊召唤。\r\n①：这张卡可以直接攻击。\r\n②：这张卡进行战斗的伤害计算后才能发动。从卡组把1张「闪刀」卡送去墓地。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8491961,'抒情歌鸲-吟诵椋鸟',replace(replace('1星怪兽×2只以上\r\n①：这张卡超量召唤成功的场合，以场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力·守备力上升这张卡的超量素材数量×300。\r\n②：1回合1次，把这张卡1个超量素材取除才能发动。从卡组把1只鸟兽族·1星怪兽加入手卡。\r\n③：超量召唤的这张卡的战斗发生的对自己的战斗伤害让对方也承受。','\r',char(13)),'\n',char(10)),'攻击力上升','卡组检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8508055,'格斗鼠 吱助','目标是成为鼠界最强的格斗家而在世界范围内流浪的热血鼠。无知的触碰它会很受伤哦。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8522996,'始源的帝王',replace(replace('①：这张卡发动后变成效果怪兽（恶魔族·暗·6星·攻1000/守2400）在怪兽区域特殊召唤。这张卡也当作陷阱卡使用。\r\n②：这张卡的效果让这张卡特殊召唤的场合，丢弃1张手卡，宣言1个属性才能发动。这张卡当作宣言的属性使用，和这张卡相同属性的怪兽上级召唤的场合，可以作为2只的数量解放。\r\n③：只要这张卡的效果特殊召唤的这张卡存在，自己不是和这张卡相同属性的怪兽不能特殊召唤。','\r',char(13)),'\n',char(10)),'属性变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8529136,'废铁拳',replace(replace('①：以自己场上1只「废品战士」为对象才能发动。这个回合，那只自己怪兽和对方怪兽进行战斗的场合，以下效果适用。\r\n●对方直到伤害步骤结束时魔法·陷阱·怪兽的效果不能发动。\r\n●作为对象的怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。\r\n●对方受到的战斗伤害变成2倍。\r\n●作为对象的怪兽不会被战斗破坏。\r\n●进行战斗的对方怪兽在伤害步骤结束时破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8559793,'急袭猛禽-鸟巢',replace(replace('「急袭猛禽-鸟巢」的效果1回合只能使用1次。\r\n①：自己场上有「急袭猛禽」怪兽2只以上存在的场合才能把这个效果发动。选自己的卡组·墓地1只「急袭猛禽」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8561192,'神树的守护兽-牙王',replace(replace('调整＋调整以外的怪兽1只以上\r\n这张卡在自己的主要阶段2以外不会成为对方的卡的效果的对象。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8567955,'均衡负载王',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：1回合1次，支付1000基本分才能发动。这个回合自己在通常召唤外加上只有1次，自己主要阶段可以把1只电子界族怪兽召唤。\r\n②：这张卡被除外的场合才能发动。从手卡把1只4星以下的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8581705,'业火王后恶魔','这张卡的控制者在自己的每1个准备阶段支付500基本分。当这张卡成为对方所控制的卡的效果对象时，在效果处理时掷1次骰子，若掷出2或5，则使此效果无效并将其破坏。只要这张卡在场上存在，在每1个回合的准备阶段指定自己场上1只名称中含有「恶魔」字样的怪兽，被指定的怪兽到结束阶段为止，攻击力上升1000点。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8594079,'朱罗纪腕龙','场上有这张卡以外的名字带有「朱罗纪」的怪兽表侧表示存在的场合，这张卡不会被战斗破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8611007,'熔岩谷龙骑士',replace(replace('调整＋调整以外的炎属性怪兽1只以上\r\n1回合1次，自己的主要阶段时才能发动。从自己卡组把1只名字带有「熔岩」的怪兽加入手卡，那之后从手卡把1只名字带有「熔岩」的怪兽送去墓地。','\r',char(13)),'\n',char(10)),'检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8628798,'D.D.炸药','给与对方基本分对方除外的卡数×300的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8632967,'进化的宿命','自己的名字带有「进化虫」的怪兽的效果让怪兽特殊召唤成功时，对方不能把魔法·陷阱·效果怪兽的效果发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8634636,'白尾黑猫','反转：将对方场上2只怪兽与自己场上1只怪兽弹回持有者手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8643186,'DD征募',replace(replace('「DD征募」在1回合只能发动1张。\r\n①：对方场上的怪兽数量比自己场上的怪兽多的场合，以最多有那个相差数量的自己墓地的「DD」怪兽或者「契约书」卡为对象才能发动。那些卡加入手卡。','\r',char(13)),'\n',char(10)),'卡片回收','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8649148,'深海清道鲎','把这张卡解放才能发动。选择场上1张魔法·陷阱卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8687195,'幻影妖精','这张卡受到对方怪兽攻击的场合，由其他的自己的场上的怪兽承受攻击，此时对这张卡的攻击不计算伤害。','代替战斗','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8692301,'宝石骑士·仿钻','「宝石骑士」怪兽＋岩石族怪兽','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8696773,'阳炎兽 许德拉',replace(replace('只要这张卡在场上表侧表示存在，对方不能把这张卡作为卡的效果的对象，自己不能把炎属性以外的怪兽特殊召唤。此外，这张卡为素材的超量怪兽得到以下效果。\r\n●这次超量召唤成功时，可以从自己墓地选择1只名字带有「阳炎兽」的怪兽在这张卡下面重叠作为超量素材。','\r',char(13)),'\n',char(10)),'补充素材','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8698851,'D-反击','自己场上表侧表示存在的名字带有「命运英雄」的怪兽被选择作为攻击对象时才能发动。攻击怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8706701,'红莲镜',replace(replace('「红莲镜」的①②的效果1回合各能使用1次。\r\n①：对方怪兽的攻击宣言时，把这张卡从手卡送去墓地，以「红莲镜」以外的自己墓地1只恶魔族·炎属性怪兽为对象才能发动。那只怪兽加入手卡。\r\n②：这张卡在墓地存在，自己同调召唤成功的场合才能发动。墓地的这张卡加入手卡。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8715625,'魔货物车辆 博科伊奇','专用的货物车辆（处理品）。可以运载任何东西。但是到达目的地时大部分货物都会损坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8719957,'深渊鳞甲-乌贼','名字带有「水精鳞」的怪兽才能装备。装备怪兽的攻击力上升400。只要这张卡在场上存在，对方场上发动的效果怪兽的效果无效。那之后，这张卡送去墓地。','效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8730435,'剑斗兽的斗器 恶魔之盾','名字带有「剑斗兽」的怪兽才能装备。装备怪兽被破坏的场合，作为代替把这张卡破坏。装备怪兽从自己场上回到卡组让这张卡被送去墓地时，这张卡回到手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8763963,'魔王超龙 天蝇王',replace(replace('暗属性调整＋调整以外的怪兽2只以上\r\n①：这张卡不会被战斗·效果破坏。\r\n②：只要这张卡在怪兽区域存在，这张卡以外的自己怪兽不能攻击。\r\n③：1回合1次，以对方场上1只怪兽为对象才能发动。那只怪兽的攻击力变成0，自己基本分回复那个原本攻击力的数值。此外，这个回合这张卡的战斗发生的对对方玩家的战斗伤害变成一半。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8783685,'生命之沙漏','掌管生命的天使。通过削减自己的寿命将力量赋予他人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8785161,'急袭猛禽-荒野秃鹫','①：这张卡召唤·特殊召唤成功的回合的自己主要阶段把这张卡解放才能发动。等级合计直到变成6星为止，从自己的手卡·墓地选2只「急袭猛禽」怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8794435,'合成魔兽 加泽特','这张卡的攻击力是这张卡祭牲召唤时作为祭品的2个怪兽原始攻击力的合计值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8806072,'连击战士','这张卡在场上表侧表示存在时自己回合的主要阶段一有连锁发生的场合，只在这个回合这张卡可以在同1次战斗阶段作2次攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8809344,'外神 奈亚拉',replace(replace('4星怪兽×2\r\n①：这张卡超量召唤成功时，把手卡任意数量丢弃才能发动。这张卡的阶级上升丢弃数量的数值。\r\n②：1回合1次，这张卡持有超量素材的场合以自己墓地1只怪兽为对象才能发动。这张卡的超量素材全部取除，把作为对象的怪兽在这张卡下面重叠作为超量素材。这张卡的种族·属性变成和这个效果作为超量素材的怪兽的原本的种族·属性相同。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8814959,'入魔曼德拉草','①：对方场上的怪兽数量比自己场上的怪兽多的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8820526,'娱乐伙伴 黏土破坏者',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：上级召唤的这张卡和对方怪兽进行战斗的伤害计算时才能发动。那只对方怪兽的攻击力直到回合结束时下降自己的额外卡组的表侧表示的灵摆怪兽数量×500。\r\n②：这张卡在墓地存在，自己把2只以上的怪兽同时灵摆召唤时才能发动。墓地的这张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8822710,'正义盟军 宇宙封闭机','对方场上包含光属性有怪兽2只以上存在的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8837932,'方界曼荼罗',replace(replace('①：自己场上有「方界」怪兽存在的场合，以这个回合被破坏送去对方墓地的怪兽任意数量为对象才能把这张卡发动。那些怪兽的攻击力变成0在对方场上特殊召唤，给那些怪兽各放置1个方界指示物。有方界指示物放置的怪兽不能攻击，效果无效化。\r\n②：只要作为对象的怪兽在对方场上存在，对方发动的怪兽的效果无效化。\r\n③：作为对象的怪兽全部从场上离开的场合这张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8842266,'隐居者的猛毒药',replace(replace('①：可以从以下效果选择1个发动。\r\n●自己回复1200基本分。\r\n●给与对方800伤害。','\r',char(13)),'\n',char(10)),'自己回复1200基本分','给予对方基本分800分伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8873112,'古遗物-环刃',replace(replace('①：这张卡可以当作魔法卡使用从手卡到魔法与陷阱区域盖放。\r\n②：魔法与陷阱区域盖放的这张卡在对方回合被破坏送去墓地的场合发动。这张卡特殊召唤。\r\n③：要让自己场上的魔法·陷阱卡破坏的效果发动时，让自己场上盖放的1张魔法·陷阱卡回到持有者手卡才能发动。这张卡从手卡特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8875971,'光天使 天翼','这张卡召唤成功时，可以从手卡把1只名字带有「光天使」的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8903700,'仪式魔人 解放者',replace(replace('①：仪式召唤进行的场合，可以作为那次仪式召唤需要的等级数值的1只怪兽，把墓地的这张卡除外。\r\n②：从使用这张卡仪式召唤的玩家来看的对方，只要仪式召唤的那只怪兽在怪兽区域表侧表示存在，不能把怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'「仪式魔人 解放者」效果适用中','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8910971,'暗黑骑士 盖亚王',replace(replace('①：对方场上有怪兽存在，自己场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：1回合1次，比这张卡攻击力高的怪兽在对方场上特殊召唤的场合才能发动。这张卡的攻击力直到回合结束时上升700。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8944575,'死亡之足','眼珠上长着脚的怪兽。高跳着用爪钩攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8949584,'英雄到来','①：自己场上没有表侧表示怪兽存在的场合，把基本分支付一半才能发动。从卡组把1只4星以下的「元素英雄」怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8951260,'正正堂堂','双方玩家必须在各自的回合把手卡全部持续公开。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8964854,'联合攻击','有同盟怪兽装备的怪兽进行过战斗的战斗阶段时才能发动。选择1只有同盟怪兽装备并进行过战斗的怪兽，把装备的同盟解除。选择的怪兽在这个回合可以再1次攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8967776,'究极时械神 赛菲隆','这张卡不能通常召唤。自己墓地有怪兽10只以上存在的场合才能特殊召唤。1回合1次，可以把1只8星以上的天使族怪兽从自己的手卡·墓地特殊召唤。这个效果特殊召唤的怪兽的效果无效化，攻击力变成4000。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(8978197,'龙之统治者','这张卡的卡名只要在场上存在当作「龙之支配者」使用。这张卡召唤成功时：可以从手卡丢弃1张魔法/陷阱卡；从卡组把「呼龙笛」，「龙觉醒旋律」，「龙复活狂想曲」的其中1张加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9000988,'娱乐伙伴 小判龙',replace(replace('←5 【灵摆】 5→\r\n①：1回合1次，以从额外卡组特殊召唤的自己场上1只龙族怪兽为对象才能发动。这个回合，那只自己怪兽和对方怪兽进行战斗的场合，那只对方怪兽在伤害计算后除外。\r\n【怪兽效果】\r\n①：只要这张卡在怪兽区域存在，这张卡以外的自己场上的龙族怪兽攻击力上升500，不会被效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9012916,'黑羽龙',replace(replace('调整＋调整以外的怪兽1只以上\r\n自己因卡的效果受到伤害的场合，作为代替给这张卡放置1个黑羽指示物。这张卡的攻击力下降这张卡放置的黑羽指示物数量×700的数值。1回合1次，可以把这张卡放置的黑羽指示物全部取除，对方场上表侧表示存在的1只怪兽的攻击力下降黑羽指示物数量×700的数值，给与对方基本分下降数值的伤害。','\r',char(13)),'\n',char(10)),'攻击下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9024367,'银河龙骑士',replace(replace('①：这张卡不能直接攻击，只能向龙族怪兽攻击。\r\n②：这张卡和龙族怪兽进行战斗的场合，只在战斗阶段内那只怪兽的效果无效化，只在伤害步骤内这张卡的攻击力上升1000。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9030160,'常暗的契约书',replace(replace('①：自己的灵摆区域有2张「DD」卡存在的场合，对方不能把场上的怪兽作为魔法·陷阱卡的效果的对象，不能作为上级召唤的解放，也不能作为融合·同调·超量召唤的素材。\r\n②：自己准备阶段发动。自己受到1000伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9032529,'天使之泪','①：选自己1张手卡加入对方手卡。那之后，自己回复2000基本分。','请选择要交给对方的手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9047460,'黑羽-隐身蓑之斯蒂姆',replace(replace('「黑羽-隐身蓑之斯蒂姆」的②的效果在决斗中只能使用1次。\r\n①：表侧表示的这张卡从场上离开的场合发动。在自己场上把1只「蒸汽衍生物」（水族·风·1星·攻/守100）特殊召唤。\r\n②：这张卡在墓地存在的场合，把自己场上1只怪兽解放才能发动。这张卡从墓地特殊召唤。把这个效果特殊召唤的这张卡作为同调素材的场合，其他的同调素材怪兽必须全部是「黑羽」怪兽。','\r',char(13)),'\n',char(10)),'特殊召唤','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9047461,'蒸汽衍生物','由「黑羽-隐身蓑之斯蒂姆」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9053187,'觉醒的勇士 魔蜥义豪','4星怪兽×3','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9056100,'冰结界的虎将 神兵','只要这张卡在场上表侧表示存在，自己的主要阶段时只有1次，自己可以在通常召唤外加上进行1只名字带有「冰结界」的怪兽的召唤。','「冰结界的虎将 神兵」的效果额外的召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9059700,'永火防护罩','对方发动魔法·陷阱卡·效果怪兽的效果时，若自己场上存在表侧攻击表示且名字带有「永火」的怪兽并且自己手卡为0张才能发动。那个发动无效并破坏那张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9074847,'大骚动','自己的怪兽被对方控制的卡的效果从场地回到手卡时才能发动。场上的全部怪兽回到持有者手卡。那之后，双方从手卡把回去数量的怪兽里侧守备表示特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9076207,'青衣忍者','反转：破坏场上的1张魔法卡。选择的卡是盖伏的场合，确认那张卡，如果是魔法卡就破坏。如果选择的卡确认是陷阱卡的场合变回原来的盖伏形式。','魔法破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9106362,'娱乐伙伴 铜锣野猫',replace(replace('←2 【灵摆】 2→\r\n①：1回合1次，对方怪兽的直接攻击宣言时才能发动。那次战斗发生的对自己的战斗伤害变成0。\r\n【怪兽效果】\r\n①：1回合1次，自己怪兽和对方怪兽进行战斗的攻击宣言时才能发动。那次战斗发生的对自己的战斗伤害变成0。','\r',char(13)),'\n',char(10)),'战斗伤害变成0','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9109991,'黑羽-铁锁之焚','这张卡可以直接攻击对方玩家。这张卡直接攻击给与对方基本分战斗伤害时，对方场上攻击表示存在的1只怪兽变成守备表示。','变成守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9113513,'固定音型','①：自己场上没有怪兽存在的场合才能发动。从自己的手卡·卡组把「幻奏」融合怪兽卡决定的2只融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。这个回合的结束阶段，这个效果融合召唤的怪兽破坏，若那一组融合素材怪兽在自己墓地齐集，可以把那一组特殊召唤。','是否把融合素材怪兽特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9126351,'鬼青蛙',replace(replace('①：这张卡可以从手卡把这张卡以外的1只水属性怪兽丢弃，从手卡特殊召唤。\r\n②：这张卡召唤·反转召唤·特殊召唤成功时才能发动。从卡组以及自己场上的表侧表示怪兽之中选1只水族·水属性·2星以下的怪兽送去墓地。\r\n③：1回合1次，让自己场上1只怪兽回到持有者手卡才能发动。这个回合，自己在通常召唤外加上只有1次，自己主要阶段可以把「鬼青蛙」以外的1只「青蛙」怪兽召唤。','\r',char(13)),'\n',char(10)),'额外的通常召唤','额外的通常召唤（鬼青蛙）','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9145181,'仪式降临封印之书','场上表侧表示存在的1只仪式怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9156135,'见习魔术师','这张卡召唤·反转召唤·特殊召唤成功时，指定场上1张表侧表示的可以放置魔力指示物的卡，在上面放置1个魔力指示物。当这张卡被战斗破坏的场合，可以从自己的卡组中选择1只2星以下的魔法师族怪兽盖伏上场。','放置魔力指示物','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9159938,'暗黑灰羚','全身灰色的野兽，是非常少见的珍贵品种。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9161357,'No.6 先史遗产 大西洲巨人',replace(replace('6星怪兽×2\r\n这张卡超量召唤成功时，可以选择自己墓地1只名字带有「No.」的怪兽当作装备卡使用给这张卡装备。这张卡的攻击力上升这个效果装备的怪兽的攻击力一半的数值。此外，1回合1次，把这张卡1个超量素材取除，把这张卡的效果装备的怪兽送去墓地才能发动。对方基本分变成一半。这个效果发动的回合，自己不能进行战斗阶段。','\r',char(13)),'\n',char(10)),'装备','LP减半','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9190563,'RAM云雄羊',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：把自己场上1只怪兽解放，以自己墓地1只电子界族怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9197735,'龙魂之石像','拥有龙的灵魂的石像战士。用自豪的剑劈开敌人。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9201964,'D-幸运','对方宣言直接攻击时才能发动。把自己墓地存在的1只名字带有「命运英雄」的怪兽从游戏中除外，战斗阶段结束。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9212051,'秘旋谍任务-救出',replace(replace('这张卡发动后，第3次的自己结束阶段破坏。\r\n①：「秘旋谍任务-救出」在自己场上只能有1张表侧表示存在。\r\n②：1回合1次，以自己墓地1只「秘旋谍」怪兽为对象才能发动。那只怪兽加入手卡。\r\n③：把墓地的这张卡除外，以自己墓地1只「秘旋谍」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'发动但不使用效果','发动并使用效果','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9236985,'遗式的写魂镜','名字带有「遗式」的仪式怪兽的降临必需。必须支付仪式召唤的怪兽等级×500基本分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9260791,'银河眼云笼','把这张卡解放才能发动。从自己的手卡·墓地选「银河眼云笼」以外的1只名字带有「银河眼」的怪兽特殊召唤。「银河眼云笼」的这个效果1回合只能使用1次。此外，这张卡在墓地存在的场合，自己的主要阶段时选择自己场上1只名字带有「银河眼」的超量怪兽才能发动。把墓地的这张卡在选择的怪兽下面重叠作为超量素材。「银河眼云笼」的这个效果在决斗中只能使用1次。','特殊召唤','补充素材','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9264485,'荷鲁斯的仆人','只要这张卡在自己场上表侧表示存在，对方的魔法·陷阱·怪兽的效果不能以「荷鲁斯之黑炎龙（包括全部等级）」的怪兽为对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9267769,'闪光弹','对方场上的怪兽直接攻击成功时才能发动。变成这个回合的结束阶段。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9272381,'星辉士 星圣冬钻龙',replace(replace('光属性5星怪兽×3只以上\r\n这张卡也能在自己主要阶段2在「星辉士 星圣冬钻龙」以外的自己场上的「星守」超量怪兽上面重叠来超量召唤。\r\n①：只要持有超量素材的这张卡在怪兽区域存在，双方不能从卡组把卡送去墓地，从墓地回到手卡的卡不回到手卡而除外。\r\n②：对方的暗属性怪兽的效果发动时，把这张卡1个超量素材取除才能发动。那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'是否在「星守」超量怪兽上面重叠超量召唤？','无效并破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9284723,'蛇毒巨蟒','1回合只有1次，可以给对方场上1只怪兽放置2个毒指示物。这个效果使用的回合这只怪兽不能攻击宣言。','放置指示物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9287078,'黑魔族复活之棺','①：对方对怪兽的召唤·特殊召唤成功时，选择那1只怪兽和自己场上1只魔法师族怪兽才能发动。选择的2只怪兽送去墓地。那之后，可以从自己的卡组·墓地选1只魔法师族·暗属性怪兽特殊召唤。','是否从自己的卡组·墓地选1只魔法师族·暗属性怪兽特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9293977,'金属龙','「钢铁巨神像」＋「下级龙」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9298235,'毛绒动物娃娃机','①：自己场上的表侧表示的「毛绒动物」怪兽被对方怪兽的攻击或者对方的效果破坏并送去自己的墓地时，以破坏的那1只自己怪兽为对象才能发动。那只怪兽加入手卡，自己从卡组抽1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9327502,'元素英雄 电流侠','①：这张卡给与对方战斗伤害时，以除外的1只自己的「元素英雄」怪兽为对象才能发动。那只怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9334391,'宝玉的牵绊',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：从卡组把1只「宝玉兽」怪兽加入手卡，从卡组选和那只怪兽卡名不同的1只「宝玉兽」怪兽当作永续魔法卡使用在自己的魔法与陷阱区域表侧表示放置。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9336190,'幻影骑士团 雾鸦爪',replace(replace('①：以除外的1只自己的「幻影骑士团」怪兽为对象才能发动。那只怪兽加入手卡。\r\n②：这张卡在墓地存在的场合，对方的直接攻击宣言时以自己墓地1只4星以下的「幻影骑士团」怪兽为对象才能发动。那只怪兽特殊召唤，这张卡变成持有和那只怪兽相同原本等级的通常怪兽（战士族·暗·攻/守0）在怪兽区域特殊召唤（不当作陷阱卡使用）。这个效果特殊召唤的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9342162,'彼岸的恶鬼 卡尼亚佐',replace(replace('「彼岸的恶鬼 卡尼亚佐」的①③的效果1回合只能有1次使用其中任意1个。\r\n①：自己场上没有魔法·陷阱卡存在的场合才能发动。这张卡从手卡特殊召唤。\r\n②：自己场上有「彼岸」怪兽以外的怪兽存在的场合这张卡破坏。\r\n③：这张卡被送去墓地的场合才能发动。从卡组把1张「彼岸」魔法·陷阱卡送去墓地。','\r',char(13)),'\n',char(10)),'特殊召唤','送去墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9348522,'浮鵺城',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：这张卡同调召唤成功时，以自己墓地1只9星怪兽为对象才能发动。那只怪兽特殊召唤。\r\n②：只要这张卡在怪兽区域存在，召唤·特殊召唤的8星以下的怪兽在那个回合不能攻击。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9354555,'光子帷幕','从自己手卡让3只光属性怪兽回到卡组，可以从自己卡组把最多3只光属性·4星以下的怪兽加入手卡。2只以上加入手卡的场合，必须全部是同名怪兽。','是否还要加入手卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9365703,'高速战士','①：这张卡的召唤成功的回合的战斗步骤才能发动。这张卡的攻击力直到战斗阶段结束时变成原本攻击力的2倍。','攻击变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9373534,'风魔手里剑','名称中含有「忍者」字样的怪兽才能装备这张卡。装备这张卡的怪兽攻击力上升700点。这张卡从场上送去墓地时，给与对方基本分700分的伤害。','给予对方700分伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9391354,'重力击龙','①：把自己场上1张表侧表示的永续魔法卡送去墓地，以对方场上1只怪兽为对象才能发动。那只对方怪兽破坏。','怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9402966,'超重武者 魂-C',replace(replace('「超重武者 魂-C」的效果1回合只能使用1次。\r\n①：自己场上没有「超重武者」怪兽以外的怪兽存在，自己墓地没有魔法·陷阱卡存在的场合，以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽和这张卡从场上送去墓地。那之后，把持有和送去墓地的那2只怪兽的原本等级合计相同等级的1只「超重武者」同调怪兽从额外卡组当作同调召唤作特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9411399,'命运英雄 魔性人','①：把墓地的这张卡除外才能发动。从卡组把1只「命运英雄 魔性人」特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9418365,'武神-日孁','这张卡不能通常召唤。把「武神-日孁」以外的自己墓地1只名字带有「武神」的怪兽从游戏中除外的场合可以特殊召唤。此外，这个方法特殊召唤的这张卡被对方破坏送去墓地的场合才能发动。自己丢弃1张手卡。那之后，对方选1张手卡丢弃。「武神-日孁」在自己场上只能有1只表侧表示存在。','丢弃手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9418534,'人造人-念力归来者','这张卡可以直接攻击对方玩家。这张卡被送去墓地时，可以把自己墓地存在的1只「人造人-念力震慑者」特殊召唤。这个效果特殊召唤的「人造人-念力震慑者」在自己回合的结束阶段时破坏。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9430387,'雷云怪','带电的云形怪兽。下着能溶掉一切的危险之雨。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9433350,'罪 青眼白龙','这张卡不能通常召唤。从卡组把1只「青眼白龙」除外的场合可以特殊召唤。名字带有「罪」的怪兽在场上只能有1只表侧表示存在。此外，只要这张卡在场上表侧表示存在，自己的其他怪兽不能攻击宣言。没有场地魔法卡表侧表示存在的场合，这张卡破坏。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9485511,'超级运动员 守门员',replace(replace('「超级运动员 守门员」的①的方法的特殊召唤1回合只能有1次。\r\n①：这张卡可以让「超级运动员 守门员」以外的自己场上1只「超级运动员」怪兽回到手卡，从手卡特殊召唤。\r\n②：对方回合1次，以自己场上1只「超级运动员」怪兽为对象才能发动。那只怪兽在这个回合只有1次不会被战斗·效果破坏。','\r',char(13)),'\n',char(10)),'破坏耐性','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9523599,'堆栈修复员',replace(replace('这个卡名的效果在决斗中只能使用1次。\r\n①：这张卡为素材作连接召唤的场合，以这张卡以外的自己墓地1只作为那次连接召唤的素材的4星以下的电子界族怪兽为对象才能发动。那只怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9540040,'岩石龟','全身由岩石做成的龟。特点是守备力非常高。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9547962,'欧拉回路',replace(replace('这个卡名的③的效果1回合只能使用1次。\r\n①：自己场上有「廷达魔三角」怪兽3只以上存在的场合，对方怪兽不能攻击。\r\n②：自己准备阶段，以自己场上1只「廷达魔三角」怪兽为对象才能发动。那只怪兽的控制权移给对方。\r\n③：把墓地的这张卡除外，从手卡丢弃1张「廷达魔三角」卡才能发动。从卡组把1张「欧拉回路」加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9553721,'灵魂的据所',replace(replace('「灵魂的据所」的②的效果1回合只能使用1次。\r\n①：自己场上的灵魂怪兽的攻击力·守备力上升500。\r\n②：自己场上的表侧表示的风属性怪兽回到自己手卡的场合才能发动。从卡组把1只灵魂怪兽或者1张仪式魔法卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9560338,'魔导冥士 拉莫尔',replace(replace('这张卡召唤·特殊召唤成功时才能发动。自己墓地的名字带有「魔导书」的魔法卡种类的以下效果适用。「魔导冥士 拉莫尔」的效果1回合只能使用1次。\r\n●3种类以上：这张卡的攻击力上升600。\r\n●4种类以上：从卡组把1张名字带有「魔导书」的魔法卡加入手卡。\r\n●5种类以上：从卡组把1只魔法师族·暗属性·5星以上的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'多种类效果','这张卡的攻击力上升600','从卡组把1张名字带有「魔导书」的魔法卡加入手卡','从卡组把1只魔法师族·暗属性·5星以上的怪兽特殊召唤','','','','','','','','','','','','');
INSERT INTO texts VALUES(9576193,'怪兽角子机','选择自己场上表侧表示存在的1只怪兽，选择和选择的怪兽相同等级的自己墓地存在的1只怪兽从游戏中除外。那之后，从自己卡组抽1张卡。这个效果抽到的卡给双方确认，和选择的怪兽相同等级的怪兽的场合，那只怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9583383,'我我我帝王','这张卡在自己场上有这张卡以外的名字带有「我我我」的怪兽存在的场合才能攻击。此外，1回合1次，把自己墓地1只怪兽从游戏中除外才能发动。自己场上的全部名字带有「我我我」的怪兽的等级变成和为这个效果发动而从游戏中除外的怪兽相同等级。这张卡不能作为同调素材。','等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9596126,'混沌巫师','这张卡不能通常召唤。把自己墓地的光属性和暗属性怪兽各1只从游戏中除外来特殊召唤。可以把场上表侧表示存在的1只怪兽从游戏中除外。这个效果发动的场合，这个回合这张卡不能攻击。这个效果1回合只能使用1次。','特殊召唤','除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9603356,'暗影骑士恶魔','这张卡的控制者在自己的每1个准备阶段支付900基本分。当这张卡成为对方所控制的卡的效果对象时，在效果处理时掷1次骰子，若掷出3，则使此效果无效并将其破坏。这张卡对对方玩家造成的战斗伤害减半。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9617996,'狮子王之圣像骑士',replace(replace('包含「圣像骑士」怪兽的效果怪兽2只\r\n这个卡名的③的效果1回合只能使用1次。\r\n①：这张卡的攻击力上升这张卡所连接区的怪兽的原本攻击力数值。\r\n②：这张卡所连接区的怪兽不能攻击。\r\n③：这张卡所连接区有效果怪兽特殊召唤的场合才能发动。从卡组把1张「圣像骑士」魔法·陷阱卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9622164,'异次元复活','①：丢弃1张手卡，以除外的1只自己怪兽为对象才能把这张卡发动。那只怪兽攻击表示特殊召唤，把这张卡装备。这张卡从场上离开时那只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9628664,'地母神 艾莉丝','连锁积累有3个的场合，从自己卡组抽1张卡。同1组连锁上有复数次同名卡的效果发动的场合，这个效果不能发动。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9633505,'守护者·凯丝特','当自己场上存在「静寂之杖-波纹」时才能召唤·反转召唤·特殊召唤。这张卡不受魔法效果影响。这张卡不能成为对方的攻击对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9637706,'死亡袋熊','只要这张卡在场上表侧表示存在，控制者受到的卡的效果的伤害为0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9653271,'雷神之怒','「耳天使」＋「大雷电球」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9659580,'方界业',replace(replace('①：这张卡的发动时，可以以「方界胤 毗贾姆」以外的自己场上1只「方界」怪兽为对象。那个场合，从手卡·卡组把「方界胤 毗贾姆」任意数量送去墓地。那之后，作为对象的怪兽的攻击力上升这个效果送去墓地的怪兽数量×800。\r\n②：对方回合「方界」怪兽的效果让「方界胤 毗贾姆」特殊召唤的场合发动。这张卡送去墓地，对方基本分变成一半。\r\n③：把墓地的这张卡除外才能发动。从卡组把1只「方界」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'是否以「方界」怪兽为对象发动？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9666558,'石龙','只要这张卡在场上表侧表示存在，对方不能选择表侧表示存在的其他的龙族怪兽作为攻击对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9718968,'光子杀戮者','场上有超量怪兽存在的场合，这张卡可以从手卡表侧守备表示特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9720537,'所有者的刻印','场上的全部怪兽的控制权回归原本持有者。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9742784,'喷气同调士',replace(replace('「喷气同调士」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：这张卡作为同调素材送去墓地的场合才能发动。从卡组把1只「废品」怪兽加入手卡。\r\n②：这张卡在墓地存在的场合，把1张手卡送去墓地才能发动。这张卡从墓地特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9744376,'哥布林的经营手腕','从卡组抽自己墓地存在的「哥布林的经营手腕」的数量＋1的卡，选择1张手卡放回到卡组最下面。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9748752,'邪帝 盖乌斯','①：这张卡上级召唤成功的场合，以场上1张卡为对象发动。那张卡除外，除外的卡是暗属性怪兽卡的场合，给与对方1000伤害。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9753964,'琰魔龙 红莲魔·渊',replace(replace('调整＋调整以外的龙族·暗属性同调怪兽1只\r\n「琰魔龙 红莲魔·渊」的①②的效果1回合各能使用1次。\r\n①：以对方场上1张表侧表示的卡为对象才能发动。那张卡的效果直到回合结束时无效。这个效果在对方回合也能发动。\r\n②：这张卡给与对方战斗伤害时，以自己墓地1只调整为对象才能发动。那只怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'效果无效','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9765723,'女武神的契约书',replace(replace('「女武神的契约书」的①的效果1回合只能使用1次。\r\n①：从手卡把1张「DD」卡或者「契约书」卡送去墓地，以场上1张卡为对象才能发动。那张卡破坏。\r\n②：只要这张卡在魔法与陷阱区域存在，自己场上的恶魔族怪兽的攻击力在对方回合内上升1000。\r\n③：自己准备阶段发动。自己受到1000伤害。','\r',char(13)),'\n',char(10)),'卡片破坏','受到伤害','是否使用效果？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9780364,'剑斗调教',replace(replace('场上有名字带有「剑斗兽」的怪兽存在的场合，可以从以下效果选择1个发动。\r\n●选择对方场上表侧表示存在的1只怪兽，把表示形式变更。\r\n●选择对方场上1只名字带有「剑斗兽」的怪兽直到结束阶段时得到控制权。','\r',char(13)),'\n',char(10)),'选择对方场上表侧表示存在的1只怪兽，把表示形式变更。','选择对方场上1只名字带有「剑斗兽」的怪兽直到结束阶段时得到控制权。','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9786492,'白龙降临','「白龙之圣骑士」降临所必需。特殊召唤时，必须以场上或手卡中合计4星以上的怪兽作为祭品。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9791914,'白银狙击手','这张卡可以当作魔法卡使用从手卡到魔法与陷阱卡区域盖放。魔法与陷阱卡区域盖放的这张卡被对方的卡的效果破坏送去墓地的回合的结束阶段时，这张卡从墓地特殊召唤，选择对方场上1张卡破坏。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9817927,'反怒的熊猫','对方场上每存在1只怪兽，这张卡的攻击力上升500点。这张卡攻击守备表示的怪兽时，攻击力超出守备力部分的数值，对方受到那个数值的战斗伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9831539,'唐怀瑟之门','选择自己场上2只攻击力1000以下的相同种族的怪兽才能发动。选择的2只怪兽变成那2只的等级合计的等级。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9837195,'薰风的神官 穆斯托','可以选择自己墓地存在的1只名字带有「薰风」的怪兽回到卡组，选择场上表侧表示存在的1只怪兽把那个效果直到结束阶段时无效。这个效果1回合只能发动1次。','效果无效化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9845733,'觉醒之证','「觉醒战士 库丘林」的降临需要。必须从场上或手卡把合计4星以上的卡作为祭品。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9848939,'树海的射手','这张卡不能通常召唤。自己墓地通常怪兽有2只以上存在的场合才能特殊召唤。可以把这张卡解放，从自己卡组把1只二重怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9852718,'诀别','①：对方战斗阶段从手卡把1张魔法卡送去墓地才能发动。那次战斗阶段结束。场上的表侧表示怪兽直到回合结束时效果无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9861795,'遁地鼹鼠','场上的这张卡被破坏送去墓地时，这张卡可以从墓地表侧守备表示特殊召唤。「遁地鼹鼠」的效果在决斗中只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9888196,'正义盟军 决战兵器',replace(replace('调整＋调整以外的怪兽2只以上\r\n对方场上有光属性怪兽表侧表示存在的场合，1回合1次，可以从下面效果选择1个发动。\r\n●把对方场上盖放的1张卡破坏。\r\n●把1张手卡送去墓地，对方场上存在的魔法·陷阱卡全部破坏。\r\n●把自己手卡全部送去墓地，把对方手卡确认并从那之中把光属性怪兽全部送去墓地。那之后给与对方基本分送去墓地的对方怪兽的攻击力合计数值的伤害。','\r',char(13)),'\n',char(10)),'把对方场上盖放的1张卡破坏','对方场上存在的魔法·陷阱卡全部破坏','把对方手卡确认并从那之中把光属性怪兽全部送去墓地','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9897998,'薰风鹫','这张卡战斗破坏对方怪兽送去墓地时，可以从自己卡组把1只守备力1500以下的念动力族·风属性怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9910360,'三死青蛙',replace(replace('「死亡青蛙」＋「死亡青蛙」＋「死亡青蛙」\r\n这只怪兽融合召唤只能使用上述的卡进行。这张卡的攻击力上升自己墓地存在的「黄泉青蛙」的数量×500的数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9925982,'黑翼复仇','①：自己场上的鸟兽族怪兽被战斗破坏送去墓地时才能发动。在自己场上把2只「黑羽-黑冠衍生物」（鸟兽族·暗·2星·攻0/守800）特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9925983,'黑羽-黑冠衍生物','由「黑翼复仇」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9929398,'黑羽-胧影之业风',replace(replace('这张卡不能通常召唤。自己场上没有怪兽存在的场合可以特殊召唤。\r\n①：这张卡从手卡的特殊召唤成功时才能发动。在自己场上把2只「胧影衍生物」（鸟兽族·暗·1星·攻/守0）特殊召唤。这衍生物不能解放，不能作为同调素材。\r\n②：把这张卡和除调整以外的怪兽1只以上从自己场上除外，以持有和那个等级合计相同等级的自己墓地1只「黑羽」同调怪兽为对象才能发动。那只怪兽当作调整使用特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤衍生物','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9929399,'胧影衍生物','由「黑羽-胧影之业风」的效果特殊召唤。这衍生物不能解放，不能作为同调素材。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9952083,'召唤连锁','①：同一连锁上没有复数次同名卡的效果发动的场合，那个连锁3以后才能发动。这个回合自己可以进行通常召唤最多3次。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9989792,'三形金字塔·大要塞',replace(replace('「三形金字塔·大要塞」的③的效果1回合只能使用1次。\r\n①：场上的岩石族怪兽的守备力上升500。\r\n②：场上的「三形金字塔」怪兽不会被效果破坏。\r\n③：场地区域的表侧表示的这张卡被送去墓地的场合，以自己墓地1只「三形金字塔」怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9995766,'宫廷的规矩','场上表侧表示存在的「宫廷的规矩」以外的永续陷阱卡不能破坏。「宫廷的规矩」在自己场上只能有1张表侧表示存在。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(9999961,'兽怒','自己场上存在的全部怪兽的攻击力上升从游戏中除外的自己的兽族以及鸟兽族怪兽数量×200的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10000000,'欧贝利斯克之巨神兵',replace(replace('这张卡通常召唤的场合，必须把3只解放作召唤。\r\n①：这张卡的召唤不会被无效化。\r\n②：在这张卡的召唤成功时，魔法·陷阱·怪兽的效果不能发动。\r\n③：这张卡不会成为效果的对象。\r\n④：把自己场上2只怪兽解放才能发动。对方场上的怪兽全部破坏。这个效果发动的回合，这张卡不能攻击宣言。\r\n⑤：这张卡特殊召唤的场合，结束阶段发动。这张卡送去墓地。','\r',char(13)),'\n',char(10)),'送去墓地','对方怪兽全部破坏','','','','','','','','','','','','','','');
