INSERT INTO texts VALUES(10275411,'超量复仇','对方场上有持有超量素材的超量怪兽存在的场合，选择自己墓地1只超量怪兽才能发动。选择的怪兽特殊召唤，把对方场上1个超量素材在选择的怪兽下面重叠作为超量素材。「超量复仇」在1回合只能发动1张。','请选择要转移的素材','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10282757,'浮朗克尺度',replace(replace('①：这个回合中，以下效果适用。\r\n●双方场上的全部3阶以下的超量怪兽的攻击力·守备力上升500。\r\n●双方场上的全部4阶以上的超量怪兽不能攻击。','\r',char(13)),'\n',char(10)),'阶级3以下的超量怪兽的攻击力·守备力上升500','阶级4以上的超量怪兽不能攻击','','','','','','','','','','','','','','');
