INSERT INTO texts VALUES(10321588,'日光独角兽','1回合1次，自己的主要阶段时才能发动。把自己卡组最上面的卡翻开，那是装备魔法卡的场合加入手卡。不是的场合，回到自己卡组最下面。','检索','','','','','','','','','','','','','','','');
