INSERT INTO texts VALUES(10389794,'死亡甜甜圈','反转：场上表侧表示存在的原本攻击力或者原本守备力是0的怪兽全部破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10406322,'森罗的守神 森精',replace(replace('8星怪兽×2\r\n1回合1次，宣言1个卡名才能发动。自己卡组最上面的卡翻开，宣言的卡的场合，加入手卡。不是的场合，翻开的卡送去墓地。此外，卡的效果从自己卡组让卡送去墓地的场合，把这张卡1个超量素材取除才能发动。选择场上1张卡回到持有者卡组最上面或者最下面。「森罗的守神 森精」的这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'宣言卡名','返回卡组','返回卡组最上面','返回卡组最下面','','','','','','','','','','','','');
INSERT INTO texts VALUES(10424147,'超级量子机舰 炎磁大母舰',replace(replace('①：丢弃1张手卡，以自己场上1只「超级量子战士」怪兽为对象才能把这个效果发动。和那只自己怪兽相同属性的1只「超级量子机兽」超量怪兽在作为对象的怪兽上面重叠当作超量召唤从额外卡组特殊召唤。\r\n②：把场地区域的这张卡送去墓地，以自己的场上·墓地的「超级量子机兽」超量怪兽3种类各1只为对象才能发动。从额外卡组把1只「超级量子机神王 大磁炎」特殊召唤，那下面把作为对象的怪兽和那些超量素材全部重叠作为超量素材。','\r',char(13)),'\n',char(10)),'丢弃手卡','送去墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10441498,'辉神鸟 贝努鸟',replace(replace('「原初的叫唤」降临。\r\n①：1回合1次，把手卡1只怪兽给对方观看，以场上1只表侧表示怪兽为对象才能发动。这个回合，作为对象的怪兽的等级变成和给人观看的怪兽相同。\r\n②：1回合1次，这张卡以外的自己的手卡·场上的怪兽被解放的场合，以自己墓地1只怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'等级变化','加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10443957,'电子龙·无限',replace(replace('机械族·光属性6星怪兽×3\r\n「电子龙·无限」1回合1次也能在自己场上的「电子龙·新星」上面重叠来超量召唤。\r\n①：这张卡的攻击力上升这张卡的超量素材数量×200。\r\n②：1回合1次，以场上1只表侧攻击表示怪兽为对象才能发动。那只怪兽在这张卡下面重叠作为超量素材。\r\n③：1回合1次，卡的效果发动时，把这张卡1个超量素材取除才能发动。那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'是否在「电子龙·新星」上面重叠来超量召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10449150,'航天机人','这张卡被选择作为攻击对象时，可以把这张卡从游戏中除外。这个效果从游戏中除外的场合，这张卡在下次的自己的准备阶段时特殊召唤。那个时候，给与对方基本分1000分伤害。','除外','特殊召唤','LP伤害','','','','','','','','','','','','','');
