INSERT INTO texts VALUES(10509340,'古代的机械兽',replace(replace('这张卡不能特殊召唤。\r\n①：这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。\r\n②：这张卡战斗破坏的对方怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
