INSERT INTO texts VALUES(10560119,'电子鱼人-博士',replace(replace('「电子鱼人-博士」的②的效果1回合只能使用1次。\r\n①：自己场上有「电子鱼人」怪兽以外的怪兽存在的场合这张卡破坏。\r\n②：这张卡在墓地存在，自己场上的怪兽只有「电子鱼人」怪兽的场合，自己主要阶段才能发动。这张卡从墓地特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10591919,'变形斗士·显微镜',replace(replace('这张卡得到这张卡的表示形式的以下效果。\r\n●攻击表示：1回合1次，可以从手卡把1只4星的名字带有「变形斗士」的怪兽特殊召唤。这个效果特殊召唤的怪兽在结束阶段时破坏。\r\n●守备表示：只要这张卡在场上表侧守备表示存在，这张卡的等级变成4星。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
