INSERT INTO texts VALUES(10678778,'魔海城 埃该翁',replace(replace('8星怪兽×2\r\n「魔海城 埃该翁」的①②的效果1回合各能使用1次，对方回合也能发动。\r\n①：从对方的额外卡组把里侧表示的怪兽随机1只除外。这张卡的攻击力变成和除外的怪兽的攻击力相同。\r\n②：把这张卡1个超量素材取除，以除外的1只对方的融合·同调·超量怪兽为对象才能发动。那只怪兽回到额外卡组，选和那只怪兽相同种类（融合·同调·超量）的对方场上1只怪兽破坏。','\r',char(13)),'\n',char(10)),'除外','怪兽破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10691144,'冰结界之镜',replace(replace('这个回合，每次对方发动的效果怪兽的效果让自己的手卡·场上·墓地的卡从游戏中除外，以下效果适用。\r\n●手卡的卡被除外的场合，对方手卡随机最多2张从游戏中除外。\r\n●自己场上存在的卡被除外的场合，对方场上存在的最多2张卡从游戏中除外。\r\n●墓地存在的卡被除外的场合，对方墓地存在的最多2张卡从游戏中除外。','\r',char(13)),'\n',char(10)),'是否要除外对方的手卡？','是否要除外对方场上的卡？','是否要除外对方墓地的卡？','除外一张手牌','除外两张手牌','','','','','','','','','','','');
INSERT INTO texts VALUES(10698416,'机怪虫·郎飞结虫',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡反转的场合，以自己墓地最多2只「机怪虫」怪兽为对象才能发动。那些怪兽加入手卡。\r\n②：表侧表示的这张卡因对方的效果从场上离开的场合才能发动。「机怪虫·郎飞结虫」以外的2只「机怪虫」怪兽从卡组里侧守备表示特殊召唤（同名卡最多1张）。','\r',char(13)),'\n',char(10)),'墓地回收','2只怪兽特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10705656,'控件小子',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合才能发动。从手卡把1只电子界族怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10712320,'斯蒂格马大日','1回合1次，可以把自己场上表侧表示存在的1张名字带有「大日」的装备卡转换给1只别的能变成正确对象的怪兽。这个效果在对方回合也能发动。','装备转移','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10719350,'炎舞-「天枢」','只要这张卡在场上存在，自己的主要阶段时只有1次，自己在通常召唤外加上可以把1只兽战士族怪兽召唤。此外，只要这张卡在场上存在，自己场上的兽战士族怪兽的攻击力上升100。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10731333,'娱乐伙伴 异色眼牛头人',replace(replace('←6 【灵摆】 6→\r\n①：自己的「娱乐伙伴」怪兽或者「异色眼」怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。\r\n【怪兽效果】\r\n①：自己的灵摆怪兽向对方怪兽攻击的伤害计算时才能发动。那只对方怪兽的攻击力只在那次伤害计算时下降自己场上的「娱乐伙伴」卡以及「异色眼」卡数量×100。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10736540,'湖中少女 薇薇安',replace(replace('把这张卡作为同调素材的场合，不是战士族怪兽的同调召唤不能使用，被同调召唤使用的这张卡除外。\r\n①：这张卡召唤成功时，以自己墓地1只「圣骑士」通常怪兽为对象才能发动。那只怪兽特殊召唤。\r\n②：这张卡在墓地存在的场合，以自己场上1只5星的「圣骑士」怪兽为对象才能发动。那只怪兽的等级下降1星，这张卡从墓地特殊召唤。','\r',char(13)),'\n',char(10)),'墓地苏生','这张卡特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10753491,'森罗的神芽 幼芽','这张卡特殊召唤成功时，可以从自己卡组上面把最多2张卡翻开。翻开的卡之中有植物族怪兽的场合，那些怪兽全部送去墓地。剩下的卡用喜欢的顺序回到卡组最下面。此外，卡组的这张卡被卡的效果翻开送去墓地的场合，可以从卡组把1只植物族·1星怪兽特殊召唤。「森罗的神芽 幼芽」的这个效果1回合只能使用1次。','翻开卡组','请选择要翻开卡组的数量','特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10755153,'守护者·希尔','当自己场上存在「流星之弓-烨焰」时才能召唤·反转召唤·特殊召唤。将1张这张卡身上装备的自己的装备卡送去墓地，破坏对方场上1只怪兽。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10755984,'大薰风天鹰',replace(replace('调整＋调整以外的名字带有「薰风」的怪兽1只以上\r\n1回合1次，自己的结束阶段时可以从自己墓地把1只风属性怪兽从游戏中除外，选择对方场上里侧表示存在的1张卡破坏。','\r',char(13)),'\n',char(10)),'场上里侧表示存在的1张卡破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10759529,'小子防御','把自己场上存在的1只「英雄小子」作为祭品。对方怪兽的攻击无效，从自己卡组把1只名字带有「元素英雄」的怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10789972,'觉醒战士 库丘林','用「觉醒之证」降临。把自己墓地存在的1只通常怪兽从游戏中除外。直到下次的自己回合的准备阶段时，这张卡的攻击力上升被这个效果从游戏中除外的通常怪兽的攻击力数值。这个效果1回合只能使用1次。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10802915,'由魔界到现世的死亡导游','①：这张卡召唤成功时才能发动。从手卡·卡组把1只恶魔族·3星怪兽特殊召唤。这个效果特殊召唤的怪兽效果无效化，不能作为同调素材。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10809984,'世纪大盗','每当这张卡给与对方战斗伤害时，宣言1张卡的名字。检视对方手卡，若其中有被宣言名字的卡，则将其全部扔进墓地。','宣言','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10813327,'打草惹蛇','①：盖放的这张卡因对方的效果从场上离开，被送去墓地的场合或者被除外的场合才能发动。从卡组·额外卡组把1只怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10817524,'始祖龙 古龙',replace(replace('通常怪兽×2\r\n这张卡不用融合召唤不能特殊召唤。\r\n①：「始祖龙 古龙」在自己场上只能有1只表侧表示存在。\r\n②：只要这张卡在怪兽区域存在，这张卡不会被和通常怪兽以外的怪兽的战斗破坏，不受这张卡以外的怪兽的效果影响。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10833828,'魔神王的禁断契约书',replace(replace('①：1回合1次，自己主要阶段才能发动。从手卡把1只「DDD」怪兽效果无效守备表示特殊召唤。\r\n②：1回合1次，自己主要阶段才能发动。恶魔族的融合怪兽卡决定的包含用这张卡的①的效果特殊召唤的怪兽的融合素材怪兽从自己的手卡·场上送去墓地，把那1只融合怪兽从额外卡组融合召唤。\r\n③：自己准备阶段发动。自己受到2000伤害。','\r',char(13)),'\n',char(10)),'特殊召唤','融合召唤','效果伤害','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10852583,'平衡侵略者',replace(replace('①：这张卡在中央以外的主要怪兽区域召唤·特殊召唤的场合破坏。\r\n②：1回合1次，自己主要阶段才能发动。这张卡向相邻的没有使用的主要怪兽区域移动。\r\n③：每次对方场上只有怪兽1只召唤·特殊召唤发动。那只对方怪兽的位置是和这张卡不同纵列的场合，这张卡向要往那只对方怪兽靠近的相邻的主要怪兽区域移动。那之后，和移动过的这张卡相同纵列的其他卡全部破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10859908,'全息投影者','使人看见各种幻想、趁机进行攻击的机器。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10860121,'武神器-五百箇',replace(replace('「武神器-五百箇」的效果1回合只能使用1次。\r\n①：自己场上的兽战士族的「武神」怪兽被战斗破坏送去墓地时，把这张卡从手卡送去墓地才能发动。从卡组把1只「武神」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10875327,'地缚神 阿斯利亚·皮斯科','名字带有「地缚神」的怪兽在场上只能有1只表侧表示存在。场上没有表侧表示场地魔法卡存在的场合这张卡破坏。对方不能选择这张卡作为攻击对象。这张卡可以直接攻击对方玩家。这张卡的效果以外让这张卡从场上离开时，对方场上表侧表示存在的怪兽全部破坏，破坏的怪兽每有1只给与对方基本分800分伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10920352,'假面英雄 雾水','这张卡用「假面变化」的效果才能特殊召唤。这张卡不会被魔法·陷阱·效果怪兽的效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10925955,'与仪水镜的交信',replace(replace('自己场上有水属性怪兽表侧表示存在的场合，从以下效果选择1个发动。自己场上有水属性的仪式怪兽表侧表示存在的场合，可以选择两方发动。\r\n●对方的魔法与陷阱卡区域盖放的卡全部确认再回到原状。\r\n●从自己或者对方的卡组上面把2张卡确认，用喜欢的顺序回到卡组上面。','\r',char(13)),'\n',char(10)),'确认对方的魔法与陷阱卡区域盖放的卡','确认卡组上方的两张卡','两个效果都使用','确认自己的卡组','确认对方的卡组','','','','','','','','','','','');
INSERT INTO texts VALUES(10928224,'亚马逊宠物仔虎',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：这张卡的卡名只要在场上·墓地存在当作「亚马逊宠物虎」使用。\r\n②：这张卡在手卡·墓地存在，自己场上有「亚马逊」怪兽召唤·特殊召唤的场合才能发动。这张卡特殊召唤。\r\n③：这张卡的攻击力上升自己墓地的「亚马逊」卡数量×100。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10960419,'时间魔术锤',replace(replace('这张卡在用「赫谟之爪」的效果把自己的手卡·场上的魔法师族怪兽送去墓地的场合才能特殊召唤。\r\n①：这张卡特殊召唤成功的场合，以这张卡以外的场上1只怪兽为对象发动。这张卡当作装备卡使用给那只怪兽装备。\r\n②：用这张卡的效果把这张卡装备的怪兽和对方怪兽进行战斗的伤害步骤开始时才能发动。掷1次骰子。那只对方怪兽直到和出现的数目相同次数的回合后的准备阶段为止除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10963799,'豪雨之结界像','只要这张卡在场上表侧表示存在，不能作水属性怪兽以外的特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10971759,'光虫异变',replace(replace('「光虫异变」在1回合只能发动1张。\r\n①：以自己墓地2只昆虫族·3星怪兽为对象才能发动。那些怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化。\r\n②：从自己墓地把这张卡和1只超量怪兽除外才能发动。自己场上的全部昆虫族·3星怪兽的等级直到回合结束时变成和除外的超量怪兽的阶级相同数值的等级。这个效果的发动后，直到回合结束时自己不是昆虫族怪兽不能特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','等级变更','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10979723,'亚马逊宠物虎','这张卡在自己的场上只能表侧表示存在1张。自己场上每存在1张名称中含有「亚马逊」字样的怪兽卡，这张卡的攻击力上升400点。对方不破坏这张卡，就不能攻击场上其他名称中含有「亚马逊」字样的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(10992251,'超时空战斗机 V形蛇','拥有后备仓库，能发挥各种能力的高性能战斗机。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(11012887,'朱罗纪瓜巴龙','这张卡战斗破坏对方怪兽的场合，可以从自己卡组把1只攻击力1700以下的名字带有「朱罗纪」的怪兽特殊召唤。这个效果特殊召唤的怪兽在这个回合不能攻击宣言。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(11021521,'招财猫王','对方回合中，当这张卡被对方的魔法、陷阱或效果怪兽的效果送去墓地时，对方的回合立刻结束。','回合结束','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(11039171,'魔玩具·剪刀狼',replace(replace('「锋利小鬼·剪刀」＋「毛绒动物」怪兽1只以上\r\n这张卡用以上记的卡为融合素材的融合召唤才能特殊召唤。\r\n①：这张卡在同1次的战斗阶段中可以作出最多有作为这张卡的融合素材的怪兽数量的攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(11047543,'念力感知区','让从游戏中除外的自己1只念动力族的调整和1只调整以外的念动力族怪兽回到墓地，和那个等级合计相同等级的1只念动力族的同调怪兽从额外卡组表侧守备表示特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(11050415,'超河马狂欢节','①：从自己的手卡·卡组·墓地选1只「娱乐伙伴 探寻河马」特殊召唤。那之后，可以在自己场上把「河马衍生物」（兽族·地·1星·攻/守0）尽可能特殊召唤。这衍生物不能解放。只要「河马衍生物」在怪兽区域存在，自己不能从额外卡组把怪兽特殊召唤。这个效果把「河马衍生物」特殊召唤的场合，直到回合结束时对方不能把「河马衍生物」以外的怪兽作为攻击对象。','是否尽可能地特殊召唤「河马衍生物」？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(11050416,'河马衍生物','由「超河马狂欢节」的效果特殊召唤。这衍生物不能解放。只要「河马衍生物」在怪兽区域存在，自己不能从额外卡组把怪兽特殊召唤。','','','','','','','','','','','','','','','','');
