INSERT INTO texts VALUES(11743119,'同盟骑手','得到对方场上1张处于怪兽状态的同盟怪兽的控制权，装备在这张卡身上。这张卡至多只能以这种方式装备1只同盟怪兽。装备在这张卡身上的同盟怪兽不能以自身效果回复成怪兽状态。','装备','','','','','','','','','','','','','','','');
