INSERT INTO texts VALUES(11954712,'飞牙鲨','这张卡向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。这张卡给与对方基本分战斗伤害的战斗阶段结束时，这张卡直到下次的自己的准备阶段时从游戏中除外。','除外','','','','','','','','','','','','','','','');
