INSERT INTO texts VALUES(12014404,'我我我枪手',replace(replace('4星怪兽×2\r\n①：1回合1次，把这张卡1个超量素材取除才能发动。这张卡的表示形式的以下效果适用。\r\n●攻击表示：这个回合，这张卡向对方怪兽攻击的伤害步骤内，这张卡的攻击力上升1000，那只对方怪兽的攻击力下降500。\r\n●守备表示：给与对方800伤害。','\r',char(13)),'\n',char(10)),'攻击变化','伤害','','','','','','','','','','','','','','');
