INSERT INTO texts VALUES(12496261,'龙骑兵团骑士-祭杖龙骑士',replace(replace('「龙骑兵团」调整＋调整以外的怪兽1只以上\r\n这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡特殊召唤成功的场合，以自己墓地1只「龙骑兵团」怪兽为对象才能发动。那只怪兽当作装备卡使用给这张卡装备。\r\n②：这张卡被送去墓地的场合，以自己场上1只「龙骑兵团」怪兽为对象才能发动。这张卡当作攻击力·守备力上升1000的装备卡使用给作为对象的自己怪兽装备。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12503902,'稀有金属化·魔法反射装甲','选择场上表侧表示存在的1只机械族怪兽发动。选择的怪兽的攻击力上升500，那只怪兽为对象的魔法卡的效果只有1次无效。那只怪兽从场上离开时这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12510878,'天空勇士 尼奥珀耳修斯',replace(replace('①：这张卡可以把自己场上1只「天空骑士 珀耳修斯」解放从手卡特殊召唤。\r\n②：场上有「天空的圣域」存在，自己基本分比对方多的场合，这张卡的攻击力·守备力上升那个相差数值。\r\n③：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。\r\n④：这张卡给与对方战斗伤害的场合发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12525049,'音响战士 吉他手',replace(replace('←7 【灵摆】 7→\r\n「音响战士 吉他手」的灵摆效果1回合只能使用1次。\r\n①：丢弃1张手卡才能发动。从卡组把「音响战士 吉他手」以外的1只「音响战士」怪兽特殊召唤。\r\n【怪兽效果】\r\n①：这张卡召唤成功时，以自己墓地1只「音响战士」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12533811,'宝贝虎龙',replace(replace('1星怪兽×3\r\n自己的主要阶段1把这张卡1个超量素材取除，选择自己场上表侧表示存在的1只1星的怪兽才能发动。选择的怪兽可以直接攻击对方玩家。','\r',char(13)),'\n',char(10)),'直接攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12538374,'黄泉青蛙','①：这张卡在墓地存在，自己场上没有「黄泉青蛙」存在的场合，自己准备阶段才能发动。这张卡特殊召唤。这个效果在自己场上没有魔法·陷阱卡存在的场合才能发动和处理。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12580477,'雷击','在对方场上存在的全部怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12600382,'艾克佐迪亚的亡灵',replace(replace('这张卡不能通常召唤。「与艾克佐迪亚的契约」的效果才能特殊召唤。\r\n①：这张卡不会被战斗以及魔法·陷阱卡的效果破坏。\r\n②：自己准备阶段发动。这张卡的攻击力上升500。\r\n③：自己墓地是「被封印的艾克佐迪亚」「被封印者的右腕」「被封印者的左腕」「被封印者的右足」「被封印者的左足」的其中任意种不存在的场合这张卡破坏。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12607053,'和睦的使者','这张卡发动的回合，由于对方怪兽受到的全部战斗伤害变为0。这个回合自己怪兽不会被战斗破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12615446,'电子光虫-散热器蜣螂',replace(replace('昆虫族·光属性3星怪兽×2只以上\r\n①：1回合1次，把这张卡2个超量素材取除，以对方场上1只怪兽为对象才能发动。那只怪兽的表示形式变更，那个效果直到回合结束时无效。这个效果在对方回合也能发动。\r\n②：1回合1次，这张卡战斗破坏对方怪兽送去墓地时才能发动。破坏的那只怪兽在这张卡下面重叠作为超量素材。','\r',char(13)),'\n',char(10)),'效果无效','增加素材','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12624008,'光明之灵','这张卡被战斗破坏送去墓地时，场上表侧表示存在的光属性怪兽以外的怪兽全部破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12632096,'超银河眼光波龙',replace(replace('9星怪兽×3\r\n①：这张卡有「光波」卡在作为超量素材的场合，得到以下效果。\r\n●1回合1次，把这张卡最多3个超量素材取除才能发动。选取除数量的对方场上的表侧表示怪兽，那些控制权直到结束阶段得到。这个效果得到控制权的怪兽的效果无效化，攻击力变成4500，卡名当作「超银河眼光波龙」使用。这个效果的发动后，直到回合结束时这张卡以外的自己怪兽不能直接攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12644061,'高等暗黑结界','双方的场上·墓地存在的名字带有「宝玉兽」的怪兽当作暗属性使用。此外，名字带有「究极宝玉神」的怪兽攻击的场合，只在战斗阶段内攻击对象怪兽的效果无效化。自己场上的名字带有「宝玉兽」的怪兽进行战斗的伤害计算时，可以从自己卡组把1只名字带有「宝玉兽」的怪兽送去墓地，那次战斗发生的对自己的战斗伤害变成0。','战斗伤害变成0','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12652643,'古代的机械究极巨人',replace(replace('「古代的机械巨人」＋「古代的机械」怪兽×2\r\n这张卡不用融合召唤不能特殊召唤。\r\n①：这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。\r\n②：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。\r\n③：这张卡被破坏的场合，以自己墓地1只「古代的机械巨人」为对象才能发动。那只怪兽无视召唤条件特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12670770,'电子网络',replace(replace('这张卡发动后，第3次的自己准备阶段破坏。\r\n①：1回合1次，场上有「电子龙」存在的场合才能发动。从卡组把1只机械族·光属性怪兽除外。\r\n②：这张卡从场上送去墓地的场合发动。除外的自己的机械族·光属性怪兽尽可能特殊召唤，自己场上的魔法·陷阱卡全部破坏。这个效果特殊召唤的怪兽不能把效果发动。这个效果发动的回合，自己不能进行战斗阶段。','\r',char(13)),'\n',char(10)),'是否现在使用「电子网络」的效果？','除外','特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12678870,'圣灵兽骑 川豚',replace(replace('「灵兽使」怪兽＋「精灵兽」怪兽\r\n把自己场上的上记卡除外的场合才能特殊召唤（不需要「融合」）。\r\n①：这张卡不会被效果破坏。\r\n②：让这张卡回到额外卡组，以除外的自己1只「灵兽使」怪兽和1只「精灵兽」怪兽为对象才能发动。那些怪兽守备表示特殊召唤。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'1只「灵兽使」怪兽和1只「精灵兽」怪兽特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12694768,'暴鬼','这张卡被战斗破坏送去墓地时，双方受到500分伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12697630,'古遗物-微怒剑','这张卡可以当作魔法卡使用从手卡到魔法与陷阱卡区域盖放。魔法与陷阱卡区域盖放的这张卡在对方回合被破坏送去墓地时，这张卡特殊召唤。对方回合中这张卡特殊召唤成功的场合，选自己场上盖放的最多2张卡破坏。「古遗物-微怒剑」的这个效果1回合只能使用1次。','特殊召唤','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12735388,'虹之衣','装备怪兽和对方怪兽进行战斗的场合，只在战斗阶段内那只对方怪兽的效果无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12744567,'混沌No.101 寂静荣誉暗黑骑士',replace(replace('5星怪兽×3\r\n1回合1次，选择对方场上1只特殊召唤的怪兽才能发动。选择的怪兽在这张卡下面重叠作为超量素材。此外，持有超量素材的这张卡被破坏送去墓地时，自己墓地有「No.101 寂静荣誉方舟骑士」存在的场合，这张卡可以从墓地特殊召唤。那之后，自己回复这张卡的原本攻击力数值的基本分。这个效果特殊召唤的这张卡在这个回合不能攻击。','\r',char(13)),'\n',char(10)),'吸收素材','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12755462,'哥布林埋穴部队','这张卡召唤成功时，陷阱卡不能发动。此外，只要这张卡在场上表侧表示存在，怪兽召唤·反转召唤·特殊召唤成功时，名字带有「落穴」的陷阱卡不能发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12766474,'影王 阴暗公爵',replace(replace('这个卡名的①②的效果1回合各能使用1次，这张卡的效果发动的回合，自己不是暗属性怪兽不能特殊召唤。\r\n①：把自己场上的暗属性怪兽任意数量解放才能发动。这张卡从手卡特殊召唤。这个效果特殊召唤的这张卡的攻击力上升解放的怪兽数量×500。\r\n②：这张卡召唤·特殊召唤成功的场合，以自己墓地1只5星以上的暗属性怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12800777,'风之精灵 迦楼罗','这张卡不能通常召唤。自己墓地1只风属性怪兽从游戏中除外特殊召唤上场。对方的回合结束时选对方1只表侧表示的怪兽改变它的表示形式。','改变表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12817939,'漆黑之魔王 LV6','用「漆黑之魔王 LV4」的效果特殊召唤的场合，这张卡战斗破坏的对方怪兽的效果无效化。这个效果把对方怪兽的效果无效化的下次的自己回合的准备阶段时，可以把这张卡送去墓地从手卡·卡组特殊召唤1只「漆黑之魔王 LV8」。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12822541,'DD 莉莉丝',replace(replace('「DD 莉莉丝」的效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合，可以从以下效果选择1个发动。\r\n●以自己墓地1只「DD」怪兽为对象才能发动。那只怪兽加入手卡。\r\n●从自己的额外卡组把1只表侧表示的「DD」灵摆怪兽加入手卡。','\r',char(13)),'\n',char(10)),'自己墓地1只「DD」怪兽加入手卡','自己额外卡组1只「DD」灵摆怪兽加入手卡','','','','','','','','','','','','','','');
