INSERT INTO texts VALUES(12923641,'暗之护封剑',replace(replace('这张卡发动后，第2次的自己准备阶段破坏。\r\n①：作为这张卡的发动时的效果处理，对方场上有表侧表示怪兽存在的场合，那些怪兽全部变成里侧守备表示。\r\n②：只要这张卡在魔法与陷阱区域存在，对方场上的怪兽不能把表示形式变更。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12927849,'闪光异热同心武器-天圣辉狼剑','这张卡召唤成功时，可以选择自己场上1只当作装备卡使用的名字带有「异热同心武器」的怪兽表侧守备表示特殊召唤。此外，自己的主要阶段时，手卡的这张卡可以当作装备卡使用给自己场上的名字带有「希望皇 霍普」的怪兽装备。装备怪兽战斗破坏对方怪兽送去墓地时，可以选择自己墓地1只名字带有「异热同心武器」的怪兽加入手卡。','特殊召唤','装备','加入手卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12940613,'魔偶甜点礼仪','选择自己墓地1只名字带有「魔偶甜点」的怪兽回到卡组，自己场上存在的全部名字带有「魔偶甜点」的怪兽的攻击力·守备力上升800。那之后，可以选自己墓地1只怪兽回到卡组。','是否要选择墓地一只怪兽回到卡组？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12948099,'古神 克图格亚',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：这张卡同调召唤成功的场合才能发动。场上的4阶超量怪兽全部回到持有者的额外卡组。\r\n②：这张卡为素材的融合召唤成功的场合发动。自己从卡组抽1张。\r\n③：场上的这张卡为素材作超量召唤的怪兽得到以下效果。\r\n●这次超量召唤成功的场合发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'回到持有者的额外卡组','抽1张','抽1张','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12953226,'女邪神 努维娅','召唤的场合，这张卡破坏。对方有怪兽控制的场合，对方场上每有1张可控制的怪兽这张卡的攻击力下降200。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12958919,'幻铳士',replace(replace('①：这张卡召唤·反转召唤成功时才能发动。把最多有自己场上的怪兽数量的「铳士衍生物」（恶魔族·暗·4星·攻/守500）在自己场上特殊召唤。\r\n②：自己准备阶段才能发动。给与对方为自己场上的「铳士」怪兽数量×300伤害。这个效果发动的回合，自己的「铳士」怪兽不能攻击宣言。','\r',char(13)),'\n',char(10)),'特殊召唤','伤害','是否还要特殊召唤Token？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12958920,'铳士衍生物','由「幻铳士」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12965761,'死亡石斛',replace(replace('1回合1次，在自己的主要阶段，这张卡可以视为1张装备卡装备在自己的「血兰」上，或者解除装备状态表侧攻击表示特殊召唤。当这张卡以自身效果变成装备卡时具有以下效果：\r\n●装备怪兽每次战斗破坏对方怪兽时，在自己场上特殊召唤1只「魔草衍生物」（地·1星·植物族·攻/守800）。（1只怪兽至多只能装备1张同盟。当装备了同盟的怪兽被战斗破坏时，同盟会代替怪兽被破坏。）','\r',char(13)),'\n',char(10)),'装备','特殊召唤','特殊召唤衍生物','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12965762,'魔草衍生物','由「死亡石斛」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12980373,'魔偶甜点·千层酥猫咪','这张卡被对方破坏送去墓地时，这张卡回到卡组。这张卡召唤成功时，可以从手卡把1只名字带有「魔偶甜点」的怪兽特殊召唤。','返回卡组','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12986778,'机皇帝的赐与','场上表侧表示存在的怪兽只有名字带有「机皇」的怪兽2只的场合才能发动。从自己卡组抽2张卡。这张卡发动的回合，自己不能进行战斗阶段。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12986807,'熔岩大石人',replace(replace('调整＋调整以外的炎属性怪兽1只以上\r\n这张卡同调召唤成功时，自己把1张手卡送去墓地。这张卡被卡的效果破坏的场合，可以作为代替把自己墓地存在的1只名字带有「熔岩」的怪兽从游戏中除外。','\r',char(13)),'\n',char(10)),'把1张手卡送去墓地','是否要把墓地的1只名字带有「熔岩」的怪兽除外代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(12989604,'来自星遗物中的觉醒',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：用自己场上的怪兽为素材把1只连接怪兽连接召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13002461,'魔导战士 法尔丝','1回合1次，让自己墓地1张名字带有「魔导书」的魔法卡回到卡组，选择场上1只魔法师族怪兽才能发动。选择的怪兽的等级上升1星，攻击力上升500。','等级攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13026402,'陷阱处理班 A组','这个效果可以在对方回合使用。对方发动陷阱时，可以把表侧表示的这张卡作祭品，陷阱的发动无效并且破坏。','陷阱无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13030280,'混沌超量 漫画英雄 传奇亚瑟',replace(replace('5星怪兽×3\r\n这张卡1回合只有1次不会被战斗破坏。此外，这张卡有「漫画英雄 亚瑟王」在作为超量素材的场合，得到以下效果。\r\n●这张卡战斗破坏怪兽送去墓地时，把这张卡1个超量素材取除才能发动。破坏的怪兽从游戏中除外，给与对方基本分那个原本攻击力数值的伤害。','\r',char(13)),'\n',char(10)),'除外并伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13032689,'超量组件','超量怪兽才能装备。装备怪兽的攻击力上升装备怪兽的阶级×200的数值。此外，自己场上的装备怪兽把超量素材取除来让效果发动的场合，这张卡可以当作取除的超量素材中的1个使用。','是否要使用「超量组件」的效果？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13035077,'龙神阵·略图',replace(replace('①：场上的「真龙」怪兽的攻击力·守备力上升300。\r\n②：只要这张卡在场地区域存在，上级召唤的「真龙」怪兽在1回合各有1次不会被战斗破坏。\r\n③：1回合1次，自己主要阶段才能发动。选这张卡以外的自己的手卡·场上1张卡破坏，从卡组把1张「真龙」卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13039848,'岩石巨兵','岩石的巨人兵，巨腕的攻击能震动大地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13048472,'仪式的事前准备',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：从卡组选1张仪式魔法卡，再从自己的卡组·墓地选1只在那张仪式魔法卡有卡名记述的仪式怪兽。那2张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13069066,'剑龙','全身都附有刀刺的恐龙。以突进为手段进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13073850,'机壳别名 愚钝',replace(replace('←1 【灵摆】 1→\r\n①：自己不是「机壳」怪兽不能特殊召唤。这个效果不会被无效化。\r\n②：自己场上的「机壳」的怪兽攻击力上升300。\r\n【怪兽效果】\r\n①：这张卡可以不用解放作召唤。\r\n②：特殊召唤或者不用解放作召唤的这张卡的等级变成4星，原本攻击力变成1800。\r\n③：通常召唤的这张卡不受原本的等级或者阶级比这张卡的等级低的怪兽发动的效果影响。\r\n④：把「机壳」怪兽解放对这张卡的上级召唤成功时，以场上1张卡为对象才能发动。那张卡回到持有者手卡。对方不能对应这个效果的发动把魔法·陷阱·怪兽的效果发动。','\r',char(13)),'\n',char(10)),'不用解放作召唤','弹回手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13093792,'命运英雄 钻石人','①：1回合1次，自己主要阶段才能发动。自己卡组最上面的卡翻开，那是通常魔法卡的场合，那张卡送去墓地。不是的场合，那张卡回到卡组最下面。这个效果把通常魔法卡送去墓地的场合，下次的自己回合的主要阶段可以把墓地的那张通常魔法卡的发动时的效果发动。','发动魔法卡效果','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13108445,'宝石骑士·蓝玉',replace(replace('「宝石骑士·青玉」＋「宝石骑士」怪兽\r\n这张卡用以上记卡为融合素材的融合召唤才能从额外卡组特殊召唤。\r\n①：这张卡攻击的场合，战斗阶段结束时变成守备表示。\r\n②：这张卡从场上送去墓地的场合，以对方场上1张卡为对象发动。那张对方的卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13140300,'神龙之圣刻印','被刻有神秘刻印的神圣古物。传说是将施展如神一般的力量之龙的力量封印之物。在黄金太阳之下，经历悠久的时光，后来随着其神圣性被称为太阳石。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13166204,'我我我突击','自己场上的名字带有「我我我」的怪兽成为对方怪兽的效果的对象时才能发动。那只对方怪兽的效果无效并破坏。那之后，给与对方基本分破坏的怪兽的攻击力和守备力之内较高方数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13166648,'双龙降临','对方的超量怪兽的直接攻击宣言时才能发动。从额外卡组把1只龙族·光属性的超量怪兽表侧攻击表示特殊召唤，攻击对象转移为那只怪兽进行伤害计算。这个效果特殊召唤的怪兽的攻击力变成和攻击怪兽的攻击力相同数值，效果无效化。「双龙降临」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13179332,'大木炭18','完全燃烧殆尽的巨木之化身。用此炭烤出的肉被公认为极品而受到珍重。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13183454,'零鸟姬 冰鸟女妖',replace(replace('鸟兽族5星怪兽×2\r\n把这张卡1个超量素材取除，选择对方场上表侧表示存在的1只怪兽才能发动。选择的怪兽的攻击力变成0。','\r',char(13)),'\n',char(10)),'攻击变成0','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13193642,'暗黑植物','在被污染的土地以及暗之力的环境下生长的花。非常凶暴。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13210191,'风暴','自己场上的魔法·陷阱卡全部破坏。那之后，把破坏的卡数量的对方场上的魔法·陷阱卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13215230,'梦幻小丑','这张卡的表示形式从攻击表示变成守备表示时，破坏对方场上的1只怪兽。','破坏对方场上1只怪兽','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13220032,'大日充能者','自己场上表侧表示存在的光属性怪兽的攻击力上升这张卡装备的装备卡数量×300的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13235258,'侵蚀鳞粉',replace(replace('①：以自己场上1只昆虫族怪兽为对象才能把这张卡发动。这张卡当作装备卡使用给那只怪兽装备。对方不能向那只自己的装备怪兽以外的昆虫族怪兽攻击。\r\n②：只要这张卡装备中，每次对方把怪兽召唤·特殊召唤或者每次对方把魔法·陷阱·怪兽的效果发动，给对方场上的表侧表示怪兽全部各放置1个鳞粉指示物。对方场上的怪兽的攻击力·守备力下降那怪兽的鳞粉指示物数量×100。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13241004,'毛绒动物·企鹅',replace(replace('「毛绒动物·企鹅」的②的效果1回合只能使用1次。\r\n①：只在这张卡在场上表侧表示存在才有1次，自己主要阶段才能发动。从手卡把「毛绒动物·企鹅」以外的1只「毛绒动物」怪兽特殊召唤。\r\n②：这张卡成为「魔玩具」融合怪兽的融合召唤的素材送去墓地的场合才能发动。自己从卡组抽2张，那之后选1张手卡丢弃。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13250922,'食大食蚁兽蚁','这张卡不能通常召唤。把自己场上2张魔法·陷阱卡送去墓地的场合才能特殊召唤。1回合只有1次，可以破坏对方场上1张魔法陷阱卡。使用这个效果的回合这只怪兽不能攻击。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13293158,'邪心英雄 荒野风魔',replace(replace('「元素英雄 羽翼侠」＋「元素英雄 荒野侠」\r\n这只怪兽不能作「暗黑融合」的融合召唤以外的特殊召唤。这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。这张卡给与对方基本分战斗伤害时，对方场上盖伏的魔法·陷阱卡全部破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13301895,'失乐园',replace(replace('「失乐园」的②的效果1回合只能使用1次。\r\n①：只要这张卡在场地区域存在，自己的怪兽区域的「神炎皇 乌利亚」「降雷皇 哈蒙」「幻魔皇 拉比艾尔」「混沌幻魔 阿米泰尔」不会成为对方的效果的对象，不会被对方的效果破坏。\r\n②：自己的怪兽区域有「神炎皇 乌利亚」「降雷皇 哈蒙」「幻魔皇 拉比艾尔」「混沌幻魔 阿米泰尔」的其中任意种存在的场合才能发动。自己从卡组抽2张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13313278,'燃烧拳击手 脱险拳手','自己受到战斗伤害时才能发动。这张卡从手卡特殊召唤，自己基本分回复受到的伤害的数值。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13314457,'深海大鳗','这张卡从场上送去墓地时，自己场上表侧表示存在的全部水属性怪兽的攻击力直到结束阶段时上升500。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13316346,'起动士兵 死亡旋转者','只要自己场上有名字带有「零件」的怪兽表侧表示存在，这张卡的攻击力上升2000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13317419,'火箭手',replace(replace('「火箭手」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：以自己场上1只攻击力800以上的攻击表示怪兽为对象才能把这张卡发动。这张卡当作攻击力上升800的装备卡使用给那只怪兽装备。\r\n②：把装备的这张卡送去墓地，以场上1张表侧表示的卡为对象才能发动。那张卡破坏。那之后，这张卡装备过的怪兽攻击力变成0，不能把表示形式变更。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13331639,'霸王龙 扎克',replace(replace('←1 【灵摆】 1→\r\n①：只要这张卡在灵摆区域存在，对方场上的融合·同调·超量怪兽不能把效果发动。\r\n②：1回合1次，对方在抽卡阶段以外从卡组把卡加入手卡时才能发动。那卡破坏。\r\n【怪兽效果】\r\n龙族的融合·同调·超量·灵摆怪兽各1只合计4只\r\n这张卡不用融合召唤不能特殊召唤。\r\n①：这张卡特殊召唤成功的场合发动。对方场上的卡全部破坏。\r\n②：这张卡不会成为对方的效果的对象，不会被对方的效果破坏。\r\n③：这张卡战斗破坏对方怪兽时才能发动。从卡组·额外卡组把1只「霸王眷龙」怪兽特殊召唤。\r\n④：怪兽区域的这张卡被战斗·效果破坏的场合才能发动。这张卡在自己的灵摆区域放置。','\r',char(13)),'\n',char(10)),'破坏加入手卡的卡','对方场上的卡全部破坏','从卡组·额外卡组把1只「霸王眷龙」怪兽特殊召唤','这张卡在自己的灵摆区域放置','','','','','','','','','','','','');
INSERT INTO texts VALUES(13361027,'龙骑兵团-黑枪龙','把自己场上存在的1只名字带有「龙骑兵团」的龙族怪兽解放发动。选择自己墓地存在的1只4星以下的鸟兽族怪兽特殊召唤。这个效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13386503,'胡狼灵骑士','可以将1只被这张卡战斗破坏并被送去墓地的对方怪兽以表侧守备表示特殊召唤到自己场上。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13391185,'圣骑士 加拉哈德',replace(replace('这张卡只要在场上表侧表示存在，当作通常怪兽使用。只要这张卡有名字带有「圣剑」的装备魔法卡装备，这张卡变成当作效果怪兽使用并得到以下效果。\r\n●选择自己墓地1只名字带有「圣骑士」的怪兽才能发动。选择的怪兽加入手卡，选自己场上1张名字带有「圣剑」的装备魔法卡破坏。「圣骑士 加拉哈德」的这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13409151,'沙貘','反转：将场上1张表侧表示的怪兽卡变成里侧守备表示。不能选择名为「沙貘」的怪兽卡。','变成里侧守备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13429800,'大白鲨','巨大的白鲨，若被咬到绝对无法脱身。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13438207,'花朵炸弹','自己场上存在的植物族怪兽战斗破坏对方怪兽送去墓地时才能发动。给与对方基本分那次战斗破坏的怪兽的攻击力数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13452889,'矢量恐吓恶魔',replace(replace('电子界族怪兽2只以上\r\n①：这张卡战斗破坏对方怪兽送去墓地时，把这张卡所连接区1只自己怪兽解放才能发动。破坏的那只怪兽在作为这张卡所连接区的自己·对方场上特殊召唤。这个效果在对方场上把怪兽特殊召唤的场合，这次战斗阶段中，这张卡只再1次可以攻击。','\r',char(13)),'\n',char(10)),'','是否在自己场上特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13455674,'水晶机巧-中枢大蛇',replace(replace('调整2只以上＋调整以外的怪兽1只\r\n①：这张卡同调召唤成功的场合，以最多有那些作为同调素材的怪兽数量的对方的场上·墓地的怪兽为对象才能发动。那些怪兽除外。\r\n②：同调召唤的这张卡被战斗·效果破坏的场合，以这张卡以外的除外的1只自己或者对方的怪兽为对象才能发动。那只怪兽在自己场上特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13455953,'极星灵 矮人','这张卡召唤成功的回合，自己在通常召唤外加上只有1次可以把1只名字带有「极星」的怪兽召唤。场上表侧表示存在的这张卡被送去墓地时，从自己墓地选择1张名字带有「极星宝」的卡加入手卡。','额外的通常召唤','加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13474291,'云魔物-风暴龙','这张卡不能通常召唤。把自己墓地1只名字带有「云魔物」的怪兽从游戏中除外特殊召唤。这张卡不会被战斗破坏。这张卡表侧守备示在场上存在的场合，这张卡破坏。1回合只有1次，可以给场上1只怪兽放置1个雾指示物。','放置指示物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13478040,'橡子松鼠','每次对方把怪兽特殊召唤，给这张卡放置1个橡子指示物。可以把这张卡放置的1个橡子指示物取除，选择对方场上存在的1只怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13482075,'复仇死者·奇美拉','「复仇死者」仪式魔法卡降临。要让场上的卡破坏的魔法·陷阱·怪兽的效果发动时（在自己或者对方回合）：可以从自己墓地把1只不死族怪兽除外；那个发动无效并破坏。这张卡为仪式召唤而被解放或者除外的场合：对方场上的怪兽的攻击力·守备力下降500。「复仇死者·奇美拉」的每个效果1回合各能使用1次。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13492423,'光子帝王','这张卡召唤·反转召唤成功时，可以从自己的手卡·卡组把1只「光子帝王」特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13504844,'加特姆士的紧急指令','①：场上有「X-剑士」怪兽存在的场合，以自己·对方的墓地的「X-剑士」怪兽合计2只为对象才能发动。那2只怪兽在自己场上特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13513663,'龙魂之城',replace(replace('①：「龙魂之城」在自己场上只能有1张表侧表示存在。\r\n②：1回合1次，从自己墓地把1只龙族怪兽除外，以自己场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升700。\r\n③：表侧表示的这张卡从场上送去墓地时，以除外的1只自己的龙族怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'攻击上升','特殊召唤','是否现在使用「龙魂之城」的效果？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13518809,'破灭之美神 露茵',replace(replace('「世界不灭」降临。\r\n①：这张卡的卡名只要在手卡·场上存在当作「破灭之女神 露茵」使用。\r\n②：只要仪式召唤的这张卡在怪兽区域存在，自己场上的仪式怪兽不会被效果破坏。\r\n③：只使用仪式怪兽作仪式召唤的这张卡在同1次的战斗阶段中可以作2次攻击。\r\n④：这张卡战斗破坏对方怪兽的场合才能发动。给与对方那只怪兽的原本攻击力数值的伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13521194,'地狱公路巡警',replace(replace('①：这张卡战斗破坏怪兽送去墓地的场合发动。给与对方那只怪兽的原本等级×100伤害。\r\n②：把墓地的这张卡除外才能发动。从手卡把1只攻击力2000以下的恶魔族怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'给与伤害','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13522325,'炎之精灵 伊夫里特','这张卡不能通常召唤。自己墓地1只炎属性怪兽从游戏中除外特殊召唤上场。这只怪兽在自己的战斗阶段中，攻击力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13529466,'召唤兽 卡利古拉',replace(replace('「召唤师 阿莱斯特」＋暗属性怪兽\r\n①：只要这张卡在怪兽区域存在，那个期间双方各自在1回合只能有1次把怪兽的效果发动，各自在战斗阶段只能用1只怪兽攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13532663,'替身石人','反转：对方选择所控制的1只怪兽。选择怪兽和这张卡的控制权交换。','控制权交换','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13536606,'V-LAN多头蛇',replace(replace('衍生物以外的怪兽2只以上\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：这张卡的攻击力上升和这张卡互相连接的怪兽数量×300。\r\n②：以这张卡所互相连接区1只连接3以下的怪兽为对象才能发动。那只怪兽解放，那个连接标记数量的「V-LAN衍生物」（电子界族·光·1星·攻/守0）在自己场上特殊召唤。这个回合自己不能把连接标记数量和作为对象的怪兽相同的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13536607,'V-LAN衍生物','由「V-LAN多头蛇」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13574687,'一击炮轰士',replace(replace('「一击喷射士」＋调整以外的怪兽1只\r\n1回合1次，把场上表侧表示存在的1只怪兽破坏，给与那个控制者破坏怪兽的攻击力一半数值的伤害。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13582837,'永火上膛者','自己手卡是0张的场合，1回合1次，可以从自己卡组抽1张卡。这个效果抽到的卡给双方确认，怪兽卡的场合，给与对方基本分那只怪兽的等级×200的数值的伤害。魔法·陷阱卡的场合，自己受到500分伤害。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13599884,'铁蝎','机械族以外的怪兽攻击这张卡的场合，那只怪兽（以对方的回合来数）第3个回合的回合结束时破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13604200,'贤者的宝石','①：自己场上有「黑魔术少女」存在的场合才能发动。从手卡·卡组把1只「黑魔术师」特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13611090,'威风妖怪音波','①：以自己场上1只「威风妖怪」怪兽为对象才能发动。那只怪兽直到回合结束时攻击力·守备力变成2倍，给与对方的战斗伤害变成一半。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13626450,'邪气退散','丢弃1张手卡。场上表侧表示存在的永续陷阱卡全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13629812,'忍法 影缝之术','把自己场上1只名字带有「忍者」的怪兽解放才能发动。选择对方场上1只怪兽从游戏中除外。只要那只怪兽从游戏中除外中，那个怪兽卡区域不能使用。这张卡从场上离开时，这个效果除外的怪兽以相同表示形式回到原本的怪兽卡区域。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13647631,'重机货列车 桅杆起重车',replace(replace('「重机货列车 桅杆起重车」的①的效果1回合只能使用1次。\r\n①：自己场上有机械族·地属性怪兽召唤·特殊召唤的场合才能发动。这张卡从手卡特殊召唤。这个效果特殊召唤的这张卡的原本的攻击力·守备力变成一半。\r\n②：超量素材的这张卡为让超量怪兽的效果发动而被取除送去墓地的场合，以对方场上1张卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13662809,'魔界台本「魔王的降临」',replace(replace('①：以最多有自己场上的攻击表示的「魔界剧团」怪兽种类数量的场上的表侧表示的卡为对象才能发动。那些卡破坏。自己场上有7星以上的「魔界剧团」怪兽存在的场合，对方不能对应这张卡的发动把效果发动。\r\n②：自己的额外卡组有表侧表示的「魔界剧团」灵摆怪兽存在，盖放的这张卡被对方的效果破坏的场合才能发动。从卡组把「魔界剧团」卡或者「魔界台本」魔法卡合计最多2张加入手卡（同名卡最多1张）。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13676474,'假面咒术师 诅咒之喉','通过咒念将敌人杀死的假面怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13683298,'大狼雷鸣',replace(replace('这张卡的效果发动的回合，自己不能进行战斗阶段。\r\n①：这张卡从墓地的特殊召唤成功的场合才能发动。对方场上的表侧表示怪兽全部破坏。','\r',char(13)),'\n',char(10)),'怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13685271,'同姓同名同盟条约',replace(replace('自己场上有衍生物以外的同名怪兽表侧表示2只以上存在的场合才能发动。那些同名怪兽的数量的以下效果适用。\r\n●2只：对方场上存在的1张魔法·陷阱卡破坏。\r\n●3只：对方场上存在的魔法·陷阱卡全部破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13708425,'火焰共鸣者','这张卡为同调素材的同调怪兽的攻击力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13722870,'黑炎之骑士',replace(replace('「黑魔术师」＋「炎之剑士」\r\n这张卡的控制者所受的战斗伤害为零。这张卡被战斗破坏送去墓地时，从自己的卡组或手卡中特殊召唤1只「幻影之骑士」上场。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13723605,'食人宝石箱','宝石箱形状的怪兽，欺骗冒险者接近并发动攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13756293,'龙魔人 龙骑士之王',replace(replace('「龙之支配者」＋「神龙 末日龙」\r\n只要这张卡在场上表侧表示存在，对方不能指定龙族为魔法·陷阱·怪兽的效果的对象。1回合1次，可以从手卡特殊召唤1只龙族怪兽到自己场上。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13758665,'魔术师的左手','①：1回合1次，自己场上有魔法师族怪兽存在的场合，对方发动的陷阱卡的效果无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13760677,'P·M 捕获者',replace(replace('←5 【灵摆】 5→\r\n①：自己对不死族怪兽的灵摆召唤成功时才能发动。那些怪兽在这个回合不会被战斗·效果破坏。\r\n【怪兽效果】\r\n①：这张卡战斗破坏怪兽的场合，以自己墓地1只灵摆怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'不会被战斗·效果破坏','加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13761956,'炎狱婴儿','自己的主要阶段时，把这张卡从手卡送去墓地发动。自己场上表侧表示存在的1只炎属性怪兽的攻击力上升400。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13764881,'锁链共鸣者','①：场上有同调怪兽存在，这张卡召唤成功时才能发动。从卡组把「锁链共鸣者」以外的1只「共鸣者」怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13803864,'悠悠王',replace(replace('「悠悠」＋「悠悠」＋「悠悠」\r\n这张卡从场上离开时，可以把自己墓地存在的「悠悠」尽可能多的特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13821299,'陷阱吞噬者','这张卡不能通常召唤。把对方场上表侧表示存在的1张陷阱卡送去墓地的场合才能特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13839120,'黄色零件','①：这张卡召唤·特殊召唤成功时才能发动。从卡组把1只「绿色零件」加入手卡。','选1只「绿色零件」加入手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13846680,'地狱日珥','这张卡被战斗破坏送去墓地时，场上表侧表示存在的炎属性怪兽以外的怪兽全部破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13851202,'星因士 参宿七',replace(replace('「星因士 参宿七」的效果1回合只能使用1次。\r\n①：这张卡召唤·反转召唤·特殊召唤成功的场合，以场上1只「星守」怪兽为对象才能发动。那只怪兽攻击力上升500，结束阶段送去墓地。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13857930,'新空间侠·闪烁青苔',replace(replace('这张卡名在规则上也当作「新空间侠·光辉青苔」使用。这张卡用「新空间侠界限」的效果才能特殊召唤。这张卡进行战斗的场合，自己抽1张卡。这个效果抽到的卡给双方确认，这张卡得到那张卡的种类的以下效果。\r\n●怪兽卡：这个回合的战斗阶段结束。\r\n●魔法卡：这张卡可以直接攻击对方玩家。\r\n●陷阱卡：这张卡变成守备表示。','\r',char(13)),'\n',char(10)),'确认手卡','是否要进行直接攻击？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13890468,'精灵兽 川豚',replace(replace('自己对「精灵兽 川豚」1回合只能有1次特殊召唤。\r\n①：1回合1次，把手卡1张「灵兽」卡除外，以对方场上1张卡为对象才能发动。那张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'回到手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13893596,'究极封印神 艾克佐迪奥斯',replace(replace('这张卡不能通常召唤。让自己墓地的怪兽全部回到卡组的场合才能特殊召唤。这张卡的②的效果让「被封印」怪兽5种类被送去自己墓地全部齐集时，自己决斗胜利。\r\n①：这张卡的攻击力上升自己墓地的通常怪兽数量×1000。\r\n②：这张卡的攻击宣言时发动。从手卡·卡组把1只怪兽送去墓地。\r\n③：表侧表示的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'怪兽送墓','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13944422,'贪食龙','这张卡召唤·反转召唤·特殊召唤成功时，自己回复1000基本分。这张卡被破坏送去墓地时，自己受到2000点伤害。','回复1000','伤害2000','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13945283,'幻影壁','向这张卡攻击的怪兽回到持有者手卡。伤害计算适用。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13955608,'机动城 堡垒',replace(replace('①：这张卡发动后变成效果怪兽（机械族·地·4星·攻0/守2000）在怪兽区域守备表示特殊召唤。这张卡也当作陷阱卡使用。\r\n②：自己场上有「绿色零件」「红色零件」「黄色零件」存在的场合，这张卡的效果特殊召唤的这张卡的攻击力上升3000。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13959634,'冰灵神 穆兰格雷斯',replace(replace('这张卡不能通常召唤。自己墓地的水属性怪兽是5只的场合才能特殊召唤。这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡特殊召唤成功的场合发动。对方手卡随机选2张丢弃。\r\n②：表侧表示的这张卡从场上离开的场合，下次的自己回合的战斗阶段跳过。','\r',char(13)),'\n',char(10)),'手牌丢弃','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13972452,'威风妖怪风暴','①：把自己场上1只魔法师族·风属性怪兽解放，以对方场上1只怪兽为对象才能发动。那只怪兽回到持有者卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13974207,'电光－雪花－',replace(replace('这张卡不能特殊召唤。\r\n①：这张卡在怪兽区域存在，自己场上没有盖放的魔法·陷阱卡存在的场合，双方不能把魔法·陷阱卡盖放，场上盖放的魔法·陷阱卡不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13995824,'流电双角兽',replace(replace('兽族调整＋调整以外的怪兽1只以上\r\n这张卡被对方破坏的场合，双方玩家从卡组上面把7张卡送去墓地。','\r',char(13)),'\n',char(10)),'卡组破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(13997673,'核心浓度压缩','把手卡1张「核成兽的钢核」给对方观看，从手卡丢弃1只名字带有「核成」的怪兽发动。从自己卡组抽2张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14001430,'魔偶甜点城堡','这张卡的发动时，自己墓地有名字带有「魔偶甜点」的怪兽存在的场合，那些怪兽全部回到卡组。只要这张卡在场上存在，场上的名字带有「魔偶甜点」的怪兽的攻击力·守备力上升500。此外，名字带有「魔偶甜点」的怪兽的效果让自己墓地的怪兽回到卡组的场合，可以不回到卡组回到手卡。','是否使用「魔偶甜点城堡」的效果？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14005031,'月舞的仪式',replace(replace('①：以自己场上1只没有超量素材的风属性超量怪兽为对象才能把这张卡发动。只要那只怪兽在场上存在，场上的表侧表示怪兽的效果无效化。作为对象的怪兽从场上离开时这张卡破坏。\r\n②：这张卡发动的回合的结束阶段发动。把场上的这张卡在这张卡的发动时作为对象的怪兽下面重叠作为超量素材。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14015067,'大森林的长老','很久以前住在森林里的白羊，真身是森林长老。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14017402,'波动龙骑士',replace(replace('龙族同调怪兽＋战士族怪兽\r\n这张卡用融合召唤才能从额外卡组特殊召唤。1回合1次，可以把墓地存在的1只龙族的同调怪兽从游戏中除外，直到结束阶段时当作和那只怪兽同名卡使用，得到相同效果。此外，只要这张卡在场上表侧攻击表示存在，对方的卡的效果发生的对自己的效果伤害由对方代受。','\r',char(13)),'\n',char(10)),'获得龙族同调怪兽效果','复制效果结束','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14037717,'书之精灵 飞鹰主教','木之精灵，拥有非常高的智慧，发动多彩的攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14047624,'熔岩裁决王','1回合1次，把自己墓地存在的1只名字带有「熔岩」的怪兽从游戏中除外才能发动。给与对方基本分1000分伤害。这个效果发动的回合，「熔岩裁决王」不能攻击宣言。','给与对方1000伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14057297,'同归于尽',replace(replace('「同归于尽」在1回合只能发动1张。\r\n①：双方手卡是3张以上的场合才能发动。双方让手卡全部用喜欢的顺序回到卡组下面，自己失去这个效果让双方回到卡组的卡数量×300基本分。那之后，双方从卡组抽5张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14087893,'月之书','①：以场上1只表侧表示怪兽为对象才能发动。那只怪兽变成里侧守备表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14089428,'蓝色雷电T45','这张卡战斗破坏对方怪兽的场合，在自己场上把1只「闪电子机衍生物」（机械族·光·4星·攻/守1500）特殊召唤。这些衍生物不能作为祭品召唤的祭品。','特殊召唤Token','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14089429,'闪电子机衍生物','由「蓝色雷电T45」特殊召唤。这个衍生物不能作为祭品召唤的祭品。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14094090,'超战士的仪式',replace(replace('「混沌战士」仪式怪兽的降临必需。「超战士的仪式」的②的效果1回合只能使用1次。\r\n①：从自己的手卡·场上把等级合计直到8的怪兽解放，从手卡把1只「混沌战士」仪式怪兽仪式召唤。\r\n②：从自己墓地把这张卡以及1只光属性怪兽和1只暗属性怪兽除外才能发动。从手卡把1只「混沌战士」仪式怪兽无视召唤条件特殊召唤。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14105623,'异色眼霸弧灵摆龙',replace(replace('←8 【灵摆】 8→\r\n这个卡名的灵摆效果1回合只能使用1次。\r\n①：自己场上的「异色眼」卡被战斗·效果破坏的场合才能发动。从自己的手卡·卡组·墓地选1只「异色眼」怪兽特殊召唤。\r\n【怪兽描述】\r\n雄壮而又美丽的拥有神秘之眼的奇迹之龙。那闪耀着双色光辉的眼睛，映出描绘于天空之中的轨迹。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14124483,'元素英雄 真诚新宇侠',replace(replace('这个卡名的①②的效果1回合各能使用1次，对方回合也能发动。\r\n①：把这张卡从手卡丢弃，以场上1只「英雄」怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升2500。\r\n②：从手卡丢弃1只「英雄」怪兽才能发动。这张卡的攻击力直到回合结束时上升丢弃的怪兽的攻击力数值。','\r',char(13)),'\n',char(10)),'「英雄」怪兽攻击力上升','丢弃手卡上升攻击力','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14141448,'大飞蛾','装备了「进化之茧」的「幼虫宝宝」4回合后（用自己的回合来数）作祭品来特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14148099,'巨大战舰 巨核','这张卡召唤时放置3个指示物。这张卡不会被战斗破坏。进行战斗的场合，伤害步骤终了时取除这张卡的1个指示物。没有指示物的状态下进行战斗的场合，伤害步骤终了时这张卡破坏。','放置指示物','移除指示物','破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14152862,'风纪宫司 祝词',replace(replace('魔法师族6星怪兽×2\r\n1回合1次，对方把魔法·陷阱卡发动时把这张卡1个超量素材取除才能发动。那个发动无效并破坏。','\r',char(13)),'\n',char(10)),'发动无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14154221,'进化的代偿','名字带有「进化虫」的怪兽的效果让怪兽特殊召唤的场合，可以选择场上1张卡破坏。这个效果1回合只能使用1次。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14181608,'蘑菇人','在潮湿的地方发挥力量！从蘑菇帽中吐丝攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14198496,'神秘之吹笛人','把这张卡解放发动。从自己卡组抽1张卡。这个效果抽到的卡给双方确认，1星怪兽的场合，自己再抽1张卡。「神秘之吹笛人」的效果1回合只能使用1次。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14212201,'影之吸血鬼',replace(replace('把这张卡作为超量召唤的素材的场合，不是暗属性怪兽的超量召唤不能使用。\r\n①：这张卡召唤成功时才能发动。从手卡·卡组把「影之吸血鬼」以外的1只暗属性「吸血鬼」怪兽特殊召唤。这个效果特殊召唤成功的回合，那只怪兽以外的自己怪兽不能攻击。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14214060,'魔法剑士 特兰斯','实力相当强的奇特魔法师。好像从异空间旅行归来了。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14225239,'元素英雄 水手侠',replace(replace('「元素英雄 水泡侠」＋「元素英雄 羽翼侠」\r\n这只怪兽不能作融合召唤以外的特殊召唤。自己的魔法·陷阱卡区域有卡盖伏的场合，这张卡可以直接攻击对方玩家。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14235211,'暴风龙防人','自己的主要阶段时，手卡或者自己场上的这只怪兽可以当作装备卡使用给自己场上1只龙族的通常怪兽装备。这张卡当作装备卡使用而装备中的场合，装备怪兽向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。此外，这张卡的装备怪兽被破坏的场合，作为代替把这张卡破坏。','装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14255590,'增幅的恶意','只要这张卡在场上表侧表示存在，对方回合的准备阶段时自己墓地存在的「增幅的恶意」的数量的对方卡组最上面的卡送去墓地。','卡组送墓','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14258627,'地球巨人','这张卡可以把自己墓地2只岩石族怪兽从游戏中除外特殊召唤。和这张卡进行战斗的对方怪兽的攻击力·守备力变成一半数值。自己的准备阶段时把自己墓地1只岩石族怪兽从游戏中除外。不除外的场合，这张卡送去墓地。','是否要把墓地的一只岩石族怪兽除外？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14261867,'八钳蝎','这张卡1个回合可以有1次变回里侧守备表示。这张卡攻击对方的里侧守备表示的怪兽的场合，这张卡在伤害计算时攻击力以2400计算。','变成里侧守备','攻击变化','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14289852,'投石区','①：只要这张卡在场地区域存在，自己怪兽被战斗破坏的场合，可以作为代替从自己卡组把1只岩石族怪兽送去墓地。这个效果1回合只能适用1次。','是否要使用「投石区」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14291024,'子机','指定自己的场上的1只表侧表示的「超时空战斗机 V形蛇」才可以特殊召唤。这张卡平常和作为对象的卡的拥有一样的攻击力·守备力的数值。作为对象的卡从场上离开的场合，这张卡破坏。','请选择一张「超时空战斗机 V形蛇」','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14306092,'牙鲛帝 鲨皇',replace(replace('3星怪兽×3只以上（最多5只）\r\n1回合1次，把这张卡1个超量素材取除才能发动。给这张卡放置1个鲨指示物。此外，这张卡进行战斗的伤害步骤内，这张卡的攻击力上升这张卡放置的鲨指示物数量×1000的数值。','\r',char(13)),'\n',char(10)),'放置指示物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14309486,'核成过量体',replace(replace('这张卡的控制者在每次自己的结束阶段从手卡把1张「核成兽的钢核」送去墓地或把手卡1张岩石族怪兽给对方观看。或者都不进行让这张卡破坏。\r\n①：对方把怪兽召唤·反转召唤·特殊召唤之际，把这张卡解放才能发动。那个召唤·反转召唤·特殊召唤无效，那些怪兽破坏。','\r',char(13)),'\n',char(10)),'选择一张「核成兽的钢核」送去墓地','选择一只岩石族怪物给对方观看','破坏「核成过量体」','召唤无效并破坏','反转召唤无效并破坏','特殊召唤无效并破坏','','','','','','','','','','');
INSERT INTO texts VALUES(14315573,'攻击无力化','①：对方怪兽的攻击宣言时，以那1只攻击怪兽为对象才能发动。那次攻击无效。那之后，战斗阶段结束。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14318794,'生命吸收装置','每次自己的准备阶段，回复之前的那个自己的回合支付的基本分的一半。','回复','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14342283,'暴走斗君','只要这张卡在场上存在，攻击表示存在的衍生物的攻击力上升1000，不会被战斗破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14344682,'侍刀男爵','1回合1次，选择对方场上守备表示存在的1只怪兽才能发动。选择的怪兽变成表侧攻击表示。','表示形式改变','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14391920,'地狱暴风','受到3000分以上的战斗伤害才能发动。双方的卡组和墓地的怪兽全部从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14430063,'拦截恶魔','只要这张卡在自己场上表侧攻击表示存在，对方怪兽的攻击宣言时，给与对方基本分500分伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14457896,'寄生虫 帕拉诺伊德',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：以场上1只表侧表示怪兽为对象才能发动。这张卡从手卡当作装备卡使用给那只怪兽装备。装备怪兽种族变成昆虫族，不能向昆虫族怪兽攻击，昆虫族怪兽为对象发动的装备怪兽的效果无效化。这个效果在对方回合也能发动。\r\n②：当作装备卡使用的这张卡被送去墓地的场合才能发动。从手卡把1只7星以上的昆虫族怪兽无视召唤条件特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14462257,'冰之女王','这张卡不能作从墓地的特殊召唤。自己场上表侧表示存在的这张卡被破坏送去墓地时，自己墓地魔法师族怪兽有3只以上存在的场合，可以把自己墓地存在的1张魔法卡加入手卡。','墓地1张魔法卡加入手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14463695,'捕食嫁接','①：以自己墓地1只「捕食植物」怪兽为对象才能把这张卡发动。那只怪兽特殊召唤，把这张卡装备。这张卡从场上离开时那只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14464864,'神之桎梏 格莱普尼尔','从自己卡组把1只名字带有「极星」的怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14466224,'大气圈神鸟','这张卡不能通常召唤。把自己场上存在的2只怪兽和自己墓地存在的1只怪兽从游戏中除外的场合可以特殊召唤。1回合1次，可以把对方场上表侧表示存在的怪兽当作装备卡使用只有1只给这张卡装备。这张卡的攻击力·守备力上升这张卡的效果装备的怪兽的各自数值。','装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14469229,'宝玉的守护者',replace(replace('←2 【灵摆】 2→\r\n①：只要这张卡在灵摆区域存在，1回合1次，自己场上的「究极宝玉神」怪兽以及「宝玉兽」卡不会被效果破坏。\r\n【怪兽效果】\r\n①：自己的「宝玉兽」怪兽和对方怪兽进行战斗的伤害计算时，把自己的手卡·场上的这张卡解放才能发动。那只进行战斗的自己怪兽攻击力·守备力只在伤害计算时变成原本数值的2倍，伤害步骤结束时破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14470845,'扰乱二人组',replace(replace('①：在对方场上把2只「扰乱衍生物」（兽族·光·2星·攻0/守1000）守备表示特殊召唤。这衍生物不能为上级召唤而解放。「扰乱衍生物」被破坏时那控制者受到每1只300伤害。\r\n②：把墓地的这张卡除外才能发动。从卡组把2只卡名不同的「扰乱」怪兽特殊召唤。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14470846,'扰乱衍生物','由「扰乱二人组」的效果特殊召唤。这衍生物不能为上级召唤而解放。「扰乱衍生物」被破坏时那控制者受到每1只300伤害。','','','','','','','','','','','','','','','','');
