INSERT INTO texts VALUES(14882493,'极夜之骑士 盖亚',replace(replace('「极夜之骑士 盖亚」的以下效果1回合各能使用1次。\r\n●把这张卡以外的自己场上1只暗属性怪兽解放才能发动。从卡组把1只战士族·光属性·4星怪兽加入手卡，那之后1张手卡送去墓地。\r\n●把自己墓地1只光属性怪兽除外，选择自己场上1只怪兽才能发动。选择的怪兽的攻击力直到对方的结束阶段时上升500。','\r',char(13)),'\n',char(10)),'从卡组把1只战士族·光属性·4星怪兽加入手卡，那之后1张手卡送去墓地','选择自己场上1只怪兽攻击力直到对方的结束阶段时上升500','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(14883228,'台风',replace(replace('对方场上有魔法·陷阱卡2张以上存在，自己场上没有魔法·陷阱卡存在的场合，这张卡的发动从手卡也能用。\r\n①：以场上1张表侧表示的魔法·陷阱卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
