INSERT INTO texts VALUES(15033525,'烈焰火星',replace(replace('「烈焰火星」的②的效果1回合只能使用1次。\r\n①：这张卡在手卡·墓地存在的场合，把这张卡以外的自己墓地3只怪兽除外才能发动。这张卡特殊召唤。这个效果的发动后，直到回合结束时自己不能把怪兽特殊召唤。\r\n②：自己主要阶段1，把这张卡以外的自己场上的怪兽全部送去墓地才能发动。给与对方为送去墓地的怪兽数量×500伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
