INSERT INTO texts VALUES(15066114,'分段龙',replace(replace('①：这张卡可以不用解放作通常召唤。\r\n②：这张卡的①的方法通常召唤的这张卡的原本的攻击力·守备力变成一半。\r\n③：1回合1次，这张卡是已通常召唤的场合才能发动。表侧表示的这张卡破坏，持有那个攻击力以下的攻击力的主要怪兽区域的怪兽全部破坏。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'不用解放召唤','怪兽破坏','','','','','','','','','','','','','','');
