INSERT INTO texts VALUES(15175429,'魔法反应机·袭式','对方把魔法卡发动时才能发动。把那张魔法卡破坏，给与对方基本分800分伤害。这个效果1回合只能使用1次。','破坏并伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(15180041,'沉默剑士',replace(replace('这张卡不能通常召唤。把自己场上1只战士族怪兽解放的场合才能特殊召唤。\r\n①：自己·对方的准备阶段发动。这张卡的攻击力上升500。\r\n②：1回合1次，魔法卡发动时才能发动。那个发动无效。\r\n③：场上的这张卡被战斗或者对方的效果破坏的场合才能发动。从手卡·卡组把「沉默剑士」以外的1只「沉默剑士」怪兽无视召唤条件特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
