INSERT INTO texts VALUES(17129783,'星圣·轩辕十四','只要这张卡在场上表侧表示存在，自己的主要阶段时只有1次，自己在通常召唤外加上可以把1只名字带有「星圣」的怪兽召唤。','「星圣·轩辕十四」的效果召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17132130,'命运英雄 教义人',replace(replace('这张卡不能通常召唤。把包含「命运英雄」怪兽的自己场上3只怪兽解放的场合才能特殊召唤。\r\n①：这个方法让这张卡特殊召唤成功的场合，下次的对方准备阶段发动。对方基本分变成一半。','\r',char(13)),'\n',char(10)),'特殊召唤','LP减半','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17141718,'花札卫-芒-',replace(replace('①：自己场上有7星以下的「花札卫」怪兽存在的场合才能发动。这张卡从手卡特殊召唤。这个效果的发动后，直到回合结束时自己不是「花札卫」怪兽不能召唤·特殊召唤。\r\n②：这张卡召唤·特殊召唤成功的场合才能发动。手卡的「花札卫」怪兽任意数量给对方观看，回到卡组洗切。那之后，自己从卡组抽出回到卡组的数量。','\r',char(13)),'\n',char(10)),'特殊召唤','手卡回到卡组','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17178486,'生命转换','双方基本分有8000以上的相差的场合才能发动。双方基本分变成3000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17183908,'龙星的辉迹',replace(replace('「龙星的辉迹」在1回合只能发动1张。\r\n①：以自己墓地3只「龙星」怪兽为对象才能发动。那3只怪兽回到卡组洗切。那之后，自己从卡组抽2张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17185260,'业火之锤','①：这张卡战斗破坏对方怪兽送去墓地时，以对方场上1只表侧表示怪兽为对象才能发动。那只对方的表侧表示怪兽变成里侧守备表示。','变成里侧守备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17189532,'幻影保镖','场上的这张卡被破坏送去墓地的场合，可以从卡组把2张名字带有「保镖」的卡加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17189677,'蛇雨','丢弃1张手卡发动。从自己卡组选择4只爬虫族怪兽送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17192817,'熔岩大巨人','从熔岩大地诞生的巨人。使用熔岩拳击攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17194258,'融合征兵',replace(replace('「融合征兵」在1回合只能发动1张。\r\n①：把额外卡组1只融合怪兽给对方观看，从自己的卡组·墓地选那只怪兽有卡名记述的1只融合素材怪兽加入手卡。这张卡的发动后，直到回合结束时自己不能把这个效果加入手卡的怪兽以及那些同名怪兽通常召唤·特殊召唤，那些怪兽效果不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17201174,'锤鲨','1回合1次，自己的主要阶段时才能发动。这张卡的等级下降1星，从手卡把1只水属性·3星以下的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17214465,'海神的巫女','只要这张卡在场上表侧表示存在，场上地形为「海」。场上有地形卡存在的场合，这个效果不适用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17228908,'失落世界',replace(replace('①：恐龙族以外的场上的怪兽的攻击力·守备力下降500。\r\n②：1回合1次，恐龙族怪兽召唤·特殊召唤的场合才能发动。在对方场上把1只「侏罗蛋衍生物」（恐龙族·地·1星·攻/守0）守备表示特殊召唤。\r\n③：只要对方场上有衍生物，对方不能把衍生物以外的场上的怪兽作为效果的对象。\r\n④：1回合1次，场上的通常怪兽被战斗·效果破坏的场合，可以作为代替把那个数量的自己的手卡·卡组的恐龙族怪兽破坏。','\r',char(13)),'\n',char(10)),'特殊召唤衍生物','是否使用「失落世界」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17228909,'侏罗蛋衍生物','由「失落世界」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17236839,'瞬间融合','①：从自己场上把融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。这个效果融合召唤的怪兽在结束阶段破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17238333,'阁楼上的妖怪','各家屋顶都有的冤魂。从不做坏事。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17241370,'遗式术师·纳塔莉娅','这张卡不能特殊召唤。召唤·反转的回合的结束阶段时回到持有者手卡。这张卡召唤·反转时，可以选择自己墓地1只名字带有「遗式」的怪兽回到卡组最上面。','返回手牌','返回卡组','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17243896,'大地蜘蛛','这张卡在自己场上表侧守备表示存在的场合对方对怪兽的召唤·特殊召唤成功时，可以把那些怪兽变成守备表示。这个效果1回合只能使用1次。','变成守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17259470,'僵尸之主','只要这张卡在场上表侧表示存在，可以把手卡1张怪兽卡送去墓地，自己或者对手墓地存在的1只4星以下不死族怪兽特殊召唤。这个效果1回合只能用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17264592,'超级运动员 角逐篮板手',replace(replace('「超级运动员 角逐篮板手」的在①的方法的特殊召唤1回合只能有1次，在②的效果1回合只能使用1次。\r\n①：这张卡可以让「超级运动员 角逐篮板手」以外的自己场上1只「超级运动员」怪兽回到手卡，从手卡特殊召唤。\r\n②：这张卡召唤或者对方回合中的特殊召唤成功的场合才能发动。从自己的手卡·墓地选「超级运动员 角逐篮板手」以外的1只「超级运动员」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17266660,'朱光之宣告者','①：对方把怪兽的效果发动时，从手卡把这张卡和1只天使族怪兽送去墓地才能发动。那个发动无效并破坏。','效果怪兽的效果的发动无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17285476,'自然蚊','只要自己场上有这张卡以外的名字带有「自然」的怪兽表侧表示存在，对方不能选择这张卡作为攻击对象。这张卡以外的自己场上表侧表示存在的名字带有「自然」的怪兽的战斗发生的对自己的战斗伤害由对方代受。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17286057,'三子太阳 赫利俄斯','这张卡可以用自己场上的1只「双子太阳 赫利俄斯」作为祭品特殊召唤。这张卡的攻击力·守备力为从游戏中除外的怪兽卡数量×300的数值。这张卡被战斗破坏送去墓地的场合，结束阶段时攻击力·守备力上升500并特殊召唤。对方场上有怪兽存在的场合，只有1次可以继续进行攻击。','特殊召唤','连续攻击','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17313545,'巨脑怪','只要这张卡在场上表侧表示存在，场上表侧表示存在的光属性怪兽的效果无效化。这张卡战斗破坏光属性怪兽的场合，只有1次可以继续攻击。','连续攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17328157,'疾行机人 竹马高跷',replace(replace('「疾行机人 竹马高跷」的②的效果1回合只能使用1次。\r\n①：这张卡召唤成功时才能发动。从手卡把1只4星以下的「疾行机人」怪兽特殊召唤。\r\n②：把墓地的这张卡除外才能发动。从卡组把1只风属性怪兽送去墓地。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17330916,'娱乐伙伴 琴键猴',replace(replace('←1 【灵摆】 1→\r\n「娱乐伙伴 琴键猴」的②的灵摆效果1回合只能使用1次。\r\n①：另一边的自己的灵摆区域没有「娱乐伙伴」卡存在的场合，这张卡的灵摆刻度变成4。\r\n②：这张卡发动的回合的自己主要阶段才能发动。从卡组把1只4星以下的「娱乐伙伴」怪兽加入手卡。\r\n【怪兽效果】\r\n①：把这张卡从手卡丢弃才能发动。手卡1只「娱乐伙伴」怪兽或者「异色眼」怪兽给对方观看。这个回合，那只怪兽以及自己手卡的同名怪兽的等级下降1星。','\r',char(13)),'\n',char(10)),'降低等级','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17358176,'高等女祭司','咏唱起闻所未闻的咒语，使所有人的心平静下来。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17363041,'茧状体·小雏鸟','场上有「新宇宙」存在时，可以把这张卡作为祭品从手卡·卡组特殊召唤1只「新空间侠·天空蜂鸟」。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17375316,'收押','支付1000分。确认对方的手卡，选择对方手卡的1张卡丢去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17377751,'黑羽-煌星之怒剑鸟',replace(replace('调整＋调整以外的怪兽1只以上\r\n这张卡用同调召唤才能从额外卡组特殊召唤。这张卡同调召唤成功时，可以从手卡把1只调整以外的4星以下的名字带有「黑羽」的怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17390179,'闪光之骑士',replace(replace('←7 【灵摆】 7→\r\n【怪兽简介】\r\n由于神之灵摆而掌握到新力量的骑士。现在是该觉醒过来，解放那股力量了！','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17393207,'守墓的司令官','把这张卡从手卡丢弃去墓地。从卡组把1张「王家长眠之谷」加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17412721,'旧神 诺登',replace(replace('同调怪兽或超量怪兽＋同调怪兽或超量怪兽\r\n①：这张卡特殊召唤成功时，以自己墓地1只4星以下的怪兽为对象才能发动。那只怪兽效果无效特殊召唤。这张卡从场上离开时那只怪兽除外。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17415895,'火山利刃','可以给与对方基本分500分伤害。这个效果1回合只能使用1次。这个效果发动的场合，这个回合这张卡不能攻击。','给予对方500伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17418744,'光子圣域',replace(replace('这张卡发动的回合，自己不是光属性怪兽不能召唤·反转召唤·特殊召唤。\r\n①：在自己场上把2只「光子衍生物」（雷族·光·4星·攻2000/守0）守备表示特殊召唤。这衍生物不能攻击，也不能作为同调素材。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17418745,'光子衍生物','由「光子圣域」的效果特殊召唤。这个衍生物不能攻击，也不能作为同调素材。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17441953,'龟狸','背着乌龟甲壳的狸猫。诱惑敌人进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17444133,'帝王海马','①：光属性怪兽上级召唤的场合，这张卡可以作为2只的数量解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17449108,'扑灭之使徒','破坏1张覆盖表示的魔法或陷阱卡，并从游戏中除外。破坏的卡是陷阱卡的场合，双方互相确认对方卡组，和破坏的陷阱卡同名的卡全部从游戏中除外。之后洗切卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17475251,'英炎星-鹰荣','这张卡被对方破坏的场合，可以从卡组选1张名字带有「炎舞」的魔法卡在自己场上盖放。此外，自己场上有名字带有「炎舞」的魔法·陷阱卡存在的场合，自己场上的全部名字带有「炎星」的怪兽的攻击力·守备力上升500。','盖放','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17484499,'现世与冥界的逆转',replace(replace('「现世与冥界的逆转」在决斗中只能发动1张。\r\n①：双方墓地的卡各自是15张以上的场合支付1000基本分才能发动。双方玩家各自把自己的卡组和墓地的卡全部交换，那之后卡组洗切。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17490535,'外星人脑','自己场上存在的爬虫族怪兽被对方怪兽的攻击破坏送去墓地时才能发动。得到那个时候进行攻击的对方怪兽的控制权，那只怪兽当作爬虫族使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17494901,'我我我电击','自己场上有名字带有「我我我」的怪兽存在的场合才能发动。选择场上1张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17502671,'永火将军','自己手卡是0张的场合，把自己墓地存在的这张卡从游戏中除外，选择自己墓地存在的2只3星以下的名字带有「永火」的怪兽才能发动。选择的怪兽从墓地特殊召唤。这个效果特殊召唤的怪兽的效果无效化。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17511156,'木灵小丑','作出令人讨厌的笑容的恶魔。以手中的镰刀熟练的回避着攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17521642,'零之力','自己场上表侧表示存在的怪兽从游戏中除外时才能发动。场上表侧表示存在的全部怪兽的攻击力变成0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17530001,'魔方人','①：1回合1次，自己主要阶段才能发动。掷1次骰子。只要这只怪兽在场上表侧表示存在，双方不能把和出现的数目相同等级的怪兽召唤·特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17535588,'甲化海星','表面坚固，守备力较高的青色海星。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17536995,'纹章变换','对方怪兽的攻击宣言时才能发动。从手卡把1只名字带有「纹章兽」的怪兽特殊召唤，战斗阶段结束。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17540705,'娱乐伙伴 奇人飞剑手',replace(replace('←2 【灵摆】 2→\r\n「娱乐伙伴 奇人飞剑手」的灵摆效果1回合只能使用1次。\r\n①：这张卡发动的回合的自己主要阶段以自己墓地1只「娱乐伙伴」怪兽为对象才能发动。那只怪兽加入手卡。\r\n【怪兽效果】\r\n「娱乐伙伴 奇人飞剑手」的怪兽效果1回合只能使用1次。\r\n①：这张卡灵摆召唤成功的回合的自己主要阶段从手卡把1只「娱乐伙伴」怪兽送去墓地才能发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17548456,'朱罗纪陨石兽',replace(replace('名字带有「朱罗纪」的调整＋调整以外的恐龙族怪兽2只以上\r\n这张卡同调召唤成功时，把场上存在的卡全部破坏。那之后，可以从自己墓地把1只调整特殊召唤。','\r',char(13)),'\n',char(10)),'破坏并特殊召唤','是否要特殊召唤一只调整？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17559367,'深海潜水员','这张卡被战斗破坏送去墓地的场合，战斗阶段结束时从卡组选择1张怪兽卡，放在卡组最上面。','检索','请选择放置在卡组最上方的怪兽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17573739,'神禽王 亚力克特','对方场上相同属性的怪兽表侧表示2只以上存在的场合，这张卡可以从手卡特殊召唤。1回合1次选择场上表侧表示存在的1张卡。选择的卡的效果在那个回合中无效。「神禽王 亚力克特」在场上只能有1张表侧表示存在。','效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17589298,'纳祭之剑','暗属性怪兽才能装备。装备怪兽的攻击力上升400。装备怪兽被作为祭品让这张卡送去墓地的场合，这张卡回到手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17597059,'电击拷问机','这张卡召唤·反转召唤·特殊召唤成功时，将场上的所有盖伏表示的卡回到持有者的手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17601919,'幽灵姬-南瓜公主-','这张卡在怪兽卡区域上被破坏的场合，可以不送去墓地当作永续魔法卡使用在自己的魔法与陷阱卡区域表侧表示放置。这个效果当作永续魔法卡使用的场合，每次双方的准备阶段给这张卡放置1个南瓜指示物。对方场上的全部怪兽的攻击力·守备力下降当作永续魔法卡使用的这张卡放置的南瓜指示物数量×100的数值。','是否使用「幽灵姬-南瓜公主-」的效果？','放置指示物','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17626381,'补给部队','①：1回合1次，自己场上的怪兽被战斗·效果破坏的场合把这个效果发动。自己从卡组抽1张。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17639150,'机壳的牲祭',replace(replace('「机壳」怪兽才能装备。\r\n①：装备怪兽的攻击力上升300，不会被战斗破坏。\r\n②：「机壳」怪兽上级召唤的场合，装备怪兽可以作为2只的数量解放。\r\n③：这张卡从场上送去墓地的场合才能发动。从卡组把1只「机壳」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17643265,'双头鲨','这张卡召唤成功时，可以让自己场上的全部鱼族·4星怪兽的等级下降1星。这张卡在同1次的战斗阶段中可以作2次攻击。','等级下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17649753,'勒库耶异虫','这张卡若不在这张卡反转的回合则不能攻击宣言。这张卡攻击的场合，战斗阶段结束时变成里侧守备表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17653779,'天使的手镜','把以场上1只怪兽为对象发动的对方的魔法，转移给其他正确的对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17655904,'毁灭之爆裂疾风弹',replace(replace('这张卡发动的回合，自己的「青眼白龙」不能攻击。\r\n①：自己场上有「青眼白龙」存在的场合才能发动。对方场上的怪兽全部破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17658803,'绿宝石龙','吞食翡翠的龙。被其美态吸引住并丢掉性命的人不计其数。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17663375,'古代的机械飞龙',replace(replace('「古代的机械飞龙」的①的效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合才能发动。从卡组把「古代的机械飞龙」以外的1张「古代的机械」卡加入手卡。这个效果的发动后，直到回合结束时自己不能把卡盖放。\r\n②：这张卡攻击的场合，对方直到伤害步骤结束时怪兽的效果不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17688543,'过深的墓穴','①：以自己或者对方的墓地1只怪兽为对象才能发动。下次的自己准备阶段把那只怪兽从墓地往自己场上特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17706537,'长毛兽化石','这张卡战斗破坏对方怪兽送去墓地时，给与对方基本分400分伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17720747,'黑蔷薇之魔女','这张卡不能特殊召唤。自己场上没有卡存在的场合这张卡召唤成功时，从自己卡组抽1张卡。这个效果抽到的卡给双方确认，怪兽卡以外的场合，抽到的卡送去墓地并把这张卡破坏。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17732278,'新空间侠·光辉青苔',replace(replace('这张卡进行战斗的场合，对方抽1张卡。这个效果抽到的卡给双方确认，这张卡得到那张卡的种类的以下效果。\r\n●怪兽卡：这个回合的战斗阶段结束。\r\n●魔法卡：这张卡可以直接攻击对方玩家。\r\n●陷阱卡：这张卡变成守备表示。','\r',char(13)),'\n',char(10)),'抽卡','是否要进行直接攻击？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17733394,'森之尸','森林守护神死后，被恶魔复活的尸体。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17745969,'超级交通机人-移动基地',replace(replace('「机人」融合怪兽＋「机人」怪兽\r\n这个卡名的①的效果1回合只能使用1次。\r\n①：以对方场上1只表侧表示怪兽为对象才能发动。把持有那只怪兽的攻击力以下的攻击力的1只「机人」怪兽从卡组·额外卡组特殊召唤。\r\n②：自己·对方的结束阶段以这张卡以外的自己的主要怪兽区域1只「机人」怪兽为对象才能发动。那只自己怪兽回到持有者手卡，这张卡的位置向那个怪兽区域移动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17760003,'盟军·次世代三武人',replace(replace('「次世代控制员」＋调整以外的怪兽1只以上\r\n作为这张卡的同调素材的调整以外的怪兽属性的以下效果1回合1次，丢弃1张手卡才能发动。\r\n●风属性：对方手卡随机1张送去墓地。\r\n●水属性：场上存在的1张魔法或者陷阱卡破坏。\r\n●暗属性：场上表侧表示存在的1只光属性怪兽破坏，从自己卡组抽1张卡。','\r',char(13)),'\n',char(10)),'对方手牌随机1张送去墓地。','场上存在的1张魔法或者陷阱卡破坏。','场上表侧表示存在的1只光属性怪兽破坏，从自己卡组抽1张卡。','风属性怪兽作为同调素材','水属性怪兽作为同调素材','暗属性怪兽作为同调素材','','','','','','','','','','');
INSERT INTO texts VALUES(17775525,'超重蒸鬼 铁道王-O',replace(replace('「超重武者」调整＋调整以外的「超重武者」怪兽2只以上\r\n这个卡名在规则上也当作「超重武者」卡使用。\r\n①：这张卡可以用表侧守备表示的状态作出攻击。那个场合，这张卡用守备力当作攻击力使用进行伤害计算。\r\n②：1回合1次，把最多2张手卡丢弃，以丢弃数量的对方场上的卡为对象才能发动。那些卡破坏。\r\n③：1回合1次，自己主要阶段才能发动。双方墓地的魔法·陷阱卡全部除外，给与对方除外数量×200伤害。','\r',char(13)),'\n',char(10)),'卡片破坏','魔陷除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17787975,'次元斯芬克斯',replace(replace('以自己场上1只表侧攻击表示怪兽为对象才能把这张卡发动。\r\n①：作为对象的怪兽被比那只怪兽攻击力高的对方怪兽攻击的战斗步骤中1次，可以把这个效果发动。给与对方那只攻击怪兽和作为对象的怪兽的攻击力差的数值的伤害。\r\n②：作为对象的怪兽从场上离开的场合这张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17810268,'云魔物-酸云','这张卡不会被战斗破坏。这张卡表侧守备表示在场上存在的场合，这张卡破坏。这张卡的召唤成功时，给这张卡放置场上存在的名字带有「云魔物」的怪兽数量的雾指示物。可以把这张卡放置的雾指示物取除2个，场上1张魔法或者陷阱卡破坏。','放置指示物','魔陷破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17814387,'援军','①：以场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17841097,'针枪手','这张卡为同调素材的同调怪兽向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17841166,'磁力','①：这个回合，原本种族是机械族或者岩石族的场上的怪兽不受自身以外的对方怪兽的效果影响。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17857780,'娱乐伙伴 啦啦队鼹鼠',replace(replace('←5 【灵摆】 5→\r\n①：自己场上的灵摆怪兽的攻击力上升300。\r\n【怪兽效果】\r\n「娱乐伙伴 啦啦队鼹鼠」的怪兽效果1回合只能使用1次。\r\n①：自己主要阶段以持有和原本攻击力不同攻击力的1只怪兽为对象才能发动。那只怪兽的攻击力数值的以下效果适用。\r\n●那只怪兽的攻击力比原本攻击力高的场合，那只怪兽的攻击力上升1000。\r\n●那只怪兽的攻击力比原本攻击力低的场合，那只怪兽的攻击力下降1000。','\r',char(13)),'\n',char(10)),'攻守变化','攻守变化','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17871506,'真红眼的凶星龙-流星之龙',replace(replace('①：这张卡只要在场上·墓地存在，当作通常怪兽使用。\r\n②：可以把场上的当作通常怪兽使用的这张卡作为通常召唤作再1次召唤。那个场合这张卡变成当作效果怪兽使用并得到以下效果。\r\n●只要这张卡在怪兽区域存在，这张卡以外的自己场上的「真红眼」怪兽不会被战斗·效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17874674,'破邪之刻印','对方的准备阶段时只有1次，选择场上表侧表示存在的1张卡。选择的卡的效果在那个回合中无效。这张卡的控制者在每次自己的准备阶段支付500基本分。或者不支付500基本分让这张卡破坏。','效果无效','是否支付500基本分维持「破邪之刻印」？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17881964,'暗黑火炎龙','「火炎草」＋「小龙」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17896384,'魔法族的结界','每次场上存在的魔法师族怪兽被破坏，给这张卡放置1个魔力指示物（最多4个）。可以把自己场上表侧表示存在的1只魔法师族怪兽和这张卡送去墓地，从自己卡组抽出这张卡放置的魔力指示物数量的卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17932494,'音速战士','①：这张卡被送去墓地的场合发动。自己场上的全部2星以下的怪兽的攻击力上升500。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17948378,'朱罗纪恐兽','这张卡战斗破坏对方怪兽的场合，那个回合的结束阶段时可以把自己场上存在的1只名字带有「朱罗纪」的怪兽解放，从自己卡组抽2张卡。这个效果1回合只能使用1次。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17955766,'新空间侠·水波海豚','丢弃1张手卡。确认对方手卡并选择1张怪兽卡。选择怪兽的攻击力以上的怪兽在自己场上存在的场合，选择的怪兽卡破坏并给与对方基本分500分伤害。选择怪兽的攻击力以上的怪兽没有在自己场上存在的场合，自己受到500分伤害。这个效果1回合只能使用1次。','确认手卡','请选择一张怪兽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17968114,'海原的女战士','侍奉海神的美丽人鱼战士，守护着神圣的区域。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17979378,'DD 高傲骑士',replace(replace('←6 【灵摆】 6→\r\n①：1回合1次，支付500基本分，以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力下降500。\r\n②：另一边的自己的灵摆区域没有「DD」卡存在的场合，这张卡的灵摆刻度变成5。\r\n【怪兽效果】\r\n①：这张卡召唤成功时才能发动。从自己的额外卡组把1只表侧表示的暗属性灵摆怪兽加入手卡。','\r',char(13)),'\n',char(10)),'攻守变化','加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17985575,'龙之支配者','①：只要这张卡在怪兽区域存在，双方玩家不能把场上的龙族怪兽作为效果的对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17988746,'派对夜骑士组','①：对方怪兽的攻击让自己受到战斗伤害时才能发动。这张卡从手卡特殊召唤。那之后，可以把持有受到的伤害数值以下的攻击力的1只怪兽从手卡特殊召唤。','这张卡从手卡特殊召唤','是否特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(17994645,'强袭黑羽-雨隐之小夜刀鸟',replace(replace('调整＋调整以外的怪兽1只\r\n①：「黑羽」怪兽为素材作同调召唤的这张卡当作调整使用。\r\n②：这张卡1回合最多2次不会被战斗破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(18000338,'复王龙EX','①：这张卡和额外怪兽区域的怪兽进行战斗的场合，只在伤害步骤内这张卡的攻击力·守备力上升800。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(18013090,'氮素战士',replace(replace('「氮素同调士」＋调整以外的怪兽1只以上\r\n自己回合自己把魔法卡发动的场合，只在那个回合的伤害计算时只有1次让这张卡的攻击力上升1000。这张卡的攻击破坏对方怪兽的场合，可以把对方场上表侧守备表示存在的1只怪兽变成攻击表示并对那只怪兽继续攻击。','\r',char(13)),'\n',char(10)),'继续攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(18027138,'河马狂欢节','①：在自己场上把3只「河马衍生物」（兽族·地·1星·攻/守0）特殊召唤。这衍生物不能解放。只要「河马衍生物」在怪兽区域存在，自己不能从额外卡组把怪兽特殊召唤。这张卡的发动后，直到回合结束时对方不能把「河马衍生物」以外的怪兽作为攻击对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(18027139,'河马衍生物','由「河马狂欢节」的效果特殊召唤。这衍生物不能解放。只要「河马衍生物」在怪兽区域存在，自己不能从额外卡组把怪兽特殊召唤。','','','','','','','','','','','','','','','','');
