INSERT INTO texts VALUES(18036057,'天空骑士 珀耳修斯',replace(replace('①：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。\r\n②：这张卡给与对方战斗伤害的场合发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(18060565,'龙骑兵团-首席百夫长','这张卡召唤·特殊召唤成功时，选择自己场上表侧表示存在的1只名字带有「龙骑兵团」的鸟兽族怪兽才能发动。从自己卡组把1只3星以下的名字带有「龙骑兵团」的龙族怪兽当作装备魔法卡使用，给选择的怪兽装备。','装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(18063928,'铁皮金鱼','①：这张卡召唤成功时才能发动。从手卡把1只4星怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(18096222,'二重推进器','发动后这张卡变成攻击力上升700的装备卡，给自己场上存在的1只二重怪兽装备。变成装备卡的这张卡被破坏送去墓地时，选择场上表侧表示存在的1只二重怪兽变成再度召唤的状态。','变成再度召唤状态','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(18108166,'幻壳龙','从别的次元突然飞来的神秘生命体。同时具备了高攻击力和奇袭能力。它的攻击使对象物的神经麻痹，并引起强烈的幻觉症状。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(18114794,'召唤断路器','只要这张卡在场上存在，回合玩家作那个回合第3次的召唤·反转召唤·特殊召唤成功时，变成那个回合的结束阶段。这个效果在主要阶段1才发动。','结束回合','','','','','','','','','','','','','','','');
