INSERT INTO texts VALUES(21179143,'爬虫妖卵','把自己墓地存在的1只名字带有「爬虫妖」的怪兽从游戏中除外发动。在自己场上把2只「爬虫妖衍生物」（爬虫族·地·1星·攻/守0）特殊召唤。','','','','','','','','','','','','','','','','');
