INSERT INTO texts VALUES(21296383,'独角鸟','把自己场上表侧表示存在的1只怪兽和这张卡从游戏中除外，从自己墓地选择持有那个原本等级合计数值以下的等级的1只同调怪兽发动。选择的怪兽从墓地特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21296502,'卡通黑魔术师',replace(replace('①：这张卡在召唤·反转召唤·特殊召唤的回合不能攻击。\r\n②：自己场上有「卡通世界」存在，对方场上没有卡通怪兽存在的场合，这张卡可以直接攻击。\r\n③：1回合1次，可以从手卡丢弃1张「卡通」卡，从以下效果选择1个发动。\r\n●从卡组把「卡通黑魔术师」以外的1只卡通怪兽无视召唤条件特殊召唤。\r\n●从卡组把1张「卡通」魔法·陷阱卡加入手卡。','\r',char(13)),'\n',char(10)),'特殊召唤','卡组检索','','','','','','','','','','','','','','');
