INSERT INTO texts VALUES(21313376,'No.14 强欲之死神犬',replace(replace('5星怪兽×2\r\n①：只要这张卡在怪兽区域存在，对方的效果发生的对自己的效果伤害由对方代受。\r\n②：这张卡战斗破坏对方怪兽送去墓地时，把这张卡1个超量素材取除才能发动。持有破坏的那只怪兽的原本攻击力以下的攻击力的场上的怪兽全部破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
