INSERT INTO texts VALUES(21350571,'幻兽之角','发动后这张卡变成攻击力上升800的装备卡，给自己场上存在的1只兽族·兽战士族怪兽装备。装备怪兽战斗破坏对方怪兽送去墓地时，从自己卡组抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21362970,'毛兽攻击者','丢弃1张手卡发动。从自己卡组把1只「毛扎」特殊召唤。这个效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21377582,'真龙剑皇 卓辉星·拼图',replace(replace('这张卡表侧表示上级召唤的场合，可以作为怪兽的代替而把自己场上的永续魔法·永续陷阱卡解放。\r\n①：这张卡不受原本种类（怪兽·魔法·陷阱）和为这张卡的上级召唤而解放的卡相同的卡的效果影响。\r\n②：上级召唤的这张卡存在的场合，1回合1次，从自己墓地把1张永续魔法·永续陷阱卡除外，以这张卡以外的场上1张卡为对象才能发动。那张卡破坏。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'解放魔法·陷阱卡上级召唤','是否继续选择魔法·陷阱卡解放？','场上卡破坏','解放怪兽卡上级召唤','解放魔法卡上级召唤','解放陷阱卡上级召唤','','','','','','','','','','');
INSERT INTO texts VALUES(21390858,'凭依装着-达克','可以把自己场上1只「暗灵使 达克」和1只暗属性怪兽送去墓地，从手卡·卡组特殊召唤。这个方法特殊召唤成功时，可以从卡组把1只3星或者4星的魔法师族·光属性怪兽加入手卡。此外，这个方法特殊召唤的这张卡向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21414674,'日珥手','①：自己场上有「魔术手」「火焰手」「寒冰手」的其中任意种存在的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21417692,'暗精灵','这张卡不支付1000基本分不能攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21420702,'烈焰加农炮-三叉戟式','把自己场上表侧表示存在的1张「烈焰加农炮」送去墓地发动。可以从手卡把1只炎族怪兽送去墓地，对方场上存在的1只怪兽破坏并给与对方基本分500分伤害。这个效果使用的回合，自己怪兽不能攻击。','对方场上存在的1只怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21435914,'冥界浊龙 龙叹',replace(replace('暗属性调整＋调整以外的龙族怪兽1只\r\n①：这张卡不会被战斗破坏。\r\n②：这张卡战斗破坏对方怪兽送去墓地时才能发动。这张卡只再1次可以继续向对方怪兽攻击。\r\n③：自己准备阶段以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力变成一半，给与对方那个数值的伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21452275,'芳香炉',replace(replace('①：只要反转过的这张卡在怪兽区域表侧表示存在，这张卡不会被战斗破坏。\r\n②：反转过的这张卡在怪兽区域表侧表示存在的场合，每次双方的结束阶段发动。自己回复500基本分。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21454943,'念力司令官','自己场上存在的念动力族怪兽进行战斗的场合，那个伤害步骤时支付100的倍数的基本分才能发动（最多500）。直到这个回合的结束阶段时，进行战斗的1只对方怪兽的攻击力·守备力下降支付的数值。','攻守下降','请选择要支付的基本分','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21466326,'残骸爆破','当自己墓地里存在30张以上卡的场合这张卡才能发动。给与对方基本分3000分的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21481146,'闪光防护罩 -光明之力-','对方场上攻击表示怪兽有3只以上存在的场合，对方的攻击宣言时才能发动。对方场上的攻击表示怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21488686,'念力治愈','自己场上表侧表示存在的念动力族怪兽每有1只，自己回复1000基本分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21495657,'宝龙星-神数负屃',replace(replace('←1 【灵摆】 1→\r\n①：自己不是「龙星」怪兽以及「神数」怪兽不能灵摆召唤。这个效果不会被无效化。\r\n【怪兽效果】\r\n这个卡名的怪兽效果1回合只能使用1次。\r\n①：这张卡灵摆召唤或者从卡组的特殊召唤成功的场合，以「宝龙星-神数负屃」以外的自己场上1只「龙星」怪兽或者「神数」怪兽为对象才能发动。这个回合，那只表侧表示怪兽当作调整使用。这个效果发动过的这张卡从场上离开的场合回到持有者卡组最下面。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21496848,'邪遗式四臂噬人魔','名字带有「遗式」的仪式魔法卡降临。1回合1次，宣言卡的种类（怪兽·魔法·陷阱）才能发动。对方可以丢弃1张手卡让这个效果无效。没丢弃的场合，双方玩家把宣言的种类的1张卡从卡组送去墓地。','宣言卡种送去墓地','怪兽卡','魔法卡','陷阱卡','是否要丢弃手牌？','请宣言卡的种类','','','','','','','','','','');
INSERT INTO texts VALUES(21501505,'暗迁士 黑蛇晶',replace(replace('4星怪兽×2\r\n场上的卡1张为对象的魔法·陷阱·效果怪兽的效果发动时，把这张卡1个超量素材取除才能发动。那个对象转移为自己·对方场上的作为正确对象的别的卡。','\r',char(13)),'\n',char(10)),'对象转移','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21502796,'光道猎犬 雷光','①：这张卡反转的场合发动。可以选场上1张卡破坏。从自己卡组上面把3张卡送去墓地。','破坏','是否要破坏一张卡？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21507589,'鱼和返还','丢弃1张手卡，选择从游戏中除外的2只自己的鱼族·海龙族·水族怪兽才能发动。选择的怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21516908,'高速疾行机人 马赫羽子板',replace(replace('调整＋调整以外的怪兽1只以上\r\n「高速疾行机人 马赫羽子板」的②的效果1回合只能使用1次。\r\n①：把这张卡解放才能发动。场上的全部表侧表示怪兽的等级直到回合结束时上升1星。这个效果在对方回合也能发动。\r\n②：这张卡在墓地存在，自己场上有「疾行机人」调整存在的场合才能发动。这张卡从墓地特殊召唤。这个效果的发动后，直到回合结束时自己不是风属性怪兽不能特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21521304,'No.39 希望皇 彼端超霍普',replace(replace('6星怪兽×2\r\n这张卡在规则上也当作名字带有「希望皇 霍普」的卡使用。这张卡超量召唤成功时，对方场上的全部怪兽的攻击力变成0。1回合1次，把这张卡1个超量素材取除才能发动。选择自己场上1只超量怪兽除外，选择自己墓地1只名字带有「希望皇 霍普」的怪兽特殊召唤。那之后，自己回复1250基本分。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'攻击力变成0','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21524779,'雷电妈妈','1回合1次，自己的主要阶段时才能发动。从手卡把「雷电妈妈」以外的1只雷族·光属性·4星的怪兽召唤。','召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21558682,'防御史莱姆','对方的怪兽对自己的怪兽攻击的时候，自己的场上的「再生史莱姆」表侧表示存在的场合，攻击对象移去「再生史莱姆」。','攻击对象转移','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21565445,'海皇子 尼普深渊王',replace(replace('「海皇子 尼普深渊王」的①②的效果1回合各能使用1次。\r\n①：从卡组把「海皇子 尼普深渊王」以外的1只「海皇」怪兽送去墓地才能发动。从卡组把「海皇子 尼普深渊王」以外的1张「海皇」卡加入手卡。\r\n②：这张卡为让水属性怪兽的效果发动而被送去墓地的场合，以「海皇子 尼普深渊王」以外的自己墓地1只「海皇」怪兽为对象发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'卡组检索','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21593977,'处刑人-摩休罗','这张卡送去墓地的那个回合，这张卡的主人可以在手卡发动陷阱卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21597117,'英雄拜访','①：对方怪兽的攻击宣言时才能发动。自己1张手卡由对方随机选。那是怪兽的场合，在自己场上特殊召唤，不是的场合送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21598948,'怪兽箱','对方的怪兽每次攻击，猜硬币的正反。猜中的场合，攻击怪兽的攻击力变为0。每次的自己的准备阶段支付500分维持。不支付维持这张卡破坏。','攻击变化','是否要支付500LP维持「怪兽箱」？','正面','反面','请选择硬币的正反面','','','','','','','','','','','');
INSERT INTO texts VALUES(21607304,'地中族邪界兽·第勒钛电怪',replace(replace('这个卡名的③的效果1回合只能使用1次。\r\n①：自己场上的表侧表示怪兽变成里侧表示时，自己场上没有表侧表示怪兽存在的场合才能发动。这张卡从手卡守备表示特殊召唤。\r\n②：自己主要阶段才能发动。这张卡变成里侧守备表示（1回合只有1次）。\r\n③：这张卡反转的场合，以对方场上1只里侧表示怪兽为对象才能发动。那只怪兽的控制权直到下次的自己结束阶段得到。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21615956,'炎狱护卫龙','能运用自如地操纵火焰的炎狱护卫战士。做出灼热防护罩把敌人的攻击反弹回去。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21620076,'痛苦油漆工','这张卡的卡名只要在场上表侧表示存在当作「僵尸带菌者」使用。此外，1回合1次，选择这张卡以外的自己场上最多2只不死族怪兽才能发动。选择的怪兽的等级直到结束阶段时变成2星。把这个效果适用的怪兽作为同调素材的场合，不是不死族怪兽的同调召唤不能使用。','等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21636650,'奇迹之残照','①：以这个回合被战斗破坏送去自己墓地的1只怪兽为对象才能发动。那只怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21648584,'急袭猛禽-战备',replace(replace('①：这个回合，自己场上的「急袭猛禽」怪兽不会被战斗破坏。\r\n②：自己墓地有「急袭猛禽」怪兽存在的场合把墓地的这张卡除外才能发动。这个回合，自己受到的全部伤害变成0。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21672573,'帝尸神官','只要这张卡在自己场上表侧表示存在，自己对怪兽的上级召唤成功时，双方玩家可以选择各自墓地存在的1张卡回到卡组最上面。','返回卡组','是否要选择墓地一张卡回到卡组最上面？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21686473,'DDD 命运王 零·拉普拉斯',replace(replace('←1 【灵摆】 1→\r\n这个卡名的灵摆效果1回合只能使用1次。\r\n①：自己主要阶段才能发动。从自己的额外卡组选「DDD 命运王 零·拉普拉斯」以外的1只表侧表示的「DDD」灵摆怪兽加入手卡。\r\n【怪兽效果】\r\n①：这张卡可以把自己场上1只「DDD」怪兽解放从手卡特殊召唤。\r\n②：这张卡和对方怪兽进行战斗的伤害计算前才能发动。这张卡的攻击力直到伤害步骤结束时变成那只对方怪兽的原本攻击力的2倍。\r\n③：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。\r\n④：这张卡1回合只有1次不会被战斗破坏。那个时候，自己受到的战斗伤害变成0。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21698716,'宝玉兽 钴尖晶鹰','可以让自己场上表侧表示存在的1张名字带有「宝玉兽」的卡回到卡组最上面。这个效果1回合只能使用1次。这张卡在怪兽卡区域上被破坏的场合，可以不送去墓地当作永续魔法卡使用在自己的魔法与陷阱卡区域表侧表示安置。','破坏时放置魔法陷阱区','返回卡组','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21702241,'反目的从者','装备怪兽的控制权转移时，给与装备怪兽的控制者装备怪兽的原本攻击力数值的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21715135,'我我我学园的紧急联络网','对方场上有怪兽存在，自己场上没有怪兽存在的场合才能发动。从卡组把1只名字带有「我我我」的怪兽特殊召唤。这张卡发动的回合，自己不能作超量召唤以外的特殊召唤。「我我我学园的紧急联络网」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21767650,'水精鳞-深渊鲵鱼兵',replace(replace('可以把墓地的这张卡从游戏中除外，从以下效果选择1个发动。\r\n●自己场上的全部名字带有「水精鳞」的怪兽的等级上升1星。\r\n●自己场上的全部名字带有「水精鳞」的怪兽的等级上升2星。','\r',char(13)),'\n',char(10)),'等级上升','等级全部上升1星','等级全部上升2星','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21768554,'集团催眠','自己场上有名字带有「外星」的怪兽存在时才能发动。选择对方场上存在的最多3只放置有A指示物的怪兽得到控制权。这张卡在发动回合的结束阶段时破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21770260,'史莱姆增殖炉','每次的自己的准备阶段，自己场上特殊召唤1只攻击表示的「史莱姆衍生物」（水族·水·1星·攻/守500）。只要这张卡在场上表侧表示的存在，自己的怪兽不可以召唤·反转召唤·特殊召唤。（「史莱姆衍生物」例外）','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21770261,'史莱姆衍生物','由「史莱姆增殖炉」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21770839,'异色眼幻灵龙',replace(replace('←0 【灵摆】 0→\r\n这个卡名的灵摆效果1回合只能使用1次。\r\n①：丢弃1张手卡才能发动。从自己的额外卡组把1只表侧表示的龙族灵摆怪兽加入手卡。\r\n【怪兽效果】\r\n这个卡名的①②的怪兽效果1回合各能使用1次。\r\n①：自己的灵摆区域有2张卡存在，自己的额外卡组有表侧表示的「异色眼」灵摆怪兽存在的场合才能发动。这张卡从手卡特殊召唤。这个效果发动的回合，自己不能灵摆召唤。\r\n②：这张卡向对方怪兽攻击的伤害计算时才能发动。那只对方怪兽的攻击力只在那次伤害计算时下降自己的额外卡组的表侧表示的灵摆怪兽数量×1000。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21772453,'花札卫-枫间鹿-',replace(replace('这张卡不能通常召唤。把「花札卫-枫间鹿-」以外的自己场上1只「花札卫」怪兽解放的场合可以特殊召唤。\r\n①：这张卡特殊召唤成功的场合发动。自己从卡组抽1张，给双方确认。那是「花札卫」怪兽的场合，可以选对方场上1张魔法·陷阱卡破坏。不是的场合，那张卡送去墓地。','\r',char(13)),'\n',char(10)),'抽卡','是否选对方场上1张魔法·陷阱卡破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21785144,'光道龙 格拉古尼斯','这张卡的攻击力·守备力上升自己墓地存在的名字带有「光道」的怪兽卡种类×300的数值。这张卡攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。这张卡在自己场上表侧表示存在的场合，每次自己的结束阶段，从卡组上面把3张卡送去墓地。','从卡组送3张卡去墓地','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21790410,'甲虫装机 蜈蚣',replace(replace('①：1回合1次，自己主要阶段才能发动。从自己的手卡·墓地选1只「甲虫装机」怪兽当作装备卡使用给这张卡装备。\r\n②：这张卡在自己场上存在，给这张卡装备的卡被送去自己墓地的场合才能发动。从卡组把1张「甲虫装机」卡加入手卡。\r\n③：把这张卡当作装备卡使用来装备的怪兽的等级上升3星。','\r',char(13)),'\n',char(10)),'装备','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21817254,'大雷电球','在地止旋转，向周围发出电击的球。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21831848,'我我我抽卡','把自己墓地3只名字带有「我我我」的怪兽从游戏中除外才能发动。从卡组抽2张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21840375,'隐藏的魔导书','这张卡在自己回合才能发动。选择自己墓地存在的2张魔法卡，加入卡组洗切。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21843307,'复制骑士','自己场上有4星以下的战士族怪兽召唤时才能发动。这张卡发动后变成和那只召唤的怪兽相同等级的同名怪兽卡（战士族·光·攻/守0）在怪兽卡区域特殊召唤。这张卡也当作陷阱卡使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21844576,'元素英雄 羽翼侠','操纵风飞舞并拥有翅膀的元素英雄。从天空一击，羽毛爆发而制裁邪恶。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21844577,'元素英雄 羽翼侠','操纵风飞舞并拥有翅膀的元素英雄。从天空一击，羽毛爆发而制裁邪恶。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21879581,'同调障壁','把自己场上存在的1只同调怪兽解放发动。直到下个回合的结束阶段时，自己受到的全部伤害变成0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21887179,'月风魔','与这张卡进行战斗的恶魔族·不死族怪兽在伤害步骤终了时被破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21888494,'被选中者','选1张自己的手卡的怪兽卡和怪兽以外的种类2张卡。对方在这之中随机选1张，如果选中怪兽卡的场合场上特殊召唤，其他的2张卡送去墓地。选不中的话，全部送去墓地。','请选择一张怪兽卡','请选择怪兽卡以外的两张卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21893603,'星杯的妖精 莉丝',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡召唤·特殊召唤成功的场合才能发动。从卡组把1只「星杯」怪兽加入手卡。\r\n②：这张卡在墓地存在的场合，把自己的手卡·场上1只怪兽送去墓地才能发动。墓地的这张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21900719,'闪光之双剑-雷震','从手卡送去墓地1张卡，这张卡才能进行装备。装备这张卡的怪兽攻击力下降500点。装备这张卡的怪兽在同1个战斗阶段中可以进行2次攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21908319,'平庸鬼的自爆装置','给与破坏盖伏的这张卡的玩家基本分1000分的伤害。','1000伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21924381,'英豪前卫','自己场上的名字带有「英豪」的怪兽被选择作为攻击对象时，选择自己场上1只其他的4星以下的名字带有「英豪」的怪兽才能发动。选择的怪兽的攻击力直到战斗阶段结束时变成2倍，把攻击对象转移为选择的怪兽进行伤害计算。双方怪兽不会被这次战斗破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21947653,'邪心英雄 闪电巨魔',replace(replace('「元素英雄 电光侠」＋「元素英雄 黏土侠」\r\n这只怪兽不能作「暗黑融合」的融合召唤以外的特殊召唤。可以把场上存在的1只怪兽破坏。这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21954587,'水精鳞-巨齿深渊鲨',replace(replace('①：从手卡把这张卡以外的2只水属性怪兽丢弃去墓地才能发动。这张卡从手卡特殊召唤。\r\n②：这张卡的①的效果特殊召唤成功时才能发动。从卡组把1张「深渊」魔法·陷阱卡加入手卡。\r\n③：把这张卡以外的自己场上1只表侧攻击表示的水属性怪兽解放才能发动。这个回合，这张卡在同1次的战斗阶段中可以作2次攻击。','\r',char(13)),'\n',char(10)),'特殊召唤','检索','两次攻击','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21970285,'龙角的狩猎者',replace(replace('←3 【灵摆】 3→\r\n①：场上的通常怪兽的攻击力上升200，自己的通常怪兽的战斗发生的对自己的战斗伤害变成0。\r\n【怪兽描述】\r\n为了拯救瘟疫中受苦的故乡村庄，因灵药原料是龙之角而滥捕的女战士。那个村庄已经遭到被赶离巢穴的龙们践踏并烧毁一事，她还并不知情…','\r',char(13)),'\n',char(10)),'攻守变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21977828,'甲虫装机 吉咖象鼻虫','这张卡可以从手卡当作装备卡使用给自己场上的名字带有「甲虫装机」的怪兽装备。这张卡当作装备卡使用而装备中的场合，装备怪兽的原本守备力变成2600。此外，给怪兽装备的这张卡被送去墓地的场合，可以从自己墓地选择1只名字带有「甲虫装机」的怪兽特殊召唤。「甲虫装机 吉咖象鼻虫」的这个效果1回合只能使用1次。','装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(21984400,'兽·魔·导',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：可以把自己场上的魔力指示物的以下数量取除，那个效果发动。\r\n●2个：选自己场上1只「魔导兽」灵摆怪兽回到持有者手卡。\r\n●4个：从自己的额外卡组把1只表侧表示的「魔导兽」灵摆怪兽特殊召唤。那之后，可以给那只怪兽放置2个魔力指示物。\r\n●6个：从自己的额外卡组把1只表侧表示的灵摆怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'取除2个','取除4个','取除6个','是否放置魔力指示物？','','','','','','','','','','','','');
INSERT INTO texts VALUES(21999001,'光波双颚机',replace(replace('「光波双颚机」的②的效果1回合只能使用1次。\r\n①：从额外卡组特殊召唤的怪兽在对方场上存在，自己场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：丢弃1张手卡才能发动。从手卡·卡组把1只「光波」怪兽特殊召唤。这个效果的发动后，直到回合结束时自己不是「光波」怪兽不能特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22009013,'侵入魔鬼·飞蛾','把名字带有「侵入魔鬼」的怪兽解放对这张卡的上级召唤成功时，可以支付1000基本分，选择对方场上存在的最多2张卡回到持有者手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22011689,'捕食植物 海鳝猪笼草',replace(replace('①：这张卡的攻击力上升场上的捕食指示物数量×200。\r\n②：这张卡战斗破坏对方怪兽时才能发动。那只破坏的怪兽当作装备卡使用给这张卡装备。\r\n③：1回合1次，以这张卡的效果装备的1张怪兽卡为对象才能发动。那张卡破坏，自己基本分回复那个原本攻击力的数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22020907,'英雄标记','①：自己场上的怪兽被战斗破坏送去墓地时才能发动。从手卡·卡组把1只4星以下的「元素英雄」怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22026707,'黑魔族的幕帘','魔术师制作的窗帘。据说可以提升魔法师的力量。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22046459,'巨大化','①：自己基本分比对方少的场合，装备怪兽的攻击力变成原本攻击力的2倍。自己基本分比对方多的场合，装备怪兽的攻击力变成原本攻击力的一半。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22047978,'战斗中止','对方怪兽的攻击宣言时才能发动。对方可以从手卡把1只怪兽给人观看让这张卡的效果无效。没给观看的场合，那只攻击怪兽破坏，战斗阶段结束。','是否要从手卡把1只怪兽给对方观看？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22056710,'吸血鬼创世主','这张卡不能通常召唤。自己场上存在的1只「吸血鬼领主」从游戏中除外的场合才能特殊召唤。从手卡丢弃1张不死族怪兽去墓地，选择比丢弃的不死族怪兽等级低的1只墓地的不死族怪兽特殊召唤上场。这个效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22061412,'元素英雄 闪光侠',replace(replace('名字带有「元素英雄」的怪兽＋光属性怪兽\r\n这张卡不用融合召唤不能特殊召唤。这张卡的攻击上升从游戏中除外的自己的名字带有「元素英雄」的怪兽数量×300的数值。这张卡从场上送去墓地时，可以选择从游戏中除外的最多2只自己的名字带有「元素英雄」的怪兽加入手卡。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22076135,'水精鳞-深渊鲟鱼兵','这张卡召唤·特殊召唤成功时，把手卡1只水属性怪兽丢弃去墓地才能发动。从自己墓地选择1只3星以下的水属性怪兽加入手卡。「水精鳞-深渊鲟鱼兵」的效果1回合只能使用1次。','回收','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22082163,'亚马逊的意志','从自己墓地选择1只名字带有「亚马逊」的怪兽，攻击表示特殊召唤。这个效果特殊召唤的怪兽不能把表示形式变更，可以攻击的场合必须作出攻击。这张卡不在场上存在时，那只怪兽破坏。那只怪兽破坏时这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22091345,'娱乐伙伴 尖刺鹰','①：1回合1次，以自己场上1只表侧表示怪兽为对象才能发动。这个回合，那只怪兽向守备表示怪兽攻击的场合，给与对方为攻击力超过那个守备力的数值的战斗伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22093873,'假面英雄 神风',replace(replace('这张卡用「假面变化」的效果才能特殊召唤。\r\n①：这张卡不会被战斗破坏。\r\n②：只要这张卡在怪兽区域存在，对方在战斗阶段只能用1只怪兽攻击。\r\n③：这张卡战斗破坏对方怪兽送去墓地时才能发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'抽1张卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22110647,'幻兽机 哥萨克龙',replace(replace('7星怪兽×2\r\n①：1回合1次，把这张卡1个超量素材取除才能发动。在自己场上把2只「幻兽机衍生物」（机械族·风·3星·攻/守0）特殊召唤。\r\n②：只要自己场上有衍生物存在，这张卡不会被战斗·效果破坏。\r\n③：1回合1次，把自己场上1只「幻兽机」怪兽解放，以场上1张卡为对象才能发动。那张卡破坏。这个效果发动的回合，这张卡不能攻击。','\r',char(13)),'\n',char(10)),'在自己场上把2只「幻兽机衍生物」特殊召唤','选择场上1张卡破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22110648,'幻兽机衍生物','由「幻兽机 哥萨克龙」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22123627,'强欲之海鳝壶','从自己手卡让2只水属性怪兽回到卡组，从自己卡组抽3张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22134079,'守墓的伏兵','这张卡反转时，可以选择对方墓地1张卡回到卡组最下面。此外，反转过的这张卡被送去墓地的场合，可以选择自己墓地1张名字带有「王家长眠之谷」的卡加入手卡。这张卡的效果不会被「王家长眠之谷」的效果无效化。','返回卡组','加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22138839,'捕食植物 尖牙鱼佛塔花',replace(replace('①：这张卡把对方场上1只有捕食指示物放置的怪兽解放的场合可以从手卡特殊召唤。\r\n②：这张卡从场上送去墓地的场合发动。给对方场上的表侧表示怪兽全部各放置1个捕食指示物。有捕食指示物放置的2星以上的怪兽的等级变成1星。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22147147,'旋风剑','名字带有「命运英雄」的怪兽才能装备。装备怪兽进行攻击的场合，那个伤害步骤结束时把场上1张魔法或者陷阱卡破坏。','魔陷破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22159429,'淘气仙星的花冠魔法',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：以自己墓地1只「淘气仙星」怪兽为对象才能把这张卡发动。那只怪兽特殊召唤，把这张卡装备。这张卡从场上离开时那只怪兽破坏。\r\n②：1回合1次，装备怪兽用战斗·效果给与对方伤害的场合才能发动。从手卡把1只「淘气仙星」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22160245,'邪心英雄 业火翼魔',replace(replace('「元素英雄 羽翼侠」＋「元素英雄 爆热女郎」\r\n这只怪兽不能作「暗黑融合」的融合召唤以外的特殊召唤。这张卡攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。这张卡战斗破坏怪兽送去墓地时，给与对方基本分破坏怪兽的攻击力或守备力的较高方数值的伤害。','\r',char(13)),'\n',char(10)),'伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22171591,'秘学家','这张卡被战斗破坏送去墓地时，可以支付800基本分从自己卡组把1只念动力族怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22174866,'芳香法师-依兰',replace(replace('①：只要自己基本分比对方多并有这张卡在怪兽区域存在，对方场上的怪兽的攻击力·守备力下降500。\r\n②：1回合1次，自己基本分回复的场合，以对方场上1张魔法·陷阱卡为对象发动。那张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22198672,'王车连接',replace(replace('①：1回合1次，自己主要阶段以场上1只连接怪兽为对象才能发动。那只怪兽的位置向那只怪兽所连接区的主要怪兽区域移动（不能向从那只怪兽来看的对方场上移动）。\r\n②：1回合1次，自己主要阶段才能发动。选自己的主要怪兽区域2只连接怪兽或者对方的主要怪兽区域2只连接怪兽，那些位置交换。','\r',char(13)),'\n',char(10)),'移动位置','交换位置','请选择要移动位置的怪兽','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22200403,'催眠妹妹',replace(replace('①：双方的灵摆区域的卡数量让这张卡得到以下效果。\r\n●1张以上：这张卡的攻击力·守备力上升800。\r\n●2张以上：这张卡和灵摆召唤的怪兽进行战斗的伤害步骤开始时发动。那只怪兽破坏。\r\n●3张以上：对方场上的怪兽可以攻击的场合，必须向这张卡作出攻击。\r\n●4张：这张卡用战斗或者这张卡的效果破坏怪兽的场合发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'破坏','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22201234,'光道防护罩','自己场上表侧表示存在的名字带有「光道」的怪兽成为攻击对象时，可以从自己卡组上面把2张卡送去墓地让1只对方怪兽的攻击无效。','是否要使用效果？','对方怪兽的攻击无效','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22205600,'连锁旋风','魔法·陷阱·效果怪兽的效果让场上存在的卡破坏时，选择场上存在的2张魔法·陷阱卡才能发动。选择的卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22211622,'霸王门 无限',replace(replace('←13 【灵摆】 13→\r\n①：自己场上有怪兽存在的场合，自己不能灵摆召唤。这个效果不会被无效化。\r\n②：1回合1次，自己场上有「霸王龙 扎克」存在的场合，以对方场上1只表侧表示怪兽为对象才能发动。自己基本分回复那只怪兽的攻击力的数值。\r\n【怪兽效果】\r\n①：1回合1次，以这张卡以外的自己场上1张表侧表示的卡为对象才能发动。那张卡和这张卡破坏，把1只龙族超量怪兽或者龙族灵摆怪兽从额外卡组特殊召唤。这个效果特殊召唤的怪兽的攻击力·守备力变成0，效果无效化，不能作为同调·超量召唤的素材。\r\n②：怪兽区域的这张卡被战斗·效果破坏的场合才能发动。这张卡在自己的灵摆区域放置。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22219822,'淘气仙星·曼德拉',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡从手卡送去墓地的场合才能发动。这张卡守备表示特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。\r\n②：这张卡作为「淘气仙星」连接怪兽的连接素材送去墓地的场合，以对方的连接怪兽的所连接区1只怪兽为对象才能发动。那只怪兽破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','怪兽破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22227683,'文具电子人004',replace(replace('①：这张卡和对方怪兽进行战斗的伤害计算时才能发动。从卡组把「文具电子人004」以外的1只「文具电子人」怪兽送去墓地，这张卡的攻击力·守备力只在那次伤害计算时上升送去墓地的那只怪兽的等级×500。这个效果的发动后，直到回合结束时对方受到的战斗伤害变成0。\r\n②：这张卡战斗破坏对方怪兽的场合才能发动。从自己的手卡·墓地选2只等级不同的「文具电子人」怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22318971,'一角兽使魔','表侧守备表示存在的这张卡被选择作为攻击对象时，可以把这张卡以外的自己场上存在的1只怪兽从游戏中除外，把这张卡从游戏中除外。那个时候的攻击怪兽必须作出攻击。这个效果除外的这张卡在下次的自己的准备阶段时回到场上。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22339232,'白骨梦魇',replace(replace('这张卡的卡名只要在墓地存在当作「白骨」使用。此外，可以把这张卡从手卡丢弃从以下效果选择1个发动。\r\n●选择从游戏中除外的1只自己的「白骨」或者「白骨梦魇」回到自己墓地。\r\n●选择从游戏中除外的1只自己的「白骨夫人」或者「白骨王」在场上特殊召唤。','\r',char(13)),'\n',char(10)),'选择从游戏中除外的1只自己的「白骨」或者「白骨梦魇」回到自己墓地。','选择从游戏中除外的1只自己的「白骨夫人」或者「白骨王」在场上特殊召唤。','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22346472,'破天荒之风','选择自己场上表侧表示存在的1只怪兽发动。选择的怪兽的攻击力·守备力直到下次的自己的准备阶段时上升1000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22359980,'银幕之镜壁',replace(replace('这张卡的控制者在每次自己准备阶段支付2000基本分。或者不支付基本分让这张卡破坏。\r\n①：只要这张卡在魔法与陷阱区域存在，对方的攻击怪兽的攻击力变成一半。','\r',char(13)),'\n',char(10)),'是否要支付2000基本分维持「银幕之镜壁」？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22371016,'正义盟军 未知轰击象','这张卡和光属性怪兽进行战斗时，把那只怪兽从游戏中除外。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22377815,'水面之艾瑞莎',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当通常召唤使用的再度召唤，这张卡当作效果怪兽使用并得到以下效果。\r\n●这张卡战斗破坏对方怪兽送去墓地时，对方手卡随机丢弃1张。','\r',char(13)),'\n',char(10)),'手牌丢弃','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22382087,'风帝家臣 迦楼姆',replace(replace('「风帝家臣 迦楼姆」的①②的效果1回合各能使用1次。\r\n①：让自己场上1只上级召唤的怪兽回到持有者手卡才能发动。这张卡从手卡特殊召唤。这个回合，自己不能从额外卡组把怪兽特殊召唤。\r\n②：这张卡为上级召唤而被解放的场合才能发动。从卡组把「风帝家臣 迦楼姆」以外的1只攻击力800/守备力1000的怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22386234,'极光仙·阿诣罗','只要这张卡在场上表侧表示存在，双方不能把怪兽特殊召唤。自己或者对方场上有怪兽召唤时，这张卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22404675,'雷帝家臣 密特拉',replace(replace('「雷帝家臣 密特拉」的①②的效果1回合各能使用1次。\r\n①：自己主要阶段才能发动。这张卡从手卡特殊召唤，在对方场上把1只「家臣衍生物」（雷族·光·1星·攻800/守1000）守备表示特殊召唤。这个回合，自己不能从额外卡组把怪兽特殊召唤。\r\n②：这张卡为上级召唤而被解放的场合才能发动。这个回合，自己在通常召唤外加上只有1次，自己主要阶段可以上级召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22404676,'家臣衍生物','「雷帝家臣 密特拉」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22419772,'妖精守护者','表侧表示的这张卡做祭品。这个回合因为对方造成的送去墓地的自己的1张魔法卡回到卡组最下面。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22423493,'机壳守护神 路径灵',replace(replace('机械族怪兽2只\r\n①：连接召唤的这张卡不受魔法·陷阱卡的效果影响，也不受这张卡以外的连接怪兽发动的效果影响。\r\n②：1回合1次，以这张卡以外的自己以及对方场上的表侧表示的卡各1张为对象才能发动。那2张卡的效果直到回合结束时无效。\r\n③：这张卡所连接区有怪兽2只同时特殊召唤时才能发动。从卡组把1只5星以上的机械族怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22431243,'超进化药','祭掉自己场上1只爬虫族怪兽。从手卡特殊召唤1只恐龙族怪兽上场。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22446869,'水精鳞-邓氏深渊鱼',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：从手卡把这张卡以外的1只水属性怪兽丢弃去墓地才能发动。这张卡从手卡特殊召唤。\r\n②：这张卡的①的效果特殊召唤成功时才能发动。从卡组把1只4星以下的「水精鳞」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'特殊召唤','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22479888,'黏土冲撞','自己场上的「元素英雄 黏土侠」被选择为攻击对象时才能发动（若选择的卡是里侧守备表示的场合，那张卡需要确认）。攻击怪兽和选择的「元素英雄 黏土侠」破坏，给与对方基本分800分的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22493811,'蚂蚁的增殖','祭掉自己场上1只昆虫族怪兽发动。在自己场上特殊召唤2只「兵队衍生物」（地·4星·昆虫族·攻500·守1200）。（不能用作上级召唤的祭品）','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22493812,'兵队衍生物','由「蚂蚁的增殖」的效果特殊召唤。这个衍生物不能用于上级召唤而解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22499034,'真龙战士 点火烈·炽热',replace(replace('这张卡表侧表示上级召唤的场合，可以作为怪兽的代替而把自己场上的永续魔法·永续陷阱卡解放。\r\n①：1回合1次，上级召唤的这张卡存在，对方把魔法·陷阱·怪兽的效果发动时才能发动。从卡组选1张「真龙」永续魔法卡加入手卡或在自己场上发动。','\r',char(13)),'\n',char(10)),'解放魔法·陷阱卡上级召唤','加入手卡或在自己场上发动','是否加入手卡？','请选择要加入手卡或在自己场上发动的卡','','','','','','','','','','','','');
INSERT INTO texts VALUES(22499463,'蛇毒眼镜蛇','被坚硬鳞片覆盖全身的巨大眼镜蛇，能射出大量的毒液攻击，但因为体型的巨大，毒液并无任何特别之处。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22512237,'机械猎犬','只要自己手卡数目是0张，对方不能发动魔法卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22512406,'覆面忍者 惠比寿','1回合1次，自己场上有「覆面忍者 惠比寿」以外的名字带有「忍者」的怪兽存在的场合才能发动。自己场上的名字带有「忍者」的怪兽数量的对方魔法·陷阱卡回到持有者手卡。这个效果适用的回合，自己场上的「忍者义贼 五卫五卫」可以直接攻击对方玩家。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22530212,'魔术手','①：只在这张卡在场上表侧表示存在才有1次，对方用抽卡以外的方法从卡组把卡加入手卡时才能发动。那卡送去墓地，给与对方800伤害。','送墓并伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22537443,'塞贝克的祝福','①：自己怪兽直接攻击给与对方战斗伤害时才能发动。自己基本分回复那个数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22539270,'废铁油带','选择自己墓地存在的1只名字带有「废铁」的怪兽发动。选择的怪兽从墓地特殊召唤。这个效果特殊召唤的怪兽的效果无效化。这张卡不在场上存在时，那只怪兽破坏。那只怪兽从场上离开时这张卡破坏。这张卡发动的回合，自己不能进行战斗阶段。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22567609,'迅捷鼯鼠','这张卡被战斗破坏送去墓地时，自己基本分回复1000分。可以再从卡组把「迅捷鼯鼠」里侧守备表示特殊召唤上场。之后卡组洗切。','回复','是否要特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22587018,'氢素龙','①：这张卡战斗破坏对方怪兽送去墓地时才能发动。从卡组把1只「氢素龙」特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22589918,'上膛','将自己的全部手卡放回卡组。那之后，抽与放回卡组的卡数量相同的卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22609617,'奇袭的又佐','这张卡在同1个战斗阶段中可以进行2次攻击。这张卡在场上以表侧表示存在时，控制权不能被变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22610082,'遗言之假面',replace(replace('①：这张卡回到卡组洗切。\r\n②：这张卡用「假面魔兽 死亡护法师」的效果装备中的场合，得到装备怪兽的控制权。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22617205,'觉星辉士-神数蝇王',replace(replace('←7 【灵摆】 7→\r\n①：自己不是「星守」怪兽以及「神数」怪兽不能灵摆召唤。这个效果不会被无效化。\r\n【怪兽效果】\r\n「觉星辉士-神数蝇王」的怪兽效果1回合只能使用1次。\r\n①：这张卡召唤·反转召唤·灵摆召唤成功的场合，以这张卡以外的自己的怪兽区域·灵摆区域1张「星守」卡或者「神数」卡和对方场上盖放的1张卡为对象才能发动。那些卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22624373,'光道魔术师 丽拉',replace(replace('①：自己主要阶段以对方场上1张魔法·陷阱卡为对象才能发动。自己场上的表侧攻击表示的这张卡变成守备表示，作为对象的对方的卡破坏。这个效果的发动后，直到下次的自己回合的结束时这张卡不能把表示形式变更。\r\n②：自己结束阶段发动。从自己卡组上面把3张卡送去墓地。','\r',char(13)),'\n',char(10)),'破坏魔法陷阱','从卡组送3张卡去墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22628574,'伪羽','从手卡把1只名字带有「黑羽」的怪兽送去墓地，选择对方墓地存在的1张通常陷阱卡发动。这张卡的效果变成和选择的通常陷阱卡的效果相同。','请选择一张通常陷阱','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22638495,'刚龙剑士 雾动星·强力',replace(replace('「龙剑士」灵摆怪兽＋灵摆怪兽\r\n把自己场上的上记卡解放的场合才能从额外卡组特殊召唤（不需要「融合」）。\r\n①：只要这张卡在怪兽区域存在，自己的怪兽区域·灵摆区域的灵摆怪兽卡不会被战斗以及对方的效果破坏。\r\n②：1回合1次，自己主要阶段才能发动。从自己的手卡·墓地选1只「龙剑士」灵摆怪兽特殊召唤。这个效果特殊召唤的怪兽不能作为融合素材。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22653490,'电光千鸟',replace(replace('风属性4星怪兽×2\r\n这张卡超量召唤成功时，选择对方场上盖放的1张卡回到持有者卡组最下面。此外，1回合1次，把这张卡1个超量素材取除才能发动。选择对方场上表侧表示存在的1张卡回到持有者卡组最上面。','\r',char(13)),'\n',char(10)),'返回卡组最下面','返回卡组最上面','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22657402,'冥界的丽人 伊索德',replace(replace('这张卡不用这张卡的①的方法不能特殊召唤。「冥界的丽人 伊索德」的②的效果1回合只能使用1次。\r\n①：自己场上有「冥界骑士 崔斯坦」存在的场合，这张卡可以从手卡特殊召唤。\r\n②：以自己场上最多2只不死族怪兽为对象，宣言5～8的任意等级才能发动。那些怪兽直到回合结束时变成宣言的等级。这个效果的发动后，直到回合结束时自己不是不死族怪兽不能特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22666164,'督战官 科文顿','可以把自己场上的「机甲士兵」「机甲狙击兵」「机甲卫兵」各1只送去墓地，从手卡·卡组特殊召唤1只「机甲部队·武装力量」。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22702055,'海','全部鱼族·海龙族·雷族·水族的怪兽攻击力·守备力上升200。机械族·炎族的怪兽攻击力·守备力下降200。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22747316,'以牙还牙','和自己墓地存在的魔法·陷阱卡同名的卡由对方发动时才能发动。那个发动无效并破坏。那之后，和用这个效果把发动无效的卡同名的卡可以从自己墓地选1张加入手卡。','是否要选择一张同名卡加入手卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22751868,'风云机巧城','自己场上表侧表示存在的名字带有「机巧」的怪兽把对方场上表侧表示存在的怪兽选择作为攻击对象时，可以把那1只对方怪兽的表示形式变更。此外，场上表侧表示存在的这张卡被破坏送去墓地时，可以选择自己墓地存在的1只4星以上的名字带有「机巧」的怪兽特殊召唤。','改变表示形式','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22754505,'小块石人','自己场上有「大块石人」表侧表示存在的场合这张卡召唤·反转召唤·特殊召唤成功时，可以从自己卡组把1只「中块石人」特殊召唤。这个效果特殊召唤的怪兽的效果无效化。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22765132,'娱乐伙伴呼唤','①：对方怪兽的直接攻击宣言时，以那1只攻击怪兽为对象才能发动。那次攻击无效，守备力合计最多到作为对象的怪兽的攻击力以下为止，从卡组把最多2只「娱乐伙伴」怪兽加入手卡。这张卡的发动后，直到下次的自己回合的结束时自己不能从额外卡组把怪兽特殊召唤。','把最多2只「娱乐伙伴」怪兽加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22790789,'巨大战舰 晶核','这张卡召唤时放置3个指示物。这张卡不会被战斗破坏。进行战斗的场合，在伤害步骤结束时取除1个指示物。没有指示物放置的状态进行战斗的场合，伤害步骤结束时这张卡破坏。1回合1次可以在自己的主要阶段，选择对方场上的1只表侧攻击表示怪兽变成表侧守备表示。','放置指示物','移除指示物','破坏','改变表示形式','','','','','','','','','','','','');
INSERT INTO texts VALUES(22796548,'恶魔的宣告','①：1回合1次，支付500基本分，宣言1个卡名才能发动。自己卡组最上面的卡翻开，宣言的卡的场合，那张卡加入手卡。不是的场合，翻开的卡送去墓地。','宣言','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22802010,'无差别崩坏','①：掷2次骰子。等级·阶级比出现的数目合计低的场上的表侧表示怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22804410,'渊眼白龙',replace(replace('①：自己场上的表侧表示的「青眼」怪兽被战斗或者对方的效果破坏时才能发动。这张卡从手卡特殊召唤，给与对方为自己墓地的龙族怪兽种类×600伤害。\r\n②：这张卡召唤·特殊召唤成功的场合，以自己墓地1只龙族怪兽为对象发动。这张卡的攻击力变成和那只怪兽的攻击力相同。\r\n③：场上的这张卡被效果破坏的场合发动。对方场上的怪兽全部破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22804644,'死亡病毒龙',replace(replace('这张卡在用「克里底亚之牙」的效果把自己的手卡·场上的「死之卡组破坏病毒」送去墓地的场合才能特殊召唤。\r\n①：这张卡特殊召唤成功的场合发动。对方场上的怪兽，对方手卡，用对方回合计算的3回合内对方抽到的卡全部确认，那之内的攻击力1500以上的怪兽全部破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22812068,'机甲忍者 地','对方场上有怪兽存在，自己场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22829942,'融合再生机构',replace(replace('①：1回合1次，丢弃1张手卡才能发动。从自己的卡组·墓地选1张「融合」加入手卡。\r\n②：自己·对方的结束阶段，以这个回合融合召唤使用过的自己墓地1只融合素材怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22835145,'黑羽-极北之布利扎德',replace(replace('这张卡不能特殊召唤。\r\n①：这张卡召唤成功时，以自己墓地1只4星以下的「黑羽」怪兽为对象才能发动。那只怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22837504,'霞之谷的战士','只要这张卡在自己场上表侧表示存在，和这张卡的战斗没被破坏的对方怪兽在伤害步骤结束时回到持有者手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22842126,'泛神的帝王',replace(replace('「泛神的帝王」的②的效果1回合只能使用1次。\r\n①：把手卡1张「帝王」魔法·陷阱卡送去墓地才能发动。自己从卡组抽2张。\r\n②：把墓地的这张卡除外才能发动。从卡组把3张「帝王」魔法·陷阱卡给对方观看，对方从那之中选1张。那1张卡加入自己手卡，剩余回到卡组。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22842214,'海洋枪兵','可以选择从游戏中除外的自己的鱼族·海龙族·水族怪兽任意数量当作装备卡使用给这张卡装备。这个效果只在这张卡在场上表侧表示存在能使用1次。这个效果把怪兽装备的场合，这张卡的攻击力上升1000。此外，场上的这张卡被破坏的场合，可以作为代替把这张卡的效果装备的1只怪兽破坏。','装备怪兽','是否要使用「海洋枪兵」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22855882,'被诅咒的魔剑','据说战胜施加在身上的诅咒的人就可以以此获得力量。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22858242,'猿魔王 塞曼',replace(replace('暗属性调整＋调整以外的兽族怪兽1只\r\n这张卡攻击的场合，对方直到伤害步骤结束时魔法·陷阱卡不能发动。对方怪兽的攻击宣言时，可以把自己的手卡或者场上1只怪兽送去墓地，让1只对方怪兽的攻击无效。','\r',char(13)),'\n',char(10)),'攻击无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22862454,'代理龙',replace(replace('怪兽2只\r\n①：自己场上的卡被战斗·效果破坏的场合，可以作为代替把这张卡所连接区1只自己怪兽破坏。','\r',char(13)),'\n',char(10)),'是否使用「代理龙」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22869904,'魔力诱爆','对方的魔法与陷阱卡区域表侧表示存在的魔法卡被送去墓地的场合才能发动。选择场上表侧表示存在的1只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22873798,'鬣狗','这张卡因战斗送去墓地时，可以把卡组的「鬣狗」特殊召唤到场上。之后卡组洗切。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22888900,'纯灰色画的牢狱','自己场上有上级召唤·仪式召唤·融合召唤的怪兽之内任意种表侧表示存在的场合才能发动。直到下次的对方回合的结束时，双方不能进行同调·超量召唤，场上的同调·超量怪兽效果无效化，不能攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22900598,'吸血鬼移地','自己的场地卡区域没有卡存在，自己场上表侧表示存在的怪兽只有不死族怪兽的场合才能发动。从卡组选1张「吸血鬼帝国」发动。那之后，可以从自己墓地选1只名字带有「吸血鬼」的暗属性怪兽表侧守备表示特殊召唤。「吸血鬼移地」在1回合只能发动1张。','是否要从墓地选1只名字带有「吸血鬼」的暗属性怪兽表侧守备表示特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22910685,'绿树之灵王','居住在被绿树包围的地方，统治森林的年轻国王。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22916281,'被星杯所选中者',replace(replace('以机怪残骸为武装，为人心地正直的少年。\r\n对星辰之森自古流传的『星之勇者』心怀憧憬，答应妖精莉丝的请求，和被授予光芒的同伴们一起踏上了将七件星遗物解放的旅程。\r\n“星光之勇者 举剑汇聚光明 斩除巨大黑暗”','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22923081,'黑魔导战士 破坏者',replace(replace('「黑魔导战士 破坏者」的④的效果1回合只能使用1次。\r\n①：这张卡召唤成功的场合发动。给这张卡放置2个魔力指示物。\r\n②：这张卡灵摆召唤成功的场合发动。给这张卡放置3个魔力指示物。\r\n③：这张卡的攻击力上升这张卡的魔力指示物数量×400。\r\n④：把这张卡1个魔力指示物取除，以场上1张魔法·陷阱卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'放置魔力指示物','放置魔力指示物','破坏一张魔法陷阱卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22959079,'虫洞','选自己场上的1只怪兽，在自己的下次的准备阶段之前除外。除外的时候，被选择的怪兽的怪兽区的位置不能使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22991179,'无视加护','对方怪兽的攻击宣言时，可以把自己墓地存在的1只昆虫族怪兽从游戏中除外，那次攻击无效。','攻击无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22993208,'炎王的急袭','对方场上有怪兽存在，自己场上没有怪兽存在的场合才能发动。从卡组把1只炎属性的兽族·兽战士族·鸟兽族怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化，结束阶段时破坏。「炎王的急袭」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(22996376,'百兽王 贝希摩斯','这张卡可以用1只怪兽做祭品通常召唤。那个场合，这张卡的原本攻击力变为2000。祭品召唤成功时，和做祭品的怪兽同数量的自己墓地的兽族怪兽可以回到持有者的手卡。','用1只怪兽做祭品通常召唤','返回手牌','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23002292,'红色重启',replace(replace('这张卡也能把基本分支付一半从手卡发动。\r\n①：对方把陷阱卡发动时才能发动。那个发动无效，那张卡直接盖放。那之后，对方可以从卡组选1张陷阱卡在自身的魔法与陷阱区域盖放。这张卡的发动后，直到回合结束时对方不能把陷阱卡发动。','\r',char(13)),'\n',char(10)),'是否从卡组盖放陷阱卡？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23008320,'呼唤共鸣者','①：从卡组把1只「共鸣者」怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23015896,'炎王神兽 大鹏不死鸟','这张卡被卡的效果破坏送去墓地的场合，下次的准备阶段时这张卡从墓地特殊召唤。这个效果特殊召唤成功时，这张卡以外的场上的怪兽全部破坏。此外，这张卡被战斗破坏送去墓地时，可以从卡组把「炎王神兽 大鹏不死鸟」以外的1只名字带有「炎王」的怪兽特殊召唤。','特殊召唤','怪兽破坏','卡组特招','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23032273,'梅基拉斯之光','从眼中放出恶光给予对方损伤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23051413,'自然锹甲','这张卡进行攻击的战斗步骤时以及伤害步骤时对方把魔法·陷阱·效果怪兽的效果发动时，选择自己墓地存在的1只名字带有「自然」的怪兽才能发动。选择的怪兽从墓地特殊召唤。这个效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23064604,'冥帝 厄瑞玻斯',replace(replace('这张卡可以把1只上级召唤的怪兽解放作上级召唤。\r\n①：这张卡上级召唤成功的场合才能发动。从手卡·卡组把「帝王」魔法·陷阱卡2种类送去墓地，从对方的手卡·场上·墓地之中选1张卡回到卡组。\r\n②：这张卡在墓地的场合，1回合1次，自己·对方的主要阶段从手卡丢弃1张「帝王」魔法·陷阱卡，以自己墓地1只攻击力2400以上而守备力1000的怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'把1只上级召唤的怪兽解放作上级召唤','从手卡·卡组把「帝王」魔法·陷阱卡2种类送去墓地，从对方的手卡·场上·墓地之中选1张卡回到卡组','以自己墓地1只攻击力2400以上而守备力1000的怪兽为对象才能发动。那只怪兽加入手卡','选对方的1张手卡回到卡组','选对方场上的1张卡回到卡组','选对方墓地的1张卡回到卡组','','','','','','','','','','');
INSERT INTO texts VALUES(23068051,'幽丽的幻泷',replace(replace('①：可以从以下效果选择1个发动。\r\n●从卡组把1只幻龙族怪兽加入手卡。\r\n●从手卡以及自己场上的表侧表示怪兽之中把幻龙族怪兽任意数量送去墓地才能发动。自己从卡组抽出送去墓地的怪兽的数量＋1张。','\r',char(13)),'\n',char(10)),'卡组检索','送去墓地并抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23085002,'No.68 魔天牢 桑达尔丰',replace(replace('8星怪兽×2\r\n①：这张卡的攻击力·守备力上升双方墓地的怪兽数量×100。\r\n②：1回合1次，把这张卡1个超量素材取除才能发动。直到对方回合结束时，这张卡不会被效果破坏，双方不能把墓地的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23087070,'蔷薇之魔女','植物族怪兽上级召唤的场合，这张卡可以作为2只的数量解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23093604,'X-剑士 珀修尔',replace(replace('①：这张卡不会被战斗破坏。\r\n②：对方准备阶段发动。自己受到1000伤害。这个效果在这张卡在怪兽区域表侧守备表示存在的场合进行发动和处理。','\r',char(13)),'\n',char(10)),'自己受到1000伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23115241,'X-剑士 安娜佩瑞拉','以华丽的攻击和冷静的判断飞舞于战场的女剑士。时而冷酷的攻击令敌人畏惧。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23116808,'怨念之魂 业火',replace(replace('①：自己场上有炎属性怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：这张卡的①的方法特殊召唤成功的场合，以自己场上1只炎属性怪兽为对象发动。那只自己的炎属性怪兽破坏。\r\n③：把这张卡以外的自己场上1只炎属性怪兽解放才能发动。这张卡的攻击力直到回合结束时上升500。\r\n④：自己准备阶段发动。在自己场上把1只「火之玉衍生物」（炎族·炎·1星·攻/守100）守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'破坏','特殊召唤','攻击上升','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23116809,'火之玉衍生物','由「怨念之魂 业火」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23118924,'元素恶魔',replace(replace('这只怪兽在场上有特定的属性的怪兽存在的场合，得到以下的效果。\r\n●地属性：这张卡战斗破坏的效果怪兽的效果无效化。\r\n●风属性：这张卡战斗破坏对方怪兽的场合，只有1次可以再度攻击。','\r',char(13)),'\n',char(10)),'连续攻击','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23122036,'阴谋之盾','发动后这张卡变成装备卡，给自己场上1只怪兽装备。装备怪兽只要表侧攻击表示存在，1回合只有1次不会被战斗破坏。此外，装备怪兽的战斗发生的对自己的战斗伤害变成0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23160024,'无形阵·假面',replace(replace('①：场上的「无形噬体」怪兽的攻击力·守备力上升300。\r\n②：只要这张卡在场地区域存在，每次自己场上的「无形噬体」怪兽被解放让自己从卡组抽1张。这个效果1回合可以适用最多2次。\r\n③：把墓地的这张卡除外才能发动。从自己的手卡·场上把等级合计直到8的灵摆怪兽解放，从手卡把「虚龙魔王 无形矢·心灵」仪式召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23166823,'影灵兽使-神数文蒂',replace(replace('←7 【灵摆】 7→\r\n①：自己不是「灵兽」怪兽以及「神数」怪兽不能灵摆召唤。这个效果不会被无效化。\r\n【怪兽效果】\r\n自己对「影灵兽使-神数文蒂」1回合只能有1次特殊召唤。\r\n①：这张卡召唤·灵摆召唤成功时才能发动。从自己的额外卡组把「影灵兽使-神数文蒂」以外的1只表侧表示的「神数」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23168060,'键战士','1回合1次，自己的主要阶段时才能发动。直到这个回合的结束阶段时，这张卡的等级变成3星。','等级变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23171610,'限制解除','①：自己场上的全部机械族怪兽的攻击力直到回合结束时变成2倍。这个效果适用中的怪兽在这个回合的结束阶段破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23187256,'No.93 希望皇 霍普·真皇',replace(replace('持有超量素材的相同阶级的「No.」超量怪兽×2只以上\r\n①：1回合1次，自己主要阶段才能发动。把最多有这张卡的超量素材种类数量的9阶以下而攻击力3000以下的「No.」怪兽从额外卡组效果无效特殊召唤（相同阶级最多1只）。那之后，这张卡1个超量素材取除。这个回合，对方受到的战斗伤害变成一半，自己不能把怪兽特殊召唤。\r\n②：只要自己场上有其他的「No.」超量怪兽存在，这张卡不会被战斗·效果破坏。','\r',char(13)),'\n',char(10)),'特殊召唤','继续特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23204029,'对极英雄 混沌侠',replace(replace('「假面英雄」怪兽×2\r\n这张卡在规则上也当作「元素英雄」怪兽使用。这张卡不用融合召唤不能特殊召唤。\r\n①：只要这张卡在怪兽区域存在，这张卡的属性也当作「光」使用。\r\n②：1回合1次，以场上1张表侧表示的卡为对象才能发动。那张卡的效果直到回合结束时无效。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23205979,'削魂的死灵','这张卡不能被战斗破坏。这张卡成为魔法·陷阱·效果怪兽效果的对象时，这张卡破坏。这张卡对对方进行直接攻击成功的场合，对方随机丢弃1张手卡。','丢弃手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23212990,'六武式风雷斩',replace(replace('把自己场上的1个武士道指示物取除，从以下效果选择1个发动。\r\n●选择对方场上存在的1只怪兽破坏。\r\n●选择对方场上存在的1张卡回到手卡。','\r',char(13)),'\n',char(10)),'对方场上存在的1只怪兽破坏。','对方场上存在的1张卡回到手牌。','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23220863,'苏鲁伯的魔导骑兵',replace(replace('←3 【灵摆】 3→\r\n【怪兽效果】\r\n①：这张卡只要在怪兽区域存在，不受灵摆怪兽以外的怪兽发动的效果影响。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23232295,'燃烧拳击手 拘束蛮兵 刺拳拳手',replace(replace('名字带有「燃烧拳击手」的4星怪兽×2\r\n自己场上的名字带有「燃烧拳击手」的怪兽被战斗或者卡的效果破坏的场合，可以作为那1只破坏的怪兽的代替而把这张卡1个超量素材取除。此外，这张卡的超量素材被取除时，这张卡的攻击力上升800。','\r',char(13)),'\n',char(10)),'攻击上升','是否要使用「燃烧拳击手 拘束蛮兵 刺拳拳手」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23234094,'进化龙·双腔龙','这张卡用名字带有「进化虫」的怪兽的效果特殊召唤成功时，可以从手卡把1只恐龙族·炎属性·6星以下的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23265313,'代价降低','①：丢弃1张手卡才能发动。这个回合，自己手卡的怪兽的等级下降2星。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23265594,'强化支援机械·重装武器',replace(replace('①：1回合1次，可以从以下效果选择1个发动。\r\n●以自己场上1只机械族怪兽为对象，把这张卡当作装备卡使用给那只怪兽装备。装备怪兽被战斗·效果破坏的场合，作为代替把这张卡破坏。\r\n●装备的这张卡特殊召唤。\r\n②：装备怪兽的攻击力·守备力上升500。','\r',char(13)),'\n',char(10)),'装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23269426,'临终的同调','选择自己场上1只3星怪兽才能发动。从自己的手卡·墓地选1只和选择的怪兽同名的怪兽特殊召唤。这个效果特殊召唤的怪兽的效果无效化。这个回合的结束阶段时，选择的怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23274061,'电气红松鼠','这张卡在同1次的战斗阶段中可以作2次攻击。和这张卡进行战斗的效果怪兽的效果在伤害计算后无效化。','效果无效化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23282832,'超越界限','支付500基本分。这个回合被战斗破坏的攻击力1000以下的通常怪兽尽可能在自己场上特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23289281,'空手道家','1回合1次，自己的主要阶段时才能发动。这张卡的攻击力直到结束阶段时变成原本攻击力2倍的数值。这个效果使用的场合，这张卡在结束阶段时破坏。','攻击变化','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23296404,'点火骑士·复仇者',replace(replace('①：以自己场上3张「点火骑士」卡为对象才能发动。那些卡破坏，这张卡从手卡特殊召唤。\r\n②：1回合1次，以这张卡以外的自己场上1只「点火骑士」怪兽为对象才能发动。那张卡回到持有者手卡，选对方场上1张魔法·陷阱卡回到持有者卡组最下面。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23297235,'炎狱地狱犬','这张卡战斗破坏对方怪兽送去墓地时，可以从自己卡组把「炎狱地狱犬」以外的1只守备力200以下的炎属性怪兽在自己场上特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23299957,'交通机人连接区','①：从自己的手卡·场上把「交通机人」融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。这个效果特殊召唤的怪兽不会被效果破坏，那只怪兽的效果不会被无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23303072,'合成龙','这张卡不能通常召唤。把手卡3张怪兽卡送去墓地的场合才能特殊召唤。这张卡的攻击力变成这张卡的特殊召唤时送去墓地的怪兽的等级合计×300的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23309606,'炎狱魔人 地狱焚魔','不把除这张卡外的自己手卡全部丢弃去墓地，并用自己场上1只攻击力2000以上的怪兽作为祭品不能通常召唤。对方场上每存在1只怪兽，这张卡的攻击力上升200。自己场上这张卡以外的怪兽每存在1只，这张卡的攻击力下降500。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23314220,'冰火之魔导书',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：「冰火之魔导书」以外的自己的手卡·场上1张「魔导书」卡或者自己场上1只魔法师族怪兽送去墓地，自己从卡组抽2张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23323812,'念导力','自己场上表侧表示存在的念动力族怪兽被对方怪兽的攻击破坏的场合才能发动。那个时候进行攻击的1只对方怪兽破坏，自己基本分回复那个攻击力的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23327298,'平行选择','自己场上存在的同调怪兽被对方破坏送去墓地时，选择从游戏中除外的1张自己的魔法卡发动。选择的魔法卡加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23331400,'连接流式鸟',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：这张卡已在怪兽区域存在的状态，自己场上有电子界族怪兽召唤·特殊召唤时才能发动。在自己场上把1只「数据衍生物」（电子界族·光·1星·攻/守0）特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23331401,'数据衍生物','由「连接流式鸟」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23338098,'强袭黑羽-泪雨之千鸟刀鸟',replace(replace('调整＋调整以外的怪兽1只以上\r\n①：「黑羽」怪兽为素材作同调召唤的这张卡当作调整使用。\r\n②：这张卡的攻击力上升自己墓地的「黑羽」怪兽数量×300。\r\n③：这张卡被破坏送去墓地时，以「强袭黑羽-泪雨之千鸟刀鸟」以外的自己墓地1只鸟兽族同调怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23377694,'娱乐伙伴 房东寄居蟹',replace(replace('←2 【灵摆】 2→\r\n①：1回合1次，自己的「娱乐伙伴」怪兽被战斗破坏时，以自己的灵摆区域1张「娱乐伙伴」卡或者「异色眼」卡为对象才能发动。那张卡特殊召唤。\r\n【怪兽效果】\r\n①：1回合1次，以自己场上1只灵摆怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升自己场上的「娱乐伙伴」怪兽数量×300。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23379054,'劫火之舟守 幽鬼冥船夫',replace(replace('「劫火之舟守 幽鬼冥船夫」的效果1回合只能使用1次，这个效果发动的回合，自己不是龙族怪兽不能特殊召唤。\r\n①：对方场上有怪兽存在，自己场上没有这张卡以外的怪兽存在的场合，以自己墓地1只融合怪兽为对象才能发动。墓地的那只怪兽和场上的这张卡除外，把持有和那2只的等级合计相同等级的1只龙族同调怪兽从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23401839,'千手神','①：这张卡召唤·反转召唤成功时才能发动。从卡组把1只仪式怪兽加入手卡。','选择1张仪式怪兽卡加入自己手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23401840,'千手神','①：这张卡召唤·反转召唤成功时才能发动。从卡组把1只仪式怪兽加入手卡。','选择1张仪式怪兽卡加入自己手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23408872,'替换蛙','把自己场上存在的1只水族怪兽解放，选择自己墓地存在的1只名字带有「青蛙」的怪兽发动。选择的怪兽从墓地特殊召唤。这个效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23421244,'再生僵尸','只要自己手卡是0张，场上攻击表示存在的这张卡不会被战斗破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23424603,'荒野','全部恐龙族·不死族·岩石族的怪兽攻击力·守备力上升200。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23434538,'增殖的G',replace(replace('这个卡名的效果1回合只能使用1次，对方回合也能发动。\r\n①：把这张卡从手卡送去墓地才能发动。这个回合，以下效果适用。\r\n●每次对方对怪兽的特殊召唤成功，自己必须从卡组抽1张。','\r',char(13)),'\n',char(10)),'对方特殊召唤时抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23440062,'邪恶苏生','支付800基本分，选择自己墓地存在的1只同调怪兽发动。选择的怪兽表侧攻击表示特殊召唤。这个效果特殊召唤的怪兽的效果无效化，这个回合不能攻击宣言。这张卡不在场上存在时，那只怪兽破坏。那只怪兽破坏时这张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23440231,'狱火机·莉莉丝',replace(replace('这张卡不能通常召唤。自己场上的全部效果怪兽的等级·阶级的合计是8以下时，从自己的手卡·墓地把3只「狱火机」怪兽除外的场合才能从手卡·墓地特殊召唤。\r\n①：这张卡特殊召唤成功时才能发动。「炼狱」卡以外的场上的魔法·陷阱卡全部破坏。\r\n②：1回合1次，这张卡以外的怪兽的效果发动时，把自己场上1只怪兽解放才能发动。那个发动无效并除外。','\r',char(13)),'\n',char(10)),'效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23454876,'混沌超量 暗黑妖精啦啦队少女',replace(replace('5星怪兽×3\r\n这张卡从场上送去墓地时，从卡组抽1张卡。此外，这张卡有「妖精啦啦队少女」在作为超量素材的场合，得到以下效果。\r\n●这张卡战斗破坏对方怪兽时，把这张卡1个超量素材取除才能发动。给与对方基本分自己手卡数量×400的数值的伤害。','\r',char(13)),'\n',char(10)),'抽卡','LP伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23471572,'所罗门的律法书','下次的自己的准备阶段跳过。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23516703,'召唤限制器','双方对怪兽的召唤·反转召唤·特殊召唤1回合只能进行合计最多2次。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23535429,'牙城守护者','自己场上守备表示存在的怪兽被攻击的场合，那次伤害步骤时可以把这张卡从手卡送去墓地，进行那次战斗的自己怪兽的守备力直到结束阶段时上升1500。','守备上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23536866,'间歇泉鲨','把墓地的这张卡从游戏中除外，选择「间歇泉鲨」以外的自己墓地2只水属性·5星怪兽才能发动。选择的2只怪兽的效果无效特殊召唤，只用那2只为素材把1只水属性的超量怪兽超量召唤。「间歇泉鲨」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23545031,'水精鳞-萨拉深渊后',replace(replace('鱼族·海龙族·水族怪兽2只\r\n这个卡名的②③的效果1回合各能使用1次。\r\n①：这张卡所连接区的怪兽的攻击力·守备力上升500。\r\n②：对方回合把1张手卡送去墓地才能发动。从卡组把1只「水精鳞」怪兽加入手卡。\r\n③：这张卡被对方怪兽的攻击或者对方的效果破坏的场合，从卡组把1只水属性怪兽送去墓地，以自己墓地1只水属性怪兽为对象才能发动。那只怪兽守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23557835,'次元融合','支付2000基本分。双方将各自被除外的怪兽尽可能特殊召唤上场。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23558733,'凤凰石蒜花','这张卡不用「凤凰花种」或者这张卡的效果不能特殊召唤。这张卡攻击的场合，伤害计算后破坏。自己场上存在的这张卡被破坏送去墓地时，给与对方基本分800分伤害。自己的结束阶段时这张卡在墓地存在的场合，可以把自己墓地存在的1只植物族怪兽从游戏中除外，这张卡从墓地守备表示特殊召唤。','对于对方800伤害','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23562407,'圣剑 石中剑','战士族怪兽才能装备。装备怪兽的攻击力上升500。此外，1回合1次，自己可以回复500基本分。场上表侧表示存在的这张卡被破坏送去墓地的场合，可以选择自己场上1只名字带有「圣骑士」的战士族怪兽把这张卡装备。「圣剑 石中剑」的这个效果1回合只能使用1次。此外，「圣剑 石中剑」在自己场上只能有1张表侧表示存在。','回复LP','装备','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23571046,'螺丝刺猬','①：自己主要阶段才能发动。这张卡从墓地特殊召唤。这个效果在自己场上有调整存在的场合才能发动和处理。这个效果特殊召唤的这张卡从场上离开的场合除外。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23581825,'升阶魔法-削魂之力','①：把基本分支付一半，以自己墓地1只「急袭猛禽」超量怪兽为对象才能发动。那只怪兽特殊召唤，比那只怪兽阶级高2阶的1只超量怪兽在作为对象的怪兽上面重叠当作超量召唤从额外卡组特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23587624,'青天霹雳','①：对方场上有怪兽存在，自己场上没有怪兽存在的场合才能发动。把1只原本等级是10星以下的不能通常召唤的怪兽无视召唤条件从手卡特殊召唤。这个效果特殊召唤的怪兽不受那只怪兽以外的自己的卡的效果影响，下次的对方结束阶段回到持有者卡组。这个回合，自己不能把怪兽通常召唤·特殊召唤，对方受到的全部伤害变成0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23603403,'急袭猛禽-卫星炮猎鹰',replace(replace('鸟兽族8星怪兽×2\r\n①：这张卡用「急袭猛禽」怪兽为素材作超量召唤成功的场合才能发动。对方场上的魔法·陷阱卡全部破坏。对方不能对应这个效果的发动把魔法·陷阱·怪兽的效果发动。\r\n②：把这张卡1个超量素材取除，以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力下降自己墓地的「急袭猛禽」怪兽数量×800。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'魔陷破坏','攻击力下降','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23615409,'除虫网','对方场上存在的全部昆虫族的怪兽不能宣言攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23626223,'苦纹样的土像',replace(replace('①：这张卡发动后变成效果怪兽（岩石族·地·7星·攻0/守2500）在怪兽区域特殊召唤。这张卡也当作陷阱卡使用。\r\n②：只要这张卡以外的当作怪兽使用的陷阱卡在怪兽区域存在，这张卡的效果特殊召唤的这张卡不会成为对方的效果的对象。\r\n③：这张卡的效果特殊召唤的这张卡存在，自己的魔法与陷阱区域的卡在怪兽区域特殊召唤的场合，以场上1张卡为对象才能发动。那张卡破坏。','\r',char(13)),'\n',char(10)),'卡片破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23635815,'软糖豆人','名叫软糖的豆战士。虽坚信自己就是世界最强的战士，其实力不得而知。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23639291,'攻击性云魔物','自己场上存在的名字带有「云魔物」的怪兽被自身的效果破坏送去墓地时才能发动。从自己墓地把那1只怪兽攻击表示特殊召唤，并给那只怪兽放置1个雾指示物。这个效果特殊召唤的怪兽不会被卡的效果变成守备表示。','「攻击性云魔物」效果适用中','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23649496,'No.18 纹章祖 素徽',replace(replace('4星怪兽×2\r\n场上有同名怪兽2只以上存在的场合，把这张卡1个超量素材取除才能发动。选那些同名怪兽之内的1只，那以外的同名怪兽全部破坏。这个效果在对方回合也能发动。只要这张卡在场上表侧表示存在，对方不能把和这个效果选的怪兽同名的怪兽召唤·反转召唤·特殊召唤。此外，这张卡被送去墓地的场合，可以从卡组把2只名字带有「纹章兽」的怪兽送去墓地。','\r',char(13)),'\n',char(10)),'破坏','送墓','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23659124,'海之龙王','海之王。长有坚硬的贝壳，从口中吐出水泡攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23681456,'魔偶甜点佳节','从手卡把名字带有「魔偶甜点」的怪兽任意数量特殊召唤。这个效果特殊召唤的怪兽在结束阶段时回到持有者卡组。','「魔偶甜点佳节」效果适用中','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23689697,'冻冰帝 美比乌斯',replace(replace('这张卡可以把1只上级召唤的怪兽解放作上级召唤。这张卡上级召唤成功时，可以选择场上最多3张魔法·陷阱卡破坏。这张卡把水属性怪兽解放作上级召唤成功的场合，那个时候的效果加上以下效果。\r\n●对方不能对应这个效果的发动把选择的卡发动。','\r',char(13)),'\n',char(10)),'把1只上级召唤的怪兽解放进行上级召唤','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23693634,'巨人斗士',replace(replace('调整＋调整以外的怪兽1只以上\r\n这张卡的攻击力上升双方墓地的战士族怪兽数量×100的数值。这张卡被战斗破坏送去墓地时，可以选择自己或者对方的墓地1只战士族怪兽在自己场上特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23701465,'原初之种','「混沌战士 -开辟的使者-」或「混沌帝龙 -终焉的使者-」在场上存在的场合，这张卡才能发动。将自己2张从游戏中除外的卡加入自己手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23740893,'妖仙兽 木魅',replace(replace('「妖仙兽 木魅」的①的效果1回合只能使用1次。\r\n①：把这张卡解放，以自己场上1张「修验的妖社」为对象才能发动。给那张卡放置3个妖仙指示物。\r\n②：自己主要阶段把墓地的这张卡除外才能发动。这个回合，自己在通常召唤外加上只有1次，自己主要阶段可以把1只「妖仙兽」怪兽召唤。','\r',char(13)),'\n',char(10)),'放置指示物','再次通常召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23756165,'魅惑的女王 LV5','用「魅惑的女王 LV3」的效果特殊召唤的场合，1回合只有1次在自己的主要阶段时可以选择对方场上的1只5星以下的怪兽，当作装备卡使用装备在这张卡上（这个效果可以装备的装备卡最多1张）。这张卡被战斗破坏的场合，装备的怪兽代替破坏。自己回合的准备阶段时，可以把因这个效果装备有装备卡的这张卡送去墓地，从手卡·卡组特殊召唤1只「魅惑的女王 LV7」。','装备','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23770284,'强风龙',replace(replace('①：把1只龙族怪兽解放对这张卡的上级召唤成功的场合发动。这张卡的攻击力上升解放的那只怪兽的原本攻击力一半数值。\r\n②：这张卡不会被和相同攻击力的怪兽的战斗破坏。\r\n③：这张卡向守备表示怪兽攻击的场合，给与攻击力超过那个守备力的数值的战斗伤害。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23771716,'彩虹鱼','非常珍奇的七色鱼，要捕捉它非常困难。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23776077,'阳炎兽 巴西利科克',replace(replace('炎属性6星怪兽×2只以上（最多5只）\r\n1回合1次，把这张卡1个超量素材取除才能发动。选择对方的场上·墓地1只怪兽从游戏中除外。此外，这张卡持有的超量素材数量让这张卡得到以下效果。\r\n●3个以上：这张卡的攻击力·守备力上升这张卡的超量素材数量×200的数值。\r\n●4个以上：这张卡不会成为对方的卡的效果的对象。\r\n●5个：这张卡不会被卡的效果破坏。','\r',char(13)),'\n',char(10)),'除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23782705,'机甲狙击兵','只要这张卡在场上表侧表示存在，「机甲狙击兵」以外的名字带有「机甲」的怪兽不能被攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23784496,'魔界台本「开幕式」',replace(replace('「魔界台本「开幕式」」的①②的效果1回合各能使用1次。\r\n①：自己回复自己场上的「魔界剧团」怪兽数量×500基本分。\r\n②：自己的额外卡组有表侧表示的「魔界剧团」灵摆怪兽存在，盖放的这张卡被对方的效果破坏的场合才能发动。自己直到手卡变成5张为止从卡组抽卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23842445,'硝化装置','对方场上的怪兽才能装备。装备怪兽被战斗破坏送去墓地时，给与对方基本分装备怪兽攻击力数值的伤害。','LP伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23846921,'秘仪之力21-世界',replace(replace('这张卡召唤·反转召唤·特殊召唤成功时，进行1次投掷硬币得到以下效果。\r\n●表：自己的结束阶段时可以把自己场上存在的2只怪兽送去墓地让下次的对方回合跳过。\r\n●里：对方的抽卡阶段时把对方墓地最上面1张卡加入对方手卡。','\r',char(13)),'\n',char(10)),'投掷硬币','跳过对方回合','回收','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23850421,'劫持翼龙',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：把自己场上1只机械族怪兽和这张卡除外，以自己墓地1只暗属性怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23851033,'异色眼重力龙',replace(replace('「异色眼降临」降临。「异色眼重力龙」的①的效果1回合只能使用1次。\r\n①：这张卡特殊召唤成功时才能发动。对方场上的魔法·陷阱卡全部回到持有者手卡。对方不能对应这个效果的发动把魔法·陷阱·怪兽的效果发动。\r\n②：只要这张卡在怪兽区域存在，对方若不支付500基本分则不能把卡的效果发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23857661,'武神器-冲津','把墓地的这张卡从游戏中除外，从手卡把1只名字带有「武神」的怪兽送去墓地才能发动。这个回合，自己受到的全部伤害变成0。这个效果在对方回合也能发动。','伤害变成0','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23869735,'化石发掘',replace(replace('①：丢弃1张手卡，以自己墓地1只恐龙族怪兽为对象才能把这张卡发动。那只恐龙族怪兽特殊召唤。\r\n②：这张卡的①的效果特殊召唤的怪兽只要这张卡在魔法与陷阱区域存在效果无效化，这张卡从场上离开时破坏。那只怪兽破坏时这张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23874409,'机巧将军 无零',replace(replace('调整＋调整以外的机械族怪兽1只以上\r\n这张卡同调召唤成功时，可以从自己卡组把1只名字带有「机巧」的怪兽特殊召唤。1回合1次，可以选择场上存在的1只怪兽，把表示形式变更。','\r',char(13)),'\n',char(10)),'特殊召唤','改变表示形式','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23893227,'电子龙核',replace(replace('这个卡名的②③的效果1回合只能有1次使用其中任意1个。\r\n①：这张卡的卡名只要在场上·墓地存在当作「电子龙」使用。\r\n②：这张卡召唤成功的场合发动。从卡组把1张「电子」魔法·陷阱卡或者「电子科技」魔法·陷阱卡加入手卡。\r\n③：只有对方场上才有怪兽存在的场合，把墓地的这张卡除外才能发动。从卡组把1只「电子龙」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'检索','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23898021,'恶魔娘 莉莉丝',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：召唤的这张卡的原本攻击力变成1000。\r\n②：把自己场上1只暗属性怪兽解放才能发动。从卡组把3张通常陷阱卡给对方观看，对方从那之中随机选1张。那1张卡在自己场上盖放，剩下的卡回到卡组。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23899727,'水精鳞-深渊琳德','场上的这张卡被破坏送去墓地的场合，可以从卡组把「水精鳞-深渊琳德」以外的1只名字带有「水精鳞」的怪兽特殊召唤。「水精鳞-深渊琳德」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23915499,'漏洞人Y','这张卡召唤成功时，自己场上有「漏洞人Z」表侧表示存在的场合，可以从自己卡组把1只「漏洞人X」特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23924608,'沙尘之大风暴',replace(replace('这张卡发动的回合，自己不能进行战斗阶段。\r\n①：以场上最多2张魔法·陷阱卡为对象才能发动。那些卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23927545,'朱罗纪原角龙','这张卡的攻击力上升对方场上存在的怪兽数量×100的数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23927567,'告知幸运的白鸮','反转：从卡组中选择1张场地魔法卡，放在自己卡组最上面。当「王家长眠之谷」在场上存在时，可以将选择的场地魔法卡加入手卡。','是否要加入手牌？','请选择一张场地魔法卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23950192,'冰结界的术者','只要这张卡以外的名字带有「冰结界」的怪兽在自己场上存在，全部4星以上的怪兽不能攻击宣言。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23965037,'树精的祈祷','「精灵术师 树精」降临必要。必须把场上·手卡合计3星以上的卡作为祭品。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23971061,'双字节龙',replace(replace('连接怪兽2只\r\n①：这张卡的攻击力上升作为这张卡的连接素材的怪兽的连接标记合计×300。\r\n②：这张卡只要在怪兽区域存在，不受连接怪兽以外的怪兽的效果影响，不会被和连接怪兽以外的怪兽的战斗破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23979249,'武神-荒樔田','自己的场上·墓地的名字带有「武神」的怪兽从游戏中除外的场合，这张卡可以从手卡表侧守备表示特殊召唤。此外，这张卡在场上表侧表示存在，名字带有「武神」的卡用抽卡以外的方法从自己卡组加入手卡的场合，那个回合的结束阶段时才能发动1次。从卡组抽1张卡，那之后选1张手卡丢弃。「武神-荒樔田」在自己场上只能有1只表侧表示存在。','特殊召唤','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23995346,'青眼究极龙','「青眼白龙」＋「青眼白龙」＋「青眼白龙」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23995347,'青眼究极龙','「青眼白龙」＋「青眼白龙」＋「青眼白龙」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(23998625,'No.53 伪骸神 心地心',replace(replace('5星怪兽×3\r\n1回合1次，这张卡被选择作为攻击对象时，这张卡的攻击力直到结束阶段时上升那只攻击怪兽的原本攻击力数值。场上的这张卡被破坏的场合，可以作为代替把这张卡1个超量素材取除。没有超量素材的这张卡被卡的效果破坏时，墓地的这张卡作为超量素材，从额外卡组把1只「No.92 伪骸神龙 心地心龙」当作超量召唤作特殊召唤。','\r',char(13)),'\n',char(10)),'攻击上升','特殊召唤','是否要使用「No.53 伪骸神 心地心」的效果代替破坏？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24010609,'闪刀机关-多任务战刀机',replace(replace('①：1回合1次，以这张卡以外的自己场上1张卡为对象才能发动。这个回合，对方不能对应自己的魔法卡的发动把魔法·陷阱·怪兽的效果发动。并且，再让作为对象的卡送去墓地。\r\n②：自己·对方的结束阶段才能发动。选最多有这个回合这张卡表侧表示存在期间自己发动的「闪刀」魔法卡数量的自己墓地的「闪刀」魔法卡在自己场上盖放（同名卡最多1张）。这个效果盖放的卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','是否继续选择？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24019092,'点火骑士·鸟铳',replace(replace('←2 【灵摆】 2→\r\n①：另一边的自己的灵摆区域有「点火骑士」卡存在的场合才能发动。自己的灵摆区域的卡全部破坏，从自己的卡组·墓地选1只战士族·炎属性怪兽加入手卡。\r\n【怪兽描述】\r\n以冷静沉着且理智出名的点火骑士参谋。其实只是头脑发热要点时间，心中好像总是快发火的样子。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24019261,'救急救命','主要阶段2才能发动。这个回合被卡的效果破坏送去墓地的1只4星的怪兽从自己墓地特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24025620,'双盾防护者','自己场上存在的这张卡被战斗破坏送去墓地时，对方场上表侧表示存在的1只怪兽的攻击力直到结束阶段时变成一半数值。','攻击变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24037702,'暗之进军',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：以自己墓地1只「光道」怪兽为对象才能发动。那只怪兽加入手卡。那之后，把加入手卡的那只怪兽的原本等级数量的卡从自己卡组上面除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24040093,'魔轰神 葛琳萝','自己场上有名字带有「魔轰神」的怪兽表侧表示存在的场合，可以从手卡把这张卡送去墓地从自己卡组把「魔轰神 葛琳萝」以外的1只名字带有「魔轰神」的怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24062258,'暗跃的德鲁伊·橡木','这张卡召唤成功时，可以从自己墓地选择「暗跃的德鲁伊·橡木」以外的1只攻击力或者守备力是0的暗属性·4星怪兽表侧守备表示特殊召唤。这个效果特殊召唤的怪兽的效果无效化。「暗跃的德鲁伊·橡木」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24068492,'自业自得','对方场上每存在1只怪兽对方受到500基本分伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24073068,'刚鬼 过肩摔霸王龙',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：这张卡召唤成功时才能发动。从手卡把1只「刚鬼」怪兽特殊召唤。\r\n②：这张卡从场上送去墓地的场合才能发动。从卡组把「刚鬼 过肩摔霸王龙」以外的1张「刚鬼」卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24082387,'麦田圈','把自己场上任意数量的怪兽送去墓地发动。从自己卡组选择1只送去墓地的怪兽的合计等级的名字带有「外星」的怪兽特殊召唤。召唤失败的场合，自己受到2000分的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24094258,'刚炼装勇士·银金公主',replace(replace('灵摆怪兽2只\r\n这个卡名的③的效果1回合只能使用1次。\r\n①：这张卡连接召唤成功的场合才能发动。从卡组选1只灵摆怪兽表侧表示加入自己的额外卡组。\r\n②：1回合1次，以这张卡以外的自己场上1张表侧表示的卡为对象才能发动。那张卡破坏。那之后，从自己的额外卡组把1只表侧表示的灵摆怪兽加入手卡。\r\n③：自己的灵摆区域的卡从场上离开的场合发动。自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'卡组灵摆怪兽加入额外卡组','破坏并从额外卡组把灵摆怪兽加入手卡','抽卡','请选择要表侧表示加入额外卡组的卡','','','','','','','','','','','','');
INSERT INTO texts VALUES(24094653,'融合','①：从自己的手卡·场上把融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24094654,'融合','①：从自己的手卡·场上把融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24096228,'二重魔法','从手卡丢弃1张魔法卡。从对方墓地里选择1张魔法卡当作自己的卡使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24096499,'森之圣兽 缬草小鹿',replace(replace('「森之圣兽 缬草小鹿」的效果1回合只能使用1次。\r\n①：丢弃1张手卡，以「森之圣兽 缬草小鹿」以外的自己墓地1只2星以下的兽族怪兽为对象才能发动。那只怪兽表侧攻击表示或者里侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24101897,'鬼计猫娘','自己场上有名字带有「鬼计」的怪兽存在的场合才能让这张卡表侧表示召唤。这张卡1回合只有1次可以变成里侧守备表示。此外，只要场上有这张卡以外的名字带有「鬼计」的怪兽存在，4星以上的怪兽召唤·特殊召唤成功时，那些怪兽变成里侧守备表示。','变成里侧守备','变成里侧守备','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24103628,'幻影魔术士','这张卡被战斗破坏送去墓地时，可以从自己卡组把1只攻击力1000以下的名字带有「英雄」的怪兽表侧守备表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24104865,'外星人母后','这张卡战斗破坏放置有A指示物的怪兽送去墓地时，破坏怪兽战斗阶段结束时在自己场上特殊召唤。这个方法特殊召唤的怪兽在这张卡从场上离开时全部破坏。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24128274,'深海战士','只要「海」在场上存在，这张卡不会受到魔法的效果。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24131534,'点火骑士·马格努姆',replace(replace('←7 【灵摆】 7→\r\n①：另一边的自己的灵摆区域有「点火骑士」卡存在的场合才能发动。自己的灵摆区域的卡全部破坏，从自己的卡组·墓地选1只战士族·炎属性怪兽加入手卡。\r\n【怪兽描述】\r\n操纵剑枪的火焰战士。虽然被冰冷的钢铁铠甲包住了身体，那里头却藏着激烈燃烧般火热的心。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24137081,'钻头藤壶','这张卡可以直接攻击对方玩家。每次这张卡直接攻击给与对方基本分战斗伤害，这张卡的攻击力上升1000。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24140059,'告知不幸的黑猫','①：这张卡反转的场合发动。从卡组选1张陷阱卡在卡组最上面放置。「王家长眠之谷」在场上存在的场合，那张陷阱卡可以作为在卡组最上面放置的代替而加入手卡。','是否要加入手牌？','请选择一张陷阱卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24150026,'机巧无双 八一八','这张卡可以攻击的场合必须作出攻击。场上表侧攻击表示存在的这张卡被选择作为攻击对象时，这张卡的表示形式变成守备表示。这张卡攻击的场合，战斗阶段结束时变成守备表示。','变成守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24154052,'原型机灵','电子空间找到的原种。它的信息量是未知数。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24184846,'燃烧拳击手 内伸臂挡拳手','把手卡或者墓地的这张卡从游戏中除外，选择自己场上1只名字带有「燃烧拳击手」的怪兽才能发动。选择的怪兽直到下次的自己的准备阶段时从游戏中除外。这个效果在对方回合也能发动。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24194033,'泥浆兽','粘呼呼的令人感到恶心的怪兽。吐出剧毒的瓦斯进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24207889,'千查万别','①：只要这张卡在魔法与陷阱区域存在，相同种族的怪兽在双方场上各自只能有1只表侧表示存在。双方玩家在自身场上有相同种族的怪兽2只以上存在的场合，直到相同种族的怪兽变成1只为止必须送去墓地。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24212820,'幻影骑士团 暗黑手甲',replace(replace('①：从卡组把1张「幻影」魔法·陷阱卡送去墓地。\r\n②：自己场上没有卡存在的场合，对方怪兽的直接攻击宣言时才能在墓地发动。这张卡变成效果怪兽（战士族·暗·4星·攻300/守600）在怪兽区域守备表示特殊召唤（不当作陷阱卡使用）。这个效果特殊召唤的这张卡从场上离开的场合除外。\r\n③：这张卡的效果特殊召唤的这张卡的守备力上升自己墓地的「幻影」魔法·陷阱卡数量×300。','\r',char(13)),'\n',char(10)),'这张卡变成效果怪兽特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24218047,'破面龙','①：这张卡被战斗破坏送去墓地时才能发动。从卡组把1只守备力1500以下的幻龙族怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24221739,'神殿守卫者','当这张卡在场上以表侧表示存在时，对方不能在抽卡阶段以外进行抽卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24221808,'精神超载恶魔',replace(replace('念动力族调整＋调整以外的念动力族怪兽2只以上\r\n1回合1次，可以选择自己墓地存在的1只念动力族怪兽从游戏中除外。这张卡从场上送去墓地时，这张卡的效果除外的怪兽尽可能在自己场上特殊召唤。','\r',char(13)),'\n',char(10)),'除外','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24224830,'墓穴的指名者','①：以对方墓地1只怪兽为对象才能发动。那只怪兽除外。直到下个回合的结束时，这个效果除外的怪兽以及原本卡名和那只怪兽相同的怪兽的效果无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24268052,'护卫防壁','对方回合的战斗伤害计算时才能发动。那次战斗发生的对自己的战斗伤害变成0，从自己卡组抽1张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24285858,'剑斗归还','墓地存在的3张名字带有「剑斗兽」的卡回到卡组。那之后，从自己卡组抽1张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24291651,'加把劲骑士','场上表侧攻击表示存在的这张卡被选择作为攻击对象时，可以把这张卡的表示形式变成守备表示。','变成守备表示','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24294108,'燃烧的大地','破坏全部的场地卡。这张卡存在的每次准备阶段，那个回合的游戏者的基本分受到500的伤害。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24311372,'恶魔 佐亚','据说会通过金属化来发挥真正力量的怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24311595,'救援机人','只要这张卡在自己场上表侧表示存在，自己场上存在的名字带有「机人」的怪兽被战斗破坏送去墓地时，可以使那只怪兽回到手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24317029,'守墓的侦察者','反转：从自己的卡组中特殊召唤1张攻击力1500以下名称中带有「守墓」的怪兽卡。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24326617,'冰帝家臣 埃舍尔','①：对方的魔法与陷阱区域有卡2张以上存在的场合，这张卡可以从手卡特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24348204,'魅惑的怪盗','披着黑色披风装模作样的怪盗。挥着手杖迷惑对方。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24348804,'休息一回',replace(replace('特殊召唤的怪兽不在自己场上存在的场合才能把这张卡发动。\r\n①：只要这张卡在魔法与陷阱区域存在，特殊召唤的怪兽直到那个回合结束时效果无效化。\r\n②：效果怪兽攻击表示特殊召唤的场合把这个效果发动。那些怪兽变成守备表示。','\r',char(13)),'\n',char(10)),'是否使用效果？','发动同时使用效果','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24361622,'天球之圣刻印',replace(replace('龙族怪兽2只\r\n这个卡名的②的效果1回合只能使用1次。\r\n①：对方回合1次，这张卡在额外怪兽区域存在的场合，把自己的手卡·场上1只怪兽解放才能发动。选场上1张表侧表示的卡回到持有者手卡。\r\n②：这张卡被解放的场合发动。从手卡·卡组选1只龙族怪兽，攻击力·守备力变成0特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24362891,'突然进化','把自己场上1只爬虫族怪兽解放才能发动。从卡组把1只名字带有「进化龙」的怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24382602,'光之灵堂',replace(replace('①：只要这张卡在场地区域存在，自己在通常召唤外加上只有1次，自己主要阶段可以把1只光属性·1星调整召唤。\r\n②：1回合1次，以自己场上1只表侧表示怪兽为对象才能发动。从手卡·卡组把1只通常怪兽送去墓地。作为对象的怪兽的攻击力·守备力直到回合结束时上升送去墓地的怪兽的等级×100。\r\n③：把墓地的这张卡除外才能发动。从卡组把1张「毁灭之爆裂疾风弹」加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24384095,'九蛇孔雀','场上的这张卡被解放送去墓地的场合，可以从自己的卡组·墓地选「九蛇孔雀」以外的1只4星以下的风属性怪兽加入手卡。「九蛇孔雀」的效果1回合只能使用1次。','加入手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24413299,'镇压冥王星',replace(replace('①：1回合1次，宣言1个卡名才能发动。对方手卡全部确认，那之中有宣言的卡的场合，从以下效果选1个适用。\r\n●选对方场上1只怪兽得到控制权。\r\n●选对方场上1张魔法·陷阱卡破坏。那之后，可以把破坏的那张魔法·陷阱卡在自己场上盖放。','\r',char(13)),'\n',char(10)),'宣言卡名','得到控制权','魔法·陷阱卡破坏','是否在自己场上盖放？','','','','','','','','','','','','');
INSERT INTO texts VALUES(24419823,'矿场钻机','①：表侧表示的这张卡从自己场上离开时，以自己墓地1张场地魔法卡为对象才能发动。那张卡加入手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24425055,'诡雷陷阱E','①：丢弃1张手卡才能发动。选自己的手卡·墓地1张永续陷阱卡在自己场上盖放。这个效果盖放的卡在盖放的回合也能发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24429467,'魔导变换',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：只要这张卡在魔法与陷阱区域存在，每次对方怪兽的效果发动，给这张卡放置1个魔力指示物。\r\n②：把有魔力指示物6个以上放置的这张卡送去墓地才能发动。从卡组把1张魔法卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24432029,'次世代鼓风人','这张卡特殊召唤成功时，可以从自己卡组把1只名字带有「次世代」的暗属性怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24433920,'单摆刃拷问机械','大型单摆刀刃把对方劈成两半！很恐怖的拷问机械。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24433921,'单摆刃拷问机械','大型单摆刀刃把对方劈成两半！很恐怖的拷问机械。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24435369,'人鱼骑士','当场上存在「海」时，这张卡在同1个战斗阶段中可以进行2次攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24449083,'天法院',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：自己主要阶段才能发动。从手卡把1只天使族怪兽特殊召唤。这个效果在自己场上有1星天使族怪兽存在的场合才能发动和处理。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24484270,'宝石骑士·幽晶原核',replace(replace('「宝石」怪兽2只\r\n这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡连接召唤成功的场合才能发动。从卡组把1张「宝石骑士」卡加入手卡。\r\n②：支付1000基本分才能发动。从自己墓地的卡以及除外的自己的卡之中让「宝石骑士」融合怪兽卡决定的融合素材怪兽回到卡组，把那1只融合怪兽从额外卡组融合召唤。这个效果特殊召唤的怪兽在这个回合不能直接攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24508238,'D.D.乌鸦','①：把这张卡从手卡丢弃去墓地，以对方墓地1张卡为对象才能发动。那张卡除外。这个效果在对方回合也能发动。','对方墓地1张卡除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24530661,'达人僵尸','一直在寻找强大对方的僵尸。传说是一个武术高手。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24545464,'同调体反射','自己场上表侧表示存在的同调怪兽成为攻击对象时才能发动。那个攻击无效，对方场上存在的1只怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24550676,'月光舞狮子姬',replace(replace('「月光舞豹姬」＋「月光」怪兽×2\r\n这张卡用以上记的卡为融合素材的融合召唤才能特殊召唤。\r\n①：这张卡不会成为对方的效果的对象，不会被对方的效果破坏。\r\n②：这张卡在同1次的战斗阶段中可以作2次攻击。\r\n③：1回合1次，这张卡向怪兽攻击的伤害步骤结束时才能发动。对方场上的特殊召唤的怪兽全部破坏。','\r',char(13)),'\n',char(10)),'特殊召唤的怪兽全部破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24566654,'深红狱炎弹','自己场上有「红莲魔龙」表侧表示存在，对方把给与伤害的魔法·陷阱卡发动时才能发动。作为自己的代替让对方受到那个效果伤害2倍数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24573625,'文具电子人008',replace(replace('←1 【灵摆】 1→\r\n①：自己不是「文具电子人」怪兽不能灵摆召唤。这个效果不会被无效化。\r\n【怪兽效果】\r\n①：这张卡的攻击力上升自己墓地的「文具电子人」卡数量×500。\r\n②：只要这张卡在怪兽区域存在，对方不能把其他的「文具电子人」卡作为效果的对象。\r\n③：这张卡在同1次的战斗阶段中最多2次可以向怪兽攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24590232,'王魂调和',replace(replace('①：对方怪兽的直接攻击宣言时才能发动。那次攻击无效。那之后，以下效果可以适用。\r\n●等级合计最多到8以下为止，从自己墓地选调整1只和调整以外的怪兽任意数量除外，把持有和除外的怪兽的等级合计相同等级的1只同调怪兽从额外卡组当作同调召唤作特殊召唤。','\r',char(13)),'\n',char(10)),'是否要把同调怪兽特殊召唤？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24610207,'画星宝宝',replace(replace('这张卡在超量召唤使用的场合，这张卡可以当作5星怪兽使用。此外，这张卡为素材的超量怪兽得到以下效果。\r\n●这次超量召唤成功时，从卡组抽1张卡。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24611934,'强化石像怪','取得暗黑之力强化而成的石像鬼，尖锐的爪子值得警惕。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24621460,'机巧小町 二二四','这张卡可以攻击的场合必须作出攻击。场上表侧表示存在的这张卡被选择作为攻击对象时，这张卡的表示形式变更。只要这张卡在场上表侧表示存在，自己的主要阶段时只有1次，自己在通常召唤外加上可以把1只名字带有「机巧」的怪兽召唤。','表示形式变更','「机巧小町 二二四」的效果额外的召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24623598,'失落','选择对方墓地的1张卡从游戏中除外。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24643836,'扰乱魔术','①：这张卡从手卡·场上送去墓地的场合发动。从卡组把「扰乱·绿」「扰乱·黄」「扰乱·黑」各1只加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24644634,'自然白橡树','这张卡成为对方的卡的效果的对象时才能发动。把自己场上表侧表示存在的这张卡送去墓地，从自己卡组把2只4星以下的名字带有「自然」的怪兽特殊召唤。这个效果特殊召唤的怪兽不能攻击宣言，自己的结束阶段时破坏。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24658418,'银河暴龙','自己场上的名字带有「银河」的怪兽被选择作为攻击对象时才能发动。这张卡从手卡表侧守备表示特殊召唤。这个效果特殊召唤成功时，可以只用自己场上的名字带有「银河」的怪兽为素材，把1只名字带有「银河」的超量怪兽超量召唤。','特殊召唤','超量召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24661486,'冰寒魔法师','可以丢弃1张手卡，给场上表侧表示存在的1只怪兽放置1个冰指示物。这张卡的攻击力上升场上存在的冰指示物数量×300的数值。','放置指示物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24668830,'细菌感染','机械族以外的怪兽装备可能。装备怪兽的攻击力在每次的自己的准备阶段攻击力下降300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24672164,'幻奏的华歌圣 花之普莉玛',replace(replace('「幻奏的音姬」怪兽＋「幻奏」怪兽1只以上\r\n①：这张卡的攻击力上升作为这张卡的融合素材的怪兽数量×300。\r\n②：这张卡在同1次的战斗阶段中可以作2次攻击。\r\n③：融合召唤的这张卡被送去墓地的场合，以自己墓地1只「幻奏」怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'加入手卡','是否继续选择融合素材？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24673894,'改变命运',replace(replace('对方怪兽的攻击宣言时才能发动。把1只对方怪兽的攻击无效，那只怪兽变成守备表示。那只怪兽只要在场上表侧表示存在，变成不能把表示形式变更。那之后，对方从以下效果选择1个适用。\r\n●自己基本分回复用这张卡的效果把攻击无效的怪兽的攻击力一半的数值。\r\n●给与对方基本分用这张卡的效果把攻击无效的怪兽的攻击力一半数值的伤害。','\r',char(13)),'\n',char(10)),'自己基本分回复攻击无效的怪兽的攻击力一半的数值。','给予对方基本分攻击无效的怪兽的攻击力一半数值的伤害。','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24694698,'自然栗子','这张卡召唤成功时，可以从自己卡组把1只名字带有「自然」的怪兽送去墓地。此外，1回合1次，可以选择自己墓地存在的2只名字带有「自然」的怪兽回到卡组，从自己卡组抽1张卡。','送去墓地','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24696097,'流星龙',replace(replace('同调怪兽调整1只＋「星尘龙」\r\n①：1回合1次，可以发动。从自己卡组上面翻开5张并回到卡组。这个回合这张卡可以作出最多有所翻开之中的调整数量的攻击。\r\n②：1回合1次，要让场上的卡破坏的效果的发动时才能发动。那个效果无效并破坏。\r\n③：1回合1次，对方的攻击宣言时以攻击怪兽为对象才能发动。场上的这张卡除外，那次攻击无效。\r\n④：这个③的效果除外的回合的结束阶段发动。这张卡特殊召唤。','\r',char(13)),'\n',char(10)),'多重攻击','把卡破坏的效果无效并破坏','无效攻击','特殊召唤','','','','','','','','','','','','');
INSERT INTO texts VALUES(24701235,'和魂','这张卡不能特殊召唤。召唤·反转的回合的结束阶段时回到持有者手卡。这张卡召唤·反转的回合，自己在通常召唤外加上只有1次可以把1只灵魂怪兽召唤。此外，这张卡被送去墓地时，自己场上有灵魂怪兽存在的场合，从卡组抽1张卡。','返回手卡','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24707869,'光明的未来','选择从游戏中除外的2只自己的念动力族怪兽发动。选择的怪兽回到墓地，从自己卡组抽1张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24725825,'电虫','把这张卡从手卡丢弃去墓地。直到这个回合的结束阶段时，得到对方场上表侧表示存在的1只机械族或者龙族怪兽的控制权。','获取控制权','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24731391,'电子界魔术师',replace(replace('「电脑网仪式」降临。\r\n①：只要这张卡在怪兽区域存在，自己受到的全部伤害变成一半。\r\n②：只要自己场上有连接怪兽存在，对方不能选择自己场上的其他怪兽作为攻击对象，也不能作为效果的对象。\r\n③：只在这张卡和连接怪兽进行战斗的伤害计算时让这张卡的攻击力上升1000。\r\n④：这张卡被对方的效果破坏的场合才能发动。从卡组把1只电子界族怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24731453,'除雪机关车 急速除雪车','自己的魔法与陷阱卡区域有卡存在的场合，对方怪兽的直接攻击宣言时才能发动。这张卡从手卡特殊召唤。那之后，自己的魔法与陷阱卡区域的卡全部破坏，给与对方基本分破坏的卡数量×200的数值的伤害。此外，只要这张卡在场上表侧表示存在，自己不能把机械族以外的怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24838456,'呼唤虚无的咒文','连锁4以后才能发动。把基本分支付一半。同1组连锁上的卡的发动和效果无效，那些卡全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24845628,'尘妖的拖把','装备怪兽不能解放，也不能作为融合·同调·超量召唤的素材。场上的这张卡被对方的卡的效果破坏送去墓地时，可以从卡组把1只名字带有「尘妖」的怪兽加入手卡。「尘妖的拖把」在自己场上只能有1张表侧表示存在。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24857466,'冥王龙 断罪神',replace(replace('对方控制的卡的发动被反击陷阱无效的场合，这张卡可以在手卡特殊召唤。这个方法特殊召唤成功时，对应无效的卡的种类发动以下的效果。\r\n●魔法：给与对方基本分1500分的伤害。\r\n●陷阱：选择对方场上的1张卡破坏。\r\n●效果怪兽：从自己墓地选择1只怪兽特殊召唤上场。','\r',char(13)),'\n',char(10)),'特殊召唤','给予对方基本分1500分的伤害。','选择对方场上的1张卡破坏。','从自己墓地选择1只怪兽特殊召唤上场。','','','','','','','','','','','','');
INSERT INTO texts VALUES(24861088,'先史遗产 内布拉星象盘','这张卡召唤成功时，可以从卡组把「先史遗产 内布拉星象盘」以外的1张名字带有「先史遗产」的卡加入手卡。此外，这张卡在墓地存在，自己场上存在的怪兽只有名字带有「先史遗产」的怪兽的场合才能发动。这张卡从墓地表侧守备表示特殊召唤。这个效果发动的回合，自己不是名字带有「先史遗产」的卡的效果不能发动。「先史遗产 内布拉星象盘」的效果1回合只能使用1次。','检索','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24874630,'恶魔的圣域','①：在自己场上把1只「金属恶魔衍生物」（恶魔族·暗·1星·攻/守0）特殊召唤。这衍生物不能攻击，这衍生物的战斗发生的对控制者的战斗伤害由对方代受。这衍生物的控制者在每次自己准备阶段支付1000基本分。或者不支付基本分让这衍生物破坏。','是否要支付1000基本分维持「金属恶魔衍生物」？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24874631,'金属恶魔衍生物','由「恶魔的圣域」的效果特殊召唤。这衍生物不能攻击，这衍生物的战斗发生的对控制者的战斗伤害由对方代受。这衍生物的控制者在每次自己准备阶段支付1000基本分。或者不支付基本分让这衍生物破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24903843,'伯吉斯异兽·皮卡虫',replace(replace('①：从手卡丢弃1张「伯吉斯异兽」卡。那之后，自己从卡组抽2张。\r\n②：陷阱卡发动时，连锁那个发动才能把这个效果在墓地发动。这张卡变成通常怪兽（水族·水·2星·攻1200/守0）在怪兽区域特殊召唤（不当作陷阱卡使用）。这个效果特殊召唤的这张卡不受怪兽的效果影响，从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24907044,'魔界剧团-可爱女主角',replace(replace('←2 【灵摆】 2→\r\n①：1回合1次，对方怪兽的攻击让自己受到战斗伤害时，可以从以下效果选择1个发动。\r\n●那只对方怪兽的攻击力下降受到的伤害的数值。\r\n●从自己的额外卡组把持有受到的伤害数值以下的攻击力的1只表侧表示的「魔界剧团」灵摆怪兽加入手卡。\r\n【怪兽效果】\r\n①：1回合1次，自己或者对方受到战斗伤害时，以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力下降那次战斗伤害的数值。\r\n②：怪兽区域的这张卡被战斗或者对方的效果破坏时才能发动。从卡组选1张「魔界台本」魔法卡在自己场上盖放。','\r',char(13)),'\n',char(10)),'攻击力下降','额外卡组灵摆怪兽加入手卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24919805,'无赖特急 蝙蝠列车',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：1回合1次，自己主要阶段才能发动。给与对方500伤害。这个效果发动的回合，自己不能进行战斗阶段。\r\n②：这张卡被送去墓地的回合的结束阶段才能发动。从卡组把1只机械族·地属性·10星怪兽加入手卡。','\r',char(13)),'\n',char(10)),'LP伤害','检索','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24920410,'过度猛拧','选择自己场上表侧表示存在的1只名字带有「发条」的怪兽发动。选择的怪兽的攻击力·守备力变成2倍，这个回合的结束阶段时回到手卡。「过度猛拧」在1回合只能发动1张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24940422,'雪花之光',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：自己墓地没有魔法·陷阱卡存在的场合才能发动。自己从卡组抽2张。这张卡的发动后，这次决斗中自己不能把「雪花之光」以外的魔法·陷阱卡的效果发动。\r\n②：把墓地的这张卡除外才能发动。把手卡1只怪兽给对方观看，回到卡组洗切。那之后，自己从卡组抽1张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24943456,'科技属 强力角斗士',replace(replace('调整＋调整以外的名字带有「科技属」的怪兽1只以上\r\n这张卡向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。场上存在的这张卡被破坏时，从自己卡组抽1张卡。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(24996659,'电气鹬鸵','只要这张卡在场上表侧表示存在，自己场上存在的名字带有「电气」的怪兽攻击的场合，攻击怪兽不会被战斗破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25005816,'鼓舞','①：以自己场上1只表侧表示怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升700。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25034083,'诱饵机人','只要这张卡在自己场上表侧表示存在，对方不能选择「诱饵机人」以外的表侧表示怪兽作为攻击对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25050038,'连锁治愈','自己回复500基本分。这张卡在连锁2或者连锁3发动的场合，这张卡加入卡组洗切。这张卡在连锁4以后发动的场合，这张卡回到手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25067275,'旗鼓堂堂','选择自己墓地1张装备魔法卡和作为那个正确对象的场上1只怪兽才能发动。选择的装备魔法卡给选择的怪兽装备。这个效果装备的装备魔法卡在结束阶段时破坏。这张卡发动过的回合，自己不能把怪兽特殊召唤。「旗鼓堂堂」在1回合只能发动1张。','请选择墓地的装备卡','请选择要装备的对象','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25090294,'花瓣','把自己场上1只「元素英雄 小花蕾」作为祭品发动。从自己的手卡·卡组特殊召唤1只「元素英雄 鲜花女郎」。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25109950,'艾尔的小剑士','每用自己场上存在的这张卡以外的1只怪兽做祭品，这张卡的攻击力在回合结束前加700。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25119460,'YZ-战车龙',replace(replace('「Y-龙头」＋「Z-金属履带」\r\n把自己场上的上记卡除外的场合才能从额外卡组特殊召唤（不需要「融合」）。这张卡不能作从墓地的特殊召唤。\r\n①：丢弃1张手卡，以对方场上1只里侧表示怪兽为对象才能发动。那只对方的里侧表示怪兽破坏。','\r',char(13)),'\n',char(10)),'怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25123082,'水卜之魔导书','选择自己场上1只魔法师族怪兽才能发动。直到这个回合的结束阶段时，选择的怪兽的攻击力上升1000，战斗破坏对方怪兽的场合，可以从卡组把1张名字带有「魔导书」的魔法卡加入手卡。「水卜之魔导书」在1回合只能发动1张。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25123713,'空牙团的豪杰 戴纳',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：这张卡特殊召唤成功的场合才能发动。选最多有自己场上的「空牙团」怪兽种类数量的对方墓地的卡除外。\r\n②：只要这张卡在怪兽区域存在，对方不能选择其他的「空牙团」怪兽作为攻击对象。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25132288,'光辉终结龙',replace(replace('调整＋调整以外的光属性怪兽1只以上\r\n这张卡进行战斗的场合，怪兽的攻击宣言时才能发动。这张卡的攻击力·守备力下降500，和这张卡进行战斗的对方怪兽的攻击力·守备力直到结束阶段时下降1500。','\r',char(13)),'\n',char(10)),'攻守下降','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25148255,'废品压阵者',replace(replace('这张卡可以作为「同调士」调整的代替而成为同调素材。\r\n①：1回合1次，丢弃1张手卡，以调整以外的自己墓地1只「废品」怪兽为对象才能发动。那只怪兽特殊召唤，只用那只怪兽和这张卡为素材，把以「同调士」调整为素材的1只同调怪兽同调召唤。那个时候的同调素材怪兽不去墓地除外。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25163248,'先史遗产 玛雅机器','机械族怪兽上级召唤的场合，这张卡可以作为2只的数量解放。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25163979,'诱向星遗物的噩梦',replace(replace('①：只要这张卡在场地区域存在，自己的互相连接状态的怪兽的战斗发生的对自己的战斗伤害变成0。\r\n②：1回合1次，自己主要阶段可以从以下效果选择1个发动。\r\n●选自己场上1只「幻崩」怪兽，那个位置向其他的自己的主要怪兽区域移动。\r\n●选自己的主要怪兽区域2只「幻崩」怪兽，那些位置交换。','\r',char(13)),'\n',char(10)),'','位置移动','位置交换','请选择要移动到的位置','','','','','','','','','','','','');
INSERT INTO texts VALUES(25165047,'生命激流龙',replace(replace('调整＋「动力工具龙」\r\n这张卡同调召唤成功时，可以把自己基本分变成4000。只要这张卡在场上表侧表示存在，自己受到的效果伤害变成0。此外，场上表侧表示存在的这张卡被破坏的场合，可以作为代替把自己墓地存在的1张装备魔法卡从游戏中除外。','\r',char(13)),'\n',char(10)),'自己LP变成4000','是否要把1张装备魔法卡除外代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25171661,'永火矮人','自己手卡是0张的场合，只要这张卡在自己场上表侧表示存在，自己场上存在的怪兽攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25173686,'同花顺','对方场上的魔法与陷阱卡区域全部有卡存在的场合才能发动。对方的魔法与陷阱卡区域存在的卡全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25206027,'异界的棘紫龙','自己场上的怪兽被战斗或者卡的效果破坏送去墓地的场合，这张卡可以从手卡特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25231813,'白银之翼','8星以上的龙族同调怪兽才能装备。装备怪兽1回合最多2次不会被战斗破坏。装备怪兽被卡的效果破坏的场合，可以作为代替把这张卡破坏。','是否要使用「白银之翼」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25236056,'稀有金属龙','这张卡不能通常召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25244515,'妖仙兽 辻斩风',replace(replace('「妖仙兽 辻斩风」的①②的效果1回合各能使用1次。\r\n①：自己的「妖仙兽」怪兽和对方怪兽进行战斗的从伤害步骤开始时到伤害计算前，把这张卡从手卡丢弃才能发动。那只自己怪兽的攻击力直到回合结束时上升1000。\r\n②：以场上1只「妖仙兽」怪兽为对象才能发动。那只怪兽的攻击力直到回合结束时上升1000。\r\n③：这张卡召唤的回合的结束阶段发动。这张卡回到持有者手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25247218,'大块石人','对方场上有怪兽存在，自己场上没有怪兽存在的场合，这张卡可以不用解放作召唤。','不解放进行召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25259669,'哥布林德伯格','①：这张卡召唤成功时才能发动。从手卡把1只4星以下的怪兽特殊召唤。这个效果发动的场合，这张卡变成守备表示。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25262697,'守墓的暗杀者','「王家长眠之谷」在场上表侧表示存在时效果才能发动。这张卡攻击宣言时，可以变更对方场上存在的一只表侧表示怪兽的表示形式。','改变表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25280974,'魔道化 利真',replace(replace('「魔道化 利真」的②的效果1回合只能使用1次。\r\n①：只要这张卡在怪兽区域存在，自己在通常召唤外加上只有1次，自己主要阶段可以把1只魔法师族怪兽表侧攻击表示上级召唤。\r\n②：这张卡从场上送去墓地的场合才能发动。从自己的卡组·墓地选1只魔法师族的通常怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25290459,'等级上升！','把场上表侧表示存在的名字有「LV」的怪兽送去墓地发动。那张卡上面记述的怪兽，无视召唤条件从手卡·卡组特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25334372,'九十九斩',replace(replace('「九十九斩」在1回合只能发动1张。\r\n①：自己怪兽向比那只怪兽攻击力高的怪兽攻击的伤害计算时才能发动。那只进行战斗的自己怪兽的攻击力只在那次伤害计算时上升自己和对方的基本分差的数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25339070,'堕天使 莫斯提马',replace(replace('自己对「堕天使 莫斯提马」1回合只能有1次特殊召唤，那个作为②的效果1回合只能使用1次。\r\n①：从手卡把这张卡以外的2张「堕天使」卡丢弃才能发动。这张卡从手卡特殊召唤。\r\n②：支付1000基本分，以自己墓地1张「堕天使」魔法·陷阱卡为对象才能发动。那张魔法·陷阱卡的效果适用。那之后，墓地的那张卡回到卡组。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25341652,'交响魔人 剑击指挥',replace(replace('4星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除，选择对方场上表侧攻击表示存在的1只怪兽才能发动。选择的怪兽变成里侧守备表示。此外，只要这张卡在场上表侧表示存在，自己场上的名字带有「魔人」的超量怪兽被破坏的场合，可以作为代替把那怪兽1个超量素材取除。','\r',char(13)),'\n',char(10)),'变成里侧守备','是否要使用「交响魔人 剑击指挥」的效果代替破坏？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25343017,'寡默的念力祭司','这张卡召唤·反转召唤成功时，变成守备表示。1回合1次，可以把1张手卡送去墓地，选择自己墓地存在的1只念动力族怪兽从游戏中除外。这张卡从场上送去墓地时，选择这张卡的效果除外的1只怪兽特殊召唤。','变成守备表示','除外','特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25343280,'法老之灵','这张卡不能进行通常召唤。这张卡只能通过「第一之棺」的效果进行特殊召唤。这张卡特殊召唤成功时，可以从自己的墓地里特殊召唤至多4只2星以下的不死族通常怪兽上场。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25345186,'灭绝战争','只能在主要阶段一发动。这个回合经过了战斗伤害计算的自己·对方怪兽在结束步骤时全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25366484,'元素英雄 闪光火焰翼侠',replace(replace('「元素英雄 火焰翼侠」＋「元素英雄 电光侠」\r\n这张卡不用融合召唤不能特殊召唤。\r\n①：这张卡的攻击力上升自己墓地的「元素英雄」卡数量×300。\r\n②：这张卡战斗破坏怪兽送去墓地的场合发动。给与对方那只怪兽的原本攻击力数值的伤害。','\r',char(13)),'\n',char(10)),'伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25373678,'旋风之沃泰克斯',replace(replace('调整＋调整以外的鸟兽族怪兽1只以上\r\n这张卡被战斗破坏送去墓地时，可以从自己卡组把1只4星以下的鸟兽族怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25377819,'召集之圣刻印','①：从卡组把1只「圣刻」怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25401880,'念力通道','①：支付800基本分，以除外的最多2只自己的念动力族怪兽为对象才能发动。那些怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25407406,'剑斗兽的斗器 短剑','名字带有「剑斗兽」的怪兽才能装备。装备怪兽的攻击力上升300。装备怪兽从自己场上回到卡组让这张卡被送去墓地时，这张卡回到手卡。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25407643,'魔法族的圣域','这张卡以外的魔法卡只有自己场上才有表侧表示存在的场合，魔法师族以外的怪兽在对方场上召唤·特殊召唤时，那个回合那些怪兽不能攻击，也不能作效果的发动。此外，自己场上没有魔法师族怪兽存在的场合，这张卡破坏。','攻击效果限制','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25415052,'灵魂鸟神-姬孔雀',replace(replace('「灵魂的降神」降临。这张卡不用仪式召唤不能特殊召唤。\r\n①：这张卡仪式召唤成功的场合才能发动。选对方场上最多3张魔法·陷阱卡回到持有者卡组。那之后，可以从卡组把1只4星以下的灵魂怪兽无视召唤条件特殊召唤。\r\n②：这张卡特殊召唤的回合的结束阶段发动。这张卡回到持有者手卡，在自己场上把2只「灵魂鸟衍生物」（鸟兽族·风·4星·攻/守1500）特殊召唤。','\r',char(13)),'\n',char(10)),'对方魔法·陷阱回到卡组','是否从卡组把灵魂怪兽特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25415053,'灵魂鸟衍生物','由「灵魂鸟神-姬孔雀」的效果特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25435080,'黑羽-力锚','把1只名字带有「黑羽」的怪兽解放，选择自己场上表侧表示存在的1只同调怪兽发动。选择的怪兽的攻击力直到结束阶段时上升为把这张卡发动而解放的怪兽的攻击力数值。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25451652,'堕天使 路西法',replace(replace('这张卡不能特殊召唤。\r\n①：这张卡上级召唤成功的场合才能发动。把最多有对方场上的效果怪兽数量的「堕天使」怪兽从手卡·卡组特殊召唤。\r\n②：只要自己场上有其他的「堕天使」怪兽存在，对方不能把这张卡作为效果的对象。\r\n③：1回合1次，自己主要阶段才能发动。把场上的「堕天使」怪兽数量的卡从自己卡组上面送去墓地。自己回复这个效果送去墓地的「堕天使」卡数量×500基本分。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25460258,'暗黑耀星龙','这张卡可以把自己墓地的光属性和暗属性的怪兽各1只从游戏中除外，从手卡特殊召唤。1回合1次，可以从手卡和卡组把龙族怪兽各1只送去墓地，选择自己或者对方的墓地1张卡从游戏中除外。','除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25472513,'天轮之双星道士',replace(replace('调整＋调整以外的怪兽1只\r\n「天轮之双星道士」的效果1回合只能使用1次。\r\n①：这张卡同调召唤成功时才能发动。从自己的手卡·墓地选最多4只调整以外的2星怪兽守备表示特殊召唤。这个效果特殊召唤的怪兽的效果无效化。这个效果的发动后，直到回合结束时自己不是同调怪兽不能从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25484449,'发条鲨',replace(replace('①：自己场上有「发条」怪兽召唤·特殊召唤时才能发动。这张卡从手卡特殊召唤。\r\n②：1回合1次，可以从以下效果选择1个发动。\r\n●这张卡的等级直到回合结束时上升1星。\r\n●这张卡的等级直到回合结束时下降1星。','\r',char(13)),'\n',char(10)),'特殊召唤','等级变化','等级上升','等级下降','','','','','','','','','','','','');
INSERT INTO texts VALUES(25494711,'文具电子人009',replace(replace('①：1回合1次，自己主要阶段1才能发动。这张卡的攻击力直到对方回合结束时上升「文具电子人009」以外的自己场上的「文具电子人」怪兽的攻击力的合计数值。这个效果发动的回合，不用这张卡不能攻击。\r\n②：这张卡进行战斗的场合，对方直到伤害步骤结束时魔法·陷阱·怪兽的效果不能发动。\r\n③：这张卡被战斗·效果破坏的场合，可以作为代替把自己场上1张「文具电子人」卡破坏。','\r',char(13)),'\n',char(10)),'是否使用「文具电子人009」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25518020,'机器开发工厂','场上表侧表示存在的机械族怪兽的攻击力上升200。每次场上存在的机械族怪兽被破坏，给这张卡放置2个废品指示物。可以把这张卡送去墓地，从自己墓地选择持有这张卡放置的废品指示物数量以下的等级的1只机械族怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25524823,'守墓的审神者',replace(replace('这张卡也能把3只怪兽解放或者把1只名字带有「守墓」的怪兽解放作召唤。这张卡召唤成功时，可以从以下效果选择最多有为那次召唤而解放的名字带有「守墓」的怪兽的数量发动。\r\n●这张卡的攻击力上升因为那次召唤而解放的怪兽的等级合计×100的数值。\r\n●对方场上盖放的怪兽全部破坏。\r\n●对方场上的全部怪兽的攻击力·守备力下降2000。','\r',char(13)),'\n',char(10)),'解放3只怪兽召唤','解放1只名字带有「守墓」的怪兽召唤','这张卡的攻击力上升解放的怪兽的等级合计×100的数值','对方场上盖放的怪兽全部破坏','对方场上的全部怪兽的攻击力·守备力下降2000','是否要继续选择效果发动？','效果发动','','','','','','','','','');
INSERT INTO texts VALUES(25531465,'魔法猫','这张卡被魔法师族怪兽的同调召唤使用送去墓地的场合，可以让自己墓地存在的1张魔法卡回到卡组最上面。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25533642,'幻变骚灵·寻道梅露辛',replace(replace('这个卡名的③的效果1回合只能使用1次。\r\n①：这张卡可以直接攻击。\r\n②：这张卡给与对方战斗伤害时，以对方场上1张卡为对象才能发动。那张卡送去墓地。\r\n③：这张卡从场上送去墓地的场合才能发动。从卡组把「幻变骚灵·寻道梅露辛」以外的1只「幻变骚灵」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25542642,'幻影雾剑',replace(replace('以场上1只效果怪兽为对象才能把这张卡发动。「幻影雾剑」的②的效果1回合只能使用1次。\r\n①：只要这张卡在魔法与陷阱区域存在，作为对象的怪兽不能攻击，不会成为攻击对象，效果无效化。那只怪兽从场上离开时这张卡破坏。\r\n②：把墓地的这张卡除外，以自己墓地1只「幻影骑士团」怪兽为对象才能发动。那只怪兽特殊召唤。这个效果特殊召唤的怪兽从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25551951,'回膛手枪龙','①：1回合1次，以对方场上1张卡为对象才能发动。进行3次投掷硬币，那之内2次以上是表的场合，那张对方的卡破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25573054,'进化之翼','把自己场上存在的1只「羽翼栗子球」和2张手卡送到墓地。从手卡·卡组特殊召唤1只「羽翼栗子球 LV10」上场。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25578802,'二人小组战斗','双方在各自的回合的结束阶段只有1次，可以从手卡特殊召唤1只4星的通常怪兽上场。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25586143,'捕食植物 奇美拉大花草',replace(replace('「捕食植物」怪兽＋暗属性怪兽\r\n①：1回合1次，以持有这张卡的等级以下的等级的场上1只怪兽为对象才能发动。那只怪兽除外。\r\n②：这张卡和对方的表侧表示怪兽进行战斗的攻击宣言时才能发动。直到回合结束时，那只对方怪兽的攻击力下降1000，这张卡的攻击力上升1000。\r\n③：这张卡被送去墓地的场合，下次的准备阶段才能发动。从卡组把1张「融合」魔法卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25614410,'英雄的遗产',replace(replace('「英雄的遗产」在1回合只能发动1张。\r\n①：让需以「英雄」怪兽为融合素材的2只融合怪兽从自己墓地回到额外卡组才能发动。自己从卡组抽3张。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25629622,'魔界剧团-大明星',replace(replace('←3 【灵摆】 3→\r\n①：1回合1次，把自己场上1只「魔界剧团」怪兽解放，以自己墓地1张「魔界台本」魔法卡为对象才能发动。那张卡加入手卡。\r\n【怪兽效果】\r\n①：在这张卡的召唤·特殊召唤成功时，对方不能把魔法·陷阱卡的效果发动。\r\n②：1回合1次，自己主要阶段才能发动。从卡组选1张「魔界台本」魔法卡在自己场上盖放。这个效果盖放的卡在结束阶段送去墓地。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25642998,'波塞冬之浪','对方怪兽的攻击宣言时才能发动。把1只对方怪兽的攻击无效。自己场上有鱼族·海龙族·水族怪兽表侧表示存在的场合，给与对方基本分那个数量×800的数值的伤害。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25652259,'王后骑士','用优美的动作愚弄敌人，抓住对方的空隙快速发出攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25652655,'变更同调士','这张卡被同调怪兽的同调召唤使用送去墓地的场合，选择对方场上存在的1只怪兽把表示形式变更。','变更表示形式','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25654671,'自然蜘蛛牙','这张卡若不在对方把魔法·陷阱·效果怪兽的效果发动的回合则不能攻击宣言。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25655502,'恶魔箱','「杀人小丑」＋「梦幻小丑」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25669282,'完全燃烧',replace(replace('「完全燃烧」在1回合只能发动1张。\r\n①：把自己场上1只表侧表示的「化合兽」怪兽除外才能发动。从卡组把2只「化合兽」怪兽特殊召唤（同名卡最多1张）。\r\n②：对方怪兽的直接攻击宣言时，把墓地的这张卡除外，以除外的1只自己的二重怪兽为对象才能发动。那只怪兽特殊召唤。这个效果特殊召唤的怪兽当作再1次召唤的状态使用。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25682811,'龙骑兵团骑士-长枪龙骑士',replace(replace('龙族调整＋调整以外的鸟兽族怪兽1只以上\r\n这张卡同调召唤成功时，可以选择自己墓地存在的名字带有「龙骑兵团」的龙族怪兽任意数量当作装备卡使用给这张卡装备。这张卡的攻击力上升这张卡装备的名字带有「龙骑兵团」的卡数量×300的数值。','\r',char(13)),'\n',char(10)),'装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25700114,'尘妖滚筒','选择场上1只怪兽才能发动。这个回合，选择的怪兽不能解放，也不能作为融合·同调·超量召唤的素材。此外，盖放的这张卡被对方的卡的效果破坏送去墓地的场合，可以从卡组把1只名字带有「尘妖」的怪兽加入手卡。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25704359,'凭依解放',replace(replace('「凭依解放」的③的效果1回合只能使用1次。\r\n①：自己的「灵使」怪兽不会被战斗破坏。\r\n②：自己的「凭依装着」怪兽的攻击力只在向对方怪兽攻击的伤害计算时上升800。\r\n③：这张卡在魔法与陷阱区域存在，自己场上的怪兽被战斗·效果破坏的场合才能把这个效果发动。把持有和那1只怪兽的原本属性不同属性的1只守备力1500的魔法师族怪兽从卡组表侧攻击表示或者里侧守备表示特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25716180,'发条猫咪','自己的主要阶段时才能发动。选择对方场上存在的1只怪兽回到持有者手卡。这个效果只在这张卡在场上表侧表示存在能使用1次。','返回手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25727454,'名匠 加米尔','①：自己怪兽进行战斗的从伤害步骤开始时到伤害计算前，把这张卡从手卡送去墓地才能发动。那只怪兽的攻击力直到回合结束时上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25733157,'闪刀机-鹰式推进器','①：自己的主要怪兽区域没有怪兽存在的场合，以场上1只表侧表示怪兽为对象才能发动。这个回合，那只表侧表示怪兽不受自身以外的卡的效果影响。自己墓地有魔法卡3张以上存在的场合，再在这个回合让那只怪兽不会被战斗破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25769732,'机械改造工厂','机械族怪兽可以装备。装备怪兽的攻击力·守备力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25771826,'正义盟军 雷剑兵','这张卡和光属性怪兽进行战斗的场合，伤害步骤内这张卡的攻击力上升200。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25773409,'传说的柔术家','与守备表示的这张卡进行战斗的怪兽在伤害步骤终了时弹回其持有者的卡组的最上面。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25774450,'死之魔术箱','①：以自己以及对方场上的怪兽各1只为对象才能发动。那只对方怪兽破坏。那之后，那只自己怪兽的控制权移给对方。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25788011,'调星师 莱斯贝尔特','这张卡特殊召唤成功时，选择场上表侧表示存在的1只怪兽才能发动。直到结束阶段时，选择的怪兽的等级上升最多3星。','等级上升','等级上升1','等级上升2','等级上升3','','','','','','','','','','','','');
INSERT INTO texts VALUES(25789292,'禁忌的圣杯','①：以场上1只表侧表示怪兽为对象才能发动。那只怪兽直到回合结束时攻击力上升400，效果无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25793414,'风魔女-水晶钟',replace(replace('「风魔女-冬铃」＋「风魔女」怪兽\r\n「风魔女-水晶钟」的①②的效果1回合各能使用1次。\r\n①：以自己或者对方的墓地1只怪兽为对象才能发动。直到结束阶段，这张卡当作和那只怪兽同名卡使用，得到相同效果。\r\n②：这张卡被对方破坏送去墓地的场合，以自己墓地1只「风魔女-冬铃」和1只4星以下的「风魔女」怪兽为对象才能发动。那些怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'复制效果','特殊召唤','结束复制效果','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25795273,'光角幻兔','①：只要这张卡在怪兽区域存在，场上的怪兽的攻击力·守备力下降那怪兽的等级或者阶级×300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25796442,'仪式之槛',replace(replace('①：只要这张卡在魔法与陷阱区域存在，自己的仪式怪兽的战斗发生的对自己的战斗伤害变成0。\r\n②：只要这张卡在魔法与陷阱区域存在，自己场上的仪式怪兽不会成为怪兽的效果的对象，不会被怪兽的效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25811989,'狱火机·阿斯蒙蒂斯',replace(replace('这张卡不能通常召唤。自己场上的全部效果怪兽的等级·阶级的合计是8以下时，从自己的手卡·墓地把2只「狱火机」怪兽除外的场合才能从手卡·墓地特殊召唤。\r\n①：这张卡向对方怪兽的攻击给与对方战斗伤害时才能发动。对方手卡随机选1张送去墓地。\r\n②：1回合1次，把自己场上1只怪兽解放，以对方墓地1张卡为对象才能发动。那张卡除外。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'手卡破坏','对方墓地的卡除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25824484,'森罗的仙树 凤凰木','1回合1次，自己的主要阶段时才能发动。自己卡组最上面的卡翻开。翻开的卡是植物族怪兽的场合，那只怪兽送去墓地，从卡组抽1张卡。不是的场合，那张卡回到卡组最下面。此外，卡组的这张卡被卡的效果翻开送去墓地的场合，从自己卡组上面把最多3张卡确认，用喜欢的顺序回到卡组上面。','确认卡组','确认卡组顺序','请选择要确认的数量','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25833572,'门之守护神','这张卡不能通常召唤。把自己场上的「雷魔神-桑迦」「风魔神-修迦」「水魔神-斯迦」各1只解放的场合可以特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25847467,'暗黑界的刺客 卡其','这张卡被其他卡的效果从手卡丢弃去墓地的场合，把场上1只怪兽破坏。','把场上1只怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25853045,'重铠装-黑鳍条枪兵',replace(replace('水属性4星怪兽×3\r\n这张卡也能在自己场上的没有超量素材的水属性·3阶的超量怪兽上面把这张卡重叠来超量召唤。这张卡的攻击力上升这张卡的超量素材数量×200的数值。场上的这张卡被破坏的场合，可以作为代替把这张卡的超量素材全部取除。此外，这张卡战斗破坏对方怪兽的场合，可以选择对方场上1张魔法·陷阱卡破坏。','\r',char(13)),'\n',char(10)),'是否在没有超量素材的水属性·3阶超量怪兽上面重叠超量召唤？','是否要使用「重铠装-黑鳍条枪兵」的效果代替破坏？','魔陷破坏','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25857246,'瓦尔基鲁斯之影灵衣',replace(replace('「影灵衣」仪式魔法卡降临。若不是只使用除8星以外的怪兽来仪式召唤则不能特殊召唤。「瓦尔基鲁斯之影灵衣」①②效果1回合只能使用其中1个效果1次。\r\n①：对方怪兽的攻击宣言时，把自己墓地1张「影灵衣」卡除外，把这张卡从手卡丢弃才能发动。那次攻击无效，战斗阶段结束。\r\n②：自己的主要阶段时发动。把自己手卡·场上最多2只怪兽解放，自己从卡组抽那个数量的卡。','\r',char(13)),'\n',char(10)),'攻击无效','抽卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25862681,'古代妖精龙',replace(replace('调整＋调整以外的怪兽1只以上\r\n1回合1次，可以从手卡把1只4星以下的怪兽特殊召唤。这个效果发动的回合，自己不能进行战斗阶段。此外，1回合1次，可以把场地魔法卡破坏。破坏的场合自己回复1000基本分。并且，可以再从自己卡组把1张场地魔法卡加入手卡。','\r',char(13)),'\n',char(10)),'从手卡把1只4星以下的怪兽特殊召唤','把场地魔法卡破坏','是否要选一张场地魔法加入手卡？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25866285,'自然椿象','自己场上表侧表示存在的名字带有「自然」的怪兽成为攻击对象时，把自己场上表侧表示存在的这张卡送去墓地才能发动。那次攻击无效，战斗阶段结束。','攻击无效并结束战斗阶段','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25880422,'『攻击』封禁','指定的对方场上的1只表侧表示的怪兽转为守备表示。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25882881,'死神骷髅','使用地狱一击夺取灵魂的死神。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25920413,'骷髅外星人','可以把对方场上1只3星以下的怪兽解放，这张卡从手卡往对方场上特殊召唤。这个方法特殊召唤的场合，这个回合自己不能通常召唤，特殊召唤时给这张卡放置1个A指示物。只要这张卡在场上表侧表示存在，有A指示物放置的怪兽和名字带有「外星」的怪兽进行战斗的场合，只在伤害计算时A指示物每有1个攻击力·守备力下降300。','放置「A指示物」','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25924653,'剑斗兽 马斗','这张卡用名字带有「剑斗兽」的怪兽的效果特殊召唤成功时，可以从自己墓地选择1只名字带有「剑斗兽」的怪兽在自己场上特殊召唤。这个效果特殊召唤的怪兽的效果无效化，这张卡从场上离开时回到自己卡组。这张卡进行战斗的战斗阶段结束时可以让这张卡回到卡组，从卡组把「剑斗兽 马斗」以外的1只名字带有「剑斗兽」的怪兽在自己场上特殊召唤。','墓地特殊召唤','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25935625,'暗龙星-椒图',replace(replace('「暗龙星-椒图」的①②的效果1回合只能有1次使用其中任意1个。\r\n①：自己场上的这张卡被战斗·效果破坏送去墓地时才能发动。从卡组把「暗龙星-椒图」以外的1只「龙星」怪兽攻击表示特殊召唤。\r\n②：自己场上没有这张卡以外的怪兽存在的场合，把手卡2张「龙星」卡送去墓地才能发动。从卡组把攻击力0和守备力0的「龙星」怪兽各1只特殊召唤。这个效果特殊召唤的怪兽在结束阶段除外。','\r',char(13)),'\n',char(10)),'特殊召唤','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25955164,'雷魔神-桑迦','对方回合的战斗伤害计算时才能发动。攻击这张卡的怪兽的攻击力变为0。只要这张卡在表侧表示存在这个效果只能使用1次。','攻击变成0','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25955749,'闪刀术式-妨害波纹','①：自己的主要怪兽区域没有怪兽存在的场合，以场上盖放的1张魔法·陷阱卡为对象才能发动。那张卡破坏。那之后，自己墓地有魔法卡3张以上存在的场合，可以选场上1只怪兽破坏。','是否选怪兽卡破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25958491,'古代圣翼龙',replace(replace('光属性调整＋调整以外的怪兽1只以上\r\n自己基本分比对方高的场合，这张卡的攻击力上升那个数值。自己基本分比对方低的场合，这张卡的攻击力下降那个数值。这张卡被战斗破坏送去墓地时，可以支付1000基本分把这张卡在自己场上特殊召唤。','\r',char(13)),'\n',char(10)),'特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(25988873,'龙骑兵团-重枪龙','这张卡召唤成功时，可以从手卡把1只名字带有「龙骑兵团」的鸟兽族怪兽特殊召唤，把这张卡当作装备卡使用来装备。这张卡被卡的效果当作装备卡使用装备中的场合，装备怪兽当作调整使用。','特殊召唤并装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26016357,'魔偶甜点·橘子酱女仆','这张卡被对方破坏送去墓地时，这张卡回到卡组。这张卡反转时，可以选择自己墓地1张名字带有「魔偶甜点」的魔法·陷阱卡加入手卡。','返回卡组','返回手牌','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26022485,'衰弱之雾','对方手卡数量限制变成最多5张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26034577,'狱火机·别西卜',replace(replace('这张卡不能通常召唤。自己场上的全部效果怪兽的等级·阶级的合计是8以下时，从自己的手卡·墓地把1只「狱火机」怪兽除外的场合才能从手卡特殊召唤。\r\n①：1回合1次，以对方场上1张表侧表示的卡为对象才能发动。那张卡回到持有者手卡。\r\n②：对方回合1次，把自己场上1只怪兽解放，以对方墓地1张卡为对象才能发动。那张卡除外。','\r',char(13)),'\n',char(10)),'卡片回手','对方墓地的卡除外','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26046205,'恶忍','场上表侧攻击表示存在的这张卡被选择作为攻击对象时，从自己卡组抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26057276,'星因士 参宿四',replace(replace('「星因士 参宿四」的效果1回合只能使用1次。\r\n①：这张卡召唤·反转召唤·特殊召唤成功的场合，以「星因士 参宿四」以外的自己墓地1张「星守」卡为对象才能发动。这张卡送去墓地，作为对象的卡加入手卡。','\r',char(13)),'\n',char(10)),'加入手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26077387,'闪刀姬-零衣',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：把这张卡解放才能发动。从额外卡组把1只「闪刀姬」怪兽在额外怪兽区域特殊召唤。这个效果在对方回合也能发动。\r\n②：这张卡在墓地存在的状态，自己场上的表侧表示的「闪刀姬」连接怪兽因对方的效果从场上离开的场合或者被战斗破坏的场合才能发动。这张卡特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26082117,'我我我魔术师','1回合1次，自己的主要阶段时宣言从1到8的任意等级才能发动。直到结束阶段时，这张卡的等级变成宣言的等级。「我我我魔术师」在自己场上只能有1只表侧表示存在。这张卡不能作为同调素材。','等级变化','请宣言一个等级','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26082229,'侵略之炎','这张卡召唤成功时，陷阱卡不能发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26084285,'守墓的监视者','对方发动效果中含有丢弃对方自己手卡的卡的效果时，可以将这张卡从手卡送去墓地，那张卡的发动与效果无效并且破坏。','丢弃手牌效果无效并且破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26099457,'下降潮流','选择自己场上表侧表示存在的1只怪兽，宣言从1到3的任意等级才能发动。选择的怪兽的等级变成宣言的等级。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26120084,'特殊二重召唤','选择自己场上表侧表示存在的1只二重怪兽，变成再度召唤的状态。这个回合的结束阶段时，选择的二重怪兽回到手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26157485,'链·射击手','把自己场上存在的1只名字带有「链」的怪兽送去墓地发动。给与对方基本分800分伤害。这个效果1回合只能使用1次。','伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26185991,'代打蝗虫','①：这张卡从自己场上送去墓地时才能发动。从手卡把1只昆虫族怪兽特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26194151,'死灵式同调','①：从自己墓地选调整1只和调整以外的怪兽最多2只除外，把持有和除外的怪兽的等级合计相同等级的1只「星尘」同调怪兽从额外卡组当作同调召唤作特殊召唤。这个效果特殊召唤的怪兽的效果无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26202165,'三眼怪',replace(replace('这个卡名的效果1回合只能使用1次。\r\n①：这张卡从场上送去墓地的场合发动。从卡组把1只攻击力1500以下的怪兽加入手卡。这个回合，自己不能作这个效果加入手卡的卡以及那些同名卡的发动。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26205777,'炎帝 泰斯塔罗斯','①：这张卡上级召唤成功的场合发动。对方手卡随机选1张丢弃。丢弃的卡是怪兽卡的场合，给与对方那只怪兽的等级×100伤害。','对方随机丢弃1张手牌去墓地','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26211048,'甲虫装机 艾可萨锹甲',replace(replace('昆虫族5星怪兽×2\r\n1回合1次，可以把这张卡1个超量素材取除，选择对方的场上·墓地1只怪兽当作装备卡使用给这张卡装备。这张卡的攻击力·守备力上升这个效果装备的怪兽的各自一半数值。','\r',char(13)),'\n',char(10)),'装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26254876,'二重枪兵',replace(replace('这张卡在墓地或者场上表侧表示存在的场合，当作通常怪兽使用。场上表侧表示存在的这张卡可以作当成通常召唤使用的再度召唤，这张卡变成当作效果怪兽使用并得到以下效果。\r\n●这张卡攻击守备表示怪兽时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26257572,'苦痛的回廊','只要这张卡在场上存在，从卡组特殊召唤的怪兽只要在场上表侧表示存在效果不能发动并无效化，也不能攻击宣言。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26268488,'圣珖神龙 星尘·零',replace(replace('同调怪兽调整1只＋调整以外的同调怪兽2只以上\r\n这张卡不用同调召唤不能特殊召唤。\r\n①：自己场上的卡在1回合各有1次不会被战斗·效果破坏。\r\n②：1回合1次，对方把怪兽的效果发动时才能发动。那个效果无效，选场上1张卡破坏。\r\n③：把墓地的这张卡除外，以自己墓地1只8星以下的「星尘」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'效果无效，选场上1张卡破坏。','特殊召唤','已经使用过防止破坏效果','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26270847,'娱乐伙伴 银爪狼',replace(replace('←5 【灵摆】 5→\r\n①：自己场上的「娱乐伙伴」怪兽的攻击力上升300。\r\n【怪兽效果】\r\n①：这张卡的攻击宣言时发动。自己场上的「娱乐伙伴」怪兽的攻击力直到战斗阶段结束时上升300。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26285557,'刚鬼反派转正',replace(replace('这个卡名的卡在1回合只能发动1张。\r\n①：以自己场上1张「刚鬼」卡和自己墓地1只「刚鬼」怪兽为对象才能发动。作为对象的场上的卡破坏，作为对象的墓地的怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26285788,'招来的对价',replace(replace('这张卡发动的回合的结束阶段时，这个回合自己从手卡·场上解放的衍生物以外的怪兽数量的以下效果适用。「招来的对价」在1回合只能发动1张。\r\n●1只：从卡组抽1张卡。\r\n●2只：选自己墓地2只怪兽加入手卡。\r\n●3只以上：选场上表侧表示存在的最多3张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26293219,'燃烧骷髅头','这张卡从手卡特殊召唤成功时，给与对方基本分1000分伤害。此外，可以把自己场上表侧表示存在的这张卡从游戏中除外，从游戏中除外的1只「骷髅炎鬼」回到墓地。','伤害','返回墓地','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26302522,'球体时限炸弹',replace(replace('①：里侧守备表示的这张卡被对方怪兽攻击的伤害计算前发动。这张卡当作装备卡使用给那只攻击怪兽装备。\r\n②：用这张卡的效果把这张卡装备的下次的对方准备阶段发动。装备怪兽破坏，给与对方那个攻击力数值的伤害。','\r',char(13)),'\n',char(10)),'装备','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26304459,'古代炎狱神',replace(replace('炎属性调整＋调整以外的炎族怪兽1只以上\r\n这张卡同调召唤成功时，选择最多有对方手卡数量的对方墓地存在的卡从游戏中除外。这张卡的攻击力上升这个效果除外的卡数量×200的数值。','\r',char(13)),'\n',char(10)),'除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26308721,'捕食植物 猪笼草翼龙',replace(replace('①：这张卡给与对方战斗伤害时，以对方场上1只表侧表示怪兽为对象才能发动。给那只怪兽放置1个捕食指示物。有捕食指示物放置的2星以上的怪兽的等级变成1星。\r\n②：1回合1次，以持有这张卡的等级以下的等级的对方场上1只怪兽为对象才能发动。那只怪兽的控制权直到结束阶段得到。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26329679,'星圣·欧米伽星云',replace(replace('光属性4星怪兽×2\r\n1回合1次，把这张卡1个超量素材取除才能发动。自己场上的全部名字带有「星圣」的怪兽在这个回合不受魔法·陷阱卡的效果影响。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'效果免疫','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26345570,'先史遗产-金字塔眼板','自己场上的名字带有「先史遗产」的怪兽的攻击力上升800。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26376390,'模仿的幻想师','这张卡召唤·反转召唤·特殊召唤成功时，选择对方的1只怪兽，拥有那只怪兽一样的原来的攻击力·守备力。','攻守变化','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26378150,'粗暴帝王','双手所持的魔人斧的破坏力相当强。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26381750,'红莲地带的飞鹰','这张卡被同调怪兽的同调召唤使用送去墓地的场合自己墓地有名字带有「熔岩」的怪兽3种类以上存在的场合，这张卡可以在自己场上特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合从游戏中除外。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26400609,'瀑征龙-潮龙','从自己的手卡·墓地把这张卡以外的龙族或者水属性的怪兽合计2只除外才能发动。这张卡从手卡·墓地特殊召唤。特殊召唤的这张卡在对方的结束阶段时回到持有者手卡。此外，可以通过把这张卡和1只水属性怪兽从手卡丢弃去墓地，从卡组把1只怪兽送去墓地。这张卡被除外的场合，可以从卡组把1只龙族·水属性怪兽加入手卡。「瀑征龙-潮龙」的效果1回合只能使用1次。','从手卡特殊召唤','返回手牌','从卡组把1只怪兽送去墓地','加入手卡','','','','','','','','','','','','');
INSERT INTO texts VALUES(26412047,'槌击','场上表侧攻击表示存在的1只攻击力最高的怪兽破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26420373,'疾行机人 超车手滑翔机',replace(replace('←3 【灵摆】 3→\r\n①：1回合1次，从手卡以及自己场上的表侧表示怪兽之中把1只「疾行机人」调整送去墓地才能发动。直到回合结束时，这张卡的灵摆刻度上升或者下降送去墓地的那只怪兽的原本等级数值（最少到1）。\r\n【怪兽效果】\r\n「疾行机人 超车手滑翔机」的①的方法的特殊召唤1回合只能有1次。\r\n①：双方场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：这张卡上级召唤成功时，以自己墓地1只4星以下的「疾行机人」怪兽为对象才能发动。那只怪兽特殊召唤。\r\n③：只要这张卡在怪兽区域存在，对方不能选择其他的「疾行机人」怪兽作为攻击对象。','\r',char(13)),'\n',char(10)),'刻度变更','刻度上升','刻度下降','墓地「疾行机人」怪兽特殊召唤','','','','','','','','','','','','');
INSERT INTO texts VALUES(26439287,'原始电子龙','只要这张卡在场上表侧表示存在，卡名当作「电子龙」使用。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26493435,'希望乡-拟声乌托邦-',replace(replace('①：只要这张卡在场地区域存在，每次自己场上有「希望皇 霍普」怪兽特殊召唤，给这张卡放置1个毅飞冲天指示物。\r\n②：自己场上的怪兽的攻击力·守备力上升这张卡的毅飞冲天指示物数量×200。\r\n③：1回合1次，把这张卡2个毅飞冲天指示物取除才能发动。从卡组把「刷拉拉」、「我我我」、「隆隆隆」、「怒怒怒」怪兽之内任意1只特殊召唤。','\r',char(13)),'\n',char(10)),'放置指示物','特殊召唤','特殊召唤','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26495087,'吸血鬼女郎','每当这张卡对对方造成战斗伤害时，宣言1个卡的种类（怪兽·魔法·陷阱），对方从其卡组中选择1张此种类的卡送去墓地。','送墓','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26509612,'地球重力','这张卡不在对方回合的战斗阶段不能发动。可以攻击的4星以下的怪兽必须对自己场上表侧表示存在的「元素英雄 地球侠」作出攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26517393,'里风之精灵','①：这张卡召唤成功的场合才能发动。从卡组把1只反转怪兽加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26533075,'保安球','对方怪兽的攻击宣言时才能发动。把那1只攻击怪兽的表示形式改变。对方的魔法·陷阱卡的效果把盖放的这张卡破坏送去墓地时，选择场上存在的1只怪兽破坏。','场上一只怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26556950,'No.84 增痛蛛',replace(replace('11星怪兽×2\r\n这张卡也能在自己场上的持有超量素材2个以上的8～10阶的暗属性超量怪兽上面重叠来超量召唤。\r\n①：这张卡的守备力上升自己场上的超量怪兽的阶级合计×200。\r\n②：只要持有超量素材的这张卡在怪兽区域存在，每次对方把魔法·陷阱卡发动给与对方600伤害。\r\n③：1回合1次，把这张卡1个超量素材取除才能发动。持有这张卡的守备力以下的守备力的对方场上的怪兽全部破坏。','\r',char(13)),'\n',char(10)),'请选择8～10阶的暗属超量怪兽','破坏怪兽','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26563200,'弦魔人 跃跃节奏',replace(replace('3星怪兽×2\r\n自己场上的名字带有「魔人」的超量怪兽向对方怪兽攻击的伤害步骤时，把这张卡1个超量素材取除才能发动。那只攻击怪兽的攻击力直到结束阶段时变成2倍。「弦魔人 跃跃节奏」的效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26566878,'恶魔蝎','寄宿着恶魔灵魂的巨大蝎子。平时蕴存着力量，但是潜在的能力很高。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26570480,'魔偶甜点·巧克力雏鸟','这张卡被对方破坏送去墓地时，这张卡回到卡组。此外，1回合1次，这张卡在场上表侧表示存在，自己场上的名字带有「魔偶甜点」的怪兽的表示形式变更时才能发动。选择场上1只怪兽变成表侧守备表示，那只怪兽是名字带有「魔偶甜点」的怪兽以外的场合，那只怪兽不能攻击，效果无效化。','返回卡组','改变表示形式','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26586849,'王宫的牢狱','只要这张卡在场上存在，双方玩家不能把墓地的怪兽特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26593852,'正义盟军 灾亡虫',replace(replace('调整＋调整以外的怪兽1只以上\r\n这张卡和暗属性以外的怪兽进行战斗时，不进行伤害计算把那只怪兽破坏。','\r',char(13)),'\n',char(10)),'暗属性以外怪兽破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26638543,'节拍器地精灵',replace(replace('←4 【灵摆】 4→\r\n①：1回合1次，以这张卡以外的自己或者对方的灵摆区域1张卡为对象才能发动。这张卡的灵摆刻度直到回合结束时变成和那张卡的灵摆刻度相同。\r\n【怪兽效果】\r\n①：自己的灵摆区域有2张卡存在，那些灵摆刻度相同的场合，这张卡的攻击力·守备力上升那个灵摆刻度×100，这张卡可以直接攻击。\r\n②：这张卡直接攻击给与对方战斗伤害的场合发动。双方的灵摆区域的卡全部破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26640671,'蜘蛛的领域','1回合1次，选择自己场上表侧表示存在的1只昆虫族怪兽发动。和选择的怪兽进行战斗的对方怪兽在战斗阶段结束时变成守备表示，只要这张卡在场上存在不能把表示形式变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26647858,'英雄光环','发动后这张卡变成装备卡，攻击力1500以下的1只战士族怪兽装备。对方的攻击力1900以上的怪兽不能攻击装备怪兽。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26655293,'马格努姆弹丸龙',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：场上的这张卡为对象的连接怪兽的效果发动时才能发动。这张卡破坏。那之后，选场上1只怪兽送去墓地。\r\n②：场上的这张卡被战斗·效果破坏送去墓地的回合的结束阶段才能发动。从卡组把「马格努姆弹丸龙」以外的1只「弹丸」怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26669055,'静寂之圣者','这张卡战斗破坏对方怪兽的场合，对方玩家在下个回合不能发动魔法卡。','发动限制','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26674724,'光枪龙之影灵衣',replace(replace('「影灵衣」仪式魔法卡降临。不作只拿「光枪龙之影灵衣」以外的怪兽来使用的仪式召唤则不能特殊召唤。「光枪龙之影灵衣」的①②的效果1回合各能使用1次。\r\n①：把这张卡从手卡丢弃才能发动。从卡组把「光枪龙之影灵衣」以外的1只「影灵衣」怪兽加入手卡。\r\n②：以从额外卡组特殊召唤的场上最多2只怪兽为对象才能发动。那些怪兽回到持有者卡组。','\r',char(13)),'\n',char(10)),'卡组检索','回到卡组','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26701483,'卡片除外士','选择对方墓地存在的1张卡从游戏中除外。这个效果1回合只能使用1次。','对方墓地存在的1张卡从游戏中除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26704411,'魔轰神兽 寇克特','这张卡战斗破坏对方怪兽送去墓地时，可以从手卡丢弃1只名字带有「魔轰神」的怪兽，从自己卡组抽1张卡。','抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26708437,'超量苏生','①：以自己墓地1只超量怪兽为对象才能发动。那只怪兽特殊召唤，把这张卡在下面重叠作为超量素材。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26722601,'侵入魔鬼的门番','只要这张卡在场上表侧表示存在，名字带有「侵入魔鬼」的怪兽表侧表示上级召唤成功的回合，自己在通常召唤外加上只有1次可以把怪兽通常召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26725158,'恶魔祓除','场上表侧表示的恶魔族的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26732909,'魔导教士 朱丝蒂','自己把名字带有「魔导书」的魔法卡发动的自己回合的结束阶段时，把场上的这张卡从游戏中除外才能发动。从卡组把1只光属性或者暗属性的魔法师族·5星以上的怪兽和1张名字带有「魔导书」的魔法卡加入手卡。「魔导教士 朱丝蒂」的效果1回合只能使用1次。','检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26775203,'黑羽-热风之基布利',replace(replace('①：对方怪兽的直接攻击宣言时才能发动。这张卡从手卡特殊召唤。\r\n②：1回合1次，自己主要阶段才能发动。这张卡的原本的攻击力·守备力直到回合结束时交换。','\r',char(13)),'\n',char(10)),'特殊召唤','原本的攻击力·守备力交换','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26781870,'老千御法度',replace(replace('①：1回合1次，对方从手卡把怪兽特殊召唤时才能发动。从手卡特殊召唤的对方场上的怪兽全部回到持有者手卡。\r\n②：场上没有「花札卫」同调怪兽存在的场合这张卡送去墓地。','\r',char(13)),'\n',char(10)),'发动但不使用效果','发动并使用①效果','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26822796,'帝王的冻志','①：自己的额外卡组没有卡存在的场合，以自己场上1只上级召唤的表侧表示怪兽为对象才能发动。那只怪兽效果无效，不受这张卡以外的效果影响。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26834022,'卸除武器','从手卡把1张名字带有「剑斗兽」的卡回到卡组。魔法卡的发动无效并破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26841274,'D-融合',replace(replace('这张卡的效果融合召唤的场合，不是「命运英雄」怪兽不能作为融合素材。\r\n①：从自己场上把融合怪兽卡决定的融合素材怪兽送去墓地，把那1只融合怪兽从额外卡组融合召唤。这个效果特殊召唤的怪兽在这个回合不会被战斗·效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26842483,'正义使者','对方场上存在的特殊召唤的怪兽的效果发动时才能发动。那次发动无效。这个效果1回合只能使用1次。','效果无效','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26864586,'共振装置','选择自己场上表侧表示存在的2只相同种族·属性的怪兽发动。选择的1只怪兽的等级直到结束阶段时变成和另1只怪兽的等级相同。','请选择等级要变化的怪兽','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26885836,'英豪挑战者 战锤兵','这张卡战斗破坏对方怪兽送去墓地时，可以把破坏的怪兽当作装备卡使用只有1只给这张卡装备。这张卡的攻击力上升这个效果装备的怪兽的攻击力数值。','装备','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26902560,'融合贤者','选1张自己的卡组的「融合」加入手卡。之后洗切卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26905245,'金属反射史莱姆',replace(replace('①：这张卡发动后变成效果怪兽（水族·水·10星·攻0/守3000）在怪兽区域守备表示特殊召唤。这张卡也当作陷阱卡使用。\r\n②：这张卡的效果特殊召唤的这张卡不能攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(26920296,'幻梦境',replace(replace('「幻梦境」在1回合只能发动1张。\r\n①：得到场上的怪兽种类的以下效果。\r\n●融合：1回合1次，自己的手卡·场上的怪兽被效果送去墓地的场合才能发动。自己从卡组抽1张。\r\n●同调：怪兽召唤·特殊召唤成功时自己才能把这个效果发动。那些怪兽的等级上升1星。\r\n●超量：自己结束阶段发动。场上的等级最高的怪兽破坏。','\r',char(13)),'\n',char(10)),'是否发动「幻梦境」的效果？','自己从卡组抽1张卡','召唤·特殊召唤成功的怪兽的等级上升1星','场上的等级最高的怪兽破坏','','','','','','','','','','','','');
INSERT INTO texts VALUES(26931058,'同盟编队',replace(replace('从下面效果选择1个发动：\r\n●选择自己场上表侧表示存在的1只同盟怪兽，给自己场上表侧表示存在的可以装备的怪兽装备。\r\n●把自己场上存在的1只当作装备卡使用的同盟怪兽的装备解除，在自己场上表侧攻击表示特殊召唤。','\r',char(13)),'\n',char(10)),'选择1只同盟怪兽，给自己场上表侧表示存在的可以装备的怪兽装备','把自己场上存在的1只当作装备卡使用的同盟怪兽的装备解除','请选择一只同盟怪兽','','','','','','','','','','','','','');
