INSERT INTO texts VALUES(27062594,'命运之扉',replace(replace('①：对方怪兽的直接攻击宣言时才能把这张卡发动。那次攻击无效。那之后，这张卡变成效果怪兽（恶魔族·光·1星·攻/守0）在怪兽区域特殊召唤。这张卡也当作陷阱卡使用。\r\n②：这张卡的效果让这张卡已特殊召唤的场合，自己准备阶段把自己墓地的「希望皇 霍普」怪兽任意数量除外才能发动（同名卡最多1张）。给与对方除外数量×500伤害，这张卡的攻击力上升那次伤害的数值。','\r',char(13)),'\n',char(10)),'特殊召唤','效果伤害','','','','','','','','','','','','','','');
