INSERT INTO texts VALUES(27107590,'时械巫女',replace(replace('①：自己场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：「时械神」怪兽上级召唤的场合，这张卡可以作为2只的数量解放。\r\n③：把这张卡解放才能发动。从卡组把1只攻击力0的「时械神」怪兽加入手卡。\r\n④：把墓地的这张卡除外才能发动。从卡组把1只攻击力0的「时械神」怪兽无视召唤条件特殊召唤。这个效果发动的回合，自己不能用这个效果以外把怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
