INSERT INTO texts VALUES(27240101,'耳边风风鸟',replace(replace('1星怪兽×2\r\n①：这张卡只要在怪兽区域存在，不受其他卡的效果影响。\r\n②：1回合1次，把这张卡2个超量素材取除才能发动。这个回合，这张卡不会被战斗破坏，这张卡的战斗发生的对自己的战斗伤害变成0。这个效果在对方回合也能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
