INSERT INTO texts VALUES(27415516,'水征龙-流龙','把1只龙族或者水属性的怪兽和这张卡从手卡丢弃才能发动。从卡组把1只「瀑征龙-潮龙」特殊召唤。这个效果特殊召唤的怪兽在这个回合不能攻击。「水征龙-流龙」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
