INSERT INTO texts VALUES(33184167,'同族感染病毒','①：丢弃1张手卡，宣言1个种族才能发动。场上的宣言种族的怪兽全部破坏。','宣言种族的怪兽全部破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33184236,'机巧屋敷','自己场上表侧表示存在的名字带有「机巧」的怪兽的表示形式变更时才能发动。选择场上存在的1张卡破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33198837,'自然兽',replace(replace('地属性调整＋调整以外的地属性怪兽1只以上\r\n只要这张卡在场上表侧表示存在，可以从自己卡组上面把2张卡送去墓地，魔法卡的发动无效并破坏。','\r',char(13)),'\n',char(10)),'魔法卡的发动无效并破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33225925,'久远之魔术师 米拉','①：这张卡召唤成功的场合，以对方场上1张里侧表示的卡为对象发动。把那张对方的卡确认。对方不能对应这个效果的发动把魔法·陷阱卡发动。','确认对方场上盖放的1张卡','请选择一张要确认的卡','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33236860,'黑羽-孤高之银风鸦',replace(replace('名字带有「黑羽」的调整＋调整以外的怪兽2只以上\r\n这张卡同调召唤成功时，可以选择场上表侧表示存在的最多2只持有比这张卡的攻击力低的守备力的怪兽破坏。这个效果发动的回合，自己不能进行战斗阶段。此外，对方回合只有1次，只要这张卡在自己场上表侧表示存在，自己场上存在的名字带有「黑羽」的怪兽不会被战斗破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33244944,'与艾克佐迪亚的契约','①：自己墓地有「被封印的艾克佐迪亚」「被封印者的右腕」「被封印者的左腕」「被封印者的右足」「被封印者的左足」存在的场合才能发动。从手卡把1只「艾克佐迪亚的亡灵」特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33245030,'球形栗子球',replace(replace('①：对方怪兽的攻击宣言时，把这张卡从手卡送去墓地才能发动。那只攻击怪兽变成守备表示。\r\n②：仪式召唤进行的场合，可以作为需要的等级数值的怪兽之内的1只，把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33248692,'子机猎手','自己场上的怪兽被战斗破坏送去墓地时发动。自己回复破坏怪兽的原本攻击力的数值的基本分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33252803,'升阶魔法-速攻混沌','①：以「混沌No.」怪兽以外的自己场上1只「No.」超量怪兽为对象才能发动。比那只自己怪兽阶级高1阶的1只持有相同「No.」数字的「混沌No.」怪兽在作为对象的怪兽上面重叠从额外卡组超量召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33256280,'炼装勇士·金驰',replace(replace('←1 【灵摆】 1→\r\n①：1回合1次，以这张卡以外的自己场上1张表侧表示的卡为对象才能发动。那张卡破坏，从卡组选1张「炼装」魔法·陷阱卡在自己场上盖放。\r\n【怪兽描述】\r\n闪耀着黄金车身的光芒，以豪爽的漂移跑法横扫敌军。尽管经常都很夸张地出现侧滑失控，但本人坚定立场地表示那就是必杀技。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33280639,'破坏剑士的摇篮',replace(replace('「破坏剑士的摇篮」在1回合只能发动1张。\r\n①：从卡组把「破坏剑士的摇篮」以外的1张「破坏剑」卡和1只「巴斯达·布雷达」怪兽送去墓地才能发动。从自己的额外卡组·墓地选1只「破戒蛮龙-破坏龙」特殊召唤。这个效果特殊召唤的怪兽在下个回合的结束阶段破坏。\r\n②：把墓地的这张卡除外才能发动。这个回合，自己场上的「破坏剑」卡不会被战斗·效果破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33282498,'弧光勇烈龙',replace(replace('①：这张卡从墓地的特殊召唤成功的场合才能发动。对方场上的表侧表示的魔法·陷阱卡全部除外，这张卡的攻击力·守备力上升这个效果除外的卡数量×200。\r\n②：这张卡被送去墓地的下个回合的准备阶段，以「弧光勇烈龙」以外的自己墓地1只7·8星的龙族怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33300669,'无形噬体·忧郁',replace(replace('←5 【灵摆】 5→\r\n这张卡的控制者在每次自己准备阶段把自己场上1只怪兽解放。或者不解放让这张卡破坏。\r\n①：只要自己场上有「无形噬体」怪兽存在，双方不能把魔法·陷阱·怪兽的效果连锁发动。\r\n【怪兽效果】\r\n①：只要灵摆召唤·反转过的这张卡在怪兽区域存在，双方不是「无形噬体」怪兽不能从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'是否解放自己场上1只怪兽？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33302407,'剧毒链','自己回合没有进行战斗的场合，结束阶段时可以把自己场上表侧表示存在的名字带有「链」的怪兽数量的卡从对方卡组上面送去墓地。','卡组送墓','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33323657,'念力之魂','把自己场上存在的1只念动力族怪兽解放发动。自己回复解放怪兽的等级×300的数值的基本分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33327029,'炼装反击',replace(replace('①：自己场上的卡被战斗·效果破坏的场合才能发动。从卡组把1只「炼装」怪兽特殊召唤。\r\n②：把墓地的这张卡除外才能发动。从自己的额外卡组把1只表侧表示的「炼装」灵摆怪兽加入手卡。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33334269,'DD 幽灵',replace(replace('①：这张卡被送去墓地的场合，以自己墓地1只「DD 幽灵」以外的「DD」怪兽或者1张「契约书」卡为对象才能发动。那1张同名卡从卡组送去墓地。\r\n②：这张卡被除外的场合，从除外的自己的卡之中以「DD 幽灵」以外的1只「DD」怪兽或者1张「契约书」卡为对象才能发动。那张卡回到墓地。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33347467,'灵魂护送船','这张卡不能通常召唤。把自己墓地存在的1只光属性怪兽从游戏中除外的场合可以特殊召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33365932,'火山弹','这张卡在墓地存在的场合，可以支付500基本分从卡组把1只「火山弹」加入手卡。这个效果1回合只有1次在自己的主要阶段才能使用。','把1只「火山弹」加入手牌','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33396948,'被封印的艾克佐迪亚','这张卡和「被封印者的右腕」「被封印者的左腕」「被封印者的右足」「被封印者的左足」在手卡全部齐集时，自己决斗胜利。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33413279,'爆破魔术师',replace(replace('调整＋调整以外的魔法师族怪兽1只以上\r\n可以把自己场上存在的2个魔力指示物取除，选择对方场上存在的1张魔法·陷阱卡破坏。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33413638,'蟑螂骑士','这张卡送去墓地时，这张卡回到卡组最上面。','返回卡组','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33420078,'僵尸带菌者','①：这张卡在墓地存在的场合，让1张手卡回到卡组最上面才能发动。这张卡从墓地特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33423043,'异次元的指名者','宣言1张卡的名称。确认对方手卡，若被宣言的卡在对方手卡中存在，则将其中1张被宣言的卡从游戏中除外；若被宣言的卡未在对方手卡中存在，则自己随机从游戏中除外1张手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33438666,'吸血鬼·红死神',replace(replace('①：自己场上的怪兽被战斗或者对方的效果破坏的场合，可以作为代替而支付那些破坏的怪兽数量×1000基本分。\r\n②：这张卡战斗破坏怪兽的战斗阶段结束时才能发动。那些怪兽从墓地尽可能往自己场上特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33453260,'漫画之手',replace(replace('自己场上有「卡通世界」存在的场合才能给对方场上的怪兽装备。\r\n①：得到装备怪兽的控制权。\r\n②：装备怪兽也当作卡通怪兽使用，对方场上没有卡通怪兽存在的场合，装备怪兽可以直接攻击。\r\n③：场上没有「卡通世界」存在的场合这张卡破坏。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33455338,'邪狂神的使者','对方的准备阶段时只有1次，可以把自己墓地存在的暗属性怪兽任意数量从游戏中除外。直到结束阶段时，这张卡的守备力上升这个效果除外的怪兽数量×500的数值。','攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33460840,'巨龙之守护骑士',replace(replace('①：这张卡召唤·特殊召唤成功的场合才能发动。从自己的手卡·墓地选1只7·8星的龙族怪兽当作装备卡使用给这张卡装备。\r\n②：这张卡的攻击力·守备力上升这张卡的效果装备的怪兽的各自数值的一半。\r\n③：把自己场上1只怪兽和这张卡解放，以自己墓地1只7·8星的龙族怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33508719,'变形壶','①：这张卡反转的场合发动。双方手卡全部丢弃。那之后，双方从卡组抽5张。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33537328,'地缚神 库西略','名字带有「地缚神」的怪兽在场上只能有1只表侧表示存在。场上没有表侧表示场地魔法卡存在的场合这张卡破坏。对方不能选择这张卡作为攻击对象。这张卡可以直接攻击对方玩家。自己场上表侧表示存在的这张卡被战斗破坏的场合，可以作为代替把自己场上存在的1只怪兽解放，对方基本分变成一半数值。','是否使用「地缚神 库西略」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33541430,'花札卫-月花见-',replace(replace('调整＋调整以外的怪兽2只\r\n①：1回合1次，自己主要阶段才能发动。自己从卡组抽1张，给双方确认。那是「花札卫」怪兽的场合，可以把那只怪兽无视召唤条件特殊召唤。这个效果特殊召唤的怪兽在这个回合可以直接攻击。这个效果发动的场合，下次的自己回合的抽卡阶段跳过。\r\n②：把场上的这张卡作为同调素材的场合，可以把包含这张卡的全部同调素材怪兽当作2星怪兽使用。','\r',char(13)),'\n',char(10)),'抽卡','是否特殊召唤？','是否把全部同调素材怪兽当作2星怪兽使用？','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33550694,'融合之门','只要这张卡在场上存在，可以代替「融合」魔法卡来使用融合召唤。这个情况融合素材的怪兽送去墓地并从游戏中除外。','融合召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33551032,'阴影无形鬼','这张卡的攻击让这张卡被战斗破坏送去墓地时，可以从卡组把2只名字带有「阴影」的怪兽特殊召唤。「阴影无形鬼」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33574806,'元素英雄 幽冥女郎',replace(replace('名字带有「元素英雄」的怪兽＋暗属性怪兽\r\n这张卡不用融合召唤不能特殊召唤。这张卡的攻击力上升自己墓地存在的名字带有「元素英雄」的怪兽数量×100的数值。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33609262,'帝王的深怨',replace(replace('「帝王的深怨」在1回合只能发动1张。\r\n①：把手卡1只攻击力2400/守备力1000的怪兽或者1只攻击力2800/守备力1000的怪兽给对方观看才能发动。从卡组把「帝王的深怨」以外的1张「帝王」魔法·陷阱卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33611061,'武神集结','自己场上没有这张卡以外的卡存在的场合才能发动。自己墓地的名字带有「武神」的兽战士族怪兽全部回到卡组，自己手卡全部送去墓地。那之后，可以从卡组把最多3只卡名不同的名字带有「武神」的兽战士族怪兽加入手卡。「武神集结」在1回合只能发动1张。','是否还要选择名字带有「武神」的兽战士族怪兽？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33621868,'铁球巨人','振舞着带刺的铁球进行攻击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33622465,'救护部队',replace(replace('「救护部队」的②的效果1回合只能使用1次。\r\n①：以自己墓地1只通常怪兽为对象才能发动。那只怪兽加入手卡。\r\n②：这张卡在墓地存在，通常怪兽被战斗破坏时才能发动。这张卡变成通常怪兽（战士族·地·3星·攻1200/守400）在怪兽区域守备表示特殊召唤（不当作陷阱卡使用）。这个效果特殊召唤的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33655493,'暗之侯爵 贝利亚','只要这张卡在场上表侧表示存在，对方不能把自己场上存在的除「暗之侯爵 贝利亚」外的其他怪兽选择作为攻击对象，也不能作为魔法·陷阱的效果的对象。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33656832,'曲芸之魔术师',replace(replace('←2 【灵摆】 2→\r\n「曲芸之魔术师」的灵摆效果1回合只能使用1次。\r\n①：自己场上的怪兽被效果破坏时才能发动。灵摆区域的这张卡特殊召唤。\r\n【怪兽效果】\r\n①：魔法·陷阱卡的发动无效的场合才能发动。这张卡从手卡特殊召唤。\r\n②：这张卡被战斗破坏时才能发动。这张卡在自己的灵摆区域放置。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33665663,'炎舞-「开阳」','这张卡发动的回合，自己场上的兽战士族怪兽向守备表示怪兽攻击时，若攻击力超过那个守备力，给与对方基本分那个数值的战斗伤害。此外，只要这张卡在场上存在，自己场上的兽战士族怪兽的攻击力上升300。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33691040,'尖刺龙','「虎纹龙」＋「火焰毒蛇」','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33695750,'死亡蚊','这张卡召唤、特殊召唤成功时，给这张卡放置2个指示物。这个效果每放置有1个指示物，这张卡的攻击力上升500。这张卡被战斗破坏的场合，把这张卡的1个指示物取除作代替。','放置指示物','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33698022,'月华龙 黑蔷薇',replace(replace('调整＋调整以外的怪兽1只以上\r\n这张卡特殊召唤成功时或者对方场上有5星以上的怪兽特殊召唤时发动。选择对方场上1只特殊召唤的怪兽回到持有者手卡。「月华龙 黑蔷薇」的效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'回到手卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33725002,'V火精灵','这张卡召唤成功时，可以选择自己墓地1只名字带有「希望皇 霍普」的怪兽特殊召唤。自己的主要阶段时，场上的这只怪兽可以给自己的「混沌No.39 希望皇 霍普雷V」装备。这张卡装备中的场合，1回合1次，把装备怪兽1个超量素材取除才能发动。装备怪兽的效果无效，对方场上的怪兽全部破坏，给与对方基本分那个数量×1000的数值的伤害。','特殊召唤','装备','破坏并伤害','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33725271,'火山充能','自己墓地存在的最多3张名字带有「火山」的怪兽卡回到卡组。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33731070,'暗黑界的尖兵 贝基','这张卡因为其他卡的效果从手卡丢弃去墓地的场合，这张卡特殊召唤到自己场上。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33734439,'2人3脚僵尸','瘦子和胖子，友好的骸骨二人组，走起路来很艰难。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33737664,'盗墓者的报应','每次自己的准备阶段，每存在1只除外的对方怪兽，对方受到100分的伤害。','LP伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33746252,'威光魔人','这张卡不能特殊召唤。只要这张卡在场上表侧表示存在，双方不能把效果怪兽的效果发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33767325,'死亡流星','给与对方基本分1000分伤害。对方基本分是3000以下的场合这张卡不能发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33776734,'羽翼栗子球 LV9','当有连锁发生时，可以从自己手卡把这张卡特殊召唤。只要这张卡在自己场上表侧表示存在，双方发动的魔法卡不送去墓地从游戏中除外。这张卡的攻击力·守备力变成对方墓地存在的魔法卡数量×500的数值。「羽翼栗子球 LV9」在自己场上只能有1张表侧表示存在。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33776843,'混沌No.15 机关傀儡-连环杀手',replace(replace('9星怪兽×3\r\n1回合1次，把这张卡1个超量素材取除，选择对方场上1张卡才能发动。选择的卡破坏。破坏的卡是怪兽的场合，再给与对方基本分那只怪兽的原本攻击力数值的伤害。','\r',char(13)),'\n',char(10)),'破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33782437,'一时休战','①：双方玩家各自从卡组抽1张。直到下次的对方回合结束时，双方受到的全部伤害变成0。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33784505,'盗壶者','「强欲之壶」发动时才能发动。使「强欲之壶」的效果无效，从自己卡组抽1张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33823832,'娱乐伙伴 火围巾狮',replace(replace('←5 【灵摆】 5→\r\n①：自己场上的灵摆怪兽被战斗破坏时才能发动。灵摆区域的这张卡特殊召唤。\r\n【怪兽效果】\r\n①：1回合1次，自己的灵摆怪兽战斗破坏对方怪兽的伤害计算后才能发动。那只自己怪兽直到战斗阶段结束时攻击力上升200，只再1次可以继续攻击。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33833230,'娱乐伙伴 贴纸海豹鳗',replace(replace('←3 【灵摆】 3→\r\n①：1回合1次，以对方场上1只表侧表示怪兽为对象才能发动。那只怪兽的效果直到回合结束时无效。\r\n【怪兽效果】\r\n①：这张卡召唤·特殊召唤成功时，以对方场上盖放的1张魔法·陷阱卡为对象才能发动。这个回合，那张卡不能发动。双方不能对应这个效果的发动把魔法·陷阱·怪兽的效果发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33846209,'二重电光','把自己场上表侧表示存在的1只4星的二重怪兽解放，选择场上存在的1张卡发动。选择的卡破坏，从自己卡组抽1张卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33866130,'自然壁','这张卡从场上送去墓地时，可以从自己卡组把1只4星以下的名字带有「自然」的怪兽在自己场上表侧攻击表示特殊召唤。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33875961,'暗黑投射手','自己的准备阶段时这张卡守备表示的场合，这张卡放置1个指示物。可以从自己墓地除外和指示物同数量的卡，破坏场上和除外的卡片数同数量的魔法·陷阱卡。之后这张卡的指示物全部取除。','放置指示物','破坏','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33878931,'撞击兽','动作意外地敏捷，把身体卷成圆形进行撞击。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33883834,'紫炎的寄子','自己场上存在的名字带有「六武众」的怪兽进行战斗的场合，那次伤害计算时把这张卡从手卡送去墓地发动。那只怪兽在这个回合不会被战斗破坏。','不被战斗破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33900648,'清净世界',replace(replace('这张卡的控制者在每次自己的结束阶段支付500基本分。或者不支付500基本分让这张卡破坏。双方被所控制属性的以下适用。\r\n●光：自己把手卡全部持续公开。\r\n●暗：自己场上的怪兽有2只以上的场合，自己不能攻击宣言。\r\n●地：自己的准备阶段时，把自己1只表侧守备表示怪兽破坏。\r\n●水：自己的结束阶段时，自己手卡丢弃1张。\r\n●炎：自己的结束阶段时，自己受到1000分伤害。\r\n●风：自己不支付500基本分则不能把魔法卡发动。','\r',char(13)),'\n',char(10)),'是否要支付500LP维持「清净世界」？','破坏','丢弃手牌','1000伤害','','','','','','','','','','','','');
INSERT INTO texts VALUES(33904024,'强欲的碎片',replace(replace('①：只要这张卡在魔法与陷阱区域存在，每次自己抽卡阶段通常抽卡，给这张卡放置1个强欲指示物。\r\n②：把有强欲指示物2个以上放置的这张卡送去墓地才能发动。自己从卡组抽2张。','\r',char(13)),'\n',char(10)),'抽卡','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33909817,'森罗的姬芽宫',replace(replace('1星怪兽×2\r\n「森罗的姬芽宫」的①②的效果1回合各能使用1次。\r\n①：把这张卡1个超量素材取除才能发动。自己卡组最上面的卡翻开。翻开的卡是魔法·陷阱卡的场合，那张卡加入手卡。不是的场合，那张卡送去墓地。\r\n②：从手卡以及这张卡以外的自己场上的表侧表示怪兽之中把1只植物族怪兽送去墓地，以自己墓地1只「森罗」怪兽为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'翻开卡组','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33911264,'太阳风帆船','自己场上没有怪兽存在的场合，这张卡可以从手卡特殊召唤。这个方法特殊召唤的这张卡的原本的攻击力·守备力变成一半。此外，每次自己的准备阶段这张卡的等级上升1星。「太阳风帆船」在场上只能有1只表侧表示存在。','等级上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33950246,'王宫的号令','全部的反转效果的怪兽的发动和效果无效化。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33951077,'超级战狮','需要「狮子的仪式」召唤。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33964637,'影六武众-理伴',replace(replace('属性不同的「六武众」怪兽×3\r\n把自己场上的上记卡送去墓地的场合才能从额外卡组特殊召唤（不需要「融合」）。这张卡不能作为融合素材。\r\n①：1回合1次，从手卡以及自己场上的表侧表示的卡之中把1张「六武众」卡除外，以场上1张卡为对象才能发动。那张卡除外。\r\n②：自己场上的「六武众」怪兽被战斗·效果破坏的场合，可以作为代替把墓地的这张卡除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33970665,'废铜烂铁的意志','选择在自己墓地存在的3只名字带有「废铁」的怪兽发动。对方选择那些中的1只。那只怪兽在自己或对方场上特殊召唤，其余的卡从游戏中除外。','在自己场上特殊召唤','在对方场上特殊召唤','请选择3只名字带有「废铁」的怪兽','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33972299,'次世代地热能人',replace(replace('「次世代控制员」＋调整以外的地属性怪兽1只以上\r\n只要自己场上有4星以下的名字带有「次世代」的怪兽表侧表示存在，可以让这张卡的原本攻击力·守备力直到结束阶段时交换。这个效果1回合只能使用1次。','\r',char(13)),'\n',char(10)),'攻守交换','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33977496,'千根针','守备表示的这张卡被对方怪兽攻击时，若这张卡的守备力比对方攻击怪兽的攻击力高，则伤害步骤终了时那只攻击怪兽破坏。','破坏','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(33981008,'魔导书院 拉迈松',replace(replace('①：自己场上或者自己墓地有魔法师族怪兽存在的场合，自己准备阶段才能发动。从自己墓地选「魔导书院 拉迈松」以外的1张「魔导书」魔法卡回到卡组最下面，自己从卡组抽1张。\r\n②：这张卡被对方破坏送去墓地时才能发动。把持有自己墓地的「魔导书」魔法卡数量以下的等级的1只魔法师族怪兽从手卡·卡组特殊召唤。','\r',char(13)),'\n',char(10)),'返回卡组并抽卡','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34002992,'虹之生命','丢弃1张手卡。直到这个回合的结束阶段时，自己受到的伤害无效，并回复那个数值的基本分。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34004470,'大土星','这张卡不能作从手卡·卡组的特殊召唤。丢弃1张手卡并支付1000基本分。直到结束阶段时这张卡的攻击力上升1000。这个效果1回合只有1次在自己的主要阶段才能使用。对方控制的卡的效果把这张卡破坏送去墓地时，双方受到那个攻击力数值的伤害。','攻击上升','伤害','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34016756,'原力','①：以场上2只表侧表示怪兽为对象才能发动。直到回合结束时，作为对象的1只怪兽的攻击力变成一半，另1只怪兽的攻击力上升那个数值。','请选择攻击力变成一半的怪兽','请选择攻击力上升的怪兽','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34022290,'守护者·艾托斯',replace(replace('①：自己墓地没有怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：把这张卡装备的自己场上1张装备魔法卡送去墓地，以对方墓地最多3只怪兽为对象才能发动。那些怪兽除外。这张卡的攻击力直到回合结束时上升这个效果除外的怪兽数量×500。','\r',char(13)),'\n',char(10)),'除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34026662,'进化的奇迹','选择名字带有「进化虫」的怪兽的效果特殊召唤的1只怪兽发动。这个回合，选择的怪兽不会被战斗以及卡的效果破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34029630,'漆黑的能量石','发动后，给这张卡放置3个魔力指示物。自己回合1次，可以把这张卡放置的1个魔力指示物取除，给场上表侧表示存在的这张卡以外的1张可以放置魔力指示物的卡放置1个魔力指示物。这张卡上放置的魔力指示物全无时，这张卡破坏。','是否现在使用「漆黑的能量石」的效果？','指示物转移','请选择要放置魔力指示物的卡','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34079868,'真龙剑士 卓辉星·拼图',replace(replace('这张卡不能通常召唤。把自己场上的「龙剑士」怪兽和「龙魔王」怪兽各1只解放的场合才能特殊召唤。\r\n①：1回合1次，魔法·陷阱·怪兽的效果发动时才能发动。那个发动无效并破坏。\r\n②：场上的这张卡被对方破坏的场合才能发动。从卡组把「龙剑士」怪兽和「龙魔王」怪兽各1只特殊召唤。','\r',char(13)),'\n',char(10)),'效果发动无效','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34086406,'熔岩谷锁链龙',replace(replace('4星怪兽×2\r\n①：1回合1次，可以把这张卡1个超量素材取除，从以下效果选择1个发动。\r\n●从卡组选1张卡送去墓地。\r\n●从卡组选1只怪兽在卡组最上面放置。','\r',char(13)),'\n',char(10)),'选择一个效果发动','从自己卡组选择1张卡送去墓地。','从自己卡组选择1只怪兽在卡组最上面放置。','请选择放置到卡组最上方的怪兽','','','','','','','','','','','','');
INSERT INTO texts VALUES(34088136,'究极昆虫 LV3','「究极昆虫 LV1」的效果特殊召唤的场合，只要这张卡在场上存在，对方全部怪兽的攻击力下降300。自己回合的准备阶段时，可以把表侧表示的这张卡送去墓地，从卡组·手卡特殊召唤1只「究极昆虫 LV5」。（召唤·特殊召唤·反转的回合不能使用此效果）','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34100324,'鹰身少女','梦想有一天能以美丽的姿态华丽飞舞、敏锐攻击的鹰身少女。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34103656,'忘却之都 利莫里亚','这张卡的卡名当作「海」使用。只要这张卡在场上存在，场上的水属性怪兽的攻击力·守备力上升200。此外，1回合1次，自己的主要阶段时才能发动。只要这张卡在场上存在，自己场上的水属性怪兽的等级直到结束阶段时上升和自己场上的水属性怪兽数量相同数值。','等级上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34109611,'大薰风飞隼',replace(replace('调整＋调整以外的名字带有「薰风」的怪兽1只以上\r\n这张卡同调召唤成功时，场上表侧表示存在的名字带有「薰风」的全部怪兽的攻击力上升600。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34116027,'龙骑兵团骑士-赤枪龙骑士',replace(replace('龙族调整＋调整以外的鸟兽族怪兽1只以上\r\n①：1回合1次，自己主要阶段才能发动。从卡组把4星以下的1只龙族或者鸟兽族的怪兽加入手卡。那之后，从手卡选1只龙族或者鸟兽族的怪兽丢弃。','\r',char(13)),'\n',char(10)),'检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34124316,'电子壶','反转：场上的怪兽全部破坏。翻开双方卡组最上面5张卡，这些卡中的4星以下的怪兽卡全部表侧攻击表示或里侧守备表示在场上特殊召唤。这些以外的卡全部加入手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34143852,'英豪挑战者 赠剑兵',replace(replace('这张卡为素材的超量怪兽得到以下效果。\r\n●这次超量召唤成功时，这张卡的攻击力上升1000。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34149830,'死力的组队换手','自己场上表侧攻击表示存在的怪兽被战斗破坏的伤害计算时，可以把那次战斗发生的对自己的战斗伤害变成0，那次伤害步骤结束时从手卡把1只4星以下的战士族怪兽特殊召唤。','战斗伤害变成0','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34160055,'使龙炎剑的高手','自己场上有「使龙炎剑的高手」以外的怪兽召唤时，可以把那只怪兽的等级上升1星，这张卡的攻击力直到结束阶段时上升300。','等级攻击上升','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34187685,'二重攻击','从手卡丢弃1张怪兽卡去墓地。选择自己场上1只比丢弃怪兽等级低的怪兽。选择的那只怪兽在这个回合可以攻击2次。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34193084,'来自黑暗的恐怖','当这张卡被对方的效果从手卡或卡组送去墓地时，这张卡特殊召唤上场。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34206604,'魔导科学家','支付1000基本分，就可以从自己的融合卡组中特殊召唤1只6星以下的融合怪兽。这只融合怪兽不能对对方进行直接攻击，回合结束时回到融合卡组。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34230233,'暗黑界的龙神 格拉法','这张卡可以让「暗黑界的龙神 格拉法」以外的自己场上表侧表示存在的1只名字带有「暗黑界」的怪兽回到手卡，从墓地特殊召唤。这张卡被卡的效果从手卡丢弃去墓地的场合，选择对方场上存在的1张卡破坏。被对方的卡的效果丢弃的场合，再把对方手卡随机1张确认。确认的卡是怪兽的场合，可以把那只怪兽在自己场上特殊召唤。','对方场上存在的1张卡破坏','是否要特殊召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34236961,'打赌胜负','双方各自选择1张手卡并展示，相互确认等级。选择等级较高卡的一方将选出的卡放回手卡，选择等级较低卡的一方受到1000点伤害，将选出的卡送去墓地。选择怪兽卡以外的卡时，等级强制计为0。双方选择的卡等级相同时，各自将选出的卡放回手卡。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34250214,'吸血鬼的使魔',replace(replace('这个卡名的①②的效果1回合各能使用1次。\r\n①：这张卡特殊召唤成功的场合，支付500基本分才能发动。从卡组把「吸血鬼的使魔」以外的1只「吸血鬼」怪兽加入手卡。\r\n②：这张卡在墓地存在的场合，从手卡以及自己场上的表侧表示的卡之中把1张「吸血鬼」卡送去墓地才能发动。这张卡特殊召唤。这个效果特殊召唤的这张卡从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34251483,'哥布林冲锋部队','这张卡的攻击宣言时，对方不能把魔法·陷阱·效果怪兽的效果发动。这张卡攻击的场合，战斗阶段结束时变成守备表示，直到下次的自己回合结束时这张卡不能把表示形式变更。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34257001,'冲刺战士','这张卡攻击的场合，伤害步骤内这张卡的攻击力上升1200。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34267821,'古遗物-圣枪',replace(replace('①：这张卡可以当作魔法卡使用从手卡到魔法与陷阱区域盖放。\r\n②：魔法与陷阱区域盖放的这张卡在对方回合被破坏送去墓地的场合发动。这张卡特殊召唤。\r\n③：把手卡或者自己场上的这张卡解放才能发动。这个回合，双方不能把卡除外。这个效果在对方回合才能发动。','\r',char(13)),'\n',char(10)),'特殊召唤','双方不能把卡除外','','','','','','','','','','','','','','');
