INSERT INTO texts VALUES(34294855,'诅咒之吸血鬼','这张卡被战斗破坏送去墓地时，支付500基本分才能发动。下个回合的准备阶段时，这张卡从墓地特殊召唤。此外，这个效果特殊召唤成功时发动。这张卡的攻击力上升500。','特殊召唤','攻击上升','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34302287,'幻煌龙的战涡',replace(replace('场上有「海」存在的场合，这张卡的发动从手卡也能用。\r\n①：自己场上的怪兽只有通常怪兽的场合，以对方场上1张卡为对象才能发动。那张卡破坏。\r\n②：把墓地的这张卡除外，以自己场上1只通常怪兽为对象才能发动。那只怪兽可以装备的自己场上的全部「幻煌龙」装备魔法卡给那只通常怪兽装备。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34318086,'柠檬魔术少女',replace(replace('①：1回合1次，把「柠檬魔术少女」以外的自己场上1只「魔术少女」怪兽解放才能发动。从卡组把1只魔法师族怪兽加入手卡。\r\n②：1回合1次，这张卡被选择作为攻击对象的场合才能发动。从手卡把1只魔法师族怪兽效果无效特殊召唤。那之后，攻击对象转移为那只怪兽，攻击怪兽的攻击力变成一半。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
