INSERT INTO texts VALUES(34379489,'娱乐伙伴 泡泡斗牛犬',replace(replace('←5 【灵摆】 5→\r\n①：灵摆怪兽以外的从额外卡组特殊召唤的自己场上的表侧表示怪兽被战斗·效果破坏的场合，可以作为代替把这张卡破坏。\r\n【怪兽效果】\r\n①：这张卡从额外卡组的特殊召唤成功时才能发动。这个回合，从额外卡组特殊召唤的自己场上的灵摆怪兽不会被效果破坏。','\r',char(13)),'\n',char(10)),'是否使用「娱乐伙伴 泡泡斗牛犬」的效果代替破坏？','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34408491,'魔王龙 蝇王',replace(replace('暗属性调整＋调整以外的怪兽1只以上\r\n这张卡不会被战斗以及卡的效果破坏。此外，这张卡的战斗或者对方的卡的效果让自己受到伤害时发动。这张卡的攻击力上升那次伤害的数值。','\r',char(13)),'\n',char(10)),'攻击上升','','','','','','','','','','','','','','','');
