INSERT INTO texts VALUES(34475451,'工作列车 红色信号','①：对方怪兽的攻击宣言时才能发动。这张卡从手卡特殊召唤，那只对方怪兽的攻击对象转移为这张卡进行伤害计算。这张卡不会被那次战斗破坏。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34487429,'虹之古代都市',replace(replace('①：得到自己的魔法与陷阱区域的「宝玉兽」卡数量的以下效果。\r\n●1张以上：这张卡不会被效果破坏。\r\n●2张以上：1回合1次，可以把对自己的战斗伤害变成一半。\r\n●3张以上：魔法·陷阱卡的发动时，把自己场上1只表侧表示的「宝玉兽」怪兽送去墓地才能发动。那个发动无效并破坏。\r\n●4张以上：1回合1次，可以发动。自己抽1张。\r\n●5张：1回合1次，以自己的魔法与陷阱区域1张「宝玉兽」卡为对象才能发动。那张卡特殊召唤。','\r',char(13)),'\n',char(10)),'伤害减半','魔法·陷阱发动无效并破坏','从自己卡组抽1张卡','把魔法与陷阱卡区域的1张「宝玉兽」卡特殊召唤','','','','','','','','','','','','');
INSERT INTO texts VALUES(34492631,'废品巨人',replace(replace('①：对方场上有5星以上的怪兽存在的场合，这张卡可以从手卡特殊召唤。\r\n②：这张卡为素材的同调召唤不会被无效化，在那次同调召唤成功时对方不能把魔法·陷阱·怪兽的效果发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34496660,'超重武者 大八-8',replace(replace('「超重武者 大八-8」的③的效果1回合只能使用1次。\r\n①：这张卡召唤·特殊召唤成功时才能发动。这张卡的表示形式变更。\r\n②：这张卡可以用表侧守备表示的状态作出攻击。那个场合，这张卡用守备力当作攻击力使用进行伤害计算。\r\n③：自己墓地没有魔法·陷阱卡存在的场合才能发动。自己场上的表侧守备表示的这张卡变成攻击表示，从卡组把1只「超重武者装留」怪兽加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
