INSERT INTO texts VALUES(34680482,'魔偶甜点·果冻天使',replace(replace('这个卡名的①的效果1回合只能使用1次。\r\n①：把这张卡解放才能发动。从卡组把1只「魔偶甜点」怪兽特殊召唤。这个效果特殊召唤的怪兽不会被战斗破坏，下次的自己回合的结束阶段回到持有者卡组。\r\n②：这张卡被对方破坏送去墓地的场合发动。这张卡回到卡组。','\r',char(13)),'\n',char(10)),'返回卡组','特殊召唤','「魔偶甜点·果冻天使」效果适用中','','','','','','','','','','','','','');
