INSERT INTO texts VALUES(34761062,'业火之重骑士',replace(replace('①：这张卡只要在场上·墓地存在，当作通常怪兽使用。\r\n②：可以把场上的当作通常怪兽使用的这张卡作为通常召唤作再1次召唤。那个场合这张卡变成当作效果怪兽使用并得到以下效果。\r\n●这张卡向特殊召唤的怪兽攻击的伤害步骤开始时才能发动。那只怪兽除外。','\r',char(13)),'\n',char(10)),'除外','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34767865,'电脑网仪式',replace(replace('电子界族仪式怪兽的降临必需。\r\n①：等级合计直到变成仪式召唤的怪兽的等级以上为止，把自己的手卡·场上的怪兽解放，从手卡把1只电子界族仪式怪兽仪式召唤。\r\n②：自己场上没有怪兽存在的场合，从自己墓地把这张卡和1只仪式怪兽除外才能发动。在自己场上把2只「电脑网衍生物」（电子界族·光·4星·攻/守0）特殊召唤。这个效果在这张卡送去墓地的回合不能发动。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
