INSERT INTO texts VALUES(34822850,'炼狱的泛烂',replace(replace('①：自己准备阶段才能把这个效果发动。在自己场上把1只「狱火机衍生物」（恶魔族·炎·1星·攻/守0）特殊召唤。\r\n②：「狱火机」怪兽用自身的方法特殊召唤的场合，从自己场上也能把「狱火机」怪兽除外。\r\n③：对方不能把除自己场上的等级最高的「狱火机」怪兽以外的自己的「狱火机」怪兽作为攻击对象，也不能作为效果的对象。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
