INSERT INTO texts VALUES(34906152,'质量加速器','每祭掉自己场上1只怪兽，给与对方基本分400分的伤害。','给予对方400分伤害','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34945480,'外神 阿撒托斯',replace(replace('5星怪兽×3\r\n这张卡也能在自己场上的「外神」超量怪兽上面把这张卡重叠来超量召唤。这张卡不能作为超量召唤的素材。\r\n①：这张卡超量召唤成功的回合，对方不能把怪兽的效果发动。\r\n②：这张卡有融合·同调·超量怪兽全部在作为超量素材的场合，把这张卡1个超量素材取除才能发动。对方场上的卡全部破坏。','\r',char(13)),'\n',char(10)),'对方场上的卡全部破坏','是否在自己场上的「外神」超量怪兽上面重叠来超量召唤？','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34961968,'幻兽 雷霆飞马','对方怪兽的攻击宣言时，把墓地的这张卡从游戏中除外，选择自己场上1只名字带有「幻兽」的怪兽才能发动。这个回合，选择的自己怪兽不会被战斗破坏。','不被战斗破坏','请选择一只名字带有「幻兽」的怪兽','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(34968834,'暗黑界的鬼神 凯勒特','这张卡被卡的效果从手卡丢弃去墓地的场合，这张卡从墓地特殊召唤。被对方的卡的效果丢弃的场合，可以再从自己卡组把1只恶魔族怪兽在自己或者对方场上特殊召唤。','特殊召唤','是否要从自己卡组特殊召唤？','在自己场上特殊召唤','在对方场上特殊召唤','','','','','','','','','','','','');
INSERT INTO texts VALUES(34989413,'复制梁龙',replace(replace('怪兽2只\r\n这个卡名的效果1回合只能使用1次。\r\n①：可以从以下效果选择1个发动。\r\n●宣言1个种族才能发动。这张卡所连接区的全部表侧表示怪兽的种族直到回合结束时变成宣言的种族。\r\n●宣言1个属性才能发动。这张卡所连接区的全部表侧表示怪兽的属性直到回合结束时变成宣言的属性。','\r',char(13)),'\n',char(10)),'改变种族','改变属性','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35011819,'帝之敕命','①：需怪兽召唤成功时发动的怪兽的效果发动时才能把这个效果发动。那个发动无效。那之后，发动无效的玩家从卡组抽1张。','无效并抽卡','是否要把怪兽的效果的发动无效？','发动同时使用效果','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35014241,'破限疾驰',replace(replace('「破限疾驰」在1回合只能发动1张。\r\n①：让自己场上1只同调怪兽调整和1只调整以外的同调怪兽回到额外卡组才能发动。和那2只怪兽的等级合计相同等级的1只同调怪兽无视召唤条件从额外卡组特殊召唤。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35027493,'魔之卡组破坏病毒','①：把自己场上1只攻击力2000以上的暗属性怪兽解放才能发动。对方场上的怪兽，对方手卡，用对方回合计算的3回合内对方抽到的卡全部确认，那之内的攻击力1500以下的怪兽全部破坏。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35035481,'伯吉斯异兽·拟油栉虫',replace(replace('①：以场上1张魔法·陷阱卡为对象才能发动。那张卡破坏。\r\n②：陷阱卡发动时，连锁那个发动才能把这个效果在墓地发动。这张卡变成通常怪兽（水族·水·2星·攻1200/守0）在怪兽区域特殊召唤（不当作陷阱卡使用）。这个效果特殊召唤的这张卡不受怪兽的效果影响，从场上离开的场合除外。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35037880,'剑之反射',replace(replace('「剑之反射」在1回合只能发动1张。\r\n①：自己场上有「X-剑士」怪兽存在，自己因战斗·效果受到伤害时才能发动。自己基本分回复受到的伤害的数值，给与对方那个数值的伤害。那之后，可以从卡组把1张「剑士」魔法·陷阱卡或者1张「加特姆士」卡加入手卡。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35050257,'电子幼体','场上表侧表示存在的这张卡被选择作为攻击对象时，这个回合战斗发生的对自己的战斗伤害变成0。这张卡被战斗破坏送去墓地时，可以从自己卡组把1只「电子幼体」在自己场上特殊召唤。','战斗伤害变成0','特殊召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35052053,'甲虫装甲骑士','在昆虫战士之中，属于精英中的精英的{无死虫团}的精锐骑士。不能无视它们强大的战斗能力。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35058588,'方界波动',replace(replace('①：以自己场上1只「方界」怪兽和对方场上1只表侧表示怪兽为对象才能发动。那只自己怪兽的攻击力变成2倍，那只对方怪兽的攻击力变成一半。\r\n②：从自己墓地把这张卡和「方界」怪兽任意数量除外，以除外的「方界」怪兽数量的对方场上的表侧表示怪兽为对象才能发动。给那些怪兽各放置1个方界指示物。有方界指示物放置的怪兽不能攻击，效果无效化。','\r',char(13)),'\n',char(10)),'改变攻击力','放置指示物','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35058857,'奇术王 月星',replace(replace('把这张卡作为同调素材的场合，不是暗属性怪兽的同调召唤不能使用。\r\n①：自己场上有调整存在的场合，这张卡可以从手卡特殊召唤。\r\n②：这张卡召唤·特殊召唤成功的场合，以自己的场上·墓地1只怪兽为对象才能发动。这张卡的等级直到回合结束时变成和那只怪兽相同。这个效果的发动后，直到回合结束时自己不能作同调召唤以外的特殊召唤。','\r',char(13)),'\n',char(10)),'等级变更','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35059553,'皇帝斗技场','当这张卡的控制者场上有1只以上的怪兽的场合，对方可以出场的怪兽数量，不能超过这张卡的控制者场上的怪兽数量。这张卡发动前已经存在于场上的卡不受这张卡的效果所影响。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35073065,'幻想夺取者','①：自己对怪兽的上级召唤成功时才能发动。这张卡从手卡特殊召唤。这个效果特殊召唤的这张卡的种族·属性·等级变成和上级召唤的那只怪兽相同。','特殊召唤','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35089369,'风龙星-蒲牢',replace(replace('「风龙星-蒲牢」的①的效果1回合只能使用1次。\r\n①：自己场上的这张卡被战斗·效果破坏送去墓地时才能发动。从卡组把「风龙星-蒲牢」以外的1只「龙星」怪兽攻击表示特殊召唤。\r\n②：1回合1次，对方的主要阶段以及战斗阶段才能发动。只用自己场上的「龙星」怪兽为同调素材作同调召唤。\r\n③：这张卡为同调素材的同调怪兽不受魔法卡的效果影响。','\r',char(13)),'\n',char(10)),'特殊召唤','「风龙星-蒲牢」效果适用中','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35112613,'光子栗子','把这张卡从手卡送去墓地，支付2000基本分才能发动。这个回合自己受到的全部伤害变成0。这个效果在对方回合也能发动。此外，这张卡在墓地存在的场合，把「光子栗子」以外的1只名字带有「光子」的怪兽从手卡送去墓地才能发动。墓地的这张卡加入手卡。「光子栗子」的这个效果1回合只能使用1次。','伤害变成0','返回手牌','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35125879,'真龙皇的复活',replace(replace('「真龙皇的复活」的①②③的效果1回合各能使用1次，作为①②的效果在同一连锁上不能发动。\r\n①：以自己墓地1只「真龙」怪兽为对象才能发动。那只怪兽守备表示特殊召唤。这个效果的发动后，直到回合结束时自己不能把怪兽特殊召唤。\r\n②：对方主要阶段才能发动。把1只「真龙」怪兽表侧表示上级召唤。\r\n③：这张卡从魔法与陷阱区域送去墓地的场合，以场上1只怪兽为对象才能发动。那只怪兽破坏。','\r',char(13)),'\n',char(10)),'墓地苏生','上级召唤','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35146019,'幻变骚灵物化',replace(replace('这个卡名的②的效果1回合只能使用1次。\r\n①：以自己墓地1只「幻变骚灵」怪兽为对象才能把这张卡发动。那只怪兽攻击表示特殊召唤，把这张卡当作装备卡使用给那只怪兽装备。这张卡离开场上时那只怪兽破坏。\r\n②：把墓地的这张卡除外，以自己墓地1张「幻变骚灵」陷阱卡为对象才能发动。那张卡加入手卡。','\r',char(13)),'\n',char(10)),'','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35149085,'家畜虐杀','自己场上表侧表示存在的1只兽族怪兽回手卡，从手卡特殊召唤1只和回手怪兽等级相同的兽族怪兽上场。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35183853,'镜鸣的武神','自己场上有名字带有「武神」的兽战士族怪兽存在，对方场上的怪兽数量比自己场上的怪兽数量多的场合，主要阶段1的开始时才能发动。直到下次的对方回合结束时，双方不能把魔法·陷阱卡的效果发动。','','','','','','','','','','','','','','','','');
INSERT INTO texts VALUES(35191415,'黑幻想之魔术师',replace(replace('「黑幻想之魔术师」的①③的效果1回合各能使用1次。\r\n①：自己在对方回合把魔法·陷阱卡的效果发动的场合才能发动。这张卡从手卡特殊召唤。\r\n②：这张卡只要在怪兽区域存在，卡名当作「黑魔术师」使用。\r\n③：只在这张卡在场上表侧表示存在才有1次，自己把魔法·陷阱卡的效果发动的场合以自己墓地1只「黑魔术师」为对象才能发动。那只怪兽特殊召唤。','\r',char(13)),'\n',char(10)),'这张卡从手卡特殊召唤','自己墓地1只「黑魔术师」特殊召唤','','','','','','','','','','','','','','');
