INSERT INTO texts VALUES(35220244,'重力炮','机械族怪兽才能装备。1回合1次，可以让装备怪兽的攻击力上升400。此外，装备怪兽和对方怪兽进行战斗的场合，只在战斗阶段内那只对方怪兽的效果无效化。','攻击上升','','','','','','','','','','','','','','','');
