INSERT INTO texts VALUES(35537251,'燃烧拳击手 假想敌拳手','自己的主要阶段时才能发动。把自己场上的名字带有「燃烧拳击手」的超量怪兽1个超量素材取除，这张卡从手卡特殊召唤。「燃烧拳击手 假想敌拳手」的效果1回合只能使用1次。','特殊召唤','','','','','','','','','','','','','','','');
