INSERT INTO texts VALUES(38124994,'毛绒动物·兔子',replace(replace('「毛绒动物·兔子」的效果1回合只能使用1次。\r\n①：这张卡成为融合召唤的素材送去墓地的场合，以自己墓地1只「锋利小鬼·剪刀」或者1只「毛绒动物·兔子」以外的「毛绒动物」怪兽为对象才能发动。那只怪兽加入手卡。','\r',char(13)),'\n',char(10)),'卡组检索','','','','','','','','','','','','','','','');
